/**
 * @file MLX Integration for Marketplace
 * @description Production-ready MLX model integration for semantic search and safety
 */

import type { ChildProcess } from "node:child_process";
import { unlink, writeFile } from "node:fs/promises";
import os from "node:os";
import path from "node:path";
import { type ServerManifest, ServerManifestSchema } from "../types.js";

export interface MLXConfig {
	modelsPath: string;
	pythonPath: string;
	embeddingModel: "qwen3-0.6b" | "qwen3-4b" | "qwen3-8b";
	enabled: boolean;
}

export interface EmbeddingResult {
	embedding: number[];
	model: string;
	dimensions: number;
}

export interface SafetyResult {
	safe: boolean;
	categories: string[];
	confidence: number;
}

export interface SemanticSearchResult {
	server: ServerManifest;
	similarity: number;
	relevanceScore: number;
}

export const createMLXService = (config: MLXConfig) => {
	if (!config.enabled) return null;

	const runGenerateEmbedding = async (
		text: string,
	): Promise<EmbeddingResult> => {
		const modelSize = config.embeddingModel.replace("qwen3-", "").toUpperCase();
		const script = `
import json, sys
import mlx.core as mx
from transformers import AutoTokenizer, AutoModel

model_name = "Qwen/Qwen3-Embedding-${modelSize}"
text = """${text.replace(/"/g, '\\"').replace(/\n/g, " ")}"""

try:
    tokenizer = AutoTokenizer.from_pretrained(model_name, cache_dir="${config.modelsPath}", local_files_only=True, trust_remote_code=True)
    model = AutoModel.from_pretrained(model_name, cache_dir="${config.modelsPath}", local_files_only=True, trust_remote_code=True)
    inputs = tokenizer(text, return_tensors="np", padding=True, truncation=True, max_length=512)
    input_ids = mx.array(inputs["input_ids"])
    attention_mask = mx.array(inputs["attention_mask"])
    outputs = model(input_ids=input_ids, attention_mask=attention_mask)
    mask = mx.expand_dims(attention_mask, -1)
    summed = mx.sum(outputs.last_hidden_state * mask, axis=1)
    counts = mx.sum(mask, axis=1)
    embedding = (summed / mx.maximum(counts, 1e-9)).tolist()[0]
    print(json.dumps({"embedding": embedding, "dimensions": len(embedding), "model": model_name}))
except Exception as e:
    print(json.dumps({"error": str(e)}), file=sys.stderr)
    sys.exit(1)
`;
		const result = await executeMLXScript(script, config.pythonPath);
		return parseEmbeddingResult(result, config.embeddingModel);
	};

	const semanticSearch = async (
		query: string,
		servers: ServerManifest[],
	): Promise<SemanticSearchResult[]> => {
		const queryEmbedding = await runGenerateEmbedding(query);
		const results = await Promise.all(
			servers.map(async (server) => {
				let validated: ServerManifest;
				try {
					validated = ServerManifestSchema.parse(server);
				} catch {
					return { server, similarity: 0, relevanceScore: 0 };
				}
				const serverText = `${validated.name} ${validated.description} ${validated.tags?.join(" ") || ""}`;
				const serverEmbedding = await runGenerateEmbedding(serverText);
				const similarity = cosineSimilarity(
					queryEmbedding.embedding,
					serverEmbedding.embedding,
				);
				const relevanceScore = calculateRelevanceScore(similarity, validated);
				return { server: validated, similarity, relevanceScore };
			}),
		);
		return results.sort((a, b) => b.relevanceScore - a.relevanceScore);
	};

	const rerank = async (
		query: string,
		servers: ServerManifest[],
	): Promise<SemanticSearchResult[]> => {
		const script = `
import json, sys
import mlx.core as mx
from transformers import AutoTokenizer, AutoModelForSequenceClassification

data = json.loads(sys.stdin.read())
query = data["query"]
texts = data["docs"]
model_name = "Qwen/Qwen3-Reranker-4B"

try:
    tokenizer = AutoTokenizer.from_pretrained(model_name, cache_dir="${config.modelsPath}", local_files_only=True, trust_remote_code=True)
    model = AutoModelForSequenceClassification.from_pretrained(model_name, cache_dir="${config.modelsPath}", local_files_only=True, trust_remote_code=True)
    scores = []
    for doc in texts:
        inputs = tokenizer(query, doc, return_tensors="np", padding=True, truncation=True, max_length=512)
        ids = mx.array(inputs["input_ids"])
        mask = mx.array(inputs["attention_mask"])
        logits = model(input_ids=ids, attention_mask=mask).logits
        score = float(mx.sigmoid(logits)[0][0])
        scores.append(score)
    print(json.dumps({"scores": scores}))
except Exception as e:
    print(json.dumps({"error": str(e)}), file=sys.stderr)
    sys.exit(1)
`;
		const docs = servers.map(
			(s) => `${s.name} ${s.description} ${s.tags?.join(" ") || ""}`,
		);
		const payload = JSON.stringify({ query, docs });
		const result = await executeMLXScriptWithInput(
			script,
			config.pythonPath,
			payload,
		);
		const scores = JSON.parse(result).scores as number[];
		return servers
			.map((server, i) => ({
				server,
				similarity: 0,
				relevanceScore: scores[i] || 0,
			}))
			.sort((a, b) => b.relevanceScore - a.relevanceScore);
	};

	const validateSafety = async (content: string): Promise<SafetyResult> => {
		const script = `
import re
from typing import List, Tuple

UNSAFE_PATTERNS = {
    'violence': [r'\\b(kill|murder|weapon|bomb|attack|violence)\\b'],
    'hate_speech': [r'\\b(hate|racist|discrimination|offensive)\\b'],
    'illegal_activity': [r'\\b(illegal|criminal|fraud|piracy|hack|crack|exploit)\\b'],
    'privacy_violation': [r'\\b(steal.*data|exfiltrat|unauthorized.*access)\\b'],
    'system_access': [r'\\b(system.*exec|root.*access|admin.*privilege)\\b']
}

SAFE_INDICATORS = [
    r'\\b(education|learning|help|assist|development|programming)\\b',
    r'\\b(open.*source|MIT|Apache|GPL|documentation)\\b'
]

content = """${content.replace(/"/g, '\\"').replace(/\n/g, " ")}"""
flagged = []
conf_scores = []
for cat, pats in UNSAFE_PATTERNS.items():
    matches = sum(len(re.findall(p, content.lower(), re.IGNORECASE)) for p in pats)
    if matches > 0:
        flagged.append(cat)
        conf_scores.append(min(matches * 0.3, 0.9))

safe_inds = sum(len(re.findall(p, content.lower(), re.IGNORECASE)) for p in SAFE_INDICATORS)
is_safe = len(flagged) == 0 or safe_inds > len(flagged)
confidence = 0.8 + min(safe_inds * 0.05, 0.2) if is_safe else (sum(conf_scores) / len(conf_scores) if conf_scores else 0.5)
print(f"SAFETY_RESULT:{str(is_safe).lower()}")
print(f"CATEGORIES:{','.join(flagged)}")
print(f"CONFIDENCE:{confidence:.3f}")
`;
		try {
			const result = await executeMLXScript(script, config.pythonPath);
			return parseSafetyResult(result);
		} catch (error) {
			console.warn("MLX safety validation failed:", error);
			return { safe: true, categories: [], confidence: 0.5 };
		}
	};

	return {
		generateEmbedding: runGenerateEmbedding,
		semanticSearch,
		rerank,
		validateSafety,
	};
};

async function executeMLXScript(
        script: string,
        pythonPath: string,
): Promise<string> {
        const tmpDir = os.tmpdir();
        const scriptPath = path.join(tmpDir, `mlx-script-${Date.now()}.py`);
        await writeFile(scriptPath, script);
        // Use the centralized Python spawner so env merging and PYTHONPATH handling are consistent
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        const { spawnPythonProcess } = await import(
                "../../../../libs/python/exec.js"
        );
        try {
                return await new Promise((resolve, reject) => {
                        const child: ChildProcess = spawnPythonProcess([scriptPath], {
                                python: pythonPath,
                        });
                        let output = "";
                        let error = "";
                        child.stdout?.on("data", (d) => {
                                output += d.toString();
                        });
                        child.stderr?.on("data", (d) => {
                                error += d.toString();
                        });
                        child.on("close", (code) =>
                                code === 0
                                        ? resolve(output)
                                        : reject(new Error(`Script failed: ${error}`)),
                        );
                        const to = setTimeout(() => {
                                try {
                                        child.kill();
                                } catch {}
                                reject(new Error("Script timeout"));
                        }, 10000);
                        // clear timeout if process exits
                        child.on("exit", () => clearTimeout(to));
                });
        } finally {
                await unlink(scriptPath).catch(() => {});
        }
}

async function executeMLXScriptWithInput(
        script: string,
        pythonPath: string,
        input: string,
): Promise<string> {
        const tmpDir = os.tmpdir();
        const scriptPath = path.join(tmpDir, `mlx-script-${Date.now()}.py`);
        await writeFile(scriptPath, script);
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        const { spawnPythonProcess } = await import(
                "../../../../libs/python/exec.js"
        );
        try {
                return await new Promise((resolve, reject) => {
                        const child: ChildProcess = spawnPythonProcess([scriptPath], {
                                python: pythonPath,
                        });
                        let output = "";
                        let error = "";
                        child.stdout?.on("data", (d) => {
                                output += d.toString();
                        });
                        child.stderr?.on("data", (d) => {
                                error += d.toString();
                        });
                        child.on("close", (code) =>
                                code === 0
                                        ? resolve(output)
                                        : reject(new Error(`Script failed: ${error}`)),
                        );
                        if (child.stdin) {
                                child.stdin.write(input);
                                child.stdin.end();
                        }
                        const to = setTimeout(() => {
                                try {
                                        child.kill();
                                } catch {}
                                reject(new Error("Script timeout"));
                        }, 10000);
                        child.on("exit", () => clearTimeout(to));
                });
        } finally {
                await unlink(scriptPath).catch(() => {});
        }
}

function parseEmbeddingResult(output: string, model: string): EmbeddingResult {
	const parsed = JSON.parse(output.trim());
	return { embedding: parsed.embedding, model, dimensions: parsed.dimensions };
}

function parseSafetyResult(output: string): SafetyResult {
	const safeRe = /SAFETY_RESULT:(true|false)/;
	const categoriesRe = /CATEGORIES:(.*)/;
	const confidenceRe = /CONFIDENCE:([\d.]+)/;
	const safeMatch = safeRe.exec(output);
	const categoriesMatch = categoriesRe.exec(output);
	const confidenceMatch = confidenceRe.exec(output);
	return {
		safe: safeMatch ? safeMatch[1] === "true" : true,
		categories: categoriesMatch
			? categoriesMatch[1].split(",").filter(Boolean)
			: [],
		confidence: confidenceMatch ? parseFloat(confidenceMatch[1]) : 0.5,
	};
}

function cosineSimilarity(a: number[], b: number[]): number {
	if (a.length !== b.length) return 0;
	let dot = 0;
	let normA = 0;
	let normB = 0;
	for (let i = 0; i < a.length; i++) {
		dot += a[i] * b[i];
		normA += a[i] * a[i];
		normB += b[i] * b[i];
	}
	return dot / (Math.sqrt(normA) * Math.sqrt(normB));
}

function calculateRelevanceScore(
	similarity: number,
	server: ServerManifest,
): number {
	let score = similarity * 0.6;
	if (server.featured) score += 0.2;
	if (server.publisher?.verified) score += 0.1;
	if (server.rating && server.rating > 4) score += 0.1;
	return Math.min(score, 1.0);
}

/**
 * @file MCP Marketplace Registry
 * @description Registry management for MCP marketplace servers
 */
import { sha256 } from "@noble/hashes/sha256";
import { bytesToHex } from "@noble/hashes/utils";
import Fuse from "fuse.js";
import { existsSync } from "node:fs";
import { mkdir, readFile, writeFile } from "node:fs/promises";
import * as path from "node:path";
import type {
	ApiResponse,
	RegistryIndex,
	SearchRequest,
	ServerHealth,
	ServerManifest,
} from "./types.js";
import { RegistryIndexSchema, ServerManifestSchema } from "./types.js";

// Security: Allowlisted domains for marketplace registries
const ALLOWED_REGISTRY_DOMAINS = [
	"registry.cortex-os.dev",
	"marketplace.cortex-os.com",
	"api.cortex-os.com",
	"localhost",
	"127.0.0.1",
	"::1",
	// Add trusted registry domains here
];

/**
 * Security: Validate registry URL to prevent SSRF attacks
 */
export function validateRegistryUrl(url: string): boolean {
	try {
		const parsedUrl = new URL(url);

		// Only allow HTTP/HTTPS protocols
		if (!["http:", "https:"].includes(parsedUrl.protocol)) {
			return false;
		}

		// Check against allowlist
		const hostname = parsedUrl.hostname.toLowerCase();
		return ALLOWED_REGISTRY_DOMAINS.includes(hostname);
	} catch {
		return false;
	}
}

export class MarketplaceRegistry {
	private registry: RegistryIndex | null = null;
	private searchIndex: Fuse<ServerManifest> | null = null;
	private healthStatus = new Map<string, ServerHealth>();
	private lastUpdate: Date | null = null;

	constructor(
		private registryUrl: string = "https://registry.cortex-os.dev/v1/registry.json",
		private cacheDir: string = "./.cortex/registry/cache",
	) { }

	/**
	 * Initialize the registry by loading from cache or fetching
	 */
	async initialize(): Promise<void> {
		await this.ensureCacheDir();

		try {
			await this.loadFromCache();

			// Fetch updates if cache is stale
			if (this.isCacheStale()) {
				await this.fetchRegistry();
			}
		} catch (error) {
			// eslint-disable-next-line no-console
			console.warn(
				"Failed to load from cache, fetching fresh registry:",
				error,
			);
			await this.fetchRegistry();
		}

		this.buildSearchIndex();
	}

	/**
	 * Fetch registry from remote URL
	 */
	async fetchRegistry(): Promise<void> {
		try {
			// Security: Validate URL to prevent SSRF attacks
			if (!validateRegistryUrl(this.registryUrl)) {
				throw new Error(
					`Invalid registry URL rejected for security: ${this.registryUrl}`,
				);
			}

			const response = await fetch(this.registryUrl, {
				redirect: 'manual',
				referrerPolicy: 'no-referrer',
				signal: AbortSignal.timeout(30000),
			});
			if (!response.ok) {
				throw new Error(
					`Failed to fetch registry: ${response.status} ${response.statusText}`,
				);
			}

			const data: unknown = await response.json();
			const result = RegistryIndexSchema.safeParse(data);

			if (!result.success) {
				throw new Error(`Invalid registry format: ${result.error.message}`);
			}

			this.registry = result.data;
			this.lastUpdate = new Date();

			// Cache the registry
			await this.saveToCache();

			// eslint-disable-next-line no-console
			console.log(
				`âœ… Registry updated: ${this.registry.serverCount} servers available`,
				` (checksum: ${this.getRegistryChecksum()})`,
			);
		} catch (error) {
			// eslint-disable-next-line no-console
			console.error("Failed to fetch registry:", error);
			throw error;
		}
	}

	/**
	 * Get the full registry
	 */
	getRegistry(): RegistryIndex | null {
		return this.registry;
	}

	/**
	 * Get a specific server by ID
	 */
	getServer(id: string): ServerManifest | null {
		if (!this.registry) return null;

		return this.registry.servers.find((server) => server.id === id) || null;
	}

	/**
	 * Search servers with filters
	 */
	async searchServers(
		request: SearchRequest,
	): Promise<ApiResponse<ServerManifest[]>> {
		if (!this.registry) {
			return {
				success: false,
				error: {
					code: "REGISTRY_NOT_LOADED",
					message: "Registry not initialized",
				},
			};
		}

		let results = [...this.registry.servers];

		// Text search using Fuse.js
		if (request.q && this.searchIndex) {
			const searchResults = this.searchIndex.search(request.q);
			results = searchResults.map((result) => result.item);
		}

		// Category filter
		if (request.category) {
			results = results.filter(
				(server) => server.category === request.category,
			);
		}

		// Capabilities filter
		if (request.capabilities && request.capabilities.length > 0) {
			results = results.filter((server) =>
				request.capabilities?.every((cap) => server.capabilities[cap]),
			);
		}

		// Verified filter
		if (request.verified !== undefined) {
			results = results.filter(
				(server) => server.publisher.verified === request.verified,
			);
		}

		// Sort by featured first, then by downloads
		results.sort((a, b) => {
			if (a.featured && !b.featured) return -1;
			if (!a.featured && b.featured) return 1;
			return b.downloads - a.downloads;
		});

		// Pagination
		const total = results.length;
		const paginatedResults = results.slice(
			request.offset,
			request.offset + request.limit,
		);

		return {
			success: true,
			data: paginatedResults,
			meta: {
				total,
				offset: request.offset,
				limit: request.limit,
			},
		};
	}

	/**
	 * Get featured servers
	 */
	getFeaturedServers(): ServerManifest[] {
		if (!this.registry) return [];

		return this.registry.servers.filter((server) => server.featured);
	}

	/**
	 * Get servers by category
	 */
	getServersByCategory(category: string): ServerManifest[] {
		if (!this.registry) return [];

		return this.registry.servers.filter(
			(server) => server.category === category,
		);
	}

	/**
	 * Get server health status
	 */
	getServerHealth(serverId: string): ServerHealth | null {
		return this.healthStatus.get(serverId) || null;
	}

	/**
	 * Update server health status
	 */
	updateServerHealth(health: ServerHealth): void {
		this.healthStatus.set(health.serverId, health);
	}

	/**
	 * Validate a server manifest
	 */
	validateServer(manifest: unknown): { valid: boolean; errors: string[] } {
		const result = ServerManifestSchema.safeParse(manifest);

		if (result.success) {
			return { valid: true, errors: [] };
		}

		return {
			valid: false,
			errors: result.error.errors.map(
				(err) => `${err.path.join(".")}: ${err.message}`,
			),
		};
	}

	/**
	 * Get registry statistics
	 */
	getStatistics() {
		if (!this.registry) return null;

		const stats = {
			totalServers: this.registry.serverCount,
			categories: Object.keys(this.registry.categories).length,
			featuredServers: this.registry.featured.length,
			verifiedPublishers: this.registry.servers.filter(
				(s) => s.publisher.verified,
			).length,
			lastUpdated: this.lastUpdate?.toISOString(),
			capabilities: {
				tools: this.registry.servers.filter((s) => s.capabilities.tools).length,
				resources: this.registry.servers.filter((s) => s.capabilities.resources)
					.length,
				prompts: this.registry.servers.filter((s) => s.capabilities.prompts)
					.length,
			},
			riskLevels: {
				low: this.registry.servers.filter((s) => s.security.riskLevel === "low")
					.length,
				medium: this.registry.servers.filter(
					(s) => s.security.riskLevel === "medium",
				).length,
				high: this.registry.servers.filter(
					(s) => s.security.riskLevel === "high",
				).length,
			},
		};

		return stats;
	}

	/**
	 * Private methods
	 */
	private async ensureCacheDir(): Promise<void> {
		if (!existsSync(this.cacheDir)) {
			await mkdir(this.cacheDir, { recursive: true });
		}
	}

	private get cacheFile(): string {
		return path.join(this.cacheDir, "registry.json");
	}

	private get metaFile(): string {
		return path.join(this.cacheDir, "meta.json");
	}

	private async loadFromCache(): Promise<void> {
		if (!existsSync(this.cacheFile) || !existsSync(this.metaFile)) {
			throw new Error("Cache files not found");
		}

		const [registryData, metaData] = await Promise.all([
			readFile(this.cacheFile, "utf-8"),
			readFile(this.metaFile, "utf-8"),
		]);

		const registry: unknown = JSON.parse(registryData);
		const meta: unknown = JSON.parse(metaData);

		const result = RegistryIndexSchema.safeParse(registry);
		if (!result.success) {
			throw new Error("Invalid cached registry format");
		}

		this.registry = result.data;
		this.lastUpdate = new Date((meta as { lastUpdate: string }).lastUpdate);
	}

	private async saveToCache(): Promise<void> {
		if (!this.registry || !this.lastUpdate) return;

		const meta = {
			lastUpdate: this.lastUpdate.toISOString(),
			checksum: this.getRegistryChecksum(),
		};

		await Promise.all([
			writeFile(this.cacheFile, JSON.stringify(this.registry, null, 2)),
			writeFile(this.metaFile, JSON.stringify(meta, null, 2)),
		]);
	}

	private isCacheStale(): boolean {
		if (!this.lastUpdate) return true;

		const cacheAge = Date.now() - this.lastUpdate.getTime();
		const maxAge = 5 * 60 * 1000; // 5 minutes

		return cacheAge > maxAge;
	}

	private buildSearchIndex(): void {
		if (!this.registry) return;

		this.searchIndex = new Fuse(this.registry.servers, {
			keys: [
				{ name: "name", weight: 0.4 },
				{ name: "description", weight: 0.3 },
				{ name: "tags", weight: 0.2 },
				{ name: "publisher.name", weight: 0.1 },
			],
			threshold: 0.4,
			includeScore: true,
		});
	}

	private getRegistryChecksum(): string {
		if (!this.registry) return "";

		const content = JSON.stringify(this.registry);
		const hash = sha256(content);
		return bytesToHex(hash);
	}
}

/**
 * @file Category Routes
 * @description API routes for server categories
 */

import type { FastifyInstance } from "fastify";
import { DEFAULT_LIMIT, MAX_LIMIT } from "../constants.js";

export async function categoryRoutes(fastify: FastifyInstance): Promise<void> {
	// Get all categories
	fastify.get(
		"/categories",
		{
			schema: {
				tags: ["categories"],
				summary: "List categories",
				description: "Get all server categories with counts and descriptions",
				response: {
					200: {
						type: "object",
						properties: {
							success: { type: "boolean" },
							data: {
								type: "object",
								properties: {
									categories: {
										type: "object",
										additionalProperties: {
											type: "object",
											properties: {
												name: { type: "string" },
												count: { type: "integer" },
												description: { type: "string" },
											},
										},
									},
								},
							},
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			const categories = await fastify.marketplaceService.getCategories();

			return {
				success: true,
				data: {
					categories,
				},
			};
		},
	);

	// Get servers by category
	fastify.get(
		"/categories/:category/servers",
		{
			schema: {
				tags: ["categories"],
				summary: "Get servers by category",
				description: "Get all servers in a specific category",
				params: {
					type: "object",
					properties: {
						category: { type: "string" },
					},
					required: ["category"],
				},
				querystring: {
					type: "object",
					properties: {
						limit: {
							type: "integer",
							minimum: 1,
							maximum: MAX_LIMIT,
							default: DEFAULT_LIMIT,
						},

