/**
 * @file Health Routes
 * @description Health check endpoints
 */

import type { FastifyInstance } from "fastify";

export async function healthRoutes(fastify: FastifyInstance): Promise<void> {
	// Basic health check
	fastify.get(
		"/health",
		{
			schema: {
				tags: ["health"],
				summary: "Health check",
				description: "Basic health check endpoint",
				response: {
					200: {
						type: "object",
						properties: {
							status: { type: "string" },
							timestamp: { type: "string" },
							uptime: { type: "number" },
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			return {
				status: "healthy",
				timestamp: new Date().toISOString(),
				uptime: process.uptime(),
			};
		},
	);

	// Detailed health check
	fastify.get(
		"/health/detailed",
		{
			schema: {
				tags: ["health"],
				summary: "Detailed health check",
				description: "Detailed health check with system information",
				response: {
					200: {
						type: "object",
						properties: {
							status: { type: "string" },
							timestamp: { type: "string" },
							uptime: { type: "number" },
							memory: { type: "object" },
							registries: { type: "object" },
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			const memoryUsage = process.memoryUsage();

			// Check registry health
			const registries = await fastify.registryService.listRegistries();
			const registryHealth = registries.reduce(
				(acc, registry) => {
					acc[registry.name] = {
						healthy: registry.healthy,
						lastUpdated: registry.lastUpdated,
						serverCount: registry.serverCount,
					};
					return acc;
				},
				{} as Record<string, unknown>,
			);

			return {
				status: "healthy",
				timestamp: new Date().toISOString(),
				uptime: process.uptime(),
				memory: {
					rss: Math.round(memoryUsage.rss / 1024 / 1024),
					heapTotal: Math.round(memoryUsage.heapTotal / 1024 / 1024),
					heapUsed: Math.round(memoryUsage.heapUsed / 1024 / 1024),
					external: Math.round(memoryUsage.external / 1024 / 1024),
				},
				registries: registryHealth,
			};
		},
	);

	// Readiness check
	fastify.get(
		"/ready",
		{
			schema: {
				tags: ["health"],
				summary: "Readiness check",
				description: "Check if service is ready to accept requests",
				response: {
					200: {
						type: "object",
						properties: {
							ready: { type: "boolean" },
							checks: { type: "object" },
						},
					},
					503: {
						type: "object",
						properties: {
							ready: { type: "boolean" },
							checks: { type: "object" },
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			const checks: Record<string, boolean> = {};
			let allReady = true;

			// Check if at least one registry is available
			try {
				const registries = await fastify.registryService.listRegistries();
				const healthyRegistries = registries.filter((r) => r.healthy);
				checks.registries = healthyRegistries.length > 0;
				if (!checks.registries) allReady = false;
			} catch (_error) {
				checks.registries = false;
				allReady = false;
			}

			// Check memory usage (fail if over 90% heap used)
			const memoryUsage = process.memoryUsage();
			const heapUsagePercent = memoryUsage.heapUsed / memoryUsage.heapTotal;
			checks.memory = heapUsagePercent < 0.9;
			if (!checks.memory) allReady = false;

			const status = allReady ? 200 : 503;

			return reply.status(status).send({
				ready: allReady,
				checks,
			});
		},
	);
}

/**
 * @file Registry Routes
 * @description API routes for registry management
 */

import type { FastifyInstance } from "fastify";

export async function registryRoutes(fastify: FastifyInstance): Promise<void> {
	// List all registries
	fastify.get(
		"/registries",
		{
			schema: {
				tags: ["registries"],
				summary: "List registries",
				description: "Get list of all configured registries with their status",
				response: {
					200: {
						type: "object",
						properties: {
							success: { type: "boolean" },
							data: {
								type: "array",
								items: {
									type: "object",
									properties: {
										name: { type: "string" },
										url: { type: "string" },
										healthy: { type: "boolean" },
										lastUpdated: { type: "string" },
										serverCount: { type: "integer" },
									},
								},
							},
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			const registries = await fastify.registryService.listRegistries();

			return {
				success: true,
				data: registries,
			};
		},
	);

	// Get registry status
	fastify.get(
		"/registries/:name/status",
		{
			schema: {
				tags: ["registries"],
				summary: "Get registry status",
				description: "Get health status and metadata for a specific registry",
				params: {
					type: "object",
					properties: {
						name: { type: "string" },
					},
					required: ["name"],
				},
				response: {
					200: {
						type: "object",
						properties: {
							success: { type: "boolean" },
							data: {
								type: "object",
								properties: {
									healthy: { type: "boolean" },
									lastUpdated: { type: "string" },
									error: { type: "string" },
								},
							},
						},
					},
					404: {
						type: "object",
						properties: {
							success: { type: "boolean" },
							error: {
								type: "object",
								properties: {
									code: { type: "string" },
									message: { type: "string" },
								},
							},
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			const { name } = request.params as { name: string };

			try {
				const status = await fastify.registryService.getRegistryStatus(name);

				return {
					success: true,
					data: status,
				};
			} catch (error) {
				if (error instanceof Error && error.message.includes("not found")) {
					return reply.status(404).send({
						success: false,
						error: {
							code: "REGISTRY_NOT_FOUND",
							message: `Registry '${name}' not found`,
						},
					});
				}
				throw error;
			}
		},
	);

	// Refresh registry cache
	fastify.post(
		"/registries/:name/refresh",
		{
			schema: {
				tags: ["registries"],
				summary: "Refresh registry",
				description: "Force refresh of registry data, clearing cache",
				params: {
					type: "object",
					properties: {
						name: { type: "string" },
					},
					required: ["name"],
				},
				response: {
					200: {
						type: "object",
						properties: {
							success: { type: "boolean" },
							message: { type: "string" },
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			const { name } = request.params as { name: string };

			try {
				await fastify.registryService.refreshRegistry(name);

				return {
					success: true,
					message: `Registry '${name}' refreshed successfully`,
				};
			} catch (error) {
				if (error instanceof Error && error.message.includes("not found")) {
					return reply.status(404).send({
						success: false,
						error: {
							code: "REGISTRY_NOT_FOUND",
							message: `Registry '${name}' not found`,
						},
					});
				}
				throw error;
			}
		},
	);

	// Refresh all registries
	fastify.post(
		"/registries/refresh",
		{
			schema: {
				tags: ["registries"],
				summary: "Refresh all registries",
				description: "Force refresh of all registry data, clearing all caches",
				response: {
					200: {
						type: "object",
						properties: {
							success: { type: "boolean" },
							message: { type: "string" },
							results: {
								type: "object",
								properties: {
									refreshed: { type: "array", items: { type: "string" } },
									failed: { type: "array", items: { type: "string" } },
								},
							},
						},
					},
				},
			},
		},
		async (_request, _reply) => {
			const registryNames = Object.keys(
				fastify.registryService.config.registries,
			);
			const results = { refreshed: [] as string[], failed: [] as string[] };

			await Promise.allSettled(
				registryNames.map(async (name) => {
					try {
						await fastify.registryService.refreshRegistry(name);
						results.refreshed.push(name);
					} catch (error) {
						results.failed.push(name);
						fastify.log.error(`Failed to refresh registry ${name}:`, error);
					}
				}),
			);

			return {
				success: true,
				message: `Refreshed ${results.refreshed.length} of ${registryNames.length} registries`,
				results,
			};
		},
	);
}

