	password: z.string().min(1),
});

const registerSchema = z.object({
	name: z.string().min(1),
	email: z.string().email(),
	password: z.string().min(8),
});

export class AuthController {
	static login(req: Request, res: Response): void {
		try {
			const { email, password } = loginSchema.parse(req.body);

			const result = AuthService.login(email, password);
			if (!result) {
				throw new HttpError(401, 'Invalid email or password');
			}

			res.json(result);
		} catch (error) {
			if (error instanceof z.ZodError) {
				res
					.status(400)
					.json({ error: 'Validation failed', details: error.errors });
			} else if (error instanceof HttpError) {
				res.status(error.statusCode).json({ error: error.message });
			} else {
				res.status(500).json({ error: 'Internal server error' });
			}
		}
	}

	static register(req: Request, res: Response): void {
		try {
			const { name, email, password } = registerSchema.parse(req.body);

			const result = AuthService.register(name, email, password);
			res.status(201).json(result);
		} catch (error) {
			if (error instanceof z.ZodError) {
				res
					.status(400)
					.json({ error: 'Validation failed', details: error.errors });
			} else if (
				error instanceof Error &&
				error.message.includes('already exists')
			) {
				res.status(409).json({ error: error.message });
			} else {
				res.status(500).json({ error: 'Internal server error' });
			}
		}
	}

	static async logout(req: Request, res: Response): Promise<void> {
		try {
			// In a real implementation, we might want to blacklist the token
			// For now, we'll just return success
			res.json({ message: 'Logged out successfully' });
		} catch (_error) {
			res.status(500).json({ error: 'Internal server error' });
		}
	}
}

// Conversation controller for Cortex WebUI backend

import type { Response } from 'express';
import { z } from 'zod';
import type { AuthRequest } from '../middleware/auth';
import { HttpError } from '../middleware/errorHandler';
import { ConversationService } from '../services/conversationService';

// Validation schemas
const createConversationSchema = z.object({
	title: z.string().min(1).max(100),
});

const updateConversationSchema = z.object({
	title: z.string().min(1).max(100).optional(),
});

const conversationIdSchema = z.object({
	id: z.string().uuid(),
});

export class ConversationController {
	static getConversations(req: AuthRequest, res: Response): void {
		try {
			if (!req.user) {
				throw new HttpError(401, 'Unauthorized');
			}

			const conversations = ConversationService.getConversationsByUserId(
				req.user.userId,
			);
			res.json(conversations);
		} catch (_error) {
			res.status(500).json({ error: 'Internal server error' });
		}
	}

	static getConversationById(req: AuthRequest, res: Response): void {
		try {
			if (!req.user) {
				throw new HttpError(401, 'Unauthorized');
			}

			const { id } = conversationIdSchema.parse(req.params);
			const conversation = ConversationService.getConversationById(id);

			if (!conversation) {
				throw new HttpError(404, 'Conversation not found');
			}

			// Check if user owns this conversation
			if (conversation.userId !== req.user.userId) {
				throw new HttpError(403, 'Forbidden');
			}

			res.json(conversation);
		} catch (error) {
			if (error instanceof z.ZodError) {
				res
					.status(400)
					.json({ error: 'Validation failed', details: error.errors });
			} else if (error instanceof HttpError) {
				res.status(error.statusCode).json({ error: error.message });
			} else {
				res.status(500).json({ error: 'Internal server error' });
			}
		}
	}

	static createConversation(req: AuthRequest, res: Response): void {
		try {
			if (!req.user) {
				throw new HttpError(401, 'Unauthorized');
			}

			const { title } = createConversationSchema.parse(req.body);
			const conversation = ConversationService.createConversation(
				req.user.userId,
				title,
			);
			res.status(201).json(conversation);
		} catch (error) {
			if (error instanceof z.ZodError) {
				res
					.status(400)
					.json({ error: 'Validation failed', details: error.errors });
			} else {
				res.status(500).json({ error: 'Internal server error' });
			}
		}
	}

	static updateConversation(req: AuthRequest, res: Response): void {
		try {
			if (!req.user) {
				throw new HttpError(401, 'Unauthorized');
			}

			const { id } = conversationIdSchema.parse(req.params);
			const updates = updateConversationSchema.parse(req.body);

			const conversation = ConversationService.getConversationById(id);
			if (!conversation) {
				throw new HttpError(404, 'Conversation not found');
			}

			// Check if user owns this conversation
			if (conversation.userId !== req.user.userId) {
				throw new HttpError(403, 'Forbidden');
			}

			const updatedConversation = ConversationService.updateConversation(
				id,
				updates,
			);
			res.json(updatedConversation);
		} catch (error) {
			if (error instanceof z.ZodError) {
				res
					.status(400)
					.json({ error: 'Validation failed', details: error.errors });
			} else if (error instanceof HttpError) {
				res.status(error.statusCode).json({ error: error.message });
			} else {
				res.status(500).json({ error: 'Internal server error' });
			}
		}
	}

	static deleteConversation(req: AuthRequest, res: Response): void {
		try {
			if (!req.user) {
				throw new HttpError(401, 'Unauthorized');
			}

			const { id } = conversationIdSchema.parse(req.params);
			const conversation = ConversationService.getConversationById(id);

			if (!conversation) {
				throw new HttpError(404, 'Conversation not found');
			}

			// Check if user owns this conversation
			if (conversation.userId !== req.user.userId) {
				throw new HttpError(403, 'Forbidden');
			}

			ConversationService.deleteConversation(id);
			res.json({ message: 'Conversation deleted successfully' });
		} catch (error) {
			if (error instanceof z.ZodError) {
				res
					.status(400)
					.json({ error: 'Validation failed', details: error.errors });
			} else if (error instanceof HttpError) {
				res.status(error.statusCode).json({ error: error.message });
			} else {
				res.status(500).json({ error: 'Internal server error' });
			}
		}
	}
}

import type { Request, Response } from 'express';
import multer from 'multer';
import pdf from 'pdf-parse';
import type { DocumentParseResult } from '../types/document';

// File size limits (50MB for documents)
const MAX_FILE_SIZE = 50 * 1024 * 1024;
const MAX_PAGES = 100;
const MAX_TEXT_LENGTH = 50000;

// Multer configuration for document uploads
const storage = multer.memoryStorage();
export const documentUploadMiddleware = multer({
	storage,
	limits: {
		fileSize: MAX_FILE_SIZE,
	},
	fileFilter: (_req, file, cb) => {
		const allowedMimes = [
			'application/pdf',
			'text/plain',
			'text/markdown',
			'image/jpeg',
			'image/png',
			'image/gif',
			'image/webp',
		];

		const allowedExtensions = [
			'.pdf',
			'.txt',
			'.md',
			'.markdown',
			'.jpg',
			'.jpeg',
			'.png',
			'.gif',
			'.webp',
		];
		const extension = file.originalname
			.toLowerCase()
			.substring(file.originalname.lastIndexOf('.'));

		if (
			allowedMimes.includes(file.mimetype) ||
			allowedExtensions.includes(extension)
		) {
			cb(null, true);
		} else {
			cb(null, false);
		}
	},
});

export class DocumentController {
	/**
	 * Parse document (PDF, text, markdown, or image) and extract content
	 */
	static async parseDocument(req: Request, res: Response) {
		try {
			if (!req.file) {
				return res.status(400).json({ error: 'No file provided' });
			}

			const file = req.file;
			const fileName = file.originalname;
			const mimeType = file.mimetype;
			const buffer = file.buffer;

			let result: DocumentParseResult;

			// Handle different file types
			if (mimeType.includes('pdf') || fileName.toLowerCase().endsWith('.pdf')) {
				result = await parsePDF(buffer, fileName);
			} else if (
				mimeType.startsWith('text/') ||
				fileName.toLowerCase().endsWith('.txt') ||
				fileName.toLowerCase().endsWith('.md') ||
				fileName.toLowerCase().endsWith('.markdown')
			) {
				result = await parseTextFile(buffer, fileName);
			} else if (mimeType.startsWith('image/')) {
				result = await parseImageFile(buffer, fileName);
			} else {
				// Try to parse as text as fallback
				try {
					result = await parseTextFile(buffer, fileName);
				} catch {
					return res.status(400).json({
						error: 'Unsupported file type',
						supportedTypes: [
							'PDF',
							'TXT',
							'MD',
							'Images (JPG, PNG, GIF, WebP)',
						],
					});
				}
			}

			return res.json(result);
		} catch (error) {
			console.error('Document parsing error:', error);

			const errorMessage =
				error instanceof Error ? error.message : 'Unknown error';
			const status =
				errorMessage.includes('Invalid PDF') ||
					errorMessage.includes('Unsupported')
					? 400
					: 500;

			return res.status(status).json({
				error: 'Document parsing failed',
				message: errorMessage,
			});
		}
	}

	/**
	 * Get supported document types
	 */
	static async getSupportedTypes(_req: Request, res: Response) {
		const supportedTypes = {
			documents: ['PDF', 'TXT', 'MD', 'Markdown'],
			images: ['JPEG', 'PNG', 'GIF', 'WebP'],
			maxFileSize: '50MB',
			maxPages: MAX_PAGES,
			maxTextLength: MAX_TEXT_LENGTH,
		};

		return res.json(supportedTypes);
	}
}

/**
 * Parse PDF document
 */
async function parsePDF(
	buffer: Buffer,
	fileName: string,
): Promise<DocumentParseResult> {
	try {
		const data = await pdf(buffer);

		// Check page limit
		if (data.numpages > MAX_PAGES) {
			throw new Error(
				`PDF has ${data.numpages} pages, maximum allowed is ${MAX_PAGES}`,
			);
		}

		let text = data.text || '';

		// Truncate if too long
		const originalLength = text.length;
		if (text.length > MAX_TEXT_LENGTH) {
			text =
				text.slice(0, MAX_TEXT_LENGTH) +
				'\n\n[Content truncated due to length]';
		}

		return {
			type: 'pdf',
			text,
			fileName,
			fileSize: buffer.length,
			pages: data.numpages,
			originalLength,
			truncated: originalLength > MAX_TEXT_LENGTH,
			metadata: {
				title: (data.info as any)?.Title,
				author: (data.info as any)?.Author,
				subject: (data.info as any)?.Subject,
				creator: (data.info as any)?.Creator,
				producer: (data.info as any)?.Producer,
				creationDate: (data.info as any)?.CreationDate,
				modDate: (data.info as any)?.ModDate,
			},
		};
	} catch (error) {
		throw new Error(
			`PDF parsing failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
		);
	}
}

/**
 * Parse text file (TXT, MD, Markdown)
 */
async function parseTextFile(
	buffer: Buffer,
	fileName: string,
): Promise<DocumentParseResult> {
	try {
		let text = buffer.toString('utf-8');
		const originalLength = text.length;

		// Truncate if too long
		if (text.length > MAX_TEXT_LENGTH) {
			text =
				text.slice(0, MAX_TEXT_LENGTH) +
				'\n\n[Content truncated due to length]';
		}

		const fileType =
			fileName.toLowerCase().endsWith('.md') ||
				fileName.toLowerCase().endsWith('.markdown')
				? 'markdown'
				: 'text';

		return {
			type: fileType,
			text,
			fileName,
			fileSize: buffer.length,
			originalLength,
			truncated: originalLength > MAX_TEXT_LENGTH,
			metadata: {
				encoding: 'utf-8',
				lines: text.split('\n').length,
			},
		};
	} catch (error) {
		throw new Error(
			`Text file parsing failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
		);
	}
}

/**
 * Parse image file (for vision models)
 */
async function parseImageFile(
	buffer: Buffer,
	fileName: string,
): Promise<DocumentParseResult> {
	try {
		// Convert to base64 for vision models
		const base64 = buffer.toString('base64');
		const mimeType = getMimeTypeFromFileName(fileName);

		return {
			type: 'image',
			text: `[Image: ${fileName}]`,
			fileName,
			fileSize: buffer.length,
			base64: `data:${mimeType};base64,${base64}`,
			metadata: {
				mimeType,
				width: undefined, // Could be extracted with image libraries if needed
				height: undefined,
			},
		};
	} catch (error) {
		throw new Error(
			`Image parsing failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
		);
	}
}

/**
 * Get MIME type from file extension
 */
function getMimeTypeFromFileName(fileName: string): string {
	const extension = fileName.toLowerCase().substring(fileName.lastIndexOf('.'));
	const mimeTypes: Record<string, string> = {
		'.jpg': 'image/jpeg',
		'.jpeg': 'image/jpeg',
		'.png': 'image/png',
		'.gif': 'image/gif',
		'.webp': 'image/webp',
	};

	return mimeTypes[extension] || 'application/octet-stream';
}

import type { NextFunction, Request, Response } from 'express';
// Error handling middleware for Cortex WebUI backend

export class HttpError extends Error {
  constructor(
    public statusCode: number,
    message: string,
    public details?: unknown,
  ) {
    super(message);
  }
}

export const errorHandler = (
  error: Error,
  req: Request,
  res: Response,
  _next: NextFunction,
): void => {
  console.error('Error:', error);

  if (error instanceof HttpError) {
    res.status(error.statusCode).json({
      error: error.message,
      details: error.details,
    });
    return;
  }

  // Handle Zod validation errors
  if (error.name === 'ZodError') {
    res.status(400).json({
      error: 'Validation failed',
      details: error,
    });
    return;
  }

  // Handle database errors
  if (error.name === 'SqliteError') {
    res.status(500).json({
      error: 'Database error',
      details:
        process.env.NODE_ENV === 'development' ? error.message : undefined,
    });
    return;
  }

  // Default error
  res.status(500).json({
    error: 'Internal server error',
    details: process.env.NODE_ENV === 'development' ? error.message : undefined,
  });
};

