// Comprehensive health check service


import { promises as fs } from 'fs';
import path from 'path';
import { getDatabase } from '../utils/database';
import logger from '../utils/logger';

export interface HealthCheckResult {
	status: 'healthy' | 'degraded' | 'unhealthy';
	checks: Record<string, HealthCheck>;
	timestamp: string;
	uptime: number;
	version: string;
}

export interface HealthCheck {
	status: 'pass' | 'fail' | 'warn';
	message?: string;
	observedValue?: any;
	observedUnit?: string;
	time?: string;
	componentId?: string;
	componentType?: string;
}

export class HealthService {
	private static instance: HealthService;

	private constructor() { }

	public static getInstance(): HealthService {
		if (!HealthService.instance) {
			HealthService.instance = new HealthService();
		}
		return HealthService.instance;
	}

	async performHealthCheck(): Promise<HealthCheckResult> {
		const startTime = Date.now();
		const checks: Record<string, HealthCheck> = {};

		// Perform all health checks
		await Promise.allSettled([
			this.checkDatabase().then((result) => (checks.database = result)),
			this.checkFileSystem().then((result) => (checks.filesystem = result)),
			this.checkMemory().then((result) => (checks.memory = result)),
			this.checkDiskSpace().then((result) => (checks.diskspace = result)),
			this.checkEnvironment().then((result) => (checks.environment = result)),
		]);

		// Determine overall status
		const overallStatus = this.determineOverallStatus(checks);

		const result: HealthCheckResult = {
			status: overallStatus,
			checks,
			timestamp: new Date().toISOString(),
			uptime: process.uptime(),
			version: process.env.npm_package_version || '1.0.0',
		};

		// Log health check results
		const duration = Date.now() - startTime;
		logger.info(`Health check completed in ${duration}ms`, {
			status: overallStatus,
			duration,
			category: 'health',
		});

		return result;
	}

	private async checkDatabase(): Promise<HealthCheck> {
		try {
			const db = getDatabase();

			// Test database connection
			const start = Date.now();
			const result = db.prepare('SELECT 1 as test').get();
			const duration = Date.now() - start;

			if (!result) {
				return {
					status: 'fail',
					message: 'Database query returned no result',
					componentId: 'database',
					componentType: 'datastore',
				};
			}

			// Check if query time is reasonable
			if (duration > 1000) {
				return {
					status: 'warn',
					message: 'Database response time is slow',
					observedValue: duration,
					observedUnit: 'ms',
					componentId: 'database',
					componentType: 'datastore',
				};
			}

			return {
				status: 'pass',
				message: 'Database is responding normally',
				observedValue: duration,
				observedUnit: 'ms',
				componentId: 'database',
				componentType: 'datastore',
			};
		} catch (error) {
			logger.error('Database health check failed', error);
			return {
				status: 'fail',
				message: `Database connection failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
				componentId: 'database',
				componentType: 'datastore',
			};
		}
	}

	private async checkFileSystem(): Promise<HealthCheck> {
		try {
			const uploadDir = process.env.UPLOAD_DIR || './uploads';
			const dataDir = path.dirname(
				process.env.DATABASE_PATH || './data/cortex.db',
			);

			// Check if directories exist and are writable
			await fs.access(uploadDir, fs.constants.W_OK);
			await fs.access(dataDir, fs.constants.W_OK);

			// Test write operation
			const testFile = path.join(uploadDir, '.health-check');
			await fs.writeFile(testFile, 'health-check');
			await fs.unlink(testFile);

			return {
				status: 'pass',
				message: 'File system is accessible and writable',
				componentId: 'filesystem',
				componentType: 'system',
			};
		} catch (error) {
			logger.error('File system health check failed', error);
			return {
				status: 'fail',
				message: `File system check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
				componentId: 'filesystem',
				componentType: 'system',
			};
		}
	}

	private async checkMemory(): Promise<HealthCheck> {
		try {
			const memUsage = process.memoryUsage();
			const totalMem = memUsage.heapTotal;
			const usedMem = memUsage.heapUsed;
			const memUtilization = (usedMem / totalMem) * 100;

			if (memUtilization > 90) {
				return {
					status: 'fail',
					message: 'Memory utilization is critically high',
					observedValue: Math.round(memUtilization),
					observedUnit: '%',
					componentId: 'memory',
					componentType: 'system',
				};
			}

			if (memUtilization > 80) {
				return {
					status: 'warn',
					message: 'Memory utilization is high',
					observedValue: Math.round(memUtilization),
					observedUnit: '%',
					componentId: 'memory',
					componentType: 'system',
				};
			}

			return {
				status: 'pass',
				message: 'Memory utilization is normal',
				observedValue: Math.round(memUtilization),
				observedUnit: '%',
				componentId: 'memory',
				componentType: 'system',
			};
		} catch (error) {
			logger.error('Memory health check failed', error);
			return {
				status: 'fail',
				message: `Memory check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
				componentId: 'memory',
				componentType: 'system',
			};
		}
	}

	private async checkDiskSpace(): Promise<HealthCheck> {
		try {
			const dataDir = path.dirname(
				process.env.DATABASE_PATH || './data/cortex.db',
			);
			const stats = await fs.stat(dataDir);

			// This is a simplified check - in production you'd want to check actual disk usage
			// For now, we'll just ensure the directory exists
			if (stats.isDirectory()) {
				return {
					status: 'pass',
					message: 'Data directory is accessible',
					componentId: 'diskspace',
					componentType: 'system',
				};
			}

			return {
				status: 'fail',
				message: 'Data directory is not accessible',
				componentId: 'diskspace',
				componentType: 'system',
			};
		} catch (error) {
			logger.error('Disk space health check failed', error);
			return {
				status: 'fail',
				message: `Disk space check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
				componentId: 'diskspace',
				componentType: 'system',
			};
		}
	}

	private async checkEnvironment(): Promise<HealthCheck> {
		try {
			const requiredEnvVars = ['JWT_SECRET', 'MODEL_API_KEY', 'NODE_ENV'];

			const missingVars = requiredEnvVars.filter(
				(varName) => !process.env[varName],
			);

			if (missingVars.length > 0) {
				return {
					status: 'fail',
					message: `Missing required environment variables: ${missingVars.join(', ')}`,
					componentId: 'environment',
					componentType: 'system',
				};
			}

			// Check JWT secret length
			if (process.env.JWT_SECRET && process.env.JWT_SECRET.length < 32) {
				return {
					status: 'warn',
					message: 'JWT secret should be at least 32 characters long',
					componentId: 'environment',
					componentType: 'system',
				};
			}

			return {
				status: 'pass',
				message: 'Environment configuration is valid',
				componentId: 'environment',
				componentType: 'system',
			};
		} catch (error) {
			logger.error('Environment health check failed', error);
			return {
				status: 'fail',
				message: `Environment check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
				componentId: 'environment',
				componentType: 'system',
			};
		}
	}

	private determineOverallStatus(
		checks: Record<string, HealthCheck>,
	): 'healthy' | 'degraded' | 'unhealthy' {
		const statuses = Object.values(checks).map((check) => check.status);

		if (statuses.includes('fail')) {
			return 'unhealthy';
		}

		if (statuses.includes('warn')) {
			return 'degraded';
		}

		return 'healthy';
	}
}

import { z } from 'zod';

export const ChatStreamStartSchema = z.object({
	ts: z.string().datetime(),
	evt: z.literal('chat.stream.start'),
	sessionId: z.string().min(1),
	model: z.string().min(1),
	lastUserId: z.string().min(1),
});
export type ChatStreamStart = z.infer<typeof ChatStreamStartSchema>;

export const ChatStreamDoneSchema = z.object({
	ts: z.string().datetime(),
	evt: z.literal('chat.stream.done'),
	sessionId: z.string().min(1),
	model: z.string().min(1),
	messageId: z.string().min(1),
	durationMs: z.number().nonnegative(),
	tokenCount: z.number().nonnegative(),
	textSize: z.number().nonnegative(),
});
export type ChatStreamDone = z.infer<typeof ChatStreamDoneSchema>;

export type ChatStreamEvent = ChatStreamStart | ChatStreamDone;

export const ChatStreamEventSchema = z.discriminatedUnion('evt', [
	ChatStreamStartSchema,
	ChatStreamDoneSchema,
]);

export function makeStartEvent(
	input: Omit<ChatStreamStart, 'ts' | 'evt'>,
): ChatStreamStart {
	const evt: ChatStreamStart = {
		ts: new Date().toISOString(),
		evt: 'chat.stream.start',
		...input,
	};
	return ChatStreamStartSchema.parse(evt);
}

export function makeDoneEvent(
	input: Omit<ChatStreamDone, 'ts' | 'evt'>,
): ChatStreamDone {
	const evt: ChatStreamDone = {
		ts: new Date().toISOString(),
		evt: 'chat.stream.done',
		...input,
	};
	return ChatStreamDoneSchema.parse(evt);
}

export function logEvent(evt: ChatStreamEvent): void {
	const parsed = ChatStreamEventSchema.safeParse(evt);
	if (parsed.success) {
		console.log(JSON.stringify(parsed.data));
	} else {
		console.error('[observability] invalid event', parsed.error.flatten());
	}
}

export type ToolEvent = {
	id: string;
	name: string;
	args?: Record<string, unknown>;
	status?: 'start' | 'complete' | 'error';
	createdAt: string;
};
declare global {
	// eslint-disable-next-line no-var
	var __cortexToolStore: Map<string, ToolEvent[]> | undefined;
}

const toolStore: Map<string, ToolEvent[]> =
	globalThis.__cortexToolStore || new Map();
globalThis.__cortexToolStore = toolStore;

export function getToolEvents(sessionId: string): ToolEvent[] {
	return toolStore.get(sessionId) || [];
}

export function addToolEvent(
	sessionId: string,
	event: Omit<ToolEvent, 'createdAt' | 'id'> & { id?: string },
) {
	const list = toolStore.get(sessionId) || [];
	const createdAt = new Date().toISOString();
	const id =
		event.id ||
		(globalThis.crypto?.randomUUID?.() ?? Math.random().toString(36).slice(2));
	const redactedArgs = event.args ? redactArgs(event.args) : undefined;
	const e: ToolEvent = {
		id,
		name: event.name,
		args: redactedArgs,
		status: event.status,
		createdAt,
	};
	list.push(e);
	toolStore.set(sessionId, list);
	return e;
}

// Basic redaction: mask values with sensitive-looking keys and common secret patterns
export function redactArgs<T extends Record<string, unknown>>(args: T): T {
	const SENSITIVE_KEYS = [
		'key',
		'token',
		'secret',
		'password',
		'authorization',
		'apikey',
	];
	const EMAIL_REGEX = /[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}/i;
	const BEARER_REGEX = /bearer\s+[a-z0-9-_.]+/i;

	const isSensitiveKey = (k: string) =>
		SENSITIVE_KEYS.some((s) => k.toLowerCase().includes(s));
	const sanitizeString = (s: string) => {
		let out = s;
		if (EMAIL_REGEX.test(out)) out = out.replace(EMAIL_REGEX, '[EMAIL]');
		if (BEARER_REGEX.test(out))
			out = out.replace(BEARER_REGEX, 'Bearer [REDACTED]');
		return out;
	};
	const sanitize = (
		val: unknown,
		visited: WeakSet<Record<string, unknown>>,
	): unknown => {
		if (typeof val === 'string') return sanitizeString(val);
		if (Array.isArray(val)) return val.map((item) => sanitize(item, visited));
		if (val && typeof val === 'object') {
			if (visited.has(val as Record<string, unknown>)) {
				return '[Circular]';
			}
			visited.add(val as Record<string, unknown>);
			const out: Record<string, unknown> = {};
			for (const [k, v] of Object.entries(val as Record<string, unknown>)) {
				if (isSensitiveKey(k)) out[k] = '[REDACTED]';
				else out[k] = sanitize(v, visited);
			}
			return out as unknown as T;
		}
		return val;
	};

	return sanitize(args, new WeakSet()) as T;
}

// Main App component

import type React from 'react';
import { useEffect, useState } from 'react';
import {
	Navigate,
	Route,
	BrowserRouter as Router,
	Routes,
	useNavigate,
} from 'react-router-dom';
import useAuth from './hooks/useAuth';
import useConversations from './hooks/useConversations';
import useMessages from './hooks/useMessages';
import ChatPage from './pages/ChatPage';
import Dashboard from './pages/Dashboard';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import SettingsPage from './pages/SettingsPage';
import type { Theme } from './utils/theme';
import {
	applyMotionPreferences,
	applyTheme,
	getEffectiveTheme,
	getStoredTheme,
} from './utils/theme';

const AppContent: React.FC = () => {
	const navigate = useNavigate();
	const [theme, setTheme] = useState<Theme>('system');
	const [effectiveTheme, setEffectiveTheme] = useState<'light' | 'dark'>(
		'light',
	);
	const [sidebarOpen, setSidebarOpen] = useState<boolean>(false);

	const auth = useAuth();
	const conversations = useConversations();
	const messages = useMessages();

	// Apply theme and accessibility preferences on mount
	useEffect(() => {
		applyTheme();
		applyMotionPreferences();
		const storedTheme = getStoredTheme();
		setTheme(storedTheme);
		setEffectiveTheme(getEffectiveTheme());
	}, []);

	// Update effective theme when theme changes
	useEffect(() => {
		setEffectiveTheme(getEffectiveTheme());
	}, [theme]);

	// Theme update handler
	const handleThemeChange = (newTheme: Theme) => {
		setTheme(newTheme);
		applyTheme();
		setEffectiveTheme(getEffectiveTheme());
	};

	const handleLogin = async (email: string, password: string) => {
		try {
			await auth.login(email, password);
		} catch (error) {
			console.error('Login failed:', error);
		}
	};

	const handleRegister = async (
		name: string,
		email: string,
		password: string,
	) => {
		try {
			await auth.register(name, email, password);
		} catch (error) {
			console.error('Registration failed:', error);
		}
	};

	const handleSendMessage = async (content: string) => {
		if (conversations.activeConversation) {
			await messages.sendMessage(conversations.activeConversation.id, content);
		}
	};

	const handleCreateConversation = async () => {
		const newConversation =
			await conversations.createConversation('New Conversation');
		if (newConversation) {
			navigate(`/chat/${newConversation.id}`);
		}
	};

	const handleSelectConversation = async (id: string) => {
		await conversations.selectConversation(id);
		navigate(`/chat/${id}`);
	};

	return (
		<div className="App">
			{/* @ts-ignore */}
			<Routes>
				{/* @ts-ignore */}
				<Route
					path="/login"
					element={
						<LoginPage
							onLogin={handleLogin}
							loading={auth.loading}
							error={auth.error}
						/>
					}
				/>
				{/* @ts-ignore */}
				<Route
					path="/register"
					element={
						<RegisterPage
							onRegister={handleRegister}
							loading={auth.loading}
							error={auth.error}
						/>
					}
				/>
				{/* @ts-ignore */}
				<Route
					path="/dashboard"
					element={
						auth.isAuthenticated ? (
							<Dashboard
								conversations={conversations.conversations}
								activeConversationId={
									conversations.activeConversation?.id || null
								}
								onSelectConversation={handleSelectConversation}
								onCreateConversation={handleCreateConversation}
								onLogout={auth.logout}
								onToggleSidebar={() => setSidebarOpen(!sidebarOpen)}
							/>
						) : (
							<Navigate to="/login" />
						)
					}
				/>
				{/* @ts-ignore */}
				<Route
					path="/chat/:conversationId"
					element={
						auth.isAuthenticated && conversations.activeConversation ? (
							<ChatPage
								conversation={conversations.activeConversation}
								messages={messages.messages}
								conversations={conversations.conversations}
								activeConversationId={
									conversations.activeConversation?.id || null
								}
								onSendMessage={handleSendMessage}
								onSelectConversation={handleSelectConversation}
								onCreateConversation={handleCreateConversation}
								onLogout={auth.logout}
								onToggleSidebar={() => setSidebarOpen(!sidebarOpen)}
								streaming={messages.streaming}
								error={messages.error}
							/>
						) : (
							<Navigate to="/dashboard" />
						)
					}
				/>
				{/* @ts-ignore */}
				<Route
					path="/settings"
					element={
						auth.isAuthenticated ? (
							<SettingsPage
								theme={effectiveTheme}
								onThemeChange={handleThemeChange}
								onToggleSidebar={() => setSidebarOpen(!sidebarOpen)}
							/>
						) : (
							<Navigate to="/login" />
						)
					}
				/>
				{/* @ts-ignore */}
				<Route
					path="/"
					element={
						<Navigate to={auth.isAuthenticated ? '/dashboard' : '/login'} />
					}
				/>
			</Routes>
		</div>
	);
};

export const App: React.FC = () => {
	return (
		<Router>
			<AppContent />
		</Router>
	);
};

