'use client';

import type React from 'react';
import { useRef, useState } from 'react';
import Modal from '@/components/common/Modal';

interface ImportModalProps {
	isOpen: boolean;
	onClose: () => void;
	onImport: (file: File, options: ImportOptions) => void;
}

interface ImportOptions {
	format: 'json' | 'csv' | 'txt' | 'markdown';
	includeAttachments: boolean;
	overwriteExisting: boolean;
}

const ImportModal: React.FC<ImportModalProps> = ({
	isOpen,
	onClose,
	onImport,
}) => {
	const [file, setFile] = useState<File | null>(null);
	const [isDragOver, setIsDragOver] = useState(false);
	const [importOptions, setImportOptions] = useState<ImportOptions>({
		format: 'json',
		includeAttachments: true,
		overwriteExisting: false,
	});
	const [isImporting, setIsImporting] = useState(false);
	const fileInputRef = useRef<HTMLInputElement>(null);

	const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		const files = e.target.files;
		if (files && files.length > 0) {
			setFile(files[0]);
		}
	};

	const handleDragOver = (e: React.DragEvent) => {
		e.preventDefault();
		setIsDragOver(true);
	};

	const handleDragLeave = (e: React.DragEvent) => {
		e.preventDefault();
		setIsDragOver(false);
	};

	const handleDrop = (e: React.DragEvent) => {
		e.preventDefault();
		setIsDragOver(false);

		const files = e.dataTransfer.files;
		if (files && files.length > 0) {
			setFile(files[0]);
		}
	};

	const handleImport = async () => {
		if (!file) return;

		setIsImporting(true);
		try {
			await onImport(file, importOptions);
			onClose();

			// @ts-expect-error
			if (typeof window !== 'undefined' && window.addNotification) {
				// @ts-expect-error
				window.addNotification('success', 'Import completed successfully!');
			}
		} catch (error) {
			// @ts-expect-error
			if (typeof window !== 'undefined' && window.addNotification) {
				// @ts-expect-error
				window.addNotification('error', 'Import failed. Please try again.');
			}
		} finally {
			setIsImporting(false);
		}
	};

