interface NodeProps {
	node: any;
	onSelect: (nodeId: string) => void;
}

const Node: React.FC<NodeProps> = ({ node, onSelect }) => {
	const getNodeStyle = () => {
		switch (node.type) {
			case 'chat':
				return 'bg-blue-100 border-blue-300';
