'use client';

import type React from 'react';
import { useState } from 'react';
import Modal from '@/components/common/Modal';

interface ShareChatModalProps {
	isOpen: boolean;
	onClose: () => void;
	chatId: string;
	chatTitle: string;
}

const ShareChatModal: React.FC<ShareChatModalProps> = ({
	isOpen,
	onClose,
	chatId,
	chatTitle,
}) => {
	const [shareLink, setShareLink] = useState('');
	const [isCopied, setIsCopied] = useState(false);
	const [shareSettings, setShareSettings] = useState({
		allowEdit: false,
		allowCopy: true,
		allowDownload: true,
		requireLogin: false,
		expiration: 'never',
	});

	const generateShareLink = () => {
		// In a real implementation, this would call an API to generate a share link
		const baseUrl = typeof window !== 'undefined' ? window.location.origin : '';
		const link = `${baseUrl}/share/${chatId}?title=${encodeURIComponent(chatTitle)}`;
		setShareLink(link);
	};

	const copyToClipboard = async () => {
		if (!shareLink) return;

		try {
			await navigator.clipboard.writeText(shareLink);
			setIsCopied(true);
			setTimeout(() => setIsCopied(false), 2000);

			// @ts-expect-error
			if (typeof window !== 'undefined' && window.addNotification) {
				// @ts-expect-error
				window.addNotification('success', 'Link copied to clipboard!');
			}
		} catch (err) {
			// @ts-expect-error
			if (typeof window !== 'undefined' && window.addNotification) {
				// @ts-expect-error
				window.addNotification('error', 'Failed to copy link');
			}
		}
	};

	const handleShare = async () => {
		// In a real implementation, this would call an API to create the share
		generateShareLink();

		// @ts-expect-error
		if (typeof window !== 'undefined' && window.addNotification) {
			// @ts-expect-error
			window.addNotification('success', 'Chat sharing link generated!');
		}
	};

	return (
		<Modal isOpen={isOpen} onClose={onClose} title="Share Chat">
			<div className="p-6">
				<div className="space-y-6">
					<div>
						<h3 className="text-lg font-medium text-gray-900 dark:text-white">
							Share "{chatTitle}"
						</h3>
						<p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
