import type React from 'react';
import { useEffect, useRef } from 'react';

interface DrawerProps {
	isOpen: boolean;
	onClose: () => void;
	children: React.ReactNode;
	title?: string;
	position?: 'left' | 'right';
	className?: string;
}

const Drawer: React.FC<DrawerProps> = ({
	isOpen,
	onClose,
	children,
	title,
	position = 'right',
	className = '',
}) => {
	const drawerRef = useRef<HTMLDivElement>(null);

	// Handle escape key press
	useEffect(() => {
		const handleEscape = (e: KeyboardEvent) => {
			if (e.key === 'Escape') {
				onClose();
			}
		};

		if (isOpen) {
			document.addEventListener('keydown', handleEscape);
			document.body.style.overflow = 'hidden';
		}

		return () => {
			document.removeEventListener('keydown', handleEscape);
			document.body.style.overflow = 'unset';
		};
	}, [isOpen, onClose]);

	// Handle click outside drawer
	const handleClickOutside = (e: React.MouseEvent) => {
		if (drawerRef.current && !drawerRef.current.contains(e.target as Node)) {
			onClose();
		}
	};

	if (!isOpen) return null;

	return (
		<div className="fixed inset-0 z-50 overflow-hidden">
			<div
				className="absolute inset-0 bg-black bg-opacity-50"
				onClick={handleClickOutside}
			/>

			<div
				ref={drawerRef}
				className={`absolute top-0 h-full bg-white shadow-xl transform transition-transform duration-300 ease-in-out ${
					position === 'left' ? 'left-0' : 'right-0'
				} ${isOpen ? 'translate-x-0' : position === 'left' ? '-translate-x-full' : 'translate-x-full'} w-96 max-w-full ${className}`}
				role="dialog"
				aria-modal="true"
				aria-labelledby={title ? 'drawer-title' : undefined}
			>
				{title && (
					<div className="p-4 border-b flex justify-between items-center">
						<h3
							id="drawer-title"
							className="text-lg font-semibold text-gray-900"
						>
							{title}
						</h3>
						<button
							onClick={onClose}
							className="text-gray-400 hover:text-gray-500"
							aria-label="Close"
						>
							<svg
								xmlns="http://www.w3.org/2000/svg"
								className="h-6 w-6"
								fill="none"
								viewBox="0 0 24 24"
								stroke="currentColor"
							>
								<path
									strokeLinecap="round"
									strokeLinejoin="round"
									strokeWidth={2}
									d="M6 18L18 6M6 6l12 12"
								/>
							</svg>
						</button>
					</div>
				)}

				<div className="h-full overflow-y-auto">{children}</div>
			</div>
		</div>
	);
};

export default Drawer;

