import type React from 'react';
import { useEffect, useRef, useState } from 'react';

interface DropdownOption {
	value: string;
	label: string;
	disabled?: boolean;
}

interface DropdownProps {
	options: DropdownOption[];
	value: string;
	onChange: (value: string) => void;
	placeholder?: string;
	disabled?: boolean;
	className?: string;
}

const Dropdown: React.FC<DropdownProps> = ({
	options,
	value,
	onChange,
	placeholder = 'Select an option',
	disabled = false,
	className = '',
}) => {
	const [isOpen, setIsOpen] = useState(false);
	const dropdownRef = useRef<HTMLDivElement>(null);

	// Close dropdown when clicking outside
	useEffect(() => {
		const handleClickOutside = (event: MouseEvent) => {
			if (
				dropdownRef.current &&
				!dropdownRef.current.contains(event.target as Node)
			) {
				setIsOpen(false);
			}
		};

		document.addEventListener('mousedown', handleClickOutside);
		return () => {
			document.removeEventListener('mousedown', handleClickOutside);
		};
	}, []);

	const selectedOption = options.find((option) => option.value === value);

	const handleOptionClick = (option: DropdownOption) => {
		if (!option.disabled) {
			onChange(option.value);
			setIsOpen(false);
		}
	};

	return (
		<div className={`relative ${className}`} ref={dropdownRef}>
			<button
				type="button"
				className={`w-full flex items-center justify-between px-3 py-2 text-sm border rounded-md shadow-sm ${
					disabled
						? 'bg-gray-100 text-gray-400 cursor-not-allowed'
						: 'bg-white text-gray-700 hover:bg-gray-50'
				}`}
				onClick={() => !disabled && setIsOpen(!isOpen)}
				disabled={disabled}
				aria-haspopup="listbox"
				aria-expanded={isOpen}
			>
				<span className={selectedOption ? 'text-gray-900' : 'text-gray-500'}>
					{selectedOption ? selectedOption.label : placeholder}
				</span>
				<svg
					xmlns="http://www.w3.org/2000/svg"
					className={`h-5 w-5 text-gray-400 transform transition-transform ${
						isOpen ? 'rotate-180' : ''
					}`}
					viewBox="0 0 20 20"
					fill="currentColor"
				>
					<path
						fillRule="evenodd"
						d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
						clipRule="evenodd"
					/>
				</svg>
			</button>

			{isOpen && (
				<div className="absolute z-10 mt-1 w-full bg-white shadow-lg rounded-md py-1 ring-1 ring-black ring-opacity-5 focus:outline-none">
					<ul role="listbox" className="max-h-60 overflow-auto">
						{options.map((option) => (
							<li
								key={option.value}
								className={`px-3 py-2 text-sm cursor-pointer ${
									option.disabled
										? 'text-gray-400 cursor-not-allowed'
										: value === option.value
											? 'bg-blue-100 text-blue-900'
											: 'text-gray-700 hover:bg-gray-100'
								}`}
								onClick={() => handleOptionClick(option)}
								aria-selected={value === option.value}
								aria-disabled={option.disabled}
							>
								{option.label}
							</li>
						))}
					</ul>
				</div>
			)}
		</div>
	);
};

export default Dropdown;

