'use client';

import type React from 'react';
import { useEffect, useRef } from 'react';

interface LoaderProps {
	onVisible?: () => void;
	children: React.ReactNode;
}

const Loader: React.FC<LoaderProps> = ({ onVisible, children }) => {
	const loaderElementRef = useRef<HTMLDivElement>(null);
	const observerRef = useRef<IntersectionObserver | null>(null);
	const intervalIdRef = useRef<NodeJS.Timeout | null>(null);

	useEffect(() => {
		if (loaderElementRef.current) {
			observerRef.current = new IntersectionObserver(
				(entries) => {
					entries.forEach((entry) => {
						if (entry.isIntersecting) {
							intervalIdRef.current = setInterval(() => {
								if (onVisible) {
									onVisible();
								}
							}, 100);
						} else {
							if (intervalIdRef.current) {
								clearInterval(intervalIdRef.current);
								intervalIdRef.current = null;
							}
						}
					});
				},
				{
					root: null,
					rootMargin: '0px',
					threshold: 0.1,
				},
			);

			observerRef.current.observe(loaderElementRef.current);
		}

		return () => {
			if (observerRef.current && loaderElementRef.current) {
				observerRef.current.unobserve(loaderElementRef.current);
			}

			if (intervalIdRef.current) {
				clearInterval(intervalIdRef.current);
			}
		};
	}, [onVisible]);

	return <div ref={loaderElementRef}>{children}</div>;
};

export default Loader;

'use client';

import type React from 'react';
import { useEffect, useState } from 'react';
import NotificationToast from './NotificationToast';

interface Notification {
	id: string;
	type: 'success' | 'error' | 'warning' | 'info';
	message: string;
	timestamp: number;
}

const NotificationsContainer: React.FC = () => {
	const [notifications, setNotifications] = useState<Notification[]>([]);

	// Function to add a new notification
	const addNotification = (
		type: 'success' | 'error' | 'warning' | 'info',
		message: string,
	) => {
		const id = Math.random().toString(36).substr(2, 9);
		const newNotification: Notification = {
			id,
			type,
			message,
			timestamp: Date.now(),
		};

		setNotifications((prev) => [...prev, newNotification]);
	};

	// Function to remove a notification
	const removeNotification = (id: string) => {
		setNotifications((prev) =>
			prev.filter((notification) => notification.id !== id),
		);
	};

	// Expose addNotification to global scope for easy access
	useEffect(() => {
		// @ts-expect-error
		window.addNotification = addNotification;

		return () => {
			// @ts-expect-error
			delete window.addNotification;
		};
	}, []);

	return (
		<div className="fixed top-4 right-4 z-50 space-y-2">
			{notifications.map((notification) => (
				<NotificationToast
					key={notification.id}
					id={notification.id}
					type={notification.type}
					message={notification.message}
					onClose={removeNotification}
				/>
			))}
		</div>
	);
};

export default NotificationsContainer;

