// WebSocket service for real-time communication

import { API_BASE_URL } from '../constants';

type Listener = (data?: unknown) => void;

class SocketService {
	private socket: WebSocket | null = null;
	private readonly listeners: Map<string, Set<Listener>> = new Map();
	private reconnectAttempts = 0;
	private readonly maxReconnectAttempts = 5;
	private readonly reconnectDelay = 1000;

	connect(token: string) {
		if (this.socket?.readyState === WebSocket.OPEN) {
			return;
		}

		// Close existing connection if any
		this.disconnect();

		const wsUrl = API_BASE_URL.replace('http', 'ws') + '/ws?token=' + token;
		this.socket = new WebSocket(wsUrl);

		this.socket.onopen = () => {
			console.log('WebSocket connected');
			this.reconnectAttempts = 0;
			this.emit('connected');
		};

		this.socket.onmessage = (event) => {
			try {
				const data = JSON.parse(event.data);
				this.emit(data.type, data.payload);
			} catch (error) {
				console.error('Error parsing WebSocket message:', error);
			}
		};

		this.socket.onclose = () => {
			console.log('WebSocket disconnected');
			this.emit('disconnected');

			// Attempt to reconnect
			if (this.reconnectAttempts < this.maxReconnectAttempts) {
				this.reconnectAttempts++;
				setTimeout(() => {
					console.log(
						`Attempting to reconnect (${this.reconnectAttempts}/${this.maxReconnectAttempts})`,
					);
					this.connect(token);
				}, this.reconnectDelay * this.reconnectAttempts);
			}
		};

		this.socket.onerror = (error) => {
			console.error('WebSocket error:', error);
			this.emit('error', error);
		};
	}

	disconnect() {
		if (this.socket) {
			this.socket.close();
			this.socket = null;
		}
	}

	send(type: string, payload: unknown = {}) {
		if (this.socket?.readyState === WebSocket.OPEN) {
			this.socket.send(JSON.stringify({ type, payload }));
		} else {
			console.warn('WebSocket is not connected. Message not sent:', type);
		}
	}

	on(event: string, callback: Listener) {
		if (!this.listeners.has(event)) {
			this.listeners.set(event, new Set());
		}
		this.listeners.get(event)?.add(callback);
	}

	off(event: string, callback: Listener) {
		const listeners = this.listeners.get(event);
		if (listeners) {
			listeners.delete(callback);
		}
	}

	private emit(event: string, data?: unknown) {
		const listeners = this.listeners.get(event);
		if (listeners) {
			listeners.forEach((callback) => {
				try {
					callback(data);
				} catch (error) {
					console.error(`Error in WebSocket listener for ${event}:`, error);
				}
			});
		}
	}

	isConnected(): boolean {
		return this.socket?.readyState === WebSocket.OPEN;
	}
}

// Export singleton instance
export default new SocketService();

/**
 * Context Manager for intelligent conversation handling
 * Provides summarization, context window management, and memory optimization
 */

export interface ConversationSummary {
	summary: string;
	keyPoints: string[];
	messageCount: number;
	tokenEstimate: number;
	timestamp: number;
}

export interface ContextWindow {
	messages: any[];
	tokenCount: number;
	summary?: ConversationSummary;
}

export class ContextManager {
	private readonly maxTokens: number;
	private readonly summaryThreshold: number;
	private readonly maxMessagesBeforeSummary: number;

	constructor(
		maxTokens = 4000,
		summaryThreshold = 3000,
		maxMessagesBeforeSummary = 20,
	) {
		this.maxTokens = maxTokens;
		this.summaryThreshold = summaryThreshold;
		this.maxMessagesBeforeSummary = maxMessagesBeforeSummary;
	}

	/**
	 * Estimate token count for a message
	 * Rough approximation: 1 token â‰ˆ 4 characters for English text
	 */
	estimateTokens(text: string): number {
		return Math.ceil(text.length / 4);
	}

	/**
	 * Calculate total token count for messages
	 */
	calculateTokenCount(messages: any[]): number {
		return messages.reduce((total, message) => {
			return total + this.estimateTokens(message.content || '');
		}, 0);
	}

	/**
	 * Check if conversation needs summarization
	 */
	needsSummarization(messages: any[]): boolean {
		const tokenCount = this.calculateTokenCount(messages);
		return (
			tokenCount > this.summaryThreshold ||
			messages.length > this.maxMessagesBeforeSummary
		);
	}

	/**
	 * Create a conversation summary
	 */
	async createSummary(messages: any[]): Promise<ConversationSummary> {
		// Extract key information from conversation
		const userMessages = messages.filter((m) => m.role === 'user');
		const assistantMessages = messages.filter((m) => m.role === 'assistant');

		// Simple extractive summary - in a real implementation, this could use an AI model
		const keyTopics = this.extractKeyTopics(messages);
		const summary = this.generateSummary(
			userMessages,
			assistantMessages,
			keyTopics,
		);

		return {
			summary,
			keyPoints: keyTopics,
			messageCount: messages.length,
			tokenEstimate: this.calculateTokenCount(messages),
			timestamp: Date.now(),
		};
	}

	/**
	 * Extract key topics from conversation
	 */
	private extractKeyTopics(messages: any[]): string[] {
		const allText = messages
			.map((m) => m.content || '')
			.join(' ')
			.toLowerCase();

		// Simple keyword extraction - could be enhanced with NLP
		const commonWords = new Set([
			'the',
			'is',
			'at',
			'which',
			'on',
			'and',
			'a',
			'to',
			'are',
			'as',
			'was',
			'were',
			'been',
			'be',
			'have',
			'has',
			'had',
			'do',
			'does',
			'did',
			'will',
			'would',
			'should',
			'could',
			'can',
			'may',
			'might',
			'must',
			'shall',
			'this',
			'that',
			'these',
			'those',
			'i',
			'you',
			'he',
			'she',
			'it',
			'we',
			'they',
			'me',
			'him',
			'her',
			'us',
			'them',
			'my',
			'your',
			'his',
			'its',
			'our',
			'their',
			'in',
			'of',
			'for',
			'with',
			'by',
			'from',
			'up',
			'about',
			'into',
			'through',
			'during',
			'before',
			'after',
			'above',
			'below',
			'between',
			'among',
			'an',
		]);

		const words = allText
			.replace(/[^\w\s]/g, ' ')
			.split(/\s+/)
			.filter((word) => word.length > 3 && !commonWords.has(word));

		// Count word frequency
		const wordCount = words.reduce(
			(acc, word) => {
				acc[word] = (acc[word] || 0) + 1;
				return acc;
			},
			{} as Record<string, number>,
		);

		// Return top keywords
		return Object.entries(wordCount)
			.sort(([, a], [, b]) => b - a)
			.slice(0, 5)
			.map(([word]) => word);
	}

	/**
	 * Generate conversation summary
	 */
	private generateSummary(
		userMessages: any[],
		assistantMessages: any[],
		keyTopics: string[],
	): string {
		const userQuestions = userMessages
			.slice(0, 3)
			.map(
				(m) =>
					m.content?.substring(0, 100) + (m.content?.length > 100 ? '...' : ''),
			);

		return (
			`Conversation covered: ${keyTopics.join(', ')}. ` +
			`User asked about: ${userQuestions.join('; ')}. ` +
			`${userMessages.length} user messages, ${assistantMessages.length} assistant responses.`
		);
	}

	/**
	 * Optimize context window by summarizing older messages
	 */
	async optimizeContext(messages: any[]): Promise<ContextWindow> {
		if (!this.needsSummarization(messages)) {
			return {
				messages,
				tokenCount: this.calculateTokenCount(messages),
			};
		}

		// Keep recent messages and summarize older ones
		const recentMessageCount = Math.floor(this.maxMessagesBeforeSummary * 0.4);
		const recentMessages = messages.slice(-recentMessageCount);
		const olderMessages = messages.slice(0, -recentMessageCount);

		const optimizedMessages = [...recentMessages];
		let summary: ConversationSummary | undefined;

		if (olderMessages.length > 0) {
			summary = await this.createSummary(olderMessages);

			// Add summary as a system message at the beginning
			optimizedMessages.unshift({
				id: `summary-${Date.now()}`,
				role: 'system',
				content: `Previous conversation summary: ${summary.summary}`,
				timestamp: Date.now(),
				model: 'context-manager',
			});
		}

		return {
			messages: optimizedMessages,
			tokenCount: this.calculateTokenCount(optimizedMessages),
			summary,
		};
	}

	/**
	 * Smart context pruning - keep important messages
	 */
	pruneContext(messages: any[], targetTokenCount?: number): any[] {
		const target = targetTokenCount || this.maxTokens;
		const currentTokens = this.calculateTokenCount(messages);

		if (currentTokens <= target) {
			return messages;
		}

		// Always keep the last few messages
		const mustKeepCount = 4;
		const mustKeep = messages.slice(-mustKeepCount);
		const canPrune = messages.slice(0, -mustKeepCount);

		// Score messages by importance (length, keywords, question marks, etc.)
		const scoredMessages = canPrune.map((message, index) => ({
			...message,
			originalIndex: index,
			score: this.scoreMessageImportance(message),
		}));

		// Sort by score (keep higher scores)
		scoredMessages.sort((a, b) => b.score - a.score);

		// Add messages back until we hit token limit
		const result = [];
		let tokens = this.calculateTokenCount(mustKeep);

		for (const message of scoredMessages) {
			const messageTokens = this.estimateTokens(message.content || '');
			if (
				tokens + messageTokens <=
				target - this.calculateTokenCount(mustKeep)
			) {
				result.push(message);
				tokens += messageTokens;
			}
		}

		// Sort back to original order and add must-keep messages
		result.sort((a, b) => a.originalIndex - b.originalIndex);
		return [...result, ...mustKeep];
	}

	/**
	 * Score message importance for context pruning
	 */
	private scoreMessageImportance(message: any): number {
		const content = message.content || '';
		let score = 0;

		// Longer messages might be more important
		score += Math.min(content.length / 100, 5);

		// Questions are often important
		score += (content.match(/\?/g) || []).length * 2;

		// Code blocks are important
		score += (content.match(/```/g) || []).length;

		// User messages are often more important than assistant messages
		if (message.role === 'user') {
			score += 3;
		}

		// Recent messages are more important
		const age = Date.now() - (message.timestamp || 0);
		const ageHours = age / (1000 * 60 * 60);
		score += Math.max(0, 10 - ageHours);

		return score;
	}

	/**
	 * Get memory usage statistics
	 */
	getMemoryStats(messages: any[]): {
		messageCount: number;
		tokenCount: number;
		estimatedMemoryKB: number;
		utilizationPercent: number;
	} {
		const tokenCount = this.calculateTokenCount(messages);
		const estimatedMemoryKB = Math.round((tokenCount * 4) / 1024); // Rough estimate
		const utilizationPercent = Math.round((tokenCount / this.maxTokens) * 100);

		return {
			messageCount: messages.length,
			tokenCount,
			estimatedMemoryKB,
			utilizationPercent,
		};
	}
}

// Default export for easy usage
export const contextManager = new ContextManager();

// Validation utility functions

export const validateEmail = (email: string): boolean => {
	const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
	return re.test(email);
};

export const validatePassword = (
	password: string,
): { isValid: boolean; message: string } => {
	if (password.length < 8) {
		return {
			isValid: false,
			message: 'Password must be at least 8 characters long',
		};
	}

	if (!/[A-Z]/.test(password)) {
		return {
			isValid: false,
			message: 'Password must contain at least one uppercase letter',
		};
	}

	if (!/[a-z]/.test(password)) {
		return {
			isValid: false,
			message: 'Password must contain at least one lowercase letter',
		};
	}

	if (!/\d/.test(password)) {
		return {
			isValid: false,
			message: 'Password must contain at least one number',
		};
	}

	return { isValid: true, message: '' };
};

export const validateFileName = (
	fileName: string,
): { isValid: boolean; message: string } => {
	if (!fileName) {
		return { isValid: false, message: 'File name is required' };
	}

	if (fileName.length > 255) {
		return { isValid: false, message: 'File name is too long' };
	}

	const invalidChars = /[<>:"/\\|?*\x00-\x1F]/;
	if (invalidChars.test(fileName)) {
		return { isValid: false, message: 'File name contains invalid characters' };
	}

	return { isValid: true, message: '' };
};

export const validateFileSize = (
	fileSize: number,
	maxSizeMB: number = 10,
): { isValid: boolean; message: string } => {
	const maxSizeBytes = maxSizeMB * 1024 * 1024;

	if (fileSize > maxSizeBytes) {
		return {
			isValid: false,
			message: `File size exceeds ${maxSizeMB}MB limit`,
		};
	}

	return { isValid: true, message: '' };
};

type Role = 'user' | 'assistant' | 'system';

export type ChatMessage = {
	id: string;
	role: Role;
	content: string;
	createdAt: string;
};
export type ChatSession = {
	id: string;
	modelId: string | null;
	createdAt: string;
	updatedAt: string;
	messages: ChatMessage[];
};

declare global {
	// eslint-disable-next-line no-var
	var __cortexChatStore: Map<string, ChatSession> | undefined;
}

const store: Map<string, ChatSession> =
	(globalThis as { __cortexChatStore?: Map<string, ChatSession> })
		.__cortexChatStore || new Map<string, ChatSession>();

(
	globalThis as { __cortexChatStore?: Map<string, ChatSession> }
).__cortexChatStore = store;

export function getOrCreateSession(id: string): ChatSession {
	let s = store.get(id);
	if (!s) {
		const now = new Date().toISOString();
		s = { id, modelId: null, createdAt: now, updatedAt: now, messages: [] };
		store.set(id, s);
	}
	return s;
}

export function setModel(id: string, modelId: string) {
	const s = getOrCreateSession(id);
	s.modelId = modelId;
	s.updatedAt = new Date().toISOString();
}

export function addMessage(id: string, msg: Omit<ChatMessage, 'createdAt'>) {
	const s = getOrCreateSession(id);
	const m: ChatMessage = { ...msg, createdAt: new Date().toISOString() };
	s.messages.push(m);
	s.updatedAt = m.createdAt;
	return m;
}

export function getSession(id: string) {
	return getOrCreateSession(id);
}

// Minimal store hook used by Chat.tsx
export function useChatStore(sessionId: string) {
	const session = getOrCreateSession(sessionId);
	return {
		messages: session.messages,
		addMessage: (msg: Omit<ChatMessage, 'createdAt'>) =>
			addMessage(sessionId, msg),
		updateMessage: (messageId: string, patch: Partial<ChatMessage>) => {
			const s = getOrCreateSession(sessionId);
			const idx = s.messages.findIndex((m) => m.id === messageId);
			if (idx !== -1) {
				s.messages[idx] = { ...s.messages[idx], ...patch } as ChatMessage;
				s.updatedAt = new Date().toISOString();
			}
		},
		deleteMessage: (messageId: string) => {
			const s = getOrCreateSession(sessionId);
			s.messages = s.messages.filter((m) => m.id !== messageId);
			s.updatedAt = new Date().toISOString();
		},
		editMessage: (messageId: string, content: string) => {
			const s = getOrCreateSession(sessionId);
			const idx = s.messages.findIndex((m) => m.id === messageId);
			if (idx !== -1) {
				s.messages[idx] = { ...s.messages[idx], content };
				s.updatedAt = new Date().toISOString();
			}
		},
		clearMessages: () => {
			const s = getOrCreateSession(sessionId);
			s.messages = [];
			s.updatedAt = new Date().toISOString();
		},
	};
}

import { z } from 'zod';

// Zod schemas for observability payloads
export const ChatStreamStartSchema = z.object({
	ts: z.string().datetime(),
	evt: z.literal('chat.stream.start'),
	sessionId: z.string().min(1),
	model: z.string().min(1),
	lastUserId: z.string().min(1),
});

export type ChatStreamStart = z.infer<typeof ChatStreamStartSchema>;

export const ChatStreamDoneSchema = z.object({
	ts: z.string().datetime(),
	evt: z.literal('chat.stream.done'),
	sessionId: z.string().min(1),
	model: z.string().min(1),
	messageId: z.string().min(1),
	durationMs: z.number().nonnegative(),
	tokenCount: z.number().nonnegative(),
	textSize: z.number().nonnegative(),
});

export type ChatStreamDone = z.infer<typeof ChatStreamDoneSchema>;

export type ChatStreamEvent = ChatStreamStart | ChatStreamDone;

export const ChatStreamEventSchema = z.discriminatedUnion('evt', [
	ChatStreamStartSchema,
	ChatStreamDoneSchema,
]);

export function makeStartEvent(
	input: Omit<ChatStreamStart, 'ts' | 'evt'>,
): ChatStreamStart {
	const evt: ChatStreamStart = {
		ts: new Date().toISOString(),
		evt: 'chat.stream.start',
		...input,
	};
	return ChatStreamStartSchema.parse(evt);
}

export function makeDoneEvent(
	input: Omit<ChatStreamDone, 'ts' | 'evt'>,
): ChatStreamDone {
	const evt: ChatStreamDone = {
		ts: new Date().toISOString(),
		evt: 'chat.stream.done',
		...input,
	};
	return ChatStreamDoneSchema.parse(evt);
}

export function logEvent(evt: ChatStreamEvent): void {
	const parsed = ChatStreamEventSchema.safeParse(evt);
	if (parsed.success) {
		// eslint-disable-next-line no-console
		console.warn(JSON.stringify(parsed.data));
	} else {
		// eslint-disable-next-line no-console
		console.error('[observability] invalid event', parsed.error.flatten());
	}
}

export type ToolEvent = {
	id: string;
	name: string;
	args?: Record<string, unknown>;
	status?: 'start' | 'complete' | 'error';
	createdAt: string;
};

const toolStore: Map<string, ToolEvent[]> =
	(globalThis as unknown).__cortexToolStore || new Map();
(globalThis as unknown).__cortexToolStore = toolStore;

export function getToolEvents(sessionId: string): ToolEvent[] {
	return toolStore.get(sessionId) || [];
}

export function addToolEvent(
	sessionId: string,
	event: Omit<ToolEvent, 'createdAt' | 'id'> & { id?: string },
) {
	const list = toolStore.get(sessionId) || [];
	const createdAt = new Date().toISOString();
	const id =
		event.id ||
		(globalThis.crypto?.randomUUID?.() ?? Math.random().toString(36).slice(2));
	const redactedArgs = event.args ? redactArgs(event.args) : undefined;
	const e: ToolEvent = {
		id,
		name: event.name,
		args: redactedArgs,
		status: event.status,
		createdAt,
	};
	list.push(e);
	toolStore.set(sessionId, list);
	return e;
}

// Basic redaction: mask values with sensitive-looking keys and common secret patterns
export function redactArgs<T extends Record<string, unknown>>(args: T): T {
	const SENSITIVE_KEYS = [
		'key',
		'token',
		'secret',
		'password',
		'authorization',
		'apikey',
	];
	const EMAIL_REGEX = /[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}/i;
	const BEARER_REGEX = /bearer\s+[a-z0-9-_.]+/i;

	const isSensitiveKey = (k: string) =>
		SENSITIVE_KEYS.some((s) => k.toLowerCase().includes(s));
	const sanitizeString = (s: string) => {
		let out = s;
		if (EMAIL_REGEX.test(out)) out = out.replace(EMAIL_REGEX, '[EMAIL]');
		if (BEARER_REGEX.test(out))
			out = out.replace(BEARER_REGEX, 'Bearer [REDACTED]');
		return out;
	};
	const sanitize = (
		val: unknown,
		visited: WeakSet<Record<string, unknown>>,
	): unknown => {
		if (typeof val === 'string') return sanitizeString(val);
		if (Array.isArray(val)) return val.map((item) => sanitize(item, visited));
		if (val && typeof val === 'object') {
			if (visited.has(val as Record<string, unknown>)) {
				return '[Circular]';
			}
			visited.add(val as Record<string, unknown>);
			const out: Record<string, unknown> = {};
			for (const [k, v] of Object.entries(val as Record<string, unknown>)) {
				if (isSensitiveKey(k)) out[k] = '[REDACTED]';
				else out[k] = sanitize(v, visited);
			}
			return out as unknown as T;
		}
		return val;
	};

	return sanitize(args, new WeakSet()) as T;
}

