	'authentication',
	'authorization',
	'rate_limit',
	'network',
	'api',
	'validation',
	'timeout',
	'internal',
	'configuration',
	'webhook',
]);
export type ErrorCategory = z.infer<typeof ErrorCategorySchema>;

// Error Context Schema
