export function createRoutingRule(
	name: string,
	conditions: RoutingRule['conditions'],
	destinations: Array<{ service: string; topic?: string; endpoint?: string }>,
	options?: {
		description?: string;
		priority?: number;
		enabled?: boolean;
	},
): RoutingRule {
	return {
		id: crypto.randomUUID(),
		name,
		description: options?.description,
		enabled: options?.enabled ?? true,
		priority: options?.priority ?? 500,
		conditions,
		actions: {
			destinations: destinations.map((dest) => ({
				service: dest.service,
				topic: dest.topic,
				endpoint: dest.endpoint,
			})),
		},
	};
}

export function validateRoutingConfiguration(
	config: unknown,
): RoutingConfiguration {
	return RoutingConfigurationSchema.parse(config);
}

export function isValidRoutingConfiguration(
	config: unknown,
): config is RoutingConfiguration {
	return RoutingConfigurationSchema.safeParse(config).success;
}

