                console.warn(`   User: ${data.firstName} ${data.lastName}`);
                console.warn(`   Email: ${data.email}`);

                // Create order event
                const orderMsg = createChildMessage(msg, {
                    type: ORDER_CREATED_TYPE,
                    source: '/order-service',
