import { withSpan } from '@cortex-os/telemetry';
import type { Envelope } from '../a2a-contracts/src/envelope.js';
import {
	type OutboxConfig,
	type OutboxMessage,
	OutboxMessageStatus,
	type OutboxRepository,
} from '../a2a-contracts/src/outbox-types.js';
import {
	DeadLetterQueue,
	InMemoryDeadLetterStore,
} from '../a2a-core/src/dlq.js';
import {
	createReliableOutboxProcessor,
	EnhancedOutbox,
	ReliableOutboxPublisher,
} from '../a2a-core/src/outbox.js';

/**
 * A2A Outbox Integration
 *
 * This module integrates the transactional outbox pattern with the A2A bus
 * to ensure reliable message delivery with database transaction consistency.
 */

export interface A2AOutboxIntegration {
	/**
	 * Publish a message through the outbox
	 */
	publish: (envelope: Envelope) => Promise<void>;

	/**
	 * Publish multiple messages through the outbox
	 */
	publishBatch: (envelopes: Envelope[]) => Promise<void>;

	/**
	 * Process pending outbox messages
	 */
	processPending: () => Promise<void>;

	/**
	 * Process retry messages
	 */
	processRetries: () => Promise<void>;

	/**
	 * Start background processing
	 */
	start: () => Promise<void>;

	/**
	 * Stop background processing
	 */
	stop: () => Promise<void>;

	/**
	 * Clean up old processed messages
	 */
	cleanup: (olderThanDays?: number) => Promise<number>;

	/**
	 * Get DLQ statistics
	 */
	getDlqStats: () => Promise<any>;
}

/**
 * Create an A2A outbox integration
 */
export function createA2AOutboxIntegration(
	transport: {
		publish: (envelope: Envelope) => Promise<void>;
	},
	repository: OutboxRepository,
	config: OutboxConfig = {},
): A2AOutboxIntegration {
	// Merge with default configuration
	const mergedConfig: Required<OutboxConfig> = {
		maxRetries: config.maxRetries ?? 3,
		initialRetryDelayMs: config.initialRetryDelayMs ?? 1000,
		maxRetryDelayMs: config.maxRetryDelayMs ?? 30000,
		backoffMultiplier: config.backoffMultiplier ?? 2,
		batchSize: config.batchSize ?? 10,
		processingIntervalMs: config.processingIntervalMs ?? 5000,
		dlqThreshold: config.dlqThreshold ?? 5,
		messageTtlMs: config.messageTtlMs ?? 86400000, // 24 hours
		enableIdempotency: config.enableIdempotency ?? true,
	};

	// Create outbox components
	const publisher = new ReliableOutboxPublisher(transport, mergedConfig);
	const processor = createReliableOutboxProcessor(
		repository,
		publisher,
		mergedConfig,
	);
	const outbox = new EnhancedOutbox(repository, publisher, processor);

	// Create DLQ components
	const dlqStore = new InMemoryDeadLetterStore();
	const dlq = new DeadLetterQueue(dlqStore);

	/**
	 * Convert Envelope to OutboxMessage
	 */
	function envelopeToOutboxMessage(
		envelope: Envelope,
	): Omit<OutboxMessage, 'id' | 'createdAt'> {
		return {
			aggregateType: 'a2a-message',
			aggregateId: envelope.id,
			eventType: envelope.type,
			payload: envelope.data,
			metadata: {
				source: envelope.source,
				headers: envelope.headers,
				ttlMs: envelope.ttlMs,
				occurredAt: envelope.time,
			},
			correlationId: envelope.correlationId,
			causationId: envelope.causationId,
			traceparent: envelope.traceparent,
			tracestate: envelope.tracestate,
			baggage: envelope.baggage,
			idempotencyKey: envelope.headers?.['idempotency-key'] as
				| string
				| undefined,
			status: OutboxMessageStatus.PENDING,
			retryCount: 0,
			maxRetries: 3,
		};
	}

	/**
	 * Enhanced publish with outbox support
	 */
	async function publish(envelope: Envelope): Promise<void> {
		return withSpan('a2a.outbox.publish', async (span) => {
			span.setAttributes({
				'envelope.id': envelope.id,
				'envelope.type': envelope.type,
			});

			try {
				// Add to outbox within transaction
				const outboxMessage = envelopeToOutboxMessage(envelope);
				await outbox.addToOutbox(outboxMessage);

				// Process immediately for better performance
				await processor.processPending();
			} catch (error) {
				// If outbox fails, fallback to direct publish
				console.warn('Outbox failed, falling back to direct publish', error);
				await transport.publish(envelope);
			}
		});
	}

	/**
	 * Enhanced batch publish with outbox support
	 */
	async function publishBatch(envelopes: Envelope[]): Promise<void> {
		return withSpan('a2a.outbox.publishBatch', async (span) => {
			span.setAttributes({
				'envelope.count': envelopes.length,
			});

			try {
				// Add to outbox within transaction
				const outboxMessages = envelopes.map(envelopeToOutboxMessage);
				await outbox.addBatchToOutbox(outboxMessages);

				// Process immediately for better performance
				await processor.processPending();
			} catch (error) {
				// If outbox fails, fallback to direct publish
				console.warn(
					'Outbox batch failed, falling back to direct publish',
					error,
				);
				await Promise.all(envelopes.map((env) => transport.publish(env)));
			}
		});
	}

	/**
	 * Process pending messages
	 */
	async function processPending(): Promise<void> {
		return withSpan('a2a.outbox.processPending', async () => {
			await processor.processPending();
		});
	}

	/**
	 * Process retry messages
	 */
	async function processRetries(): Promise<void> {
		return withSpan('a2a.outbox.processRetries', async () => {
			await processor.processRetries();
		});
	}

	/**
	 * Start background processing
	 */
	async function start(): Promise<void> {
		return withSpan('a2a.outbox.start', async () => {
			await processor.start();
		});
	}

	/**
	 * Stop background processing
	 */
	async function stop(): Promise<void> {
		return withSpan('a2a.outbox.stop', async () => {
			await processor.stop();
		});
	}

	/**
	 * Clean up old processed messages
	 */
	async function cleanup(olderThanDays: number = 30): Promise<number> {
		return withSpan('a2a.outbox.cleanup', async (span) => {
			const count = await outbox.cleanup(olderThanDays);
			span.setAttributes({
				'cleanup.count': count,
				'cleanup.olderThanDays': olderThanDays,
			});
			return count;
		});
	}

	/**
	 * Get DLQ statistics
	 */
	async function getDlqStats() {
		return withSpan('a2a.outbox.getDlqStats', async () => {
			return await dlq.getStats();
		});
	}

	return {
		publish,
		publishBatch,
		processPending,
		processRetries,
		start,
		stop,
		cleanup,
		getDlqStats,
	};
}

/**
 * JSON-RPC 2.0 Handler for A2A Protocol
 * Implements proper JSON-RPC message handling with A2A methods
 */

import { z } from 'zod';
import {
	A2A_ERROR_CODES,
	type JsonRpcRequest,
	JsonRpcRequestSchema,
	type JsonRpcResponse,
	TaskCancelParamsSchema,
	TaskGetParamsSchema,
	TaskSendParamsSchema,
} from './protocol.js';
import { TaskManager } from './task-manager.js';

// Simple implementations for dependencies that don't exist yet
class StructuredError extends Error {
	constructor(
		public code: string,
		message: string,
		public details?: unknown,
	) {
		super(message);
		this.name = 'StructuredError';
	}
}

const createJsonOutput = (data: unknown): string =>
	JSON.stringify(data, null, 2);

export interface RpcHandler {
	handle(request: JsonRpcRequest): Promise<JsonRpcResponse>;
}

export class A2ARpcHandler implements RpcHandler {
	constructor(private readonly taskManager: TaskManager) {}

	async handle(request: JsonRpcRequest): Promise<JsonRpcResponse> {
		try {
			const result = await this.dispatchMethod(request);
			return {
				jsonrpc: '2.0',
				id: request.id,
				result,
			};
		} catch (error) {
			return this.createErrorResponse(request.id, error);
		}
	}

	private async dispatchMethod(request: JsonRpcRequest): Promise<unknown> {
		switch (request.method) {
			case 'tasks/send': {
				const params = TaskSendParamsSchema.parse(request.params);
				return this.taskManager.sendTask(params);
			}

			case 'tasks/get': {
				const params = TaskGetParamsSchema.parse(request.params);
				return this.taskManager.getTask(params);
			}

			case 'tasks/cancel': {
				const params = TaskCancelParamsSchema.parse(request.params);
				await this.taskManager.cancelTask(params);
				return { success: true };
			}

			case 'tasks/list': {
				// Utility method for debugging
				const status =
					request.params &&
					typeof request.params === 'object' &&
					'status' in request.params
						? (request.params.status as any)
						: undefined;
				return this.taskManager.listTasks(status);
			}

			default:
				throw new StructuredError(
					'METHOD_NOT_FOUND',
					`Method '${request.method}' not found`,
					{
						method: request.method,
						code: A2A_ERROR_CODES.METHOD_NOT_FOUND,
					},
				);
		}
	}

	private createErrorResponse(
		id: JsonRpcRequest['id'],
		error: unknown,
	): JsonRpcResponse {
		if (error instanceof z.ZodError) {
			return {
				jsonrpc: '2.0',
				id,
				error: {
					code: A2A_ERROR_CODES.INVALID_PARAMS,
					message: 'Invalid parameters',
					data: { issues: error.issues },
				},
			};
		}

		// Handle StructuredError - check by name to avoid instanceof issues
		if (error instanceof Error && error.name === 'StructuredError') {
			const structuredError = error as any; // Cast to access custom properties

			// Map StructuredError codes to A2A error codes
			let errorCode: number = A2A_ERROR_CODES.INTERNAL_ERROR;
			if (structuredError.code === 'TASK_NOT_FOUND') {
				errorCode = A2A_ERROR_CODES.TASK_NOT_FOUND;
			} else if (structuredError.code === 'METHOD_NOT_FOUND') {
				errorCode = A2A_ERROR_CODES.METHOD_NOT_FOUND;
			}

			return {
				jsonrpc: '2.0',
				id,
				error: {
					code: errorCode,
					message: error.message,
					data: structuredError.details,
				},
			};
		}

		return {
			jsonrpc: '2.0',
			id,
			error: {
				code: A2A_ERROR_CODES.INTERNAL_ERROR,
				message: error instanceof Error ? error.message : 'Internal error',
				data: error instanceof Error ? { stack: error.stack } : error,
			},
		};
	}
}

// Global task manager instance for stateful operations
let globalTaskManager: TaskManager | undefined;

// Main handler function for the package
export async function handleA2A(input: unknown): Promise<string> {
	try {
		// Parse JSON-RPC request
		const parseResult = JsonRpcRequestSchema.safeParse(input);
		if (!parseResult.success) {
			const errorResponse: JsonRpcResponse = {
				jsonrpc: '2.0',
				id: null,
				error: {
					code: A2A_ERROR_CODES.INVALID_REQUEST,
					message: 'Invalid JSON-RPC request',
					data: { issues: parseResult.error.issues },
				},
			};
			return createJsonOutput(errorResponse);
		}

		const request = parseResult.data;

		// Use global task manager to maintain state across requests
		if (!globalTaskManager) {
			globalTaskManager = new TaskManager();
		}
		const rpcHandler = new A2ARpcHandler(globalTaskManager);

		// Handle the request
		const response = await rpcHandler.handle(request);

		return createJsonOutput(response);
	} catch (error) {
		// Fallback error response
		const errorResponse: JsonRpcResponse = {
			jsonrpc: '2.0',
			id: null,
			error: {
				code: A2A_ERROR_CODES.INTERNAL_ERROR,
				message: error instanceof Error ? error.message : 'Unknown error',
				data: error instanceof Error ? { stack: error.stack } : error,
			},
		};

		return createJsonOutput(errorResponse);
	}
}

// Factory function
export const createA2ARpcHandler = (
	taskManager?: TaskManager,
): A2ARpcHandler => {
	return new A2ARpcHandler(taskManager || new TaskManager());
};

