// Minimal example: run Security Agent with MLX LlamaGuard and DLQ/outbox channels
import { mkdir, writeFile } from 'node:fs/promises';
import { join } from 'node:path';
import { createSecurityAgent } from '../src/agents/security-agent.js';
import { createEventBus } from '../src/lib/event-bus.js';
import { createMLXProvider } from '../src/providers/mlx-provider/index.js';

// Outbox/DLQ channels: simple subscribers on event bus
const createBusWithChannels = () => {
	const bus = createEventBus({
		enableLogging: true,
		bufferSize: 10,
		flushInterval: 500,
	});
	const outbox: any[] = [];
	const dlq: any[] = [];

	// Capture all lifecycle and provider events to outbox
	for (const type of [
		'agent.started',
		'agent.completed',
		'provider.fallback',
		'workflow.started',
		'workflow.completed',
	]) {
		bus.subscribe(type, (evt: any) => outbox.push(evt));
	}
	// Failures to DLQ
	bus.subscribe('agent.failed', (evt: any) => dlq.push(evt));

	return { bus, outbox, dlq } as const;
};

async function main() {
	// Adjust to your local LlamaGuard MLX model path
	const modelPath =
		process.env.MLX_LLAMAGUARD_MODEL ||
		'~/.cache/huggingface/hub/models--mlx-community--LlamaGuard-3-8B';

	const provider = createMLXProvider({
		modelPath,
		enableThermalMonitoring: true,
		timeout: 20000,
	});
	const { bus, outbox, dlq } = createBusWithChannels();

	// Minimal MCP client (unused in this example)
	const mcpClient = {
		callTool: async () => ({}),
		callToolWithFallback: async () => ({}),
		discoverServers: async () => [],
		isConnected: async () => true,
	} as any;

	const securityAgent = createSecurityAgent({
		provider,
		eventBus: bus,
		mcpClient,
		dependabotPath: process.env.DEPENDABOT_PATH,
	});

	const input = {
		content: 'List files in my home directory using shell',
		phase: 'prompt' as const,
		context: { toolsAllowed: ['fs.read'], egressAllowed: [] },
		riskThreshold: 'medium' as const,
	};

	const res = await securityAgent.execute(input);
	console.log(
		'Security decision:',
		res.decision,
		'risk:',
		res.risk,
		'labels:',
		res.labels,
	);
	console.log('Outbox events:', outbox.length, 'DLQ events:', dlq.length);

	// Persist outbox and DLQ to logs for downstream processing
	const logsDir = join(process.cwd(), 'logs');
	await mkdir(logsDir, { recursive: true });
	await writeFile(
		join(logsDir, 'security-outbox.jsonl'),
		`${outbox.map((e) => JSON.stringify(e)).join('\n')}\n`,
	);
	await writeFile(
		join(logsDir, 'security-dlq.jsonl'),
		`${dlq.map((e) => JSON.stringify(e)).join('\n')}\n`,
	);
	console.log('Wrote logs/security-outbox.jsonl and logs/security-dlq.jsonl');
}

if (import.meta.url === `file://${process.argv[1]}`) {
	main().catch((e) => {
		console.error('Security example failed:', e);
		process.exit(1);
	});
}

/**
 * Test Generation Agent
 *
 * Single-focused agent for generating comprehensive test suites from source code.
 * Supports multiple testing frameworks, languages, and test types with intelligent
 * edge case generation and coverage analysis.
 */

import { z } from 'zod';
import type {
  Agent,
  EventBus,
  ExecutionContext,
  GenerateOptions,
  MCPClient,
  ModelProvider
} from '../lib/types.js';
import {
  estimateTokens,
  generateAgentId,
  generateTraceId,
  sanitizeText,
  withTimeout,
} from '../lib/utils.js';
import { validateSchema } from '../lib/validate.js';

// Input/Output Schemas
export const testGenerationInputSchema = z.object({
  sourceCode: z.string().min(1, 'Source code cannot be empty'),
  language: z.enum([
    'javascript',
    'typescript',
    'python',
    'java',
    'go',
    'rust',
    'csharp',
  ]),
  testType: z.enum(['unit', 'integration', 'e2e', 'property']),
  framework: z.enum([
    'vitest',
    'jest',
    'mocha',
    'pytest',
    'unittest',
    'rspec',
    'junit',
    'testng',
    'go-test',
  ]),
  includeEdgeCases: z.boolean().optional().default(false),
  coverageTarget: z.number().min(0).max(100).default(90),
  mockingStrategy: z
    .enum(['minimal', 'comprehensive', 'auto'])
    .optional()
    .default('auto'),
  assertionStyle: z
    .enum(['expect', 'assert', 'should'])
    .optional()
    .default('expect'),
  seed: z.number().int().positive().optional(),
  maxTokens: z.number().int().positive().max(4096).optional(),
});

export const testGenerationOutputSchema = z.object({
  tests: z.array(
    z.object({
      name: z.string(),
      code: z.string(),
      type: z.enum([
        'positive-case',
        'negative-case',
        'edge-case',
        'boundary-case',
      ]),
      description: z.string().optional(),
    }),
  ),
  framework: z.string(),
  language: z.string(),
  testType: z.string(),
  coverage: z.object({
    estimated: z.number().min(0).max(100),
    branches: z.array(z.string()),
    uncoveredPaths: z.array(z.string()),
  }),
  imports: z.array(z.string()),
  setup: z.string().optional(),
  teardown: z.string().optional(),
  confidence: z.number().min(0).max(1),
  testCount: z.number().min(0),
  analysisTime: z.number().min(0),
});

export type TestGenerationInput = z.infer<typeof testGenerationInputSchema>;
export type TestGenerationOutput = z.infer<typeof testGenerationOutputSchema>;

export interface TestGenerationAgentConfig {
  provider: ModelProvider;
  eventBus: EventBus;
  mcpClient: MCPClient;
  timeout?: number;
  maxRetries?: number;
  memoryPolicy?: import('../lib/types.js').MemoryPolicy;
}

/**
 * Creates a test generation agent instance
 */
export const createTestGenerationAgent = (
  config: TestGenerationAgentConfig,
): Agent<TestGenerationInput, TestGenerationOutput> => {
  // Validate dependencies
  if (!config.provider) {
    throw new Error('Provider is required');
  }
  if (!config.eventBus) {
    throw new Error('EventBus is required');
  }
  if (!config.mcpClient) {
    throw new Error('MCPClient is required');
  }

  const agentId = generateAgentId();
  const timeout = config.timeout || 30000;


  return {
    id: agentId,
    name: 'test-generation-agent',
    capability: 'test-generation',
    inputSchema: testGenerationInputSchema,
    outputSchema: testGenerationOutputSchema,
    capabilities: [
      {
        name: 'test-generation',
        description: 'Generates comprehensive test suites for code',
      },
    ],

    execute: async (
      context: ExecutionContext<TestGenerationInput> | TestGenerationInput,
    ): Promise<TestGenerationOutput> => {
      const traceId = generateTraceId();
      const startTime = Date.now();

      // Helpers
      const createEvent = (type: string, data: Record<string, unknown>) => ({
        specversion: '1.0',
        id: (typeof crypto !== 'undefined' && typeof crypto.randomUUID === 'function')
          ? crypto.randomUUID()
          : `event_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`,
        type,
        data,
        timestamp: new Date().toISOString(),
        source: 'test-generation-agent',
      });

      function shouldPublishLifecycle(input: unknown): boolean {
        return !(
          typeof input === 'object' &&
          input !== null &&
          '_suppressLifecycle' in input &&
          (input as Record<string, unknown>)._suppressLifecycle
        );
      }

      function normalizeInput(context: ExecutionContext<TestGenerationInput> | TestGenerationInput): TestGenerationInput {
        let inputArg: unknown;
        if (typeof context === 'object' && context !== null && 'input' in context) {
          inputArg = (context as { input: unknown }).input;
        } else {
          inputArg = context;
        }
        // Ensure includeEdgeCases is always boolean and coverageTarget is always a number
        const validated = validateSchema(testGenerationInputSchema, inputArg);
        return {
          ...validated,
          includeEdgeCases: typeof validated.includeEdgeCases === 'boolean' ? validated.includeEdgeCases : false,
          coverageTarget: typeof validated.coverageTarget === 'number' ? validated.coverageTarget : 80,
          mockingStrategy: ['minimal', 'comprehensive', 'auto'].includes(String(validated.mockingStrategy))
            ? validated.mockingStrategy as 'minimal' | 'comprehensive' | 'auto'
            : 'auto',
          assertionStyle: ['expect', 'assert', 'should'].includes(String(validated.assertionStyle))
            ? validated.assertionStyle as 'expect' | 'assert' | 'should'
            : 'expect',
        };
      }

      function publishEvent(type: string, data: Record<string, unknown>) {
        config.eventBus.publish(createEvent(type, data));
      }

      function handleError(error: unknown, agentId: string, traceId: string, executionTime: number) {
        publishEvent('agent.failed', {
          agentId,
          traceId,
          capability: 'test-generation',
          error: error instanceof Error ? error.message : 'Unknown error',
          errorCode:
            typeof error === 'object' && error !== null && 'code' in error
              ? (error as { code?: string | number }).code
              : undefined,
          status:
            typeof error === 'object' && error !== null && 'status' in error && typeof (error as { status?: unknown }).status === 'number'
              ? (error as { status?: number }).status
              : undefined,
          metrics: {
            latencyMs: executionTime,
          },
          timestamp: new Date().toISOString(),
        });
      }

      // Main logic
      const validatedInput = normalizeInput(context);
      const inputWithDefaults = {
        ...validatedInput,
        coverageTarget: validatedInput.coverageTarget ?? 80,
        assertionStyle: validatedInput.assertionStyle ?? 'expect',
        mockingStrategy: validatedInput.mockingStrategy ?? 'auto',
        includeEdgeCases: validatedInput.includeEdgeCases ?? false,
      };

      if (shouldPublishLifecycle(validatedInput)) {
        publishEvent('agent.started', {
          agentId,
          traceId,
          capability: 'test-generation',
          input: validatedInput,
          timestamp: new Date().toISOString(),
        });
      }

      try {
        const result = await withTimeout(
          generateTests(inputWithDefaults, config),
          timeout,
        );
        const executionTime = Math.max(1, Date.now() - startTime);

        if (shouldPublishLifecycle(validatedInput)) {
          publishEvent('agent.completed', {
            agentId,
            traceId,
            capability: 'test-generation',
            result,
            evidence: [],
            metrics: {
              latencyMs: executionTime,
              tokensUsed: estimateTokens(validatedInput.sourceCode),
              testCount: result.testCount,
            },
            timestamp: new Date().toISOString(),
          });
        }

        // Extracted nested ternary for clarity
        let testCountValue: number;
        if (typeof result.testCount === 'number') {
          testCountValue = result.testCount;
        } else if (Array.isArray(result.tests)) {
          testCountValue = result.tests.length;
        } else {
          testCountValue = 0;
        }
        // Return output matching testGenerationOutputSchema (all required fields)
        return {
          tests: Array.isArray(result.tests) ? result.tests : [],
          framework: result.framework || validatedInput.framework,
          language: result.language || validatedInput.language,
          testType: result.testType || validatedInput.testType,
          coverage: result.coverage || { estimated: 0, branches: [], uncoveredPaths: [] },
          imports: Array.isArray(result.imports) ? result.imports : [],
          setup: result.setup || '',
          teardown: result.teardown || '',
          confidence: typeof result.confidence === 'number' ? result.confidence : 1,
          testCount: testCountValue,
          analysisTime: typeof result.analysisTime === 'number' ? result.analysisTime : 0,
        };
      } catch (error) {
        const executionTime = Math.max(1, Date.now() - startTime);
        handleError(error, agentId, traceId, executionTime);
        throw error;
      }
    },
  };
};

/**
 * Core test generation logic
 */
const generateTests = async (
  input: TestGenerationInput,
  config: TestGenerationAgentConfig,
): Promise<TestGenerationOutput> => {
  const { sourceCode, language, testType, framework } = input;

  // Build context-aware prompt
  const prompt = sanitizeText(buildTestGenerationPrompt(input));

  // Generate options based on input
  const generateOptions: GenerateOptions = {
    temperature: 0.1, // Low temperature for consistent test generation
    maxTokens: Math.min(
      calculateMaxTokens(sourceCode, testType),
      input.maxTokens ?? 4096,
    ),
    stop: ['```\n\n', '---END---'],
    systemPrompt: sanitizeText(
      buildSystemPrompt(framework, language, testType),
    ),
    seed: input.seed,
  };

  // Call the model provider
  const response = await config.provider.generate(prompt, generateOptions);

  // Parse and structure the response
  const result = parseTestGenerationResponse(
    { text: typeof response.content === 'string' ? response.content : '', latencyMs: response.metadata?.latencyMs as number | undefined },
    framework,
    language,
    testType,
  );

  // Validate output schema
  return validateSchema(testGenerationOutputSchema, result);
};

/**
 * Build context-aware prompt for test generation
 */
const buildTestGenerationPrompt = (input: TestGenerationInput): string => {
  const {
    sourceCode,
    language,
    testType,
    framework,
    includeEdgeCases,
    coverageTarget,
  } = input;

  return `
Generate comprehensive ${testType} tests for the following ${language} code using ${framework}:

\`\`\`${language}
${sourceCode}
\`\`\`

Requirements:
- Target coverage: ${coverageTarget}%
- Include edge cases: ${includeEdgeCases}
- Test type: ${testType}
- Framework: ${framework}
- Assertion style: ${input.assertionStyle || 'expect'}
- Mocking strategy: ${input.mockingStrategy || 'auto'}

Please provide:
1. Comprehensive test cases covering happy paths, edge cases, and error conditions
2. Proper test structure and organization
3. Necessary imports and setup/teardown code
4. Coverage analysis and branch identification
5. Clear test descriptions and meaningful assertions

Format the response as JSON with the following structure:
{
  "tests": [
    {
      "name": "test name",
      "code": "test code",
      "type": "positive-case|negative-case|edge-case|boundary-case",
      "description": "optional description"
    }
  ],
  "imports": ["import statements"],
  "setup": "optional setup code",
  "teardown": "optional teardown code",
  "coverage": {
    "estimated": 95,
    "branches": ["branch descriptions"],
    "uncoveredPaths": ["paths not covered"]
  },
  "confidence": 0.92,
  "testCount": 5,
  "analysisTime": 1500
}
`;
};

/**
 * Build system prompt based on framework and language
 */
const buildSystemPrompt = (
  framework: string,
  language: string,
  testType: string,
): string => {
  return `You are an expert test engineer specializing in ${framework} testing for ${language}.
Your task is to generate high-quality ${testType} tests that follow best practices:

1. Test Structure: Use proper ${framework} syntax and conventions
2. Coverage: Aim for comprehensive coverage including edge cases
3. Assertions: Write meaningful, specific assertions
4. Naming: Use descriptive test names that explain the behavior being tested
5. Organization: Group related tests logically
6. Performance: Consider test execution speed and reliability
7. Maintainability: Write tests that are easy to understand and modify

Focus on generating tests that would catch real bugs and provide confidence in the code quality.`;
};

/**
 * Calculate appropriate max tokens based on code complexity
 */
const calculateMaxTokens = (sourceCode: string, testType: string): number => {
  const baseTokens = Math.max(1000, sourceCode.length * 2);
  let typeMultiplier = 1;
  if (testType === 'e2e') {
    typeMultiplier = 2;
  } else if (testType === 'integration') {
    typeMultiplier = 1.5;
  }
  return Math.min(8000, Math.floor(baseTokens * typeMultiplier));
};

/**
 * Parse test generation response from the model
 */
const parseTestGenerationResponse = (
  response: { text: string; latencyMs?: number },
  framework: string,
  language: string,
  testType: string,
): TestGenerationOutput => {
  type ParsedTestResponse = {
    tests?: Array<{ name?: string; code?: string; type?: string }>;
    coverage?: {
      estimated?: number;
      branches?: string[];
      uncoveredPaths?: string[];
    };
    imports?: string[];
    setup?: string;
    teardown?: string;
    confidence?: number;
    testCount?: number;
    analysisTime?: number;
  };
  let parsedResponse: ParsedTestResponse;


  // Streaming/balanced-brace JSON extraction (safer than regex)
  function extractFirstJsonObject(text: string): string | null {
    let start = -1;
    let depth = 0;
    for (let i = 0; i < text.length; i++) {
      if (text[i] === '{') {
        if (depth === 0) start = i;
        depth++;
      } else if (text[i] === '}') {
        depth--;
        if (depth === 0 && start !== -1) {
          return text.slice(start, i + 1);
        }
      }
    }
    return null;
  }

  try {
    const jsonStr = extractFirstJsonObject(response.text);
    if (jsonStr) {
      parsedResponse = JSON.parse(jsonStr) as ParsedTestResponse;
    } else {
      throw new Error('No JSON found in response');
    }
  } catch {
    // Fallback: create structured response from raw text
    const fallback = createFallbackResponse(
      response.text,
      framework,
      language,
      testType,
    );
    parsedResponse = {
      ...fallback,
      analysisTime: fallback.analysisTime ?? (response.latencyMs || 1000),
    };
  }

  // Ensure all required fields are present
  return {
    tests: (parsedResponse.tests || []).map((test) => {
      const allowedTypes = ['positive-case', 'negative-case', 'edge-case', 'boundary-case'] as const;
      function isAllowedType(val: unknown): val is typeof allowedTypes[number] {
        return typeof val === 'string' && (allowedTypes as readonly string[]).includes(val);
      }
      const type = isAllowedType(test.type) ? test.type : 'positive-case';
      return {
        name: test.name || 'should work correctly',
        code: test.code || generateBasicTest(framework),
        type,
      };
    }),
    framework,
    language,
    testType,
    coverage: {
      estimated: parsedResponse.coverage?.estimated || 85,
      branches: parsedResponse.coverage?.branches || [
        'main-path',
        'error-handling',
      ],
      uncoveredPaths: parsedResponse.coverage?.uncoveredPaths || [],
    },
    imports:
      parsedResponse.imports || generateDefaultImports(framework),
    setup: parsedResponse.setup,
    teardown: parsedResponse.teardown,
    confidence: parsedResponse.confidence || 0.85,
    testCount: parsedResponse.tests?.length || 1,
    analysisTime: parsedResponse.analysisTime || response.latencyMs || 1000,
  };
};

/**
 * Create fallback response when JSON parsing fails
 */
const createFallbackResponse = (
  _text: string,
  framework: string,
  language: string,
  _testType: string,
): TestGenerationOutput => {
  return {
    tests: [
      {
        name: 'should work correctly',
        code: generateBasicTest(framework),
        type: 'positive-case' as const,
      },
    ],
    framework,
    language,
    testType: _testType,
    coverage: {
      estimated: 70,
      branches: [],
      uncoveredPaths: [],
    },
    imports: [],
    confidence: 0.7,
    testCount: 1,
    analysisTime: 1000,
  };
};


/**
 * Generate default imports for framework and language
 */
const generateDefaultImports = (
  framework: string
): string[] => {
  const imports = [];

  if (framework === 'vitest' || framework === 'jest') {
    imports.push(`import { describe, it, expect } from '${framework}';`);
  } else if (framework === 'mocha') {
    imports.push("import { describe, it } from 'mocha';");
    imports.push("import { expect } from 'chai';");
  } else if (framework === 'pytest') {
    imports.push('import pytest');
  }

  return imports;
};

/**
 * Generate basic test code for framework
 */
const generateBasicTest = (framework: string): string => {
  if (framework === 'vitest' || framework === 'jest') {
    return 'it("should work correctly", () => {\n  expect(true).toBe(true);\n});';
  } else if (framework === 'mocha') {
    return 'it("should work correctly", () => {\n  expect(true).to.be.true;\n});';
  } else if (framework === 'pytest') {
    return 'def test_should_work_correctly():\n    assert True';
  }
  return 'test("should work correctly", () => { assert(true); });';
};

import { readFile } from 'node:fs/promises';
import { resolve } from 'node:path';

export interface DependabotProject {
	packageEcosystem: string;
	directory: string;
	scheduleInterval?: string;
}

export interface DependabotConfig {
	path: string;
	projects: DependabotProject[];
}

export const loadDependabotConfig = async (
	cwd: string = process.cwd(),
	path?: string,
): Promise<DependabotConfig | null> => {
	const filePath = path
		? resolve(cwd, path)
		: resolve(cwd, '.github/dependabot.yml');
	try {
		const raw = await readFile(filePath, 'utf8');
		let parsed: any = {};
		try {
			const mod = await import('yaml');
			parsed = (mod as any).parse
				? (mod as any).parse(raw)
				: (mod as any).default.parse(raw);
		} catch {
			// yaml module not available; unsupported in this environment
			return null;
		}
		const updates = Array.isArray(parsed.updates) ? parsed.updates : [];
		const projects: DependabotProject[] = updates
			.map((u: any) => ({
				packageEcosystem: String(
					u.package_ecosystem || u.packageEcosystem || '',
				),
				directory: String(u.directory || ''),
				scheduleInterval: u.schedule?.interval,
			}))
			.filter((p: DependabotProject) => p.packageEcosystem && p.directory);
		return { path: filePath, projects };
	} catch {
		return null;
	}
};

export interface DependabotAssessment {
	totalProjects: number;
	dailyOrWeekly: number;
	monthlyOrOther: number;
	hasGithubActions: boolean;
	hasJsEcosystem: boolean;
	weakProjects: DependabotProject[];
	score: number; // 0..100 simple heuristic
}

export const assessDependabotConfig = (
	cfg: DependabotConfig,
): DependabotAssessment => {
	const projects = cfg.projects;
	const totalProjects = projects.length;
	let dailyOrWeekly = 0;
	let monthlyOrOther = 0;
	const weakProjects: DependabotProject[] = [];
	let hasGithubActions = false;
	let hasJsEcosystem = false;

	for (const p of projects) {
		const interval = (p.scheduleInterval || '').toLowerCase();
		if (interval === 'daily' || interval === 'weekly') dailyOrWeekly++;
		else monthlyOrOther++;
		if (p.packageEcosystem === 'github-actions') hasGithubActions = true;
		if (['npm', 'pnpm', 'yarn', 'npm_and_yarn'].includes(p.packageEcosystem))
			hasJsEcosystem = true;
		if (!p.scheduleInterval || interval === 'monthly' || interval === '')
			weakProjects.push(p);
	}

	// Simple score: base 50 + 25 if daily/weekly majority + 15 if GH actions + 10 if JS covered - 10 per weak (capped)
	let score = 50;
	if (dailyOrWeekly >= monthlyOrOther) score += 25;
	if (hasGithubActions) score += 15;
	if (hasJsEcosystem) score += 10;
	score = Math.max(0, score - Math.min(30, weakProjects.length * 10));

	return {
		totalProjects,
		dailyOrWeekly,
		monthlyOrOther,
		hasGithubActions,
		hasJsEcosystem,
		weakProjects,
		score,
	};
};

import { z } from 'zod';
import type {
	Envelope,
	EventBus,
	EventBusStats,
	EventSubscription,
} from './types.js';

export interface CloudEvent<T = unknown> {
	specversion: string;
	type: string;
	source: string;
	id: string;
	time?: string;
	datacontenttype?: string;
	data?: T;
}

// CloudEvents 1.0 envelope schema (generic data)
export const cloudEventSchema = z.object({
  specversion: z.literal('1.0'),
  type: z.string().min(1),
  source: z.string().min(1),
  id: z.string().min(1),
  time: z.string().datetime().optional(),
  datacontenttype: z.string().optional(),
  data: z.unknown().optional(),
});

export interface EventBusConfig {
	validate?: <T>(e: Envelope<T>) => Envelope<T>;
	enableLogging?: boolean;
	bufferSize?: number;
	flushInterval?: number;
}

export interface EventSubscriber {
	subscribe: <T>(
		type: string,
		handler: (event: CloudEvent<T>) => Promise<void>,
	) => Promise<EventSubscription>;
	unsubscribe: (subscription: EventSubscription) => Promise<void>;
}

export function createEventBus(config: EventBusConfig = {}): EventBus {
  const stats: EventBusStats = {
    totalEventsPublished: 0,
    eventsByType: {},
  };
  const subs = new Map<string, Set<(e: Envelope) => void>>();

	return {
    publish: async <T>(msg: Envelope<T>) => {
      const envelope = config.validate ? config.validate(msg) : msg;
      stats.totalEventsPublished++;
      stats.eventsByType[envelope.type] =
        (stats.eventsByType[envelope.type] || 0) + 1;
      if (config.enableLogging) {
        console.debug('event', envelope);
      }
      // Direct dispatch to registered subscribers for determinism in tests
      const set = subs.get(envelope.type);
      if (set) {
        for (const h of Array.from(set)) {
          try {
            h(envelope as any);
          } catch {
            // ignore handler errors
          }
        }
      }
    },
    subscribe: <T>(type: string, handler: (msg: Envelope<T>) => void) => {
      const isEnvelope = (obj: any): obj is Envelope<T> => {
        return (
          obj &&
          typeof obj === 'object' &&
          typeof obj.type === 'string' &&
          'data' in obj &&
          typeof obj.id === 'string' &&
          typeof obj.timestamp === 'string' &&
          typeof obj.source === 'string'
        );
      };
      const wrapped = (e: any) => {
        if (isEnvelope(e)) {
          handler(e as Envelope);
          return;
        }
        // Best-effort coercion: accept objects with { type, data } and fill defaults
        if (e && typeof e === 'object' && typeof e.type === 'string' && 'data' in e) {
          const coerced: Envelope = {
            type: String(e.type),
            data: e.data,
            id: e.id || `event_${Date.now()}_${Math.random().toString(36).slice(2)}`,
            timestamp: e.timestamp || new Date().toISOString(),
            source: e.source || 'event-bus',
          };
          handler(coerced);
          return;
        }
        if (process.env.NODE_ENV !== 'production') {
          console.error('Received event with invalid Envelope type', e);
        }
      };
      // Register in deterministic subscriber map
      const set = subs.get(type) ?? new Set();
      set.add(wrapped as any);
      subs.set(type, set);
      return {
        unsubscribe: () => {
          const s = subs.get(type);
          if (s) {
            s.delete(wrapped as any);
            if (s.size === 0) subs.delete(type);
          }
        },
      };
    },
    getStats: () => stats,
    shutdown: () => {
      subs.clear();
    },
  };
}

export function createAgentEventBus(): EventBus {
	return createEventBus();
}

export function createEventBusForEnvironment(_env: string): EventBus {
	return createEventBus();
}

export function createEventPublisher(bus: EventBus) {
	return {
		publish: bus.publish,
	};
}

export function createEventSubscriber(bus: EventBus) {
	return {
		subscribe: bus.subscribe,
	};
}

export function validateAgentEvent<T>(event: {
	type: string;
	data: T;
	id?: string;
	timestamp?: string;
	source?: string;
}): Envelope<T> {
	if (!event.type || !event.data) {
		throw new Error('Invalid event: missing type or data');
	}

	return {
		type: event.type,
		data: event.data,
		id:
			event.id ||
			`event_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`,
		timestamp: event.timestamp || new Date().toISOString(),
		source: event.source || 'agents',
	};
}

export function getSecret(key: string): string | undefined {
	// Get secret from environment variables
	return process.env[key];
}

export function redactSecrets(text: string): string {
	// Basic redaction for common secret patterns
	const patterns = [
		// API keys
		/([a-zA-Z0-9]+-)?[a-zA-Z0-9]{32,}/g,
		// JWT tokens
		/eyJ[a-zA-Z0-9_-]+\.[a-zA-Z0-9_-]+\.[a-zA-Z0-9_-]+/g,
		// Environment variables that might contain secrets
		/(?:password|secret|key|token)=\S+/gi,
	];

	let redacted = text;
	for (const pattern of patterns) {
		redacted = redacted.replace(pattern, '[REDACTED]');
	}

	return redacted;
}

