		options: EventStreamOptions,
		attempt?: number,
	): Promise<{ events: Event[]; backoffMs?: number }>;
	stop(): void;
	getStats(): {
		totalEvents: number;
		activeSubscriptions: number;
		bufferSizes: Record<string, number>;
	};
}

/**
 * Event Manager with SSE and WebSocket support
 */
class EventManagerClass extends EventEmitter {
	private config: Config;
	private subscriptions = new Map<string, EventSubscription>();
	private eventBuffer = new Map<string, Event[]>(); // taskId -> events
	private globalEvents: Event[] = [];
	private lastEventTimes = new Map<string, number>();
	private heartbeatIntervals = new Map<string, NodeJS.Timeout>();
	private cleanupInterval?: NodeJS.Timeout;
	private eventCounter = 0;
	private io?: IOServer;

	constructor(config: Config) {
		super();
		this.config = config;
		this.setupCleanupInterval();
	}

	attachIO(io: IOServer): void {
		this.io = io;
		io.on(
			'connection',
			(
				socket: Socket<
					DefaultEventsMap,
					DefaultEventsMap,
					DefaultEventsMap,
					SocketData
				>,
			) => {
				socket.on(
					'subscribe',
					(
						{
							taskId,
							eventTypes,
						}: { taskId?: string; eventTypes?: EventType[] },
						ack?: (res: unknown) => void,
					) => {
						const subId = this.subscribe({ taskId, eventTypes }, (event) => {
							socket.emit(event.type, event);
						});
						socket.data.subscriptionId = subId;
						if (taskId) socket.join(taskId);
						ack?.({ ok: true });
					},
				);

				socket.on(
					'unsubscribe',
					(
						{ taskId }: { taskId?: string } = {},
						ack?: (res: unknown) => void,
					) => {
						const subId = socket.data.subscriptionId;
						if (subId) {
							this.unsubscribe(subId);
							socket.data.subscriptionId = undefined;
						}
						if (taskId) socket.leave(taskId);
						ack?.({ ok: true });
					},
				);

				socket.on('disconnect', () => {
					const subId = socket.data.subscriptionId;
					if (subId) {
						this.unsubscribe(subId);
					}
				});
			},
		);
	}

	/**
	 * Emit an event to all subscribers
	 */
	async emitEvent(event: Event): Promise<void> {
		// Store event in buffer
		if (!this.eventBuffer.has(event.taskId)) {
			this.eventBuffer.set(event.taskId, []);
		}

		const taskEvents = this.eventBuffer.get(event.taskId)!;
		taskEvents.push(event);
		this.lastEventTimes.set(event.taskId, Date.now());

		// Also store in global events
		this.globalEvents.push(event);

		// Keep buffer size manageable
		this.maintainBufferSize(event.taskId);

		// Persist to NDJSON ledger
		await this.persistEvent(event);

		// Notify subscribers
		this.notifySubscribers(event);

		// Emit to EventEmitter for internal use
		this.emit('event', event);
		this.emit(`event:${event.type}`, event);
		this.emit(`task:${event.taskId}`, event);
		if (this.io) {
			this.io.to(event.taskId).emit(event.type, event);
		}
	}

	/**
	 * Subscribe to events with SSE or WebSocket transport
	 */
	subscribe(
		options: EventStreamOptions,
		callback: (event: Event) => void,
	): string {
		const subscriptionId = `sub_${Date.now()}_${Math.random().toString(36).substring(2)}`;

		const subscription: EventSubscription = {
			id: subscriptionId,
			taskId: options.taskId,
			eventTypes: options.eventTypes || [
				'PlanStarted',
				'StepCompleted',
				'AwaitingApproval',
				'Canceled',
				'Resumed',
				'DeliverableReady',
				'Failed',
			],
			callback,
			transport: options.transport || this.config.events.transport,
			lastEventId: options.lastEventId,
			createdAt: Date.now(),
		};

		this.subscriptions.set(subscriptionId, subscription);

		// Send existing events if requested
		if (subscription.lastEventId) {
			this.sendMissedEvents(subscription);
		}

		// Set up heartbeat for SSE
		if (subscription.transport === 'sse') {
			this.setupHeartbeat(subscriptionId);
		}

		return subscriptionId;
	}

	/**
	 * Unsubscribe from events
	 */
	unsubscribe(subscriptionId: string): void {
		this.subscriptions.delete(subscriptionId);

		// Clear heartbeat if exists
		const heartbeat = this.heartbeatIntervals.get(subscriptionId);
		if (heartbeat) {
			clearInterval(heartbeat);
			this.heartbeatIntervals.delete(subscriptionId);
		}
	}

	/**

   * Retrieve events
   */
	getEvents(options: EventStreamOptions): Event[] {
		const { taskId, eventTypes, lastEventId } = options;

		let events: Event[];

		if (taskId) {
			events = this.eventBuffer.get(taskId) || [];
		} else {
			events = this.globalEvents;
		}

		// Filter by event types if specified
		if (eventTypes && eventTypes.length > 0) {
			events = events.filter((e) => eventTypes.includes(e.type));
		}

		// Filter by lastEventId if specified
		if (lastEventId) {
			const lastIndex = events.findIndex((e) => e.id === lastEventId);
			if (lastIndex >= 0) {
				events = events.slice(lastIndex + 1);
			}
		}

		return events;
	}

	/**

   * Create SSE stream for Express response
   */
	createSSEStream(res: Response, options: EventStreamOptions): string {
		const subscriptionId = this.subscribe(options, (event) => {
			this.writeSSEEvent(res, event);
		});

		// Set SSE headers
		res.writeHead(200, {
			'Content-Type': 'text/event-stream',
			'Cache-Control': 'no-cache',
			Connection: 'keep-alive',
			'Access-Control-Allow-Origin': '*',
			'X-Accel-Buffering': 'no', // Disable nginx buffering
		});

		// Send initial heartbeat
		this.writeSSEHeartbeat(res);

		// Handle client disconnect
		const cleanup = () => {
			this.unsubscribe(subscriptionId);
		};

		res.on('close', cleanup);
		res.on('error', cleanup);

		return subscriptionId;
	}

	/**
	 * Get event statistics
	 */
	getStats(): {
		totalEvents: number;
		activeSubscriptions: number;
		bufferSizes: Record<string, number>;
	} {
		const bufferSizes: Record<string, number> = {};
		for (const [taskId, events] of this.eventBuffer) {
			bufferSizes[taskId] = events.length;
		}

		return {
			totalEvents: this.globalEvents.length,
			activeSubscriptions: this.subscriptions.size,
			bufferSizes,
		};
	}

	private notifySubscribers(event: Event): void {
		for (const subscription of this.subscriptions.values()) {
			// Check if subscription matches this event
			if (this.shouldNotifySubscription(subscription, event)) {
				try {
					subscription.callback(event);
				} catch (error) {
					logError('Error in event callback', { error });
				}
			}
		}
	}

	private shouldNotifySubscription(
		subscription: EventSubscription,
		event: Event,
	): boolean {
		// Check task filter
		if (subscription.taskId && subscription.taskId !== event.taskId) {
			return false;
		}

		// Check event type filter
		if (!subscription.eventTypes.includes(event.type)) {
			return false;
		}

		return true;
	}

	private setupHeartbeat(subscriptionId: string): void {
		const interval = setInterval(() => {
			const subscription = this.subscriptions.get(subscriptionId);
			if (!subscription) {
				this.heartbeatIntervals.delete(subscriptionId);
				return;
			}
			// Heartbeat is handled in SSE stream creation
		}, this.config.events.heartbeat_ms);

		this.heartbeatIntervals.set(subscriptionId, interval);
	}

	private writeSSEEvent(res: Response, event: Event): void {
		const eventId = this.eventCounter++;
		const data = JSON.stringify(event);

		res.write(`id: ${eventId}\n`);
		res.write(`event: ${event.type}\n`);
		res.write(`data: ${data}\n\n`);
	}

	private writeSSEHeartbeat(res: Response): void {
		res.write('event: heartbeat\n');
		res.write('data: {}\n\n');
	}

	private sendMissedEvents(subscription: EventSubscription): void {
		let events = subscription.taskId
			? this.eventBuffer.get(subscription.taskId) || []
			: this.globalEvents;

		events = events.filter((e) => subscription.eventTypes.includes(e.type));

		if (subscription.lastEventId) {
			const lastIndex = events.findIndex(
				(e) => e.id === subscription.lastEventId,
			);
			if (lastIndex >= 0) {
				events = events.slice(lastIndex + 1);
			}
		}

		for (const event of events) {
			subscription.callback(event);
		}
	}

	private maintainBufferSize(taskId: string): void {
		const taskLimit = this.config.events.max_task_events;
		const globalLimit = this.config.events.max_global_events;

		const events = this.eventBuffer.get(taskId);
		if (events && events.length > taskLimit) {
			events.splice(0, events.length - taskLimit);
		}

		if (this.globalEvents.length > globalLimit) {
			this.globalEvents.splice(0, this.globalEvents.length - globalLimit);
		}
	}

	async pollEvents(
		options: EventStreamOptions,
		attempt: number = 0,
	): Promise<{ events: Event[]; backoffMs?: number }> {
		const events = this.getEvents(options);

		// If no events and this is not the first attempt, apply backoff
		if (events.length === 0 && attempt > 0) {
			const backoffMs = Math.min(1000 * 2 ** (attempt - 1), 30000);
			return { events: [], backoffMs };
		}

		return { events };
	}

	stop(): void {
		if (this.cleanupInterval) {
			clearInterval(this.cleanupInterval);
			this.cleanupInterval = undefined;
		}
		for (const interval of this.heartbeatIntervals.values()) {
			clearInterval(interval);
		}
		this.heartbeatIntervals.clear();
	}

	private async persistEvent(event: Event): Promise<void> {
		try {
			const ledgerPath = getStatePath('ledger.ndjson');
			const eventLine = `${JSON.stringify(event)}\n`;
			await appendFile(ledgerPath, eventLine, 'utf-8');
		} catch (error) {
			logError('Failed to persist event', { error });
		}
	}

	private setupCleanupInterval(): void {
		// Clean up expired subscriptions every minute
		this.cleanupInterval = setInterval(() => {
			const now = Date.now();
			const idleTimeout = this.config.events.idle_timeout_ms;

			for (const [id, subscription] of this.subscriptions) {
				if (now - subscription.createdAt > idleTimeout) {
					this.unsubscribe(id);
				}
			}

			for (const [taskId, lastTime] of this.lastEventTimes) {
				if (now - lastTime > idleTimeout) {
					this.eventBuffer.delete(taskId);
					this.lastEventTimes.delete(taskId);
				}
			}
		}, 60000);
	}
}

/**
 * Create event manager singleton
 */
export function createEventManager(config: Config): EventManager {
	return new EventManagerClass(config) as EventManager;
}

let eventManagerInstance: EventManager | null = null;

export async function getEventManager(): Promise<EventManager> {
	if (!eventManagerInstance) {
		const config = await loadConfig();
		eventManagerInstance = createEventManager(config);
	}
	return eventManagerInstance;
}

export function stopEventManager(): void {
	if (eventManagerInstance) {
		eventManagerInstance.stop();
		eventManagerInstance = null;
	}
}

/**
 * Utility function to create accessibility-aware events
 */
export function createA11yEvent(
	type: EventType,
	taskId: string,
	data: Partial<Event> = {},
): Omit<Event, 'id' | 'timestamp'> {
	const ariaLiveHints: Record<EventType, string> = {
		PlanStarted: 'Planning has started',
		StepCompleted: 'Step completed successfully',
		AwaitingApproval: 'Waiting for approval',
		Canceled: 'Task has been canceled',
		Resumed: 'Task has been resumed',
		DeliverableReady: 'Deliverable is ready',
		Failed: 'Task has failed',
	};

	return {
		type,
		taskId,
		ariaLiveHint: ariaLiveHints[type],
		...data,
	};
}

/**
 * Evidence Collector
 * Manages evidence pointer collection and validation per blueprint specification
 */

import { readFile } from 'node:fs/promises';
import { createHash } from 'crypto';
import { v4 as uuidv4 } from 'uuid';
import type {
	Evidence,
	EvidencePointer,
	EvidenceRisk,
} from '../types/index.js';
import { EvidenceSchema, ValidationError } from '../types/index.js';
import { pathExists } from '../xdg/index.js';

export interface EvidenceCollectionOptions {
	includeContent?: boolean;
	maxContentLength?: number;
	allowedSources?: string[];
	confidenceThreshold?: number;
}

export interface EvidenceContext {
	taskId: string;
	step?: string;
	claim: string;
	sources: Array<{
		type: 'file' | 'url' | 'repo' | 'note';
		path?: string;
		url?: string;
		content?: string;
		range?: { start: number; end: number };
	}>;
}

/**
 * Evidence collector for tracking decision provenance
 */
export class EvidenceCollector {
	private evidenceMap = new Map<string, Evidence>();

	/**
	 * Collect evidence from various sources
	 */
	async collectEvidence(
		context: EvidenceContext,
		options: EvidenceCollectionOptions = {},
	): Promise<Evidence> {
		const pointers: EvidencePointer[] = [];

		// Process each source
		for (const source of context.sources) {
			const pointer = await this.createEvidencePointer(source, options);
			pointers.push(pointer);
		}

		// Calculate confidence based on sources
		const confidence = this.calculateConfidence(pointers, context.claim);

		// Assess risk level
		const risk = this.assessRisk(pointers, confidence, context.claim);

		const evidence: Evidence = {
			id: uuidv4(),
			source: this.determinePrimarySource(context.sources),
			pointers,
			claim: context.claim,
			confidence,
			risk,
			createdAt: new Date().toISOString(),
			schema: 'cortex.evidence@1',
		};

		// Validate evidence against schema
		const validation = EvidenceSchema.safeParse(evidence);
		if (!validation.success) {
			throw new ValidationError('Invalid evidence structure', {
				errors: validation.error.errors,
			});
		}

		// Store evidence
		this.evidenceMap.set(evidence.id, evidence);

		return evidence;
	}

	/**
	 * Link evidence to existing claims
	 */
	async linkEvidence(
		evidenceId: string,
		relatedEvidenceIds: string[],
	): Promise<void> {
		const evidence = this.evidenceMap.get(evidenceId);
		if (!evidence) {
			throw new ValidationError(`Evidence ${evidenceId} not found`);
		}

		// Validate related evidence exists
		for (const relatedId of relatedEvidenceIds) {
			if (!this.evidenceMap.has(relatedId)) {
				throw new ValidationError(`Related evidence ${relatedId} not found`);
			}
		}

		// In a full implementation, this would create evidence relationships
		// For now, we store the linking information as metadata
	}

	/**
	 * Validate evidence integrity
	 */
	async validateEvidence(evidenceId: string): Promise<{
		isValid: boolean;
		errors: string[];
		warnings: string[];
	}> {
		const evidence = this.evidenceMap.get(evidenceId);
		if (!evidence) {
			return {
				isValid: false,
				errors: ['Evidence not found'],
				warnings: [],
			};
		}

		const errors: string[] = [];
		const warnings: string[] = [];

		// Validate pointers
		for (const pointer of evidence.pointers) {
			const pointerValidation = await this.validatePointer(pointer);
			if (!pointerValidation.isValid) {
				errors.push(`Invalid pointer: ${pointerValidation.error}`);
			}
			if (pointerValidation.warnings.length > 0) {
				warnings.push(...pointerValidation.warnings);
			}
		}

		// Validate confidence score
		if (evidence.confidence < 0 || evidence.confidence > 1) {
			errors.push('Confidence score must be between 0 and 1');
		}

		// Check for missing claims
		if (!evidence.claim || evidence.claim.trim() === '') {
			errors.push('Evidence must have a non-empty claim');
		}

		// Validate risk assessment
		if (!['low', 'medium', 'high', 'unknown'].includes(evidence.risk)) {
			errors.push('Invalid risk level');
		}

		// Warning for low confidence
		if (evidence.confidence < 0.3) {
			warnings.push('Low confidence evidence may need additional validation');
		}

		// Warning for high risk with high confidence
		if (evidence.risk === 'high' && evidence.confidence > 0.8) {
			warnings.push('High-risk evidence with high confidence requires review');
		}

		return {
			isValid: errors.length === 0,
			errors,
			warnings,
		};
	}

	/**
	 * Get evidence by ID
	 */
	getEvidence(evidenceId: string): Evidence | undefined {
		return this.evidenceMap.get(evidenceId);
	}

	/**
	 * Search evidence by claim or content
	 */
	searchEvidence(query: {
		claim?: string;
		source?: string;
		riskLevel?: EvidenceRisk;
		minConfidence?: number;
		maxConfidence?: number;
	}): Evidence[] {
		let results = Array.from(this.evidenceMap.values());

		if (query.claim) {
			const searchTerm = query.claim.toLowerCase();
			results = results.filter((e) =>
				e.claim.toLowerCase().includes(searchTerm),
			);
		}

		if (query.source) {
			results = results.filter((e) => e.source === query.source);
		}

		if (query.riskLevel) {
			results = results.filter((e) => e.risk === query.riskLevel);
		}

		if (query.minConfidence !== undefined) {
			results = results.filter((e) => e.confidence >= query.minConfidence!);
		}

		if (query.maxConfidence !== undefined) {
			results = results.filter((e) => e.confidence <= query.maxConfidence!);
		}

		return results.sort((a, b) => b.confidence - a.confidence);
	}

	/**
	 * Generate evidence summary for a task
	 */
	generateEvidenceSummary(evidenceIds: string[]): {
		totalEvidence: number;
		averageConfidence: number;
		riskDistribution: Record<EvidenceRisk, number>;
		sourceDistribution: Record<string, number>;
		missingEvidence: string[];
	} {
		const evidence = evidenceIds
			.map((id) => this.evidenceMap.get(id))
			.filter(Boolean) as Evidence[];

		const missing = evidenceIds.filter((id) => !this.evidenceMap.has(id));

		const riskDistribution: Record<EvidenceRisk, number> = {
			low: 0,
			medium: 0,
			high: 0,
			unknown: 0,
		};

		const sourceDistribution: Record<string, number> = {};

		let totalConfidence = 0;

		for (const e of evidence) {
			riskDistribution[e.risk]++;
			sourceDistribution[e.source] = (sourceDistribution[e.source] || 0) + 1;
			totalConfidence += e.confidence;
		}

		return {
			totalEvidence: evidence.length,
			averageConfidence:
				evidence.length > 0 ? totalConfidence / evidence.length : 0,
			riskDistribution,
			sourceDistribution,
			missingEvidence: missing,
		};
	}

	private async createEvidencePointer(
		source: EvidenceContext['sources'][0],
		_options: EvidenceCollectionOptions,
	): Promise<EvidencePointer> {
		let hash: string;
		let path: string;

		switch (source.type) {
			case 'file': {
				if (!source.path) {
					throw new ValidationError('File source requires path');
				}

				if (!(await pathExists(source.path))) {
					throw new ValidationError(`File not found: ${source.path}`);
				}

				const content = await readFile(source.path, 'utf-8');
				hash = createHash('sha256').update(content).digest('hex');
				path = source.path;
				break;
			}

			case 'url':
				if (!source.url) {
					throw new ValidationError('URL source requires url');
				}

				// For URLs, hash the URL itself as a placeholder
				// In a real implementation, this would fetch and hash the content
				hash = createHash('sha256').update(source.url).digest('hex');
				path = source.url;
				break;

			case 'repo': {
				if (!source.path) {
					throw new ValidationError('Repo source requires path');
				}

				// For repo sources, create a hash based on path and current state
				const repoContent = source.content || `repo:${source.path}`;
				hash = createHash('sha256').update(repoContent).digest('hex');
				path = source.path;
				break;
			}

			case 'note':
				if (!source.content) {
					throw new ValidationError('Note source requires content');
				}

				hash = createHash('sha256').update(source.content).digest('hex');
				path = `note:${hash.substring(0, 8)}`;
				break;

			default:
				throw new ValidationError(`Unknown source type: ${source.type}`);
		}

		const pointer: EvidencePointer = {
			path,
			hash,
		};

		if (source.range) {
			pointer.start = source.range.start;
			pointer.end = source.range.end;
		}

		if (source.url) {
			pointer.url = source.url;
		}

		return pointer;
	}

	private calculateConfidence(
		pointers: EvidencePointer[],
		claim: string,
	): number {
		// Simplified confidence calculation
		// In a real implementation, this would use ML/statistical models

		let baseConfidence = 0.5; // Start with neutral confidence

		// More pointers generally increase confidence
		const pointerBonus = Math.min(pointers.length * 0.1, 0.3);
		baseConfidence += pointerBonus;

		// Penalty for very short claims (likely low quality)
		if (claim.length < 10) {
			baseConfidence -= 0.2;
		}

		// Bonus for specific, detailed claims
		if (claim.length > 100 && claim.includes('because')) {
			baseConfidence += 0.1;
		}

		// Ensure confidence is within bounds
		return Math.max(0, Math.min(1, baseConfidence));
	}

	private assessRisk(
		pointers: EvidencePointer[],
		confidence: number,
		claim: string,
	): EvidenceRisk {
		// Risk assessment based on confidence and claim content

		if (confidence < 0.3) {
			return 'high'; // Low confidence is risky
		}

		if (confidence > 0.8 && pointers.length >= 2) {
			return 'low'; // High confidence with multiple sources
		}

		// Check for risk indicators in claim
		const riskKeywords = [
			'security',
			'password',
			'private',
			'confidential',
			'delete',
			'remove',
			'destroy',
			'permanent',
			'financial',
			'payment',
			'billing',
			'cost',
		];

		const hasRiskKeywords = riskKeywords.some((keyword) =>
			claim.toLowerCase().includes(keyword),
		);

		if (hasRiskKeywords) {
			return confidence > 0.6 ? 'medium' : 'high';
		}

		// Default to medium risk
		return 'medium';
	}

	private determinePrimarySource(
		sources: EvidenceContext['sources'],
	): 'file' | 'url' | 'repo' | 'note' {
		// Return the first source type as primary
		return sources[0]?.type || 'note';
	}

	private async validatePointer(pointer: EvidencePointer): Promise<{
		isValid: boolean;
		error?: string;
		warnings: string[];
	}> {
		const warnings: string[] = [];

		// Validate hash format
		if (!/^[a-f0-9]{64}$/.test(pointer.hash)) {
			return {
				isValid: false,
				error: 'Invalid SHA-256 hash format',
				warnings,
			};
		}

		// Validate path
		if (!pointer.path || pointer.path.trim() === '') {
			return {
				isValid: false,
				error: 'Missing or empty path',
				warnings,
			};
		}

		// Validate range if present
		if (pointer.start !== undefined && pointer.end !== undefined) {
			if (pointer.start < 0 || pointer.end < 0) {
				return {
					isValid: false,
					error: 'Range values cannot be negative',
					warnings,
				};
			}

			if (pointer.start > pointer.end) {
				return {
					isValid: false,
					error: 'Range start cannot be greater than end',
					warnings,
				};
			}
		}

		// Check if file exists (for file pointers)
		if (!pointer.path.startsWith('http') && !pointer.path.startsWith('note:')) {
			if (!(await pathExists(pointer.path))) {
				warnings.push('Referenced file does not exist');
			}
		}

		return {
			isValid: true,
			warnings,
		};
	}
}

/**
 * ASBR SDK - Typed interfaces for task creation and management
 * Implements the SDK surface as specified in the blueprint
 */

import { createHash } from 'crypto';
import type {
	ArtifactRef,
	CreateProfileRequest,
	CreateProfileResponse,
	CreateTaskRequest,
	CreateTaskResponse,
	Event,
	EventType,
	GetTaskResponse,
	ListArtifactsQuery,
	ListArtifactsResponse,
	Profile,
	Task,
	TaskInput,
	TaskRef,
	UnsubscribeFunction,
} from '../types/index.js';
// NOTE: structured logger import removed to avoid cross-package coupling in quick lint-fix.
// We'll keep console usage but explicitly allow it on these lines.

/**
 * Main ASBR SDK Client
 */
export class ASBRClient {
	private baseUrl: string;
	private token?: string;
	private eventSubscriptions = new Map<string, Set<(event: Event) => void>>();

	constructor(
		options: {
			baseUrl?: string;
			token?: string;
		} = {},
	) {
		this.baseUrl = options.baseUrl || 'http://127.0.0.1:7439';
		this.token = options.token;
	}

	/**
	 * Create a new task with optional idempotency key
	 */
	async createTask(
		input: TaskInput,
		opts?: { idempotencyKey?: string },
	): Promise<TaskRef> {
		const request: CreateTaskRequest = {
			input,
			idempotencyKey: opts?.idempotencyKey,
		};

		const response = await this.fetch('/v1/tasks', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
				...(opts?.idempotencyKey && { 'Idempotency-Key': opts.idempotencyKey }),
			},
			body: JSON.stringify(request),
		});

		const data: CreateTaskResponse = await response.json();
		return new TaskRefImpl(data.task, this);
	}

	/**
	 * Get an existing task by ID
	 */
	async getTask(id: string): Promise<Task> {
		const response = await this.fetch(`/v1/tasks/${id}`);
		const data: GetTaskResponse = await response.json();
		return data.task;
	}

	/**
	 * Subscribe to events for a specific task or all tasks
	 */
	subscribe(
		taskId: string | undefined,
		eventTypes: EventType[],
		callback: (event: Event) => void,
	): UnsubscribeFunction {
		const subscriptionKey = taskId || '__all__';

		if (!this.eventSubscriptions.has(subscriptionKey)) {
			this.eventSubscriptions.set(subscriptionKey, new Set());
		}

		const callbacks = this.eventSubscriptions.get(subscriptionKey)!;
		callbacks.add(callback);

		// Set up SSE connection if this is the first subscription
		if (callbacks.size === 1) {
			this.setupEventStream(taskId, eventTypes);
		}

		return () => {
			callbacks.delete(callback);
			if (callbacks.size === 0) {
				this.eventSubscriptions.delete(subscriptionKey);
				this.closeEventStream(taskId);
			}
		};
	}

	/**
	 * List artifacts with optional query parameters
	 */
	async listArtifacts(query?: ListArtifactsQuery): Promise<ArtifactRef[]> {
		const searchParams = new URLSearchParams();
		if (query) {
			Object.entries(query).forEach(([key, value]) => {
				if (value !== undefined) {
					searchParams.append(key, String(value));
				}
			});
		}

		const url = `/v1/artifacts${searchParams.toString() ? `?${searchParams.toString()}` : ''}`;
		const response = await this.fetch(url);
		const data: ListArtifactsResponse = await response.json();
		return data.artifacts;
	}

	/**
	 * Create or update a user profile
	 */
	async upsertProfile(
		profile: Omit<Profile, 'id'> | Profile,
	): Promise<Profile> {
		const method = 'id' in profile ? 'PUT' : 'POST';
		const url = 'id' in profile ? `/v1/profiles/${profile.id}` : '/v1/profiles';

		const request: CreateProfileRequest = {
			profile: 'id' in profile ? profile : profile,
		};

		const response = await this.fetch(url, {
			method,
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify(request),
		});

		const data: CreateProfileResponse = await response.json();
		return data.profile;
	}

	/**
	 * Get profile by ID
	 */
	async getProfile(id: string): Promise<Profile> {
		const response = await this.fetch(`/v1/profiles/${id}`);
		return await response.json();
	}

	/**
	 * Get artifact content with digest verification
	 */
	async getArtifact(id: string): Promise<{
		content: ArrayBuffer;
		digest: string;
		etag: string;
	}> {
		const response = await this.fetch(`/v1/artifacts/${id}`);
		const content = await response.arrayBuffer();
		const digest = response.headers.get('Digest') || '';
		const etag = response.headers.get('ETag') || '';

		return { content, digest, etag };
	}

	/**
	 * Get service map of enabled connectors
	 */
	async getConnectorServiceMap(): Promise<
		Record<
			string,
			{
				enabled: boolean;
				scopes: string[];
				ttl?: number;
			}
		>
	> {
		const response = await this.fetch('/v1/connectors/service-map');
		return await response.json();
	}

	/**
	 * Cancel a task
	 */
	async cancelTask(taskId: string): Promise<void> {
		await this.fetch(`/v1/tasks/${taskId}/cancel`, {
			method: 'POST',
		});
	}

	/**
	 * Resume a task
	 */
	async resumeTask(taskId: string): Promise<void> {
		await this.fetch(`/v1/tasks/${taskId}/resume`, {
			method: 'POST',
		});
	}

	protected async fetch(path: string, init?: RequestInit): Promise<Response> {
		const url = `${this.baseUrl}${path}`;
		const headers = new Headers(init?.headers);

		if (this.token) {
			headers.set('Authorization', `Bearer ${this.token}`);
		}

		const response = await fetch(url, {
			...init,
			headers,
		});

		if (!response.ok) {
			const error = await response.text();
			throw new Error(`API request failed: ${response.status} ${error}`);
		}

		return response;
	}

	private setupEventStream(taskId?: string, eventTypes?: EventType[]): void {
		const params = new URLSearchParams();
		params.set('stream', 'sse');
		if (taskId) params.set('taskId', taskId);
		if (eventTypes) params.set('events', eventTypes.join(','));

		const url = `${this.baseUrl}/v1/events?${params}`;
		const eventSource = new EventSource(url);

		eventSource.onmessage = (event) => {
			try {
				const data: Event = JSON.parse(event.data);
				this.dispatchEvent(data);
			} catch (error) {
				// Prefer structured logger when available

				console.error('Failed to parse event:', error);
			}
		};

		eventSource.onerror = (error) => {
			console.error('Event stream error:', error);
			// Implement reconnection logic here
		};
	}

	private closeEventStream(_taskId?: string): void {
		// Implementation would close the specific event stream
		// This is a simplified version
	}

	private dispatchEvent(event: Event): void {
		// Dispatch to specific task subscribers
		const taskCallbacks = this.eventSubscriptions.get(event.taskId);
		if (taskCallbacks) {
			for (const callback of taskCallbacks) {
				callback(event);
			}
		}

		// Dispatch to global subscribers
		const globalCallbacks = this.eventSubscriptions.get('__all__');
		if (globalCallbacks) {
			for (const callback of globalCallbacks) {
				callback(event);
			}
		}
	}
}

/**
 * Implementation of TaskRef interface
 */
class TaskRefImpl implements TaskRef {
	public readonly id: string;
	public readonly status: Task['status'];
	private client: ASBRClient;
	private eventListeners = new Set<(event: Event) => void>();

	constructor(task: Task, client: ASBRClient) {
		this.id = task.id;
		this.status = task.status;
		this.client = client;
	}

	subscribe(callback: (event: Event) => void): UnsubscribeFunction {
		this.eventListeners.add(callback);

		const unsubscribe = this.client.subscribe(
			this.id,
			[
				'PlanStarted',
				'StepCompleted',
				'AwaitingApproval',
				'Canceled',
				'Resumed',
				'DeliverableReady',
				'Failed',
			],
			callback,
		);

		return () => {
			this.eventListeners.delete(callback);
			unsubscribe();
		};
	}

	async getTask(): Promise<Task> {
		return await this.client.getTask(this.id);
	}

	async cancel(): Promise<void> {
		await this.client.cancelTask(this.id);
	}

	async resume(): Promise<void> {
		await this.client.resumeTask(this.id);
	}
}

/**
 * Create a default ASBR client instance
 */
export function createASBRClient(options?: {
	baseUrl?: string;
	token?: string;
}): ASBRClient {
	return new ASBRClient(options);
}

/**
 * Utility function to create a task input with defaults
 */
export function createTaskInput(
	title: string,
	brief: string,
	options: Partial<Omit<TaskInput, 'title' | 'brief' | 'schema'>> = {},
): TaskInput {
	return {
		title,
		brief,
		inputs: options.inputs || [],
		scopes: options.scopes || [],
		deadlines: options.deadlines,
		a11yProfileId: options.a11yProfileId,
		preferences: options.preferences,
		schema: 'cortex.task.input@1',
	};
}

/**
 * Utility function to create an idempotency key
 */
export function createIdempotencyKey(input: TaskInput): string {
	// Create a deterministic key based on task input
	const key = JSON.stringify({
		title: input.title,
		brief: input.brief,
		inputs: input.inputs,
		scopes: input.scopes.sort(),
	});
	const hash = createHash('sha256').update(key).digest('hex');
	return hash.slice(0, 32);
}

