interface AiAppEvents {
	'task:queued': [taskId: string, taskType: AITaskType];
	'task:started': [taskId: string, model: GitHubModel];
	'task:completed': [result: AITaskResult];
	'task:failed': [taskId: string, error: Error];
	rate_limit: [remaining: number, resetAt: Date];
}

export class CortexAiGitHubApp extends EventEmitter<AiAppEvents> {
	private config: GitHubModelsConfig;
	private taskQueue = new Map<string, AITaskParams>();
	private activeRequests = new Set<string>();
	private rateLimitInfo = { remaining: 1000, resetAt: new Date() };

	constructor(config: GitHubModelsConfig) {
		super();
		this.config = {
			...config,
			baseUrl: config.baseUrl || 'https://models.inference.ai.azure.com',
			maxTokens: config.maxTokens || 4096,
			temperature: config.temperature || 0.3,
			requestTimeoutMs: config.requestTimeoutMs ?? 10000,
		};
	}

	async queueTask(taskParams: AITaskParams): Promise<string> {
		const taskId = this.generateTaskId(taskParams.taskType);

		const validatedParams = this.validateTaskParams(taskParams);
		this.taskQueue.set(taskId, validatedParams);

		this.emit('task:queued', taskId, validatedParams.taskType);

		// Process immediately if within rate limits
		if (this.rateLimitInfo.remaining > 0) {
			setImmediate(() => this.processTask(taskId));
		}

		return taskId;
	}

	private async processTask(taskId: string): Promise<void> {
		const taskParams = this.taskQueue.get(taskId);
		if (!taskParams || this.activeRequests.has(taskId)) {
			return;
		}

		this.activeRequests.add(taskId);
		const startTime = Date.now();

		try {
			this.emit(
				'task:started',
				taskId,
				taskParams.model ?? this.config.defaultModel,
			);

			const messages = this.buildTaskMessages(taskParams);
			const model = taskParams.model ?? this.config.defaultModel;

			const response = await this.callModel(model, messages);

			const result: AITaskResult = {
				taskId,
				taskType: taskParams.taskType,
				status: 'success',
				result: this.parseTaskResult(taskParams.taskType, response.content),
				model: model,
				tokensUsed: response.tokensUsed,
				executionTime: Date.now() - startTime,
			};

			this.emit('task:completed', result);
		} catch (error) {
			const failureResult: AITaskResult = {
				taskId,
				taskType: taskParams.taskType,
				status: 'error',
				result: {
					summary: 'Task failed due to error',
					details: {},
					recommendations: [],
					confidence: 0,
				},
				model: taskParams.model ?? this.config.defaultModel,
				tokensUsed: 0,
				executionTime: Date.now() - startTime,
				error: error instanceof Error ? error.message : String(error),
			};

			this.emit(
				'task:failed',
				taskId,
				error instanceof Error ? error : new Error(String(error)),
			);
			this.emit('task:completed', failureResult);
		} finally {
			this.activeRequests.delete(taskId);
			this.taskQueue.delete(taskId);
		}
	}

	private async callModel(
		model: GitHubModel,
		messages: ModelMessage[],
	): Promise<{
		content: string;
		tokensUsed: number;
		finishReason: string;
	}> {
		const request: ModelCompletionRequest = {
			model,
			messages,
			max_tokens: this.config.maxTokens,
			temperature: this.config.temperature,
		};

		const response = await fetchWithTimeout(
			`${this.config.baseUrl}/chat/completions`,
			{
				method: 'POST',
				headers: {
					Authorization: `Bearer ${this.config.token}`,
					'Content-Type': 'application/json',
				},
				body: JSON.stringify(request),
			},
			this.config.requestTimeoutMs,
		);

		if (!response.ok) {
			// Update rate limit info from headers
			this.updateRateLimitInfo(response.headers);
			throw new Error(
				`GitHub Models API error: ${response.status} ${response.statusText}`,
			);
		}

		this.updateRateLimitInfo(response.headers);

		const completion = (await response.json()) as ModelCompletionResponse;

		return {
			content: completion.choices[0]?.message?.content ?? '',
			tokensUsed: completion.usage.total_tokens,
			finishReason: completion.choices[0]?.finish_reason ?? 'unknown',
		};
	}

	private buildTaskMessages(params: AITaskParams): ModelMessage[] {
		const baseMessages: ModelMessage[] = [
			{
				role: 'system',
				content: this.getSystemPromptForTask(params.taskType),
			},
		];

		const contextMessage = this.buildContextMessage(
			params.githubContext,
			params.taskType,
		);
		baseMessages.push(contextMessage);

		if (params.instructions) {
			baseMessages.push({
				role: 'user',
				content: `Additional instructions: ${params.instructions}`,
			});
		}

		return baseMessages;
	}

	private getSystemPromptForTask(taskType: AITaskType): string {
		return SYSTEM_PROMPTS[taskType] || SYSTEM_PROMPTS.code_review;
	}

	private buildPrSection(
		pr: GitHubContext['pr'],
		includePatch: boolean,
	): string {
		let section = `Pull Request #${pr.number}: ${pr.title}\n`;
		section += `Description: ${pr.body}\n`;
		section += `Base: ${pr.base} <- Head: ${pr.head}\n\n`;

		if (pr.files.length > 0) {
			section += `Changed Files (${pr.files.length}):\n`;
			pr.files.forEach((file) => {
				section += `- ${file.filename} (${file.status})\n`;
				if (includePatch && file.patch) {
					section += `\`\`\`diff\n${truncatePatch(file.patch)}\n\`\`\`\n`;
				}
			});
		}

		return section;
	}

	private buildIssueSection(issue: GitHubContext['issue']): string {
		let section = `Issue #${issue.number}: ${issue.title}\n`;
		section += `Description: ${issue.body}\n`;
		if (issue.labels.length > 0) {
			section += `Labels: ${issue.labels.join(', ')}\n`;
		}
		return section + '\n';
	}

	private buildCommitSection(commit: GitHubContext['commit']): string {
		return `Commit: ${commit.sha.substring(0, 7)}\nMessage: ${
			commit.message
		}\nAuthor: ${commit.author}\n\n`;
	}

	private buildContextMessage(
		context: GitHubContext,
		taskType: AITaskType,
	): ModelMessage {
		let content = `Repository: ${context.owner}/${context.repo}\n\n`;

		if (
			context.pr &&
			['code_review', 'pr_analysis', 'security_scan'].includes(taskType)
		) {
			content += this.buildPrSection(context.pr, taskType === 'code_review');
		}

		if (context.issue && ['issue_triage', 'documentation'].includes(taskType)) {
			content += this.buildIssueSection(context.issue);
		}

		if (context.commit) {
			content += this.buildCommitSection(context.commit);
		}

		return {
			role: 'user',
			content,
		};
	}

	private parseTaskResult(
		_taskType: AITaskType,
		content: string,
	): AITaskResult['result'] {
		const lines = content
			.split('\n')
			.map((line) => line.trim())
			.filter(Boolean);
		let summary = 'No summary available';
		const recommendations: string[] = [];
		let confidence = 0.5;

		for (const line of lines) {
			if (line.toLowerCase().startsWith('summary:')) {
				summary = line.slice(8).trim();
			} else if (line.toLowerCase().startsWith('confidence:')) {
				const value = parseFloat(line.split(':')[1]);
				if (!Number.isNaN(value)) {
					confidence = Math.min(Math.max(value, 0), 1);
				}
			} else if (line.startsWith('-')) {
				recommendations.push(line.replace(/^-\s*/, ''));
			} else if (summary === 'No summary available') {
				summary = line;
			}
		}

		return {
			summary,
			details: { rawContent: content },
			recommendations,
			confidence,
		};
	}

	private validateTaskParams(params: AITaskParams): AITaskParams {
		return taskParamsSchema.parse(params);
	}

	private generateTaskId(taskType: AITaskType): string {
		const timestamp = Date.now().toString(36);
		const random = Math.random().toString(36).substring(2, 8);
		return `${taskType}_${timestamp}_${random}`;
	}

	private updateRateLimitInfo(headers: Headers): void {
		const remaining = headers.get('x-ratelimit-remaining');
		const resetTime = headers.get('x-ratelimit-reset');

		if (remaining) {
			this.rateLimitInfo.remaining = parseInt(remaining, 10);
		}

		if (resetTime) {
			this.rateLimitInfo.resetAt = new Date(parseInt(resetTime, 10) * 1000);
		}

		this.emit(
			'rate_limit',
			this.rateLimitInfo.remaining,
			this.rateLimitInfo.resetAt,
		);
	}

	// Public getters for monitoring
	get queueSize(): number {
		return this.taskQueue.size;
	}
	get activeTaskCount(): number {
		return this.activeRequests.size;
	}
	get rateLimit(): { remaining: number; resetAt: Date } {
		return { ...this.rateLimitInfo };
	}
}

/**
 * MLX AI Engine - Functional approach
 * Real MLX integration without mocks or classes
 */

import type { AITaskType, GitHubContext } from '../types/github-models.js';

export interface MlxAnalysisResult {
	summary: string;
	vulnerabilities: Array<{
		type: string;
		severity: 'critical' | 'high' | 'medium' | 'low.js';
		description: string;
		location: string;
		remediation: string;
	}>;
	codeQuality: number; // 0-100
	recommendations: string[];
	confidence: number; // 0-1
	processingTime: number;
}

const SYSTEM_PROMPTS: Record<AITaskType, string> = {
	code_review:
		'You are a security-focused code reviewer. Analyze for OWASP Top 10 vulnerabilities and code quality issues.',
	security_scan:
		'You are a security analyst. Focus on finding injection vulnerabilities, authentication bypasses, and cryptographic failures.',
	pr_analysis:
		'You are a PR reviewer. Assess impact, breaking changes, and deployment risks.',
	documentation:
		'You are a technical writer. Generate comprehensive documentation.',
	issue_triage: 'You are an issue triager. Categorize and prioritize issues.',
	workflow_optimize: 'You are a DevOps expert. Optimize CI/CD workflows.',
	repo_health:
		'You are a repository analyst. Assess overall health and technical debt.',
	auto_fix:
		'You are a code improvement specialist. Generate safe, targeted fixes.',
};

// Input sanitization for MLX security
const sanitizePromptInput = (input: string): string => {
	if (typeof input !== 'string') throw new Error('Prompt must be string');
	if (input.length > 8000) throw new Error('Prompt too long (max 8000 chars)');
	// Remove potentially dangerous characters
	const withoutMeta = input.replace(/[`$\\]/g, '');
	// Remove null bytes without using regex control-char class
	return withoutMeta.split('\x00').join('');
};

const validateMlxParams = (params: string[]): void => {
	const allowedFlags = ['--model', '--prompt', '--max-tokens', '--temp'];
	for (let i = 0; i < params.length; i += 2) {
		if (!allowedFlags.includes(params[i])) {
			throw new Error(`Invalid MLX parameter: ${params[i]}`);
		}
	}
};

export const analyzeCodeWithMlx = async (
	code: string,
	context: GitHubContext,
	taskType: AITaskType,
): Promise<MlxAnalysisResult> => {
	const startTime = Date.now();

	try {
		// Use actual MLX through the mlx-lm Python package
		const { spawn } = await import('node:child_process');

		const prompt = buildAnalysisPrompt(code, context, taskType);
		const sanitizedPrompt = sanitizePromptInput(prompt);

		// Validate all parameters before spawning
		const mlxArgs = [
			'-m',
			'mlx_lm.generate',
			'--model',
			'mlx-community/Qwen2.5-Coder-7B-Instruct-4bit',
			'--prompt',
			sanitizedPrompt,
			'--max-tokens',
			'2048',
			'--temp',
			'0.1',
		];

		validateMlxParams(mlxArgs.filter((_, i) => i % 2 === 0 && i > 1));

		// Use mlx-lm generate command with sanitized input
		const mlxProcess = spawn('python3', mlxArgs, {
			stdio: 'pipe',
		});

		// Manual timeout
		const timeout = setTimeout(() => {
			mlxProcess.kill('SIGTERM');
		}, 30000);

		let output = '.js';
		let error = '.js';

		mlxProcess.stdout?.on('data', (data) => {
			output += data.toString();
		});
		mlxProcess.stderr?.on('data', (data) => {
			error += data.toString();
		});

		await new Promise((resolve, reject) => {
			mlxProcess.on('close', (exitCode) => {
				clearTimeout(timeout);
				if (exitCode === 0) resolve(void 0);
				else reject(new Error(`MLX process failed: ${error}`));
			});
			mlxProcess.on('error', (err) => {
				clearTimeout(timeout);
				reject(err);
			});
		});

		// Parse JSON from output
		const jsonMatch = output.match(/\{[\s\S]*\}/);
		if (!jsonMatch) {
			throw new Error('No JSON output from MLX model');
		}

		const result = JSON.parse(jsonMatch[0]);
		const processingTime = Date.now() - startTime;

		return {
			summary: result.summary || 'MLX analysis completed',
			vulnerabilities: result.vulnerabilities || [],
			codeQuality: result.codeQuality || 75,
			recommendations: result.recommendations || ['Review code manually'],
			confidence: result.confidence || 0.8,
			processingTime,
		};
	} catch (error) {
		console.error('MLX analysis failed:', error);

		// Fallback to basic analysis
		return {
			summary: 'Basic analysis (MLX unavailable)',
			vulnerabilities: [],
			codeQuality: 70,
			recommendations: ['Manual review recommended'],
			confidence: 0.3,
			processingTime: Date.now() - startTime,
		};
	}
};

export const buildAnalysisPrompt = (
	code: string,
	context: GitHubContext,
	taskType: AITaskType,
): string => {
	const systemPrompt = SYSTEM_PROMPTS[taskType];

	// Sanitize inputs to prevent injection
	const sanitizedCode = code.slice(0, 4000); // Limit code length
	const sanitizedOwner = context.owner.replace(/[^a-zA-Z0-9_.-]/g, '');
	const sanitizedRepo = context.repo.replace(/[^a-zA-Z0-9_.-]/g, '');
	const sanitizedTitle = context.pr?.title?.slice(0, 100) || '.js';

	return `${systemPrompt}

Repository: ${sanitizedOwner}/${sanitizedRepo}
${context.pr ? `PR #${context.pr.number}: ${sanitizedTitle}` : ''}

Code to analyze:
\`\`\`
${sanitizedCode}
\`\`\`

Provide JSON analysis with vulnerabilities, quality score, and recommendations.`;
};

/**
 * Runtime entrypoint for cortex-ai-github
 * Reads env vars, initializes the AI app, and starts the webhook server.
 */

import dotenv from 'dotenv';
import { CortexAiGitHubApp } from '../core/ai-github-app.js';
import type { GitHubModel } from '../types/github-models.js';
import { CortexWebhookServer } from './webhook-server.js';

// Load environment variables from .env file
dotenv.config();

const required = (name: string, value: string | undefined) => {
	if (!value || value.trim() === '') {
		console.error(`[startup] Missing required environment variable: ${name}`);
		process.exit(1);
	}
	return value;
};

async function main() {
	const token = required('GITHUB_TOKEN', process.env.GITHUB_TOKEN);
	const webhookSecret = required('WEBHOOK_SECRET', process.env.WEBHOOK_SECRET);

	const port = Number(process.env.PORT ?? '3001');
	const baseUrl =
		process.env.GITHUB_MODELS_BASE_URL ||
		'https://models.inference.ai.azure.com';
	const defaultModel = (process.env.GITHUB_DEFAULT_MODEL ||
		'claude-3-5-sonnet') as GitHubModel;
	const maxTokens = Number(process.env.GITHUB_MAX_TOKENS ?? '4096');
	const temperature = Number(process.env.GITHUB_TEMPERATURE ?? '0.3');

	const aiApp = new CortexAiGitHubApp({
		token,
		baseUrl,
		defaultModel,
		maxTokens,
		temperature,
	});

	const server = new CortexWebhookServer(aiApp, webhookSecret);

	await server.start(port);
	console.log(
		`[startup] cortex-ai-github listening on :${port} (hosted via Cloudflare Tunnel if configured)`,
	);

	const shutdown = async (signal: string) => {
		console.log(`[shutdown] ${signal} received, stopping server...`);
		try {
			await server.stop();
		} finally {
			process.exit(0);
		}
	};

	process.on('SIGINT', () => shutdown('SIGINT'));
	process.on('SIGTERM', () => shutdown('SIGTERM'));
}

main().catch((err) => {
	console.error('[startup] Failed to start server:', err);
	process.exit(1);
});

/**
 * Webhook Server for Cortex AI GitHub App
 * Handles GitHub webhook events and comment-as-API triggers
 */

import crypto from 'node:crypto';
import { EventEmitter } from 'node:events';
import express from 'express';
import { z } from 'zod';
import type { CortexAiGitHubApp } from '../core/ai-github-app.js';
import type { CommentTrigger, GitHubContext } from '../types/github-models.js';
import type {
	GitHubWebhookPayload,
	ProgressiveStatus,
} from '../types/webhook-types.js';

interface WebhookEvents {
	'comment:trigger': [
		trigger: CommentTrigger,
		context: GitHubContext,
		user: string,
	];
	'webhook:verified': [event: string, delivery: string];
	'webhook:invalid': [reason: string, headers: Record<string, string>];
}

export class CortexWebhookServer extends EventEmitter<WebhookEvents> {
	private app: express.Application;
	private aiApp: CortexAiGitHubApp;
	private webhookSecret: string;
	private triggers: CommentTrigger[];
	private server?: import('http').Server;

	constructor(aiApp: CortexAiGitHubApp, webhookSecret: string) {
		super();

		this.aiApp = aiApp;
		this.webhookSecret = webhookSecret;
		this.app = express();
		this.triggers = this.initializeDefaultTriggers();

		this.setupMiddleware();
		this.setupRoutes();
	}

	/**
	 * Get the current queue size from the AI app
	 */
	get queueSize(): number {
		return this.aiApp.queueSize || 0;
	}

	private initializeDefaultTriggers(): CommentTrigger[] {
		return [
			{
				pattern: /@cortex\s+review(?:\s+(.+))?/i,
				taskType: 'code_review',
				description: 'Perform AI code review on PR',
				requiredPermissions: ['read'],
			},
			{
				pattern: /@cortex\s+analyze(?:\s+(.+))?/i,
				taskType: 'pr_analysis',
				description: 'Comprehensive PR analysis',
				requiredPermissions: ['read'],
			},
			{
				pattern: /@cortex\s+secure(?:\s+(.+))?/i,
				taskType: 'security_scan',
				description: 'Security vulnerability analysis',
				requiredPermissions: ['read'],
			},
			{
				pattern: /@cortex\s+document(?:\s+(.+))?/i,
				taskType: 'documentation',
				description: 'Generate documentation',
				requiredPermissions: ['write'],
			},
			{
				pattern: /@cortex\s+triage(?:\s+(.+))?/i,
				taskType: 'issue_triage',
				description: 'Intelligent issue triage',
				requiredPermissions: ['read'],
			},
			{
				pattern: /@cortex\s+optimize(?:\s+(.+))?/i,
				taskType: 'workflow_optimize',
				description: 'Workflow optimization analysis',
				requiredPermissions: ['read'],
			},
			{
				pattern: /@cortex\s+health(?:\s+(.+))?/i,
				taskType: 'repo_health',
				description: 'Repository health check',
				requiredPermissions: ['read'],
			},
			{
				pattern: /@cortex\s+fix(?:\s+(.+))?/i,
				taskType: 'auto_fix',
				description: 'Automated code fixes',
				requiredPermissions: ['write', 'admin'],
			},
		];
	}

	private setupMiddleware(): void {
		// Raw body parsing for webhook signature verification
		this.app.use('/webhook', express.raw({ type: 'application/json' }));

		// Standard JSON parsing for other endpoints
		this.app.use(express.json());

		// Security headers
		this.app.use((_req, res, next) => {
			res.set({
				'X-Content-Type-Options': 'nosniff',
				'X-Frame-Options': 'DENY',
				'X-XSS-Protection': '1; mode=block',
				'Strict-Transport-Security': 'max-age=31536000; includeSubDomains',
			});
			next();
		});
	}

	private setupRoutes(): void {
		// Health check endpoint
		this.app.get('/health', (_req, res) => {
			res.json({
				status: 'healthy',
				timestamp: new Date().toISOString(),
				queueSize: this.aiApp.queueSize,
				activeTaskCount: this.aiApp.activeTaskCount,
				rateLimit: this.aiApp.rateLimit,
			});
		});

		// Main webhook endpoint
		this.app.post('/webhook', async (req, res) => {
			try {
				const signature = req.get('X-Hub-Signature-256');
				const delivery = req.get('X-GitHub-Delivery');
				const event = req.get('X-GitHub-Event');

				if (!signature || !delivery || !event) {
					this.emit(
						'webhook:invalid',
						'Missing required headers',
						req.headers as Record<string, string>,
					);
					return res
						.status(400)
						.json({ error: 'Missing required webhook headers' });
				}

				if (!this.verifyWebhookSignature(req.body, signature)) {
					this.emit('webhook:invalid', 'Invalid signature', {
						signature,
						delivery,
						event,
					});
					return res.status(401).json({ error: 'Invalid webhook signature' });
				}

				this.emit('webhook:verified', event, delivery);

				const payload = JSON.parse(req.body.toString());
				await this.handleWebhookEvent(event, payload);

				return res.status(200).json({ received: true, delivery });
			} catch (error) {
				console.error('Webhook processing error', { error });
				return res.status(500).json({
					error: 'Internal server error',
					message: error instanceof Error ? error.message : String(error),
				});
			}
		});

		// Trigger management endpoints
		this.app.get('/triggers', (_req, res) => {
			res.json({
				triggers: this.triggers.map((t) => ({
					pattern: t.pattern.source,
					taskType: t.taskType,
					description: t.description,
					requiredPermissions: t.requiredPermissions,
				})),
			});
		});

		this.app.post('/triggers', (req, res) => {
			try {
				const triggerSchema = z.object({
					pattern: z.string(),
					taskType: z.enum([
						'code_review',
						'pr_analysis',
						'security_scan',
						'documentation',
						'issue_triage',
						'workflow_optimize',
						'repo_health',
						'auto_fix',
					]),
					description: z.string(),
					requiredPermissions: z.array(z.enum(['read', 'write', 'admin'])),
				});

				const validated = triggerSchema.parse(req.body);

				const newTrigger: CommentTrigger = {
					pattern: new RegExp(validated.pattern, 'i'),
					taskType: validated.taskType,
					description: validated.description,
					requiredPermissions: validated.requiredPermissions,
				};

				this.triggers.push(newTrigger);

				res.status(201).json({
					message: 'Trigger added successfully',
					trigger: {
						pattern: newTrigger.pattern.source,
						taskType: newTrigger.taskType,
						description: newTrigger.description,
						requiredPermissions: newTrigger.requiredPermissions,
					},
				});
			} catch (error) {
				res.status(400).json({
					error: 'Invalid trigger configuration',
					details: error instanceof Error ? error.message : String(error),
				});
			}
		});
	}

	private verifyWebhookSignature(payload: Buffer, signature: string): boolean {
		try {
			const expectedSignature = crypto
				.createHmac('sha256', this.webhookSecret)
				.update(payload)
				.digest('hex');

			const expected = `sha256=${expectedSignature}`;
			return crypto.timingSafeEqual(
				Buffer.from(signature),
				Buffer.from(expected),
			);
		} catch (error) {
			console.error('Signature verification error', { error });
			return false;
		}
	}

	private async handleWebhookEvent(
		event: string,
		payload: GitHubWebhookPayload,
	): Promise<void> {
		switch (event) {
			case 'issue_comment':
				if (payload.action === 'created') {
					await this.handleCommentCreated(payload);
				}
				break;

			case 'pull_request_review_comment':
				if (payload.action === 'created') {
					await this.handleReviewCommentCreated(payload);
				}
				break;

			case 'pull_request':
				if (['opened', 'synchronize'].includes(payload.action)) {
					await this.handlePullRequestEvent(payload);
				}
				break;

			case 'issues':
				if (payload.action === 'opened') {
					await this.handleIssueOpened(payload);
				}
				break;

			default:
				console.log('Unhandled webhook event', { event });
		}
	}

	private async handleCommentCreated(
		payload: GitHubWebhookPayload,
	): Promise<void> {
		if (!payload.comment) {
			console.warn('handleCommentCreated called without comment in payload');
			return;
		}

		const comment = payload.comment.body;
		const user = payload.comment.user.login;

		for (const trigger of this.triggers) {
			if (trigger.pattern.test(comment)) {
				const context = this.buildGitHubContext(payload);

				this.emit('comment:trigger', trigger, context, user);

				// Progressive status: Step 1 - Processing
				await this.updateProgressiveStatus(payload, 'processing');

				try {
					// Progressive status: Step 2 - Working
					await this.updateProgressiveStatus(payload, 'working');

					const taskId = await this.aiApp.queueTask({
						taskType: trigger.taskType,
						githubContext: context,
						instructions: this.extractInstructions(comment, trigger.pattern),
					});

					console.log('Queued task', {
						status: 'ok',
						taskType: trigger.taskType,
						taskId,
						user,
					});

					// Progressive status: Step 3 - Success
					await this.updateProgressiveStatus(payload, 'success');
				} catch (error) {
					console.error('Failed to queue task', {
						taskType: trigger.taskType,
						error,
					});

					// Progressive status: Step 3 - Error
					await this.updateProgressiveStatus(payload, 'error');
				}

				break; // Only process first matching trigger
			}
		}
	}

	private async handleReviewCommentCreated(
		payload: GitHubWebhookPayload,
	): Promise<void> {
		// Similar to handleCommentCreated but for PR review comments
		await this.handleCommentCreated(payload);
	}

	private async handlePullRequestEvent(
		payload: GitHubWebhookPayload,
	): Promise<void> {
		// Auto-trigger analysis for new/updated PRs
		const context = this.buildGitHubContext(payload);

		if (payload.action === 'opened') {
			// Auto-trigger code review for new PRs
			await this.aiApp.queueTask({
				taskType: 'code_review',
				githubContext: context,
				instructions: 'Automated code review for new PR',
			});
		} else if (payload.action === 'synchronize') {
			// Auto-trigger review for updated PRs
			await this.aiApp.queueTask({
				taskType: 'code_review',
				githubContext: context,
				instructions: 'Automated review for PR updates',
			});
		}

		// Additional triggers based on labels or content
		if (payload.pull_request?.labels) {
			const labels = payload.pull_request.labels.map((label) => label.name);

			if (labels.some((label) => label.includes('security'))) {
				await this.aiApp.queueTask({
					taskType: 'security_scan',
					githubContext: context,
					instructions: 'Automated security scan for security-related PR',
				});
			}

			if (
				labels.some(
					(label) => label.includes('docs') || label.includes('documentation'),
				)
			) {
				await this.aiApp.queueTask({
					taskType: 'documentation',
					githubContext: context,
					instructions: 'Automated documentation review for docs PR',
				});
			}
		}
	}

	private async handleIssueOpened(
		payload: GitHubWebhookPayload,
	): Promise<void> {
		// Auto-triage for new issues
		if (!payload.issue) {
			console.warn('handleIssueOpened called without issue in payload');
			return;
		}

		const context = this.buildGitHubContext(payload);
		const title = payload.issue.title.toLowerCase();
		const body = payload.issue.body?.toLowerCase() || '';

		// Always triage new issues
		await this.aiApp.queueTask({
			taskType: 'issue_triage',
			githubContext: context,
			instructions: 'Automated triage for new issue',
		});

		// Security analysis for security-related issues
		if (
			title.includes('security') ||
			title.includes('vulnerability') ||
			body.includes('security') ||
			body.includes('vulnerability')
		) {
			await this.aiApp.queueTask({
				taskType: 'security_scan',
				githubContext: context,
				instructions: 'Automated security analysis for security issue',
			});
		}

		// Performance analysis for performance issues
		if (
			title.includes('performance') ||
			title.includes('slow') ||
			body.includes('performance') ||
			body.includes('slow')
		) {
			await this.aiApp.queueTask({
				taskType: 'repo_health',
				githubContext: context,
				instructions: 'Automated performance analysis for performance issue',
			});
		}
	}

	private buildGitHubContext(payload: GitHubWebhookPayload): GitHubContext {
		const context: GitHubContext = {
			owner: payload.repository.owner.login,
			repo: payload.repository.name,
		};

		if (payload.pull_request) {
			context.pr = {
				number: payload.pull_request.number,
				title: payload.pull_request.title,
				body: payload.pull_request.body || '',
				base: payload.pull_request.base.ref,
				head: payload.pull_request.head.ref,
				files: [], // Would need additional API call to fetch files
			};
		}

		if (payload.issue && !payload.pull_request) {
			context.issue = {
				number: payload.issue.number,
				title: payload.issue.title,
				body: payload.issue.body || '',
				labels: payload.issue.labels?.map((label) => label.name) || [],
			};
		}

		return context;
	}

	private extractInstructions(
		comment: string,
		pattern: RegExp,
	): string | undefined {
		const match = pattern.exec(comment);
		if (match?.[1]) {
			return match[1].trim();
		}
		return undefined;
	}

	public addTrigger(trigger: CommentTrigger): void {
		this.triggers.push(trigger);
	}

	public removeTrigger(pattern: string): boolean {
		const initialLength = this.triggers.length;
		this.triggers = this.triggers.filter((t) => t.pattern.source !== pattern);
		return this.triggers.length < initialLength;
	}

	public async start(port: number = 3000): Promise<void> {
		return new Promise((resolve, reject) => {
			try {
				this.server = this.app.listen(port, () => {
					console.log(
						`Cortex AI GitHub webhook server running on port ${port}`,
					);
					resolve();
				});

				this.server.on('error', (error) =>
					reject(new Error(`Server error: ${error.message}`)),
				);
			} catch (error) {
				reject(error);
			}
		});
	}

	public async stop(): Promise<void> {
		return new Promise((resolve) => {
			if (this.server) {
				this.server.close(() => {
					console.log('Cortex AI GitHub webhook server stopped');
					resolve();
				});
			} else {
				resolve();
			}
		});
	}

	/**
	 * Add emoji reaction to a comment to show the bot is processing
	 */
	private async addReaction(
		payload: GitHubWebhookPayload,
		reaction: string,
	): Promise<void> {
		try {
			const { Octokit } = await import('@octokit/rest');
			const octokit = new Octokit({ auth: process.env.GITHUB_TOKEN });

			// Determine if it's an issue comment or PR review comment
			if (payload.comment && payload.repository) {
				const owner = payload.repository.owner.login;
				const repo = payload.repository.name;

				if (payload.issue) {
					// Issue comment
					await octokit.rest.reactions.createForIssueComment({
						owner,
						repo,
						comment_id: payload.comment.id,
						content: reaction as
							| '+1'
							| '-1'
							| 'laugh'
							| 'confused'
							| 'heart'
							| 'hooray'
							| 'rocket'
							| 'eyes',
					});
				} else if (payload.pull_request) {
					// PR review comment
					await octokit.rest.reactions.createForPullRequestReviewComment({
						owner,
						repo,
						comment_id: payload.comment.id,
						content: reaction as
							| '+1'
							| '-1'
							| 'laugh'
							| 'confused'
							| 'heart'
							| 'hooray'
							| 'rocket'
							| 'eyes',
					});
				}
			}
		} catch (error) {
			console.error('Failed to add reaction', { reaction, error });
			// Don't throw - reactions are non-critical
		}
	}

	/**
	 * Progressive status update system - Copilot-inspired feedback
	 * Updates reactions in sequence: ðŸ‘€ â†’ âš™ï¸ â†’ ðŸš€/âŒ
	 */
	private async updateProgressiveStatus(
		payload: GitHubWebhookPayload,
		status: ProgressiveStatus,
	): Promise<void> {
		try {
			switch (status) {
				case 'processing':
					await this.addReaction(payload, 'eyes');
					break;
				case 'working':
					await this.addReaction(payload, 'gear');
					break;
				case 'success':
					await this.addReaction(payload, 'rocket');
					break;
				case 'error':
					await this.addReaction(payload, 'x');
					break;
				case 'warning':
					await this.addReaction(payload, 'warning');
					break;
			}
		} catch (error) {
			console.error('Failed to update progressive status', { status, error });
		}
	}
}

