import pino, { type DestinationStream } from 'pino';

export enum LogLevel {
  DEBUG = 0,
  INFO = 1,
  WARN = 2,
  ERROR = 3,
}

export interface Logger {
  debug(message: string, ...args: unknown[]): void;
  info(message: string, ...args: unknown[]): void;
  warn(message: string, ...args: unknown[]): void;
  error(message: string, ...args: unknown[]): void;
}

export interface LoggerConfig {
  level?: LogLevel;
  stream?: DestinationStream;
}

const levelMap = {
  [LogLevel.DEBUG]: 'debug',
  [LogLevel.INFO]: 'info',
  [LogLevel.WARN]: 'warn',
  [LogLevel.ERROR]: 'error',
} as const;

export function createLogger(moduleName: string, config: LoggerConfig = {}): Logger {
  const defaultLevel = process.env.NODE_ENV === 'production' ? LogLevel.INFO : LogLevel.DEBUG;
  const level = config.level ?? defaultLevel;

  const base = { module: moduleName };
  const logger = pino(
    {
      level: levelMap[level],
      base,
      timestamp: pino.stdTimeFunctions.isoTime,
    },
    config.stream,
  );

  const wrap = (method: 'debug' | 'info' | 'warn' | 'error') =>
    (message: string, ...args: unknown[]) => {
      const [context] = args;
      if (context && typeof context === 'object') {
        (logger as any)[method](context, message);
      } else {
        (logger as any)[method](message, ...args);
      }
    };

  return {
    debug: wrap('debug'),
    info: wrap('info'),
    warn: wrap('warn'),
    error: wrap('error'),
  };
}

/**
 * Scan operations - functional approach
 * Broken down from large handleScanCommand method
 */

import type { EmitterWebhookEvent } from '@octokit/webhooks';
import { runSemgrepScan, type SecurityScanResult } from './semgrep-scanner.js';

export interface ScanRequest {
	owner: string;
	repo: string;
	sha: string;
	issueNumber: number;
	userId: string;
}

export const validateScanRequest = (
	payload:
		| EmitterWebhookEvent<'issue_comment.created'>['payload']
		| EmitterWebhookEvent<'pull_request_review_comment.created'>['payload'],
): ScanRequest => {
	const repo = payload.repository;
	const ownerLogin = repo.owner?.login;

	if (!ownerLogin) {
		throw new Error('Repository owner.login not available');
	}

	let issueNumber: number | undefined;
	if ('issue' in payload && payload.issue) {
		issueNumber = payload.issue.number;
	}

	if (!issueNumber) {
		throw new Error('No issue number found in payload');
	}

	const sha =
		payload.pull_request?.head?.sha ||
		payload.repository.default_branch ||
		'HEAD';

	const userId = payload.comment.user?.login;
	if (!userId) {
		throw new Error('No user login found in payload');
	}

	return {
		owner: ownerLogin,
		repo: repo.name,
		sha,
		issueNumber,
		userId,
	};
};

export const executeScan = async (
	request: ScanRequest,
): Promise<SecurityScanResult[]> => {
	console.warn(`ðŸ” ${request.userId} requested security scan`);
	console.log(`Scanning ${request.owner}/${request.repo} at ${request.sha}`);

	return runSemgrepScan(request.owner, request.repo, request.sha);
};

export const shouldCreateCheckRun = (
	payload:
		| EmitterWebhookEvent<'issue_comment.created'>['payload']
		| EmitterWebhookEvent<'pull_request_review_comment.created'>['payload'],
): boolean => {
	// Only create check runs for PR comments, not regular issues
	return 'pull_request' in payload && payload.pull_request !== null;
};

