/**
 * Captured example for teaching and behavior extension
 */
export interface CapturedExample {
	id: string;
	type: 'workflow' | 'validation' | 'decision' | 'correction';
	context: {
		prpPhase: PRPState['phase'];
		blueprint: PRPState['blueprint'];
		inputState: Partial<PRPState>;
	};
	userAction: {
		type:
			| 'validation_override'
			| 'gate_adjustment'
			| 'neuron_guidance'
			| 'workflow_modification';
		description: string;
		parameters: Record<string, unknown>;
		timestamp: string;
	};
	outcome: {
		resultingState: Partial<PRPState>;
		success: boolean;
		learningValue: number; // 0-1 score of how valuable this example is
	};
	metadata: {
		capturedBy: string;
		environment: string;
		tags: string[];
	};
}

/**
 * Teaching pattern extracted from examples
 */
export interface TeachingPattern {
	id: string;
	name: string;
	description: string;
	trigger: {
		conditions: Record<string, unknown>; // Conditions that trigger this pattern
		confidence: number;
	};
	adaptation: {
		type:
			| 'gate_modification'
			| 'workflow_adjustment'
			| 'validation_enhancement';
		parameters: Record<string, unknown>;
	};
	examples: string[]; // CapturedExample IDs that support this pattern
	effectiveness: number; // Success rate of this pattern
}

export interface SuggestedAction {
	type: string;
	description: string;
	parameters: Record<string, unknown>;
	confidence: number;
}

/**
 * Example Capture and Replay System
 *
 * Captures user interactions and system decisions to build
 * adaptive behavior patterns for the Cortex Kernel.
 */
export class ExampleCaptureSystem {
	private examples: Map<string, CapturedExample> = new Map();
	private patterns: Map<string, TeachingPattern> = new Map();

	/**
	 * Capture user interaction as learning example
	 */
	captureExample(
		type: CapturedExample['type'],
		context: CapturedExample['context'],
		userAction: CapturedExample['userAction'],
		outcome: CapturedExample['outcome'],
		metadata: Partial<CapturedExample['metadata']> = {},
		deterministic = false,
	): CapturedExample {
		const example: CapturedExample = {
			id: generateId('example', deterministic),
			type,
			context,
			userAction,
			outcome,
			metadata: {
				capturedBy: 'system',
				environment: 'development',
				tags: [],
				...metadata,
			},
		};

		this.examples.set(example.id, example);

		// Trigger pattern learning
		this.updatePatternsFromExample(example);

		return example;
	}

	/**
	 * Capture validation override example
	 */
	captureValidationOverride(
		prpState: PRPState,
		originalValidation: {
			passed: boolean;
			blockers: string[];
			majors: string[];
		},
		userOverride: {
			passed: boolean;
			reasoning: string;
			adjustments: Record<string, unknown>;
		},
		finalOutcome: { success: boolean; feedback: string },
		deterministic = false,
	): CapturedExample {
		return this.captureExample(
			'validation',
			{
				prpPhase: prpState.phase,
				blueprint: prpState.blueprint,
				inputState: { validationResults: prpState.validationResults },
			},
			{
				type: 'validation_override',
				description: `User override: ${userOverride.reasoning}`,
				parameters: {
					originalValidation,
					override: userOverride,
				},
				timestamp: new Date().toISOString(),
			},
			{
				resultingState: { validationResults: prpState.validationResults },
				success: finalOutcome.success,
				learningValue: finalOutcome.success ? 0.8 : 0.3,
			},
			{
				tags: ['validation', 'override', prpState.phase],
			},
			deterministic,
		);
	}

	/**
	 * Capture workflow modification example
	 */
	captureWorkflowModification(
		prpState: PRPState,
		modification: {
			type: 'gate_adjustment' | 'neuron_reordering' | 'phase_skipping';
			description: string;
			changes: Record<string, unknown>;
		},

		outcome: { improved: boolean; metrics: any },
		deterministic = false,
	): CapturedExample {
		return this.captureExample(
			'workflow',
			{
				prpPhase: prpState.phase,
				blueprint: prpState.blueprint,
				inputState: { phase: prpState.phase, outputs: prpState.outputs },
			},
			{
				type: 'workflow_modification',
				description: modification.description,
				parameters: modification,
				timestamp: new Date().toISOString(),
			},
			{
				resultingState: prpState,
				success: outcome.improved,
				learningValue: outcome.improved ? 0.9 : 0.2,
			},
			{
				tags: ['workflow', modification.type, prpState.phase],
			},
			deterministic,
		);
	}

	/**
	 * Replay captured example to validate or extend behavior
	 */
	async replayExample(
		exampleId: string,
		currentState: PRPState,
	): Promise<{
		applicable: boolean;
		suggestedAction?: SuggestedAction;
		confidence: number;
	}> {
		const example = this.examples.get(exampleId);
		if (!example) {
			throw new Error(`Example not found: ${exampleId}`);
		}

		// Analyze context similarity
		const contextSimilarity = this.calculateContextSimilarity(example.context, {
			prpPhase: currentState.phase,
			blueprint: currentState.blueprint,
			inputState: currentState,
		});

		if (contextSimilarity < 0.6) {
			return { applicable: false, confidence: 0 };
		}

		// Suggest action based on example
		const suggestedAction = this.adaptExampleToCurrentContext(
			example,
			currentState,
		);

		return {
			applicable: true,
			suggestedAction,
			confidence: contextSimilarity * example.outcome.learningValue,
		};
	}

	/**
	 * Update learning patterns from new examples
	 */
	private updatePatternsFromExample(example: CapturedExample): void {
		// Simple pattern extraction - in real implementation would use ML
		const patternKey = `${example.type}-${example.context.prpPhase}-${example.userAction.type}`;

		let pattern = this.patterns.get(patternKey);
		if (pattern) {
			// Update existing pattern
			pattern.examples.push(example.id);
			const totalExamples = pattern.examples.length;
			const successfulExamples = pattern.examples
				.map((id) => this.examples.get(id))
				.filter((ex) => ex?.outcome.success).length;

			pattern.effectiveness = successfulExamples / totalExamples;
			pattern.trigger.confidence = Math.min(
				0.9,
				pattern.trigger.confidence + 0.1,
			);
		} else {
			pattern = {
				id: patternKey,
				name: `${example.type} pattern for ${example.context.prpPhase}`,
				description: `Learned pattern from ${example.userAction.type} actions`,
				trigger: {
					conditions: {
						phase: example.context.prpPhase,
						actionType: example.userAction.type,
					},
					confidence: 0.5,
				},
				adaptation: {
					type: 'gate_modification',
					parameters: example.userAction.parameters,
				},
				examples: [example.id],
				effectiveness: example.outcome.success ? 1.0 : 0.0,
			};
		}

		this.patterns.set(patternKey, pattern);
	}

	/**
	 * Calculate similarity between contexts
	 */
	private calculateContextSimilarity(
		context1: CapturedExample['context'],
		context2: CapturedExample['context'],
	): number {
		let similarity = 0;

		// Phase similarity
		if (context1.prpPhase === context2.prpPhase) {
			similarity += 0.3;
		}

		// Blueprint similarity (simple keyword matching)
		const keywords1 = this.extractKeywords(context1.blueprint);
		const keywords2 = this.extractKeywords(context2.blueprint);
		const keywordOverlap = this.calculateKeywordOverlap(keywords1, keywords2);
		similarity += keywordOverlap * 0.4;

		// State similarity (basic structure comparison)
		const statesSimilar = this.compareStates(
			context1.inputState,
			context2.inputState,
		);
		similarity += statesSimilar * 0.3;

		return Math.min(1.0, similarity);
	}

	/**
	 * Adapt example to current context
	 */
	private adaptExampleToCurrentContext(
		example: CapturedExample,
		currentState: PRPState,
	): SuggestedAction {
		// Simple adaptation - in real implementation would be more sophisticated
		return {
			type: example.userAction.type,
			description: `Adapted from example: ${example.userAction.description}`,
			parameters: {
				...example.userAction.parameters,
				adaptedFor: currentState.id,
				originalExample: example.id,
			},
			confidence: 0.7,
		};
	}

	/**
	 * Extract keywords from blueprint for similarity comparison
	 */
	private extractKeywords(blueprint: PRPState['blueprint']): string[] {
		const text = `${blueprint.title} ${blueprint.description} ${blueprint.requirements?.join(' ')}`;
		return text
			.toLowerCase()
			.split(/\s+/)
			.filter((word) => word.length > 3)
			.slice(0, 10); // Top 10 keywords
	}

	/**
	 * Calculate keyword overlap between two sets
	 */
	private calculateKeywordOverlap(
		keywords1: string[],
		keywords2: string[],
	): number {
		const set1 = new Set(keywords1);
		const set2 = new Set(keywords2);
		const intersection = new Set([...set1].filter((x) => set2.has(x)));
		const union = new Set([...set1, ...set2]);

		return union.size > 0 ? intersection.size / union.size : 0;
	}

	/**
	 * Compare states for similarity
	 */
	private compareStates(
		state1: Partial<PRPState>,
		state2: Partial<PRPState>,
	): number {
		// Simple structural comparison
		const keys1 = Object.keys(state1);
		const keys2 = Object.keys(state2);
		const commonKeys = keys1.filter((key) => keys2.includes(key));

		return commonKeys.length / Math.max(keys1.length, keys2.length, 1);
	}

	/**
	 * Get captured examples for analysis
	 */
	getExamples(filter?: {
		type?: CapturedExample['type'];
		phase?: PRPState['phase'];
		tags?: string[];
	}): CapturedExample[] {
		let examples = Array.from(this.examples.values());

		if (filter) {
			if (filter.type) {
				examples = examples.filter((ex) => ex.type === filter.type);
			}
			if (filter.phase) {
				examples = examples.filter(
					(ex) => ex.context.prpPhase === filter.phase,
				);
			}
			if (filter.tags) {
				examples = examples.filter((ex) =>
					filter.tags?.some((tag) => ex.metadata.tags.includes(tag)),
				);
			}
		}

		return examples;
	}

	/**
	 * Get learned patterns
	 */
	getPatterns(): TeachingPattern[] {
		return Array.from(this.patterns.values());
	}
}

import os from 'node:os';
import path, { dirname } from 'node:path';
import { fileURLToPath } from 'node:url';
import type { Embedder } from '../ports/Embedder.js';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

const DEFAULT_MLX_MODELS_DIR = path.join(os.homedir(), '.cache', 'huggingface');

// Available MLX embedding models - paths configurable via environment variables
const MLX_MODELS = {
	'qwen3-0.6b': {
		name: 'Qwen3-Embedding-0.6B',
		dimensions: 768,
		path:
			process.env.MLX_MODEL_QWEN3_0_6B_PATH ||
			path.join(DEFAULT_MLX_MODELS_DIR, 'models--Qwen--Qwen3-Embedding-0.6B'),
		recommendedFor: ['quick_search', 'development'],
	},
	'qwen3-4b': {
		name: 'Qwen3-Embedding-4B',
		dimensions: 768,
		path:
			process.env.MLX_MODEL_QWEN3_4B_PATH ||
			path.join(DEFAULT_MLX_MODELS_DIR, 'models--Qwen--Qwen3-Embedding-4B'),
		recommendedFor: ['production', 'balanced_performance'],
	},
	'qwen3-8b': {
		name: 'Qwen3-Embedding-8B',
		dimensions: 768,
		path:
			process.env.MLX_MODEL_QWEN3_8B_PATH ||
			path.join(DEFAULT_MLX_MODELS_DIR, 'models--Qwen--Qwen3-Embedding-8B'),
		recommendedFor: ['high_accuracy', 'research'],
	},
} as const;

type MLXModelName = keyof typeof MLX_MODELS;

const DEFAULT_MLX_MODEL: MLXModelName = 'qwen3-4b';

export class MLXEmbedder implements Embedder {
	private readonly modelName: MLXModelName;
	private readonly modelConfig: (typeof MLX_MODELS)[keyof typeof MLX_MODELS];

	constructor(modelName?: MLXModelName) {
		this.modelName = modelName || DEFAULT_MLX_MODEL;
		this.modelConfig = MLX_MODELS[this.modelName];

		if (!this.modelConfig) {
			throw new Error(`Unsupported MLX model: ${this.modelName}`);
		}
	}

	name(): string {
		return this.modelName;
	}

	async embed(texts: string[]): Promise<number[][]> {
		try {
			// Try to use existing MLX service if available
			if (process.env.MLX_EMBED_BASE_URL || process.env.MLX_SERVICE_URL) {
				return await this.embedViaService(texts);
			}

			// Fallback to direct Python execution
			return await this.embedViaPython(texts);
		} catch (error) {
			console.warn('MLX embedding failed:', error);
			throw error;
		}
	}

	private async embedViaService(texts: string[]): Promise<number[][]> {
		const base = process.env.MLX_EMBED_BASE_URL || process.env.MLX_SERVICE_URL;
		if (!base) throw new Error('MLX service URL not configured');
		const response = await fetch(`${base.replace(/\/$/, '')}/embed`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			// Provide both fields for compatibility with various servers
			body: JSON.stringify({
				texts,
				input: texts,
				model: this.modelName,
			}),
			signal: AbortSignal.timeout(30000),
		});

		if (!response.ok) {
			throw new Error(
				`MLX service error: ${response.status} ${response.statusText}`,
			);
		}

		const data = await response.json();

		// Accept either {embeddings: number[][]} or single {embedding: number[]}
		if (Array.isArray((data as any).embeddings)) {
			return (data as any).embeddings as number[][];
		}
		if (Array.isArray((data as any).embedding)) {
			return [((data as any).embedding as number[])];
		}
		throw new Error('Invalid response format from MLX service');
	}

	private async embedViaPython(texts: string[]): Promise<number[][]> {
		// Use centralized Python runner to handle PYTHONPATH and env merging
		const pythonScriptPath = path.join(__dirname, 'mlx-embedder.py');

		// Dynamic import from shared python exec utility; typed as any to avoid coupling
		type PyExec = { runPython: (script: string, args: string[], opts: Record<string, unknown>) => Promise<unknown> };
		const mod = (await import('../../../../libs/python/exec.js')) as unknown as PyExec;
		const { runPython } = mod;

		const run = () =>
			runPython(
				pythonScriptPath,
				[this.modelConfig.path, JSON.stringify(texts)],
				{
					envOverrides: {
						MLX_MODELS_DIR:
							process.env.MLX_MODELS_DIR || DEFAULT_MLX_MODELS_DIR,
					},
					python: process.env.PYTHON_EXEC || 'python3',
					setModulePath: process.env.PYTHONPATH || undefined,
				} as unknown as Record<string, unknown>,
			);

		const timer = new Promise<never>((_, reject) =>
			setTimeout(
				() => reject(new Error('MLX embedding timeout after 30000ms')),
				30000,
			),
		);

		const out = await Promise.race([run(), timer]);
		try {
			const result = JSON.parse(String(out || '{}'));
			if (result.error) throw new Error(String(result.error));
			if (!Array.isArray(result.embeddings))
				throw new Error('Invalid embeddings format from MLX');
			return result.embeddings as number[][];
		} catch (err) {
			throw new Error(`Failed to parse MLX response: ${err}`);
		}
	}
}

import axios from 'axios';
import type { Embedder } from '../ports/Embedder.js';

const DEFAULT_OLLAMA_MODEL = 'nomic-embed-text';
const OLLAMA_BASE_URL = process.env.OLLAMA_BASE_URL || 'http://localhost:11434';

export class OllamaEmbedder implements Embedder {
	private readonly modelName: string;
	private readonly client: any;

	constructor(modelName?: string) {
		this.modelName = modelName || DEFAULT_OLLAMA_MODEL;
		this.client = axios.create({
			baseURL: OLLAMA_BASE_URL,
			timeout: 30000,
			headers: {
				'Content-Type': 'application/json',
			},
		});
	}

	name(): string {
		return `ollama-${this.modelName}`;
	}

	async embed(texts: string[]): Promise<number[][]> {
		try {
			const requests = texts.map((text) =>
				this.client
					.post('/api/embeddings', { model: this.modelName, prompt: text })
					.then((response: any) => {
						if (response.data && Array.isArray(response.data.embedding)) {
							return response.data.embedding as number[];
						}
						throw new Error('Invalid response from Ollama embedding API');
					}),
			);

			return await Promise.all(requests);
		} catch (error) {
			console.error('Ollama embedding failed:', error);
			throw error;
		}
	}
}

import type { Memory, MemoryId } from '../domain/types.js';
import type { MemoryStore, TextQuery, VectorQuery } from '../ports/MemoryStore.js';
import type { EncryptionService } from '../ports/Encryption.js';

// Fields we encrypt at-rest: text (string) and provenance (object serialized)
function isEncrypted(value: unknown): value is string {
  return typeof value === 'string' && value.startsWith('enc:');
}

async function encryptText(es: EncryptionService, text?: string) {
  if (!text) return undefined;
  const c = await es.encrypt(text);
  return `enc:${c}`;
}

async function decryptText(es: EncryptionService, text?: string) {
  if (!text) return undefined;
  if (!isEncrypted(text)) return text; // backward compatibility
  const body = text.slice(4);
  return es.decrypt(body);
}

export type EncryptedOptions = {
  encryptVectors?: boolean;
  encryptTags?: boolean;
};

export class EncryptedStore implements MemoryStore {
  constructor(
    private readonly inner: MemoryStore,
    private readonly crypto: EncryptionService,
    private readonly opts: EncryptedOptions = {},
  ) {}

  private async toEncrypted(m: Memory): Promise<Memory> {
    const encText = await encryptText(this.crypto, m.text);
    const encProv = m.provenance ? await encryptText(this.crypto, JSON.stringify(m.provenance)) : undefined;
    const out: any = {
      ...m,
      text: encText,
      provenance: encProv as unknown as Memory['provenance'],
    };
    if (this.opts.encryptVectors && m.vector) {
      out.vector = await encryptText(this.crypto, JSON.stringify(m.vector));
    }
    if (this.opts.encryptTags && m.tags) {
      out.tags = (await encryptText(this.crypto, JSON.stringify(m.tags))) as unknown as string[];
    }
    return out as Memory;
  }

  private async toDecrypted(m: Memory): Promise<Memory> {
    const decText = await decryptText(this.crypto, m.text);
    let prov: Memory['provenance'] | undefined;
    if (typeof m.provenance === 'string') {
      const dec = await decryptText(this.crypto, m.provenance);
      prov = dec ? (JSON.parse(dec) as Memory['provenance']) : undefined;
    } else {
      prov = m.provenance;
    }
    const out: any = { ...m, text: decText, provenance: prov ?? m.provenance };
    if (this.opts.encryptVectors && typeof m.vector === 'string') {
      const dec = await decryptText(this.crypto, m.vector);
      out.vector = dec ? (JSON.parse(dec) as number[]) : undefined;
    }
    if (this.opts.encryptTags && Array.isArray(m.tags) === false && typeof (m as any).tags === 'string') {
      const dec = await decryptText(this.crypto, (m as any).tags);
      out.tags = dec ? (JSON.parse(dec) as string[]) : [];
    }
    return out as Memory;
  }

  async upsert(m: Memory, namespace?: string): Promise<Memory> {
    const enc = await this.toEncrypted(m);
    await this.inner.upsert(enc, namespace);
    // Return logical view
    return m;
  }

  async get(id: MemoryId, namespace?: string): Promise<Memory | null> {
    const got = await this.inner.get(id, namespace);
    if (!got) return null;
    return this.toDecrypted(got);
  }

  async delete(id: MemoryId, namespace?: string): Promise<void> {
    return this.inner.delete(id, namespace);
  }

  async searchByText(q: TextQuery, namespace?: string): Promise<Memory[]> {
    const res = await this.inner.searchByText(q, namespace);
    return Promise.all(res.map((m) => this.toDecrypted(m)));
  }

  async searchByVector(q: VectorQuery, namespace?: string): Promise<Memory[]> {
    const res = await this.inner.searchByVector(q, namespace);
    return Promise.all(res.map((m) => this.toDecrypted(m)));
  }

  async purgeExpired(nowISO: string, namespace?: string): Promise<number> {
    return this.inner.purgeExpired(nowISO, namespace);
  }
}

import { trace } from '@opentelemetry/api';

export const tracer = trace.getTracer('@cortex-os/memories');

export async function withSpan<T>(
	name: string,
	fn: () => Promise<T>,
): Promise<T> {
	return tracer.startActiveSpan(name, async (span) => {
		try {
			const res = await fn();
			span.end();
			return res;
		} catch (err: any) {
			span.recordException(err);
			span.end();
			throw err;
		}
	});
}

import { randomUUID } from 'node:crypto';
import fs from 'node:fs';
import readline from 'node:readline';
import { z } from 'zod';
import { createEmbedderFromEnv } from '../service/embedder-factory.js';
import { InMemoryStore } from '../adapters/store.memory.js';
import type { Memory } from '../domain/types.js';
import { createMemoryService } from '../service/memory-service.js';

const cliSchema = z.object({
	input: z.string(),
	output: z.string(),
	tags: z.array(z.string()).optional(),
});

function parseArgs() {
	const args = process.argv.slice(2);
	const opts: Record<string, unknown> = {};
	for (let i = 0; i < args.length; i++) {
		const arg = args[i];
		if (arg === '--input' || arg === '-i') opts.input = args[++i];
		else if (arg === '--output' || arg === '-o') opts.output = args[++i];
		else if (arg === '--tags' || arg === '-t')
			opts.tags = args[++i]
				.split(',')
				.map((t) => t.trim())
				.filter(Boolean);
	}
	return cliSchema.parse(opts);
}

async function main() {
	const { input, output, tags } = parseArgs();

	const rl = readline.createInterface({
		input: fs.createReadStream(input),
		crlfDelay: Infinity,
	});

  const service = createMemoryService(new InMemoryStore(), createEmbedderFromEnv());
	const memories: Memory[] = [];

	for await (const line of rl) {
		if (!line.trim()) continue;
		const obj = JSON.parse(line);
		const text = obj.text ?? obj.content ?? JSON.stringify(obj);
		const now = new Date().toISOString();
		const raw = {
			id: obj.id ?? randomUUID(),
			kind: 'note' as const,
			text,
			tags: [...(tags ?? []), ...(obj.tags ?? [])].filter(Boolean),
			createdAt: obj.createdAt ?? now,
			updatedAt: now,
			provenance: { source: 'system' as const },
		};
		const saved = await service.save(raw);
		memories.push(saved);
	}

	fs.writeFileSync(output, JSON.stringify(memories, null, 2));
	console.log(`Wrote ${memories.length} memories to ${output}`);
}

main().catch((err) => {
	console.error(err);
	process.exit(1);
});

