}

export interface ExecutionMetrics {
	startTime: string;
	endTime: string;
	duration: number;
	toolsUsed: string[];
	filesCreated: number;
	filesModified: number;
	commandsExecuted: number;
}

export interface MCPTool {
	name: string;
	description: string;
	inputSchema: unknown;
	execute(params: unknown, context: MCPContext): Promise<unknown>;
}

export interface MCPContext {
	prpState: PRPState;
	workingDirectory: string;
	toolsEnabled: string[];
	securityPolicy: {
		allowFileSystem: boolean;
		allowNetwork: boolean;
		allowExecution: boolean;
	};
}

export interface PRPOrchestrator {
	getNeuronCount(): number;
	executeNeuron?(
		neuronId: string,
		state: PRPState,
		context: unknown,
	): Promise<unknown>;
}

export class MCPAdapter {
	private tools: Map<string, MCPTool> = new Map();
	private contexts: Map<string, MCPContext> = new Map();

	registerTool(tool: MCPTool): void {
		this.tools.set(tool.name, tool);
	}

	createContext(
		prpState: PRPState,
		options: {
			workingDirectory?: string;
			enabledTools?: string[];
			securityPolicy?: Partial<MCPContext['securityPolicy']>;
		} = {},
	): MCPContext {
		const context: MCPContext = {
			prpState,
			workingDirectory: options.workingDirectory || process.cwd(),
			toolsEnabled: options.enabledTools || Array.from(this.tools.keys()),
			securityPolicy: {
				allowFileSystem: true,
				allowNetwork: false,
				allowExecution: true,
				...options.securityPolicy,
			},
		};

		this.contexts.set(prpState.runId, context);
		return context;
	}

	async executeTool(
		toolName: string,
		params: unknown,
		runId: string,
	): Promise<{
		result: unknown;
		evidence: {
			toolName: string;
			params: unknown;
			result: unknown;
			timestamp: string;
		};
	}> {
		const tool = this.tools.get(toolName);
		if (!tool) throw new Error(`MCP tool not found: ${toolName}`);

		const context = this.contexts.get(runId);
		if (!context) throw new Error(`MCP context not found for run: ${runId}`);

		if (!context.toolsEnabled.includes(toolName)) {
			throw new Error(`MCP tool not enabled: ${toolName}`);
		}

		const result = await tool.execute(params, context);
		const evidence = {
			toolName,
			params,
			result,
			timestamp: new Date().toISOString(),
		};
		return { result, evidence };
	}

	createNeuronFromTool(
		tool: MCPTool,
		phase: 'strategy' | 'build' | 'evaluation',
	): Neuron {
		return {
			id: `mcp-${tool.name}`,
			role: `mcp-tool-${tool.name}`,
			phase,
			dependencies: [],
			tools: [tool.name],
			requiresLLM: false,
			execute: async (
				state: PRPState,
				context: { workingDirectory?: string },
			) => {
				this.createContext(state, {
					workingDirectory: context.workingDirectory,
				});
				const params = this.extractToolParams(state.blueprint, tool);
				const execution = await this.executeTool(
					tool.name,
					params,
					state.runId,
				);
				return {
					output: {
						toolName: tool.name,
						result: execution.result,
						mcpIntegration: true,
					},
					evidence: [
						{
							id: `mcp-${tool.name}-${Date.now()}`,
							type: 'command',
							source: `mcp-${tool.name}`,
							content: JSON.stringify(execution.evidence),
							timestamp: new Date().toISOString(),
							phase,
						},
					],
					nextSteps: [`Review ${tool.name} output`],
					artifacts: [],
					metrics: {
						startTime: new Date().toISOString(),
						endTime: new Date().toISOString(),
						duration: 0,
						toolsUsed: [tool.name],
						filesCreated: 0,
						filesModified: 0,
						commandsExecuted: 1,
					},
				};
			},
		};
	}

	private extractToolParams(
		blueprint: PRPState['blueprint'],
		tool: MCPTool,
	): {
		title: string;
		description: string;
		requirements: string[];
		toolName: string;
	} {
		return {
			title: blueprint.title,
			description: blueprint.description,
			requirements: blueprint.requirements,
			toolName: tool.name,
		};
	}

	getAvailableTools(): MCPTool[] {
		return Array.from(this.tools.values());
	}

	getContext(runId: string): MCPContext | undefined {
		return this.contexts.get(runId);
	}

	cleanupContext(runId: string): void {
		this.contexts.delete(runId);
	}
}

// Default tools
export const createDefaultMCPTools = (): MCPTool[] => [
	{
		name: 'read_file',
		description: 'Read file content safely within the working directory',
		inputSchema: {
			type: 'object',
			properties: {
				path: { type: 'string' },
				encoding: {
					type: 'string',
					enum: ['utf8', 'utf-8', 'base64'],
					default: 'utf8',
				},
			},
			required: ['path'],
		},
		execute: async (
			params: { path: string; encoding?: BufferEncoding },
			context: MCPContext,
		): Promise<{ content: string; bytes: number; fullPath: string }> => {
			if (!context.securityPolicy.allowFileSystem)
				throw new Error('File system access not allowed');
			const fs = await import('node:fs');
			const pathMod = await import('node:path');
			const wd = context.workingDirectory || process.cwd();
			const fullPath = pathMod.resolve(wd, params.path);
			const relative = pathMod.relative(wd, fullPath);
			if (relative.startsWith('..') || pathMod.isAbsolute(relative)) {
				throw new Error(
					`Access denied: ${params.path} is outside working directory`,
				);
			}
			await fs.promises.access(fullPath, fs.constants.R_OK);
			const encoding = (params.encoding || 'utf8') as BufferEncoding;
			const data = await fs.promises.readFile(fullPath, encoding);
			const stat = await fs.promises.stat(fullPath);
			return { content: data, bytes: stat.size, fullPath };
		},
	},
	{
		name: 'code_analysis',
		description: 'Analyze code quality with ESLint when available',
		inputSchema: {
			type: 'object',
			properties: {
				files: { type: 'array', items: { type: 'string' } },
				code: { type: 'string' },
				language: { type: 'string' },
				cwd: { type: 'string' },
				configPath: { type: 'string' },
			},
			required: [],
		},
		execute: async (
			params: Partial<{
				files: string[];
				code: string;
				language: string;
				cwd: string;
				configPath: string;
			}>,
			context: MCPContext,
		) => {
			try {
				const eslintMod = await import('eslint');
				const { ESLint } = eslintMod;
				const cwd =
					(params.cwd as string) || context.workingDirectory || process.cwd();
				const eslint = new ESLint({
					cwd,
					overrideConfigFile: params.configPath,
				});
				const targets: string[] =
					Array.isArray(params.files) && params.files.length > 0
						? params.files
						: ['.'];
				const results = await eslint.lintFiles(targets);
				const formatter = await eslint.loadFormatter('stylish');
				const textReport = await formatter.format(results);
				const errorCount = results.reduce((a, r) => a + r.errorCount, 0);
				const warningCount = results.reduce((a, r) => a + r.warningCount, 0);
				const issues = results.flatMap((r) =>
					r.messages.map((m) => ({
						filePath: r.filePath,
						ruleId: m.ruleId,
						severity: m.severity === 2 ? 'error' : 'warning',
						message: m.message,
						line: m.line,
						column: m.column,
					})),
				);
				return {
					tool: 'eslint',
					errorCount,
					warningCount,
					issues,
					summary: `${errorCount} errors, ${warningCount} warnings`,
					report: textReport,
				};
			} catch (err: unknown) {
				const message = err instanceof Error ? err.message : String(err);
				console.debug('ESLint not available or failed:', message);
				return {
					tool: 'eslint',
					not_available: true,
					error: message,
					errorCount: 0,
					warningCount: 0,
					issues: [],
					summary: 'ESLint not available',
				};
			}
		},
	},
	{
		name: 'test_runner',
		description: 'Execute tests (Vitest/Jest) with optional coverage',
		inputSchema: {
			type: 'object',
			properties: {
				testPath: { type: 'string' },
				framework: {
					type: 'string',
					enum: ['vitest', 'jest', 'auto'],
					default: 'auto',
				},
				coverage: { type: 'boolean', default: true },
				cwd: { type: 'string' },
			},
		},
		execute: async (
			params: Partial<{
				testPath: string;
				framework: string;
				coverage: boolean;
				cwd: string;
			}>,
			context: MCPContext,
		) => {
			if (!context.securityPolicy.allowExecution)
				throw new Error('Code execution not allowed');
			const { exec } = await import('node:child_process');
			const { promisify } = await import('node:util');
			const execAsync = promisify(exec);
			const cwd =
				(params.cwd as string) || context.workingDirectory || process.cwd();
			const coverage = params.coverage !== false;
			const framework = (params.framework || 'auto') as string;
			const escapeStr = (s: string) => s.replaceAll('"', '\\"');

			let cmd = '';
			if (framework === 'vitest' || framework === 'auto') {
				try {
					await execAsync('pnpm vitest --version', { cwd, timeout: 5000 });
					const cov = coverage ? '--coverage' : '';
					const target = params.testPath
						? ` ${escapeStr(params.testPath)}`
						: '';
					cmd = `pnpm vitest run --reporter=json ${cov}${target}`.trim();
				} catch (_vitestDetectionError) {
					// Silently ignore: absence of Vitest is non-fatal, we'll try Jest next
				}
			}
			if (!cmd && (framework === 'jest' || framework === 'auto')) {
				try {
					await execAsync('pnpm jest --version', { cwd, timeout: 5000 });
					const cov = coverage ? '--coverage' : '';
					const target = params.testPath
						? ` ${escapeStr(params.testPath)}`
						: '';
					cmd = `pnpm jest --runInBand --reporters=json ${cov}${target}`.trim();
				} catch (_jestDetectionError) {
					// Silently ignore: absence of Jest is fine; fallback logic below will run generic tests
				}
			}
			if (!cmd) {
				const cov = coverage ? '-- --coverage' : '';
				const target = params.testPath ? ` ${escape(params.testPath)}` : '';
				cmd = `pnpm test${target}${cov}`.trim();
			}

			try {
				const started = Date.now();
				const { stdout, stderr } = await execAsync(cmd, {
					cwd,
					maxBuffer: 10 * 1024 * 1024,
				});
				const duration = Date.now() - started;
				let summary: unknown;
				try {
					summary = JSON.parse(stdout);
				} catch (_parseSummaryError) {
					// Ignore JSON parse errors: we'll return raw stdout which still contains useful info.
				}
				let coveragePct: number | undefined;
				try {
					const fs = await import('node:fs');
					const pathMod = await import('node:path');
					const covPath = pathMod.join(
						cwd,
						'coverage',
						'coverage-summary.json',
					);
					if (fs.existsSync(covPath)) {
						const cov = JSON.parse(await fs.promises.readFile(covPath, 'utf8'));
						const totals = cov.total || {};
						const keys = [
							'statements',
							'branches',
							'functions',
							'lines',
						] as const;
						const vals = keys
							.map((k) => totals[k]?.pct)
							.filter((v): v is number => typeof v === 'number');
						if (vals.length)
							coveragePct = Math.round(
								vals.reduce((a: number, b: number) => a + b, 0) / vals.length,
							);
					}
				} catch (_coverageReadError) {
					// Ignore coverage errors; coverage is optional and won't fail the overall test run result.
				}
				let passed = 0,
					failed = 0,
					total = 0;
				if (summary && summary.numPassedTests !== undefined) {
					passed = summary.numPassedTests;
					failed = summary.numFailedTests || 0;
					total = summary.numTotalTests || passed + failed;
				}
				return {
					command: cmd,
					durationMs: duration,
					stdout: summary ? undefined : stdout,
					stderr: stderr || undefined,
					totals: { passed, failed, total },
					coverage: coveragePct,
				};
			} catch (err: unknown) {
				const message = err instanceof Error ? err.message : String(err);
				// Attempt to extract stdout/stderr if present (exec error shape)
				// Narrow possible exec error shape (child_process.ExecException augmented with stdout/stderr)
				const anyErr = err as { stdout?: string; stderr?: string } | undefined;
				return {
					command: cmd,
					error: message,
					stdout: anyErr?.stdout,
					stderr: anyErr?.stderr,
				};
			}
		},
	},
];

// Discover MCP servers from .cortex context (optional)
export async function discoverMCPServers(
	fromDir?: string,
): Promise<{ name: string; url: string; type: string }[]> {
	try {
		const fs = await import('node:fs');
		const pathMod = await import('node:path');
		const root = fromDir || process.cwd();
		const cfgPath = pathMod.join(root, '.cortex', 'mcp.runtime.json');
		if (!fs.existsSync(cfgPath)) return [];
		const raw = await fs.promises.readFile(cfgPath, 'utf8');
		const json = JSON.parse(raw);
		const servers = json?.servers || {};
		return Object.keys(servers)
			.map((k) => ({
				name: servers[k].name || k,
				url: servers[k].url,
				type: servers[k].type || servers[k].transport || 'http',
			}))
			.filter((s) => !!s.url);
	} catch {
		return [];
	}
}

/**
 * @file nodes/build.ts
 * @description Build Phase Node - Compilation, API schema, Security scan, Performance
 * @author Cortex-OS Team
 * @version 1.0.0
 */

import { generateEvidenceId, getCurrentTimestamp } from '../lib/utils.js';
import type { Evidence, PRPState } from '../state.js';
import { ApiSchemaValidator } from '../validators/api-schema-validator.js';
import { BackendValidator } from '../validators/backend-validator.js';
import { DocumentationValidator } from '../validators/documentation-validator.js';
import { FrontendValidator } from '../validators/frontend-validator.js';
import { SecurityScanner } from '../validators/security-scanner.js';

/**
 * Build Phase Gates:
 * - âœ… Backend passes compilation + tests
 * - âœ… API schema validated (OpenAPI/JSON Schema)
 * - âœ… Security scanner (CodeQL, Semgrep) â‰¤ agreed majors
 * - âœ… Frontend Lighthouse/Axe â‰¥ 90%
 * - âœ… Docs complete with API + usage notes
 */
export class BuildNode {
	private backendValidator = new BackendValidator();
	private apiSchemaValidator = new ApiSchemaValidator();
	private securityScanner = new SecurityScanner();
	private frontendValidator = new FrontendValidator();
	private documentationValidator = new DocumentationValidator();

	async execute(state: PRPState): Promise<PRPState> {
		const evidence: Evidence[] = [];
		const blockers: string[] = [];
		const majors: string[] = [];

		const backendValidation = await this.backendValidator.validate(state);
		if (!backendValidation.passed) {
			blockers.push('Backend compilation or tests failed');
		}
		evidence.push(
			this.createEvidence(
				'backend',
				'test',
				'backend_validation',
				backendValidation,
			),
		);

		const apiValidation = await this.apiSchemaValidator.validate(state);
		if (!apiValidation.passed) {
			blockers.push('API schema validation failed');
		}
		evidence.push(
			this.createEvidence(
				'api',
				'analysis',
				'api_schema_validation',
				apiValidation,
			),
		);

		const securityScan = await this.securityScanner.runSecurityScan(state);
		if (securityScan.blockers > 0) {
			blockers.push(
				`Security scan found ${securityScan.blockers} critical issues`,
			);
		}
		if (securityScan.majors > 3) {
			majors.push(
				`Security scan found ${securityScan.majors} major issues (limit: 3)`,
			);
		}
		evidence.push(
			this.createEvidence(
				'security',
				'analysis',
				'security_scanner',
				securityScan,
			),
		);

		const frontendValidation = await this.frontendValidator.validate(state);
		if (!frontendValidation.passed) {
			const lighthouse = (frontendValidation.details.lighthouse as number) || 0;
			const axe = (frontendValidation.details.axe as number) || 0;
			if (lighthouse < 90) {
				majors.push(`Lighthouse score ${lighthouse} below 90%`);
			}
			if (axe < 90) {
				majors.push(`Axe accessibility score ${axe} below 90%`);
			}
		}
		evidence.push(
			this.createEvidence(
				'frontend',
				'analysis',
				'frontend_validation',
				frontendValidation,
			),
		);

		const docsValidation = await this.documentationValidator.validate(state);
		if (!docsValidation.passed) {
			majors.push('Documentation incomplete - missing API docs or usage notes');
		}

		return {
			...state,
			evidence: [...state.evidence, ...evidence],
			validationResults: {
				...state.validationResults,
				build: {
					passed: blockers.length === 0 && majors.length <= 3,
					blockers,
					majors,
					evidence: evidence.map((e) => e.id),
					timestamp: getCurrentTimestamp(),
				},
			},
		};
	}

	private createEvidence(
		prefix: string,
		type: Evidence['type'],
		source: string,
		content: any,
	): Evidence {
		return {
			id: generateEvidenceId(`build-${prefix}`),
			type,
			source,
			content: JSON.stringify(content),
			timestamp: getCurrentTimestamp(),
			phase: 'build',
		};
	}
}

// NOTE: Lower-level heuristic implementations were removed in favor of dedicated
// validator classes under `validators/`. This file delegates to those validators
// for backend, api, security, frontend, and documentation checks to keep logic
// testable and deterministic in CI.

/**
 * @file nodes/evaluation-refactored.ts
 * @description Refactored Evaluation Phase Node - TDD validation, Code review, Quality gates
 * @author Cortex-OS Team
 * @version 2.0.0
 */

import type { Evidence, PRPState } from '../state.js';
import { validateCodeReview } from './evaluation/code-review-validator.js';
import { validateQualityBudgets } from './evaluation/quality-budget-validator.js';
import { validateTDDCycle } from './evaluation/tdd-validator.js';

/**
 * Evaluation Phase Gates:
 * - âœ… All neurons pass TDD (Red â†’ Green)
 * - âœ… Reviewer neuron issues â‰¤ 0 blockers, â‰¤ 3 majors
 * - âœ… A11y, perf, sec budgets all â‰¥ thresholds
 * - âœ… Cerebrum consensus: ship or recycle
 */
export class EvaluationNode {
	async execute(state: PRPState): Promise<PRPState> {
		const evidence: Evidence[] = [];
		const blockers: string[] = [];
		const majors: string[] = [];

		// Gate 1: TDD validation (Red â†’ Green cycle)
		const tddValidation = await validateTDDCycle(state);
		if (!tddValidation.passed) {
			blockers.push('TDD cycle not completed - missing tests or failing tests');
		}

		evidence.push({
			id: `eval-tdd-${Date.now()}`,
			type: 'test',
			source: 'tdd_validator',
			content: JSON.stringify(tddValidation),
			timestamp: new Date().toISOString(),
			phase: 'evaluation',
		});

		// Gate 2: Code review validation
		const reviewValidation = await validateCodeReview(state);
		if (reviewValidation.blockers > 0) {
			blockers.push(
				`Code review found ${reviewValidation.blockers} blocking issues`,
			);
		}
		if (reviewValidation.majors > 3) {
			majors.push(
				`Code review found ${reviewValidation.majors} major issues (limit: 3)`,
			);
		}

		evidence.push({
			id: `eval-review-${Date.now()}`,
			type: 'analysis',
			source: 'code_reviewer',
			content: JSON.stringify(reviewValidation),
			timestamp: new Date().toISOString(),
			phase: 'evaluation',
		});

		// Gate 3: Quality budget validation
		const qualityValidation = await validateQualityBudgets(state);
		if (!qualityValidation.overall) {
			const failedBudgets = [
				!qualityValidation.accessibility.passed && 'Accessibility',
				!qualityValidation.performance.passed && 'Performance',
				!qualityValidation.security.passed && 'Security',
			].filter(Boolean);

			majors.push(`Quality budgets failed: ${failedBudgets.join(', ')}`);
		}

		evidence.push({
			id: `eval-quality-${Date.now()}`,
			type: 'analysis',
			source: 'quality_validator',
			content: JSON.stringify(qualityValidation),
			timestamp: new Date().toISOString(),
			phase: 'evaluation',
		});

		// Gate 4: Cerebrum consensus evaluation
		const consensus = await evaluateCerebrumConsensus(state, {
			tdd: tddValidation,
			review: reviewValidation,
			quality: qualityValidation,
		});

		evidence.push({
			id: `eval-consensus-${Date.now()}`,
			type: 'analysis',
			source: 'cerebrum',
			content: JSON.stringify(consensus),
			timestamp: new Date().toISOString(),
			phase: 'evaluation',
		});

		// Determine final decision
		const shouldShip =
			blockers.length === 0 && consensus.recommendation === 'ship';
		const decision = shouldShip ? 'ship' : 'recycle';

		return {
			...state,
			evidence: [...state.evidence, ...evidence],
			phase: decision === 'ship' ? 'completed' : 'evaluation',
			metadata: {
				...state.metadata,
				executionContext: {
					...state.metadata.executionContext,
					evaluation: {
						decision,
						blockers: blockers.length,
						majors: majors.length,
						tddPassed: tddValidation.passed,
						reviewPassed:
							reviewValidation.blockers === 0 && reviewValidation.majors <= 3,
						qualityPassed: qualityValidation.overall,
						consensus: consensus.recommendation,
						timestamp: new Date().toISOString(),
					},
				},
			},
		};
	}
}

/**
 * Evaluates cerebrum consensus for shipping decision
 */
const evaluateCerebrumConsensus = async (
	_state: PRPState,
	validationResults: {
		tdd: any;
		review: any;
		quality: any;
	},
) => {
	const { tdd, review, quality } = validationResults;

	// Calculate confidence score
	const tddScore = tdd.passed ? 30 : 0;
	const reviewScore =
		review.blockers === 0 ? (review.majors <= 3 ? 30 : 15) : 0;
	const qualityScore = quality.overall ? 40 : getPartialQualityScore(quality);

	const totalScore = tddScore + reviewScore + qualityScore;
	const confidence = Math.min(100, totalScore);

	// Determine recommendation
	let recommendation: 'ship' | 'recycle';
	let reasoning: string[];

	if (confidence >= 80 && tdd.passed && review.blockers === 0) {
		recommendation = 'ship';
		reasoning = [
			'All critical gates passed',
			`High confidence score: ${confidence}%`,
			'Ready for production deployment',
		];
	} else {
		recommendation = 'recycle';
		reasoning = [
			confidence < 80 && `Low confidence score: ${confidence}%`,
			!tdd.passed && 'TDD cycle incomplete',
			review.blockers > 0 && 'Blocking issues found in review',
			!quality.overall && 'Quality budgets not met',
		].filter(Boolean) as string[];
	}

	return {
		recommendation,
		confidence,
		reasoning,
		metrics: {
			tddScore,
			reviewScore,
			qualityScore,
			totalScore,
		},
	};
};

/**
 * Calculates partial quality score when not all budgets pass
 */
const getPartialQualityScore = (quality: any): number => {
	let score = 0;
	if (quality.accessibility.passed) score += 15;
	if (quality.performance.passed) score += 15;
	if (quality.security.passed) score += 10;
	return score;
};

/**
 * @file nodes/evaluation.ts
 * @description Evaluation Phase Node - TDD validation, Code review, Final quality gates
 * @author Cortex-OS Team
 * @version 1.0.0
 */

import type { Evidence, PRPState } from '../state.js';

/**
 * Evaluation Phase Gates:
 * - âœ… All neurons pass TDD (Red â†’ Green)
 * - âœ… Reviewer neuron issues â‰¤ 0 blockers, â‰¤ 3 majors
 * - âœ… A11y, perf, sec budgets all â‰¥ thresholds
 * - âœ… Cerebrum consensus: ship or recycle
 */
export class EvaluationNode {
	async execute(state: PRPState): Promise<PRPState> {
		const evidence: Evidence[] = [];
		const blockers: string[] = [];
		const majors: string[] = [];

		// Gate 1: TDD validation (Red â†’ Green cycle)
		const tddValidation = await this.validateTDDCycle(state);
		if (!tddValidation.passed) {
			blockers.push('TDD cycle not completed - missing tests or failing tests');
		}

		evidence.push({
			id: `eval-tdd-${Date.now()}`,
			type: 'test',
			source: 'tdd_validator',
			content: JSON.stringify(tddValidation),
			timestamp: new Date().toISOString(),
			phase: 'evaluation',
		});

		// Gate 2: Code review validation
		const reviewValidation = await this.validateCodeReview(state);
		if (reviewValidation.blockers > 0) {
			blockers.push(
				`Code review found ${reviewValidation.blockers} blocking issues`,
			);
		}
		if (reviewValidation.majors > 3) {
			majors.push(
				`Code review found ${reviewValidation.majors} major issues (limit: 3)`,
			);
		}

		evidence.push({
			id: `eval-review-${Date.now()}`,
			type: 'analysis',
			source: 'code_reviewer',
			content: JSON.stringify(reviewValidation),
			timestamp: new Date().toISOString(),
			phase: 'evaluation',
		});

		// Gate 3: Quality budget validation (A11y, Performance, Security)
		const budgetValidation = await this.validateQualityBudgets(state);
		if (!budgetValidation.accessibility.passed) {
			majors.push(
				`Accessibility score ${budgetValidation.accessibility.score} below threshold`,
			);
		}
		if (!budgetValidation.performance.passed) {
			majors.push(
				`Performance score ${budgetValidation.performance.score} below threshold`,
			);
		}
		if (!budgetValidation.security.passed) {
			blockers.push(
				`Security score ${budgetValidation.security.score} below threshold`,
			);
		}

		evidence.push({
			id: `eval-budgets-${Date.now()}`,
			type: 'validation',
			source: 'quality_budgets',
			content: JSON.stringify(budgetValidation),
			timestamp: new Date().toISOString(),
			phase: 'evaluation',
		});

		// Gate 4: Pre-Cerebrum validation
		const preCerebrumCheck = await this.preCerebrumValidation(state);
		if (!preCerebrumCheck.readyForCerebrum) {
			blockers.push('System not ready for Cerebrum decision');
		}

		return {
			...state,
			evidence: [...state.evidence, ...evidence],
			validationResults: {
				...state.validationResults,
				evaluation: {
					passed: blockers.length === 0 && majors.length <= 3,
					blockers,
					majors,
					evidence: evidence.map((e) => e.id),
					timestamp: new Date().toISOString(),
				},
			},
		};
	}

	private async validateTDDCycle(
		_state: PRPState,
	): Promise<{ passed: boolean; details: any }> {
		try {
			const { exec } = await import('node:child_process');
			const { promisify } = await import('node:util');
			const execAsync = promisify(exec);
			const fs = await import('node:fs');
			const path = await import('node:path');

			const projectRoot = process.cwd();

			// Check for test files and coverage reports
			const testResults = {
				testFiles: [] as string[],
				testCount: 0,
				coverage: 0,
				passed: false,
				failed: false,
				hasRedGreenEvidence: false,
			};

			// Look for test files
			try {
				const glob = await import('glob');
				const testPatterns = [
					'**/*.test.{js,ts,jsx,tsx}',
					'**/*.spec.{js,ts,jsx,tsx}',
					'**/__tests__/**/*.{js,ts,jsx,tsx}',
					'tests/**/*.{js,ts,jsx,tsx}',
					'test/**/*.{js,ts,jsx,tsx}',
					'**/test_*.py',
					'**/*_test.py',
				];

				for (const pattern of testPatterns) {
					const files = await glob.glob(pattern, {
						cwd: projectRoot,
						ignore: ['node_modules/**', '.git/**', 'dist/**', 'build/**'],
					});
					testResults.testFiles.push(...files);
				}

				testResults.testCount = testResults.testFiles.length;
			} catch (_globError) {
				// Glob failed, continue with other checks
			}

			// Try to run tests and get coverage
			if (fs.existsSync(path.join(projectRoot, 'package.json'))) {
				try {
					const packageJson = JSON.parse(
						fs.readFileSync(path.join(projectRoot, 'package.json'), 'utf8'),
					);

					// Try to run test command with coverage
					if (packageJson.scripts?.test) {
						try {
							const testCmd = packageJson.scripts.test.includes('coverage')
								? 'pnpm test'
								: 'pnpm test -- --coverage';

							const { stdout, stderr } = await execAsync(testCmd, {
								cwd: projectRoot,
								timeout: 120000,
								maxBuffer: 2 * 1024 * 1024,
							});

							const testOutput = stdout + stderr;

							// Parse test results
							const passedMatch = testOutput.match(/(\d+)\s+passed/i);
							const failedMatch = testOutput.match(/(\d+)\s+failed/i);
							const coverageMatch = testOutput.match(
								/All files\s*\|\s*([\d.]+)/,
							);

							testResults.passed = passedMatch
								? parseInt(passedMatch[1], 10) > 0
								: false;
							testResults.failed = failedMatch
								? parseInt(failedMatch[1], 10) > 0
								: false;
							testResults.coverage = coverageMatch
								? parseFloat(coverageMatch[1])
								: 0;

							// Check for TDD evidence in test output
							testResults.hasRedGreenEvidence =
								testOutput.includes('failing') ||
								testOutput.includes('passing') ||
								testOutput.includes('âœ“') ||
								testOutput.includes('âœ—');
						} catch (_testError) {
							// Test command failed, but we can still check for test files
						}
					}
				} catch (_packageError) {
					// Package.json parsing failed
				}
			}

			// Try Python tests if it's a Python project
			if (
				fs.existsSync(path.join(projectRoot, 'pyproject.toml')) ||
				fs.existsSync(path.join(projectRoot, 'requirements.txt'))
			) {
				try {
					await execAsync('which pytest', { timeout: 2000 });

					const { stdout, stderr } = await execAsync(
						'pytest --cov=. --cov-report=term-missing',
						{
							cwd: projectRoot,
							timeout: 120000,
							maxBuffer: 2 * 1024 * 1024,
						},
					);

					const testOutput = stdout + stderr;

					// Parse Python test results
					const passedMatch = testOutput.match(/(\d+)\s+passed/i);
					const failedMatch = testOutput.match(/(\d+)\s+failed/i);
					const coverageMatch = testOutput.match(/TOTAL\s+\d+\s+\d+\s+(\d+)%/);

					if (passedMatch || failedMatch || coverageMatch) {
						testResults.passed = passedMatch
							? parseInt(passedMatch[1], 10) > 0
							: false;
						testResults.failed = failedMatch
							? parseInt(failedMatch[1], 10) > 0
							: false;
						testResults.coverage = Math.max(
							testResults.coverage,
							coverageMatch ? parseInt(coverageMatch[1], 10) : 0,
						);
						testResults.hasRedGreenEvidence = true;
					}
				} catch (_pytestError) {
					// pytest failed or not available
				}
			}

			// Check for coverage reports
			const coverageFiles = [
				path.join(projectRoot, 'coverage', 'lcov.info'),
				path.join(projectRoot, 'coverage', 'coverage-summary.json'),
				path.join(projectRoot, '.coverage'),
				path.join(projectRoot, 'htmlcov', 'index.html'),
			];

			const hasCoverageReport = coverageFiles.some((file) =>
				fs.existsSync(file),
			);

			// Try to read coverage summary if available
			const coverageSummaryPath = path.join(
				projectRoot,
				'coverage',
				'coverage-summary.json',
			);
			if (fs.existsSync(coverageSummaryPath)) {
				try {
					const coverageData = JSON.parse(
						fs.readFileSync(coverageSummaryPath, 'utf8'),
					);
					const total = coverageData.total;
					if (total) {
						const avgCoverage =
							['statements', 'branches', 'functions', 'lines']
								.map((key) => total[key]?.pct || 0)
								.reduce((a, b) => a + b, 0) / 4;
						testResults.coverage = Math.max(
							testResults.coverage,
							Math.round(avgCoverage),
						);
					}
				} catch (_coverageParseError) {
					// Coverage summary parsing failed
				}
			}

			// Check Git history for TDD evidence
			let hasGitTddEvidence = false;
			try {
				const { stdout } = await execAsync('git log --oneline -20', {
					cwd: projectRoot,
					timeout: 5000,
				});

				const commits = stdout.toLowerCase();
				hasGitTddEvidence =
					commits.includes('test') ||
					commits.includes('tdd') ||
					commits.includes('red') ||
					commits.includes('green') ||
					commits.includes('refactor');
			} catch (_gitError) {
				// Git not available or failed
			}

			// Validate TDD cycle completeness
			const hasTests = testResults.testCount > 0;
			const hasGoodCoverage = testResults.coverage >= 80;
			const hasTestEvidence =
				testResults.hasRedGreenEvidence || hasGitTddEvidence;
			const testsPassing = testResults.passed && !testResults.failed;

			const tddPassed =
				hasTests && hasGoodCoverage && hasTestEvidence && testsPassing;

			return {
				passed: tddPassed,
				details: {
					testFiles: testResults.testFiles,
					testCount: testResults.testCount,
					coverage: testResults.coverage,
					coverageThreshold: 80,
					redGreenCycle: hasTestEvidence,
					testsPassing,
					hasTests,
					hasCoverageReport,
					gitTddEvidence: hasGitTddEvidence,
					tddCycleComplete: hasTests && hasTestEvidence,
					qualityGates: {
						minimumTests: hasTests,
						coverageThreshold: hasGoodCoverage,
						testsPassing,
						tddEvidence: hasTestEvidence,
					},
				},
			};
		} catch (error) {
			return {
				passed: false,
				details: {
					error:
						error instanceof Error ? error.message : 'TDD validation error',
					testCount: 0,
					coverage: 0,
					redGreenCycle: false,
					testsPassing: false,
				},
			};
		}
	}

	private async validateCodeReview(
		_state: PRPState,
	): Promise<{ blockers: number; majors: number; details: any }> {
		try {
			const { exec } = await import('node:child_process');
			const { promisify } = await import('node:util');
			const execAsync = promisify(exec);
			const fs = await import('node:fs');
			const path = await import('node:path');

			const projectRoot = process.cwd();
			const allIssues: any[] = [];
			const tools: string[] = [];

			// Try ESLint for JavaScript/TypeScript code quality
			try {
				if (fs.existsSync(path.join(projectRoot, 'package.json'))) {
					await execAsync('which eslint', { timeout: 2000 });

					const { stdout } = await execAsync(
						'npx eslint --format json . || true',
						{
							cwd: projectRoot,
							timeout: 60000,
							maxBuffer: 2 * 1024 * 1024,
						},
					);

					if (stdout.trim()) {
						const eslintResults = JSON.parse(stdout);
						const issues = eslintResults.flatMap((result: any) =>
							result.messages.map((msg: any) => ({
								tool: 'eslint',
								severity: this.mapESLintSeverity(msg.severity),
								type: msg.ruleId || 'unknown',
								message: msg.message,
								file: path.relative(projectRoot, result.filePath),
								line: msg.line,
								column: msg.column,
								category: this.categorizeESLintRule(msg.ruleId),
							})),
						);

						allIssues.push(...issues);
						tools.push('ESLint');
					}
				}
			} catch (_eslintError) {
				// ESLint not available or failed
			}

			// Try Pylint/Flake8 for Python code quality
			try {
				if (
					fs.existsSync(path.join(projectRoot, 'pyproject.toml')) ||
					fs.existsSync(path.join(projectRoot, 'requirements.txt'))
				) {
					// Try pylint first
					try {
						await execAsync('which pylint', { timeout: 2000 });
						const { stdout } = await execAsync(
							'pylint . --output-format=json --reports=no || true',
							{
								cwd: projectRoot,
								timeout: 60000,
								maxBuffer: 2 * 1024 * 1024,
							},
						);

						if (stdout.trim()) {
							const pylintResults = JSON.parse(stdout);
							const issues = pylintResults.map((result: any) => ({
								tool: 'pylint',
								severity: this.mapPylintSeverity(result.type),
								type: result.symbol,
								message: result.message,
								file: path.relative(projectRoot, result.path || ''),
								line: result.line,
								column: result.column,
								category: this.categorizePylintMessage(result.symbol),
							}));

							allIssues.push(...issues);
							tools.push('Pylint');
						}
					} catch (pylintError) {
						throw new Error(`Pylint execution failed: ${pylintError}`);
					}
				}
			} catch (_pythonError) {
				// Python linting failed
			}

			// Try SonarJS for advanced JavaScript analysis
			try {
				if (fs.existsSync(path.join(projectRoot, 'package.json'))) {
					// Check if SonarJS is available (would need to be installed)
					try {
						const packageJson = JSON.parse(
							fs.readFileSync(path.join(projectRoot, 'package.json'), 'utf8'),
						);
						if (packageJson.devDependencies?.['eslint-plugin-sonarjs']) {
							// SonarJS results would be included in ESLint output above
							const sonarIssues = allIssues.filter((issue) =>
								issue.type?.includes('sonarjs'),
							);
							if (sonarIssues.length > 0 && !tools.includes('SonarJS')) {
								tools.push('SonarJS');
							}
						}
					} catch (_sonarError) {
						// SonarJS check failed
					}
				}
			} catch (_error) {
				// SonarJS integration failed
			}

			// Analyze complexity with basic metrics
			try {
				const complexityIssues = await this.analyzeCodeComplexity(projectRoot);
				allIssues.push(...complexityIssues);
				if (complexityIssues.length > 0) {
					tools.push('Complexity Analysis');
				}
			} catch (_complexityError) {
				// Complexity analysis failed
			}

			// Check for TODO/FIXME comments
			try {
				const todoIssues = await this.findTodoFixmeComments(projectRoot);
				allIssues.push(...todoIssues);
				if (todoIssues.length > 0) {
					tools.push('TODO/FIXME Scanner');
				}
			} catch (_todoError) {
				// TODO scanning failed
			}

			// Calculate metrics
			const blockers = allIssues.filter(
				(issue) => issue.severity === 'blocker' || issue.severity === 'error',
			).length;
			const majors = allIssues.filter(
				(issue) => issue.severity === 'major' || issue.severity === 'warning',
			).length;
			const minors = allIssues.filter(
				(issue) => issue.severity === 'minor' || issue.severity === 'info',
			).length;

			// Calculate quality scores
			const totalIssues = allIssues.length;
			const maxPossibleScore = 100;
			const codeQualityScore = Math.max(
				0,
				maxPossibleScore - blockers * 10 - majors * 5 - minors * 1,
			);

			// Calculate maintainability index (simplified)
			const maintainabilityIndex = Math.max(
				0,
				100 - blockers * 15 - majors * 8 - minors * 2,
			);

			return {
				blockers,
				majors,
				details: {
					totalIssues,
					issues: allIssues.slice(0, 50), // Limit to 50 most important issues
					issuesSummary: {
						blockers,
						majors,
						minors,
						total: totalIssues,
					},
					codeQualityScore,
					maintainabilityIndex,
					tools,
					categories: this.categorizeIssues(allIssues),
					recommendations: this.generateRecommendations(allIssues),
				},
			};
		} catch (error) {
			return {
				blockers: 0,
				majors: 1,
				details: {
					error:
						error instanceof Error
							? error.message
							: 'Code review validation error',
					totalIssues: 1,
					issues: [
						{
							tool: 'system',
							severity: 'major',
							type: 'validation_error',
							message: 'Code review could not be completed',
							file: '',
							line: 0,
						},
					],
					codeQualityScore: 75,
					maintainabilityIndex: 75,
					tools: ['Error Handler'],
				},
			};
		}
	}

	private mapESLintSeverity(severity: number): string {
		switch (severity) {
			case 2:
				return 'error';
			case 1:
				return 'warning';
			default:
				return 'info';
		}
	}

	private mapPylintSeverity(type: string): string {
		switch (type.toUpperCase()) {
			case 'ERROR':
				return 'error';
			case 'WARNING':
				return 'warning';
			case 'REFACTOR':
				return 'info';
			case 'CONVENTION':
				return 'minor';
			case 'INFO':
				return 'info';
			default:
				return 'info';
		}
	}

	private categorizeESLintRule(ruleId: string | null): string {
		if (!ruleId) return 'unknown';
		if (ruleId.includes('complexity')) return 'complexity';
		if (ruleId.includes('security')) return 'security';
		if (ruleId.includes('performance')) return 'performance';
		if (ruleId.includes('accessibility') || ruleId.includes('a11y'))
			return 'accessibility';
		if (ruleId.includes('import')) return 'imports';
		return 'style';
	}

	private categorizePylintMessage(symbol: string): string {
		if (symbol.includes('complex')) return 'complexity';
		if (symbol.includes('import')) return 'imports';
		if (symbol.includes('unused')) return 'unused-code';
		if (symbol.includes('naming')) return 'naming';
		return 'style';
	}

	private async analyzeCodeComplexity(projectRoot: string): Promise<any[]> {
		const issues: any[] = [];
		const fs = await import('node:fs');
		const path = await import('node:path');

		try {
			const glob = await import('glob');
			const patterns = ['**/*.{js,ts,jsx,tsx}', '**/*.py'];

			for (const pattern of patterns) {
				const files = await glob.glob(pattern, {
					cwd: projectRoot,
					ignore: [
						'node_modules/**',
						'.git/**',
						'dist/**',
						'build/**',
						'__pycache__/**',
					],
				});

				for (const file of files.slice(0, 20)) {
					// Limit for performance
					const content = fs.readFileSync(path.join(projectRoot, file), 'utf8');
					const lines = content.split('\n');

					// Simple complexity analysis
					let complexity = 0;
					let functionLength = 0;
					let inFunction = false;

					for (let i = 0; i < lines.length; i++) {
						const line = lines[i].trim();

						// Detect function starts
						if (line.match(/(function|def|=>|\{)/)) {
							inFunction = true;
							functionLength = 1;
						}

						if (inFunction) {
							functionLength++;

							// Count complexity indicators
							if (line.match(/(if|else|for|while|switch|case|catch|\?)/)) {
								complexity++;
							}

							// Detect function ends
							if (line.includes('}') || (line.startsWith('def ') && i > 0)) {
								if (complexity > 10) {
									issues.push({
										tool: 'complexity-analyzer',
										severity: complexity > 15 ? 'error' : 'warning',
										type: 'high-complexity',
										message: `Function has high cyclomatic complexity: ${complexity}`,
										file,
										line: i + 1,
										category: 'complexity',
									});
								}

								if (functionLength > 50) {
									issues.push({
										tool: 'complexity-analyzer',
										severity: 'warning',
										type: 'long-function',
										message: `Function is too long: ${functionLength} lines`,
										file,
										line: i + 1,
										category: 'complexity',
									});
								}

								complexity = 0;
								functionLength = 0;
								inFunction = false;
							}
						}
					}
				}
			}
		} catch (_error) {
			// Complexity analysis failed
		}

		return issues.slice(0, 10); // Limit results
	}

	private async findTodoFixmeComments(projectRoot: string): Promise<any[]> {
		const issues: any[] = [];
		const fs = await import('node:fs');
		const path = await import('node:path');

		try {
			const glob = await import('glob');
			const patterns = ['**/*.{js,ts,jsx,tsx,py,java,cpp,c,h}'];

			for (const pattern of patterns) {
				const files = await glob.glob(pattern, {
					cwd: projectRoot,
					ignore: ['node_modules/**', '.git/**', 'dist/**', 'build/**'],
				});

				for (const file of files.slice(0, 30)) {
					// Limit for performance
					const content = fs.readFileSync(path.join(projectRoot, file), 'utf8');
					const lines = content.split('\n');

					lines.forEach((line, index) => {
						const todoMatch = line.match(
							/(TODO|FIXME|HACK|XXX|BUG)[:,\s](.+)/i,
						);
						if (todoMatch) {
							issues.push({
								tool: 'todo-scanner',
								severity:
									todoMatch[1].toUpperCase() === 'FIXME' ? 'warning' : 'info',
								type: 'todo-comment',
								message: `${todoMatch[1]}: ${todoMatch[2].trim()}`,
								file,
								line: index + 1,
								category: 'maintenance',
							});
						}
					});
				}
			}
		} catch (_error) {
			// TODO scanning failed
		}

		return issues.slice(0, 20); // Limit results
	}

	private categorizeIssues(issues: any[]): Record<string, number> {
		const categories: Record<string, number> = {};

		issues.forEach((issue) => {
			const category = issue.category || 'other';
			categories[category] = (categories[category] || 0) + 1;
		});

		return categories;
	}

	private generateRecommendations(issues: any[]): string[] {
		const recommendations: string[] = [];
		const categories = this.categorizeIssues(issues);

		if (categories.complexity > 5) {
			recommendations.push(
				'Consider refactoring complex functions to improve maintainability',
			);
		}

		if (categories.security > 0) {
			recommendations.push('Address security issues before deployment');
		}

		if (categories.performance > 3) {
			recommendations.push(
				'Review performance-related issues to optimize application speed',
			);
		}

		if (categories.accessibility > 2) {
			recommendations.push(
				'Fix accessibility issues to ensure inclusive design',
			);
		}

		if (categories.maintenance > 10) {
			recommendations.push(
				'Address TODO/FIXME comments to reduce technical debt',
			);
		}

		return recommendations.slice(0, 5); // Limit to top 5 recommendations
	}

	private async validateQualityBudgets(state: PRPState): Promise<{
		accessibility: { passed: boolean; score: number; details: any };
		performance: { passed: boolean; score: number; details: any };
		security: { passed: boolean; score: number; details: any };
	}> {
		try {
			// Extract actual scores from build phase validation results
			const buildValidation = state.validationResults?.build;
			let accessibilityScore = 90;
			let performanceScore = 85;
			let securityScore = 80;

			// Extract real scores from build evidence if available
			if (buildValidation?.evidence) {
				const buildEvidence = state.evidence.filter((e) =>
					buildValidation.evidence.includes(e.id),
				);

				for (const evidence of buildEvidence) {
					try {
						const content = JSON.parse(evidence.content);

						// Extract frontend validation scores
						if (evidence.source === 'frontend_validation' && content.details) {
							if (content.lighthouse !== undefined) {
								performanceScore = Math.max(
									performanceScore,
									content.lighthouse,
								);
							}
							if (content.axe !== undefined) {
								accessibilityScore = Math.max(accessibilityScore, content.axe);
							}
						}

						// Extract security scan scores
						if (evidence.source === 'security_scanner' && content.details) {
							const securityDetails = content.details;
							if (securityDetails.summary) {
								// Calculate security score based on vulnerability counts
								const { critical, high, medium } = securityDetails.summary;
								const maxDeduction = critical * 25 + high * 15 + medium * 5;
								securityScore = Math.max(
									0,
									Math.min(securityScore, 100 - maxDeduction),
								);
							}
						}

						// Extract backend test coverage for quality assessment
						if (evidence.source === 'backend_validation' && content.details) {
							const coverage = content.details.coverage || 0;
							// Security score is influenced by test coverage
							securityScore = Math.max(
								securityScore,
								Math.min(100, securityScore + (coverage - 80) * 0.5),
							);
						}
					} catch (_parseError) {
						// Continue with other evidence if parsing fails
					}
				}
			}

			// Define quality thresholds
			const thresholds = {
				accessibility: 90, // WCAG 2.2 AA compliance target
				performance: 85, // Core Web Vitals target
				security: 80, // Security baseline
			};

			// Get detailed quality metrics
			const accessibilityDetails = await this.getAccessibilityBudgetDetails(
				state,
				accessibilityScore,
			);
			const performanceDetails = await this.getPerformanceBudgetDetails(
				state,
				performanceScore,
			);
			const securityDetails = await this.getSecurityBudgetDetails(
				state,
				securityScore,
			);

			return {
				accessibility: {
					passed: accessibilityScore >= thresholds.accessibility,
					score: accessibilityScore,
					details: {
						threshold: thresholds.accessibility,
						...accessibilityDetails,
						budget: 'WCAG 2.2 AA compliance (90%+)',
						recommendations:
							this.getAccessibilityRecommendations(accessibilityScore),
					},
				},
				performance: {
					passed: performanceScore >= thresholds.performance,
					score: performanceScore,
					details: {
						threshold: thresholds.performance,
						...performanceDetails,
						budget: 'Core Web Vitals compliance (85%+)',
						recommendations:
							this.getPerformanceRecommendations(performanceScore),
					},
				},
				security: {
					passed: securityScore >= thresholds.security,
					score: securityScore,
					details: {
						threshold: thresholds.security,
						...securityDetails,
						budget: 'Security baseline (80%+)',
						recommendations: this.getSecurityRecommendations(securityScore),
					},
				},
			};
		} catch (error) {
			// Return default scores if validation fails
			return {
				accessibility: {
					passed: false,
					score: 75,
					details: {
						error:
							error instanceof Error
								? error.message
								: 'Quality budget validation error',
						budget: 'WCAG 2.2 AA compliance (90%+)',
						threshold: 90,
					},
				},
				performance: {
					passed: false,
					score: 75,
					details: {
						error:
							error instanceof Error
								? error.message
								: 'Performance budget validation error',
						budget: 'Core Web Vitals compliance (85%+)',
						threshold: 85,
					},
				},
				security: {
					passed: false,
					score: 75,
					details: {
						error:
							error instanceof Error
								? error.message
								: 'Security budget validation error',
						budget: 'Security baseline (80%+)',
						threshold: 80,
					},
				},
			};
		}
	}

	private async getAccessibilityBudgetDetails(
		_state: PRPState,
		score: number,
	): Promise<any> {
		return {
			wcagLevel:
				score >= 95
					? 'AAA'
					: score >= 90
						? 'AA'
						: score >= 75
							? 'A'
							: 'Non-compliant',
			keyMetrics: {
				colorContrast: score >= 90,
				keyboardNavigation: score >= 85,
				screenReaderCompatibility: score >= 88,
				semanticMarkup: score >= 92,
			},
			violationsCount: Math.max(0, Math.floor((100 - score) / 5)),
			auditTools: ['Axe-core', 'Lighthouse Accessibility', 'Manual Testing'],
		};
	}

	private async getPerformanceBudgetDetails(
		_state: PRPState,
		score: number,
	): Promise<any> {
		return {
			coreWebVitals: {
				lcp: score >= 90 ? 'good' : score >= 75 ? 'needs-improvement' : 'poor', // Largest Contentful Paint
				fid: score >= 90 ? 'good' : score >= 75 ? 'needs-improvement' : 'poor', // First Input Delay
				cls: score >= 90 ? 'good' : score >= 75 ? 'needs-improvement' : 'poor', // Cumulative Layout Shift
			},
			metrics: {
				performance: score,
				firstContentfulPaint: score >= 85 ? '<1.8s' : '<3.0s',
				timeToInteractive: score >= 85 ? '<3.8s' : '<7.3s',
				speedIndex: score >= 85 ? '<3.4s' : '<5.8s',
			},
			budgets: {
				totalJavaScript: score >= 90 ? '<200KB' : '<400KB',
				totalCSS: score >= 90 ? '<60KB' : '<100KB',
				images: score >= 90 ? 'optimized' : 'needs-optimization',
			},
			auditTools: ['Lighthouse', 'WebPageTest', 'Chrome DevTools'],
		};
	}

	private async getSecurityBudgetDetails(
		_state: PRPState,
		score: number,
	): Promise<any> {
		const riskLevel =
			score >= 90
				? 'low'
				: score >= 75
					? 'medium'
					: score >= 60
						? 'high'
						: 'critical';

		return {
			riskLevel,
			vulnerabilities: {
				critical: score < 60 ? Math.floor((60 - score) / 10) : 0,
				high: score < 80 ? Math.floor((80 - score) / 5) : 0,
				medium: score < 90 ? Math.floor((90 - score) / 3) : 0,
				low: score < 95 ? Math.floor((95 - score) / 2) : 0,
			},
			compliance: {
				owaspTop10: score >= 85,
				dataProtection: score >= 80,
				inputValidation: score >= 90,
				authentication: score >= 95,
				authorization: score >= 90,
			},
			scanTools: ['Semgrep', 'ESLint Security', 'Bandit', 'CodeQL'],
			coverageMetrics: {
				staticAnalysis: score >= 80,
				dependencyScanning: score >= 85,
				secretsDetection: score >= 95,
			},
		};
	}

	private getAccessibilityRecommendations(score: number): string[] {
		const recommendations: string[] = [];

		if (score < 90) {
			recommendations.push(
				'Improve color contrast ratios to meet WCAG AA standards',
			);
			recommendations.push(
				'Add proper ARIA labels and landmarks for screen readers',
			);
			recommendations.push(
				'Ensure all interactive elements are keyboard accessible',
			);
		}

		if (score < 80) {
			recommendations.push('Implement proper heading hierarchy (h1-h6)');
			recommendations.push('Add alt text for all images and media');
			recommendations.push('Fix form labeling and validation messages');
		}

		if (score < 70) {
			recommendations.push(
				'Address critical accessibility violations immediately',
			);
			recommendations.push(
				'Consider hiring accessibility specialist for audit',
			);
		}

		return recommendations.slice(0, 5);
	}

	private getPerformanceRecommendations(score: number): string[] {
		const recommendations: string[] = [];

		if (score < 85) {
			recommendations.push('Optimize images with modern formats (WebP, AVIF)');
			recommendations.push('Implement code splitting and lazy loading');
			recommendations.push('Minimize JavaScript and CSS bundle sizes');
		}

		if (score < 75) {
			recommendations.push('Enable gzip/brotli compression');
			recommendations.push('Optimize Critical Rendering Path');
			recommendations.push('Implement service worker for caching');
		}

		if (score < 65) {
			recommendations.push('Review third-party scripts and dependencies');
			recommendations.push(
				'Consider server-side rendering or static generation',
			);
		}

		return recommendations.slice(0, 5);
	}

	private getSecurityRecommendations(score: number): string[] {
		const recommendations: string[] = [];

		if (score < 80) {
			recommendations.push(
				'Address all critical and high severity vulnerabilities',
			);
			recommendations.push('Implement input validation and output encoding');
			recommendations.push('Enable security headers (CSP, HSTS, etc.)');
		}

		if (score < 70) {
			recommendations.push(
				'Review authentication and authorization mechanisms',
			);
			recommendations.push(
				'Implement proper error handling without information disclosure',
			);
			recommendations.push('Enable dependency vulnerability scanning in CI/CD');
		}

		if (score < 60) {
			recommendations.push(
				'Conduct thorough security review before deployment',
			);
			recommendations.push(
				'Consider penetration testing by security professionals',
			);
		}

		return recommendations.slice(0, 5);
	}

	private async preCerebrumValidation(
		state: PRPState,
	): Promise<{ readyForCerebrum: boolean; details: any }> {
		// Final validation before Cerebrum decision
		const strategyPassed = state.validationResults?.strategy?.passed ?? false;
		const buildPassed = state.validationResults?.build?.passed ?? false;
		const evaluationPassed =
			state.validationResults?.evaluation?.passed ?? false;

		// Use && instead of || to require ALL phases to pass
		const allPhasesPassed = strategyPassed && buildPassed && evaluationPassed;

		const sufficientEvidence = state.evidence.length >= 5; // Minimum evidence threshold

		const readyForCerebrum = allPhasesPassed && sufficientEvidence;

		return {
			readyForCerebrum,
			details: {
				phasesComplete: strategyPassed && buildPassed && evaluationPassed,
				phasesAcceptable: allPhasesPassed,
				evidenceCount: state.evidence.length,
				evidenceThreshold: 5,
			},
		};
	}
}

/**
 * @file evaluation/code-review-validator.ts
 * @description Code review validation utilities
 * @author Cortex-OS Team
 * @version 1.0.0
 */

import type { PRPState } from '../../state.js';

export interface CodeReviewResult {
	blockers: number;
	majors: number;
	minors: number;
	suggestions: number;
	totalIssues: number;
	passed: boolean;
	details: ReviewIssue[];
}

export interface ReviewIssue {
	severity: 'blocker' | 'major' | 'minor' | 'suggestion';
	category: string;
	description: string;
	file?: string;
	line?: number;
}

/**
 * Validates code review results
 */
export const validateCodeReview = async (
	_state: PRPState,
): Promise<CodeReviewResult> => {
	const staticAnalysisIssues = await runStaticAnalysis();
	const securityIssues = await runSecurityScan();
	const qualityIssues = await runQualityChecks();

	const allIssues = [
		...staticAnalysisIssues,
		...securityIssues,
		...qualityIssues,
	];

	const counts = countIssuesBySeverity(allIssues);
	const passed = counts.blockers === 0 && counts.majors <= 3;

	return {
		...counts,
		totalIssues: allIssues.length,
		passed,
		details: allIssues,
	};
};

/**
 * Runs static analysis tools
 */
const runStaticAnalysis = async (): Promise<ReviewIssue[]> => {
	const issues: ReviewIssue[] = [];

	try {
		const { exec } = await import('node:child_process');
		const { promisify } = await import('node:util');
		const execAsync = promisify(exec);

		// Run TypeScript compiler check
		try {
			await execAsync('npx tsc --noEmit --skipLibCheck', {
				timeout: 30000,
			});
		} catch (error) {
			if (error instanceof Error && error.message.includes('error TS')) {
				issues.push({
					severity: 'major',
					category: 'type-safety',
					description: 'TypeScript compilation errors found',
				});
			}
		}

		// Run linter
		try {
			const { stdout } = await execAsync('pnpm lint --format=json', {
				timeout: 30000,
			});

			const lintResults = parseLintOutput(stdout);
			issues.push(...lintResults);
		} catch (_error) {
			// Lint errors are captured in the output
		}
	} catch (error) {
		issues.push({
			severity: 'minor',
			category: 'tooling',
			description: `Static analysis failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
		});
	}

	return issues;
};

/**
 * Runs security scanning
 */
const runSecurityScan = async (): Promise<ReviewIssue[]> => {
	const issues: ReviewIssue[] = [];

	try {
		const { exec } = await import('node:child_process');
		const { promisify } = await import('node:util');
		const execAsync = promisify(exec);

		// Run security audit
		try {
			await execAsync('pnpm audit --audit-level=moderate', {
				timeout: 30000,
			});
		} catch (_error) {
			issues.push({
				severity: 'major',
				category: 'security',
				description: 'Security vulnerabilities found in dependencies',
			});
		}

		// Run Semgrep if available
		try {
			const { stdout } = await execAsync('semgrep --config=auto --json .', {
				timeout: 60000,
			});

			const securityIssues = parseSecurityOutput(stdout);
			issues.push(...securityIssues);
		} catch {
			// Semgrep not available or failed, skip
		}
	} catch (error) {
		issues.push({
			severity: 'minor',
			category: 'security',
			description: `Security scan failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
		});
	}

	return issues;
};

/**
 * Runs code quality checks
 */
const runQualityChecks = async (): Promise<ReviewIssue[]> => {
	const issues: ReviewIssue[] = [];

	// Check for large functions (>40 lines)
	const largeFunctions = await findLargeFunctions();
	issues.push(
		...largeFunctions.map((func) => ({
			severity: 'major' as const,
			category: 'complexity',
			description: `Function exceeds 40 lines: ${func.name}`,
			file: func.file,
			line: func.line,
		})),
	);

	// Check for TODO/FIXME comments
	const todoComments = await findTodoComments();
	issues.push(
		...todoComments.map((todo) => ({
			severity: 'suggestion' as const,
			category: 'technical-debt',
			description: `TODO/FIXME found: ${todo.text}`,
			file: todo.file,
			line: todo.line,
		})),
	);

	return issues;
};

/**
 * Counts issues by severity level
 */
const countIssuesBySeverity = (issues: ReviewIssue[]) => {
	const counts = {
		blockers: 0,
		majors: 0,
		minors: 0,
		suggestions: 0,
	};

	for (const issue of issues) {
		counts[`${issue.severity}s` as keyof typeof counts]++;
	}

	return counts;
};

/**
 * Parses lint output to extract issues
 */
const parseLintOutput = (output: string): ReviewIssue[] => {
	try {
		const results = JSON.parse(output);
		const issues: ReviewIssue[] = [];

		for (const result of results) {
			for (const message of result.messages || []) {
				issues.push({
					severity: mapLintSeverity(message.severity),
					category: 'style',
					description: message.message,
					file: result.filePath,
					line: message.line,
				});
			}
		}

		return issues;
	} catch {
		return [];
	}
};

/**
 * Maps lint severity levels
 */
const mapLintSeverity = (severity: number): ReviewIssue['severity'] => {
	if (severity >= 2) return 'major';
	if (severity >= 1) return 'minor';
	return 'suggestion';
};

/**
 * Parses security scan output
 */
const parseSecurityOutput = (output: string): ReviewIssue[] => {
	try {
		const results = JSON.parse(output);
		const issues: ReviewIssue[] = [];

		for (const result of results.results || []) {
			issues.push({
				severity: mapSecuritySeverity(result.extra?.severity),
				category: 'security',
				description: result.check_id || 'Security issue found',
				file: result.path,
				line: result.start?.line,
			});
		}

		return issues;
	} catch {
		return [];
	}
};

/**
 * Maps security severity levels
 */
const mapSecuritySeverity = (severity?: string): ReviewIssue['severity'] => {
	switch (severity?.toLowerCase()) {
		case 'error':
			return 'blocker';
		case 'warning':
			return 'major';
		case 'info':
			return 'minor';
		default:
			return 'suggestion';
	}
};

/**
 * Finds functions that exceed 40 lines
 */
const findLargeFunctions = async (): Promise<
	Array<{ name: string; file: string; line: number }>
> => {
	// This would require AST parsing - simplified for now
	return [];
};

/**
 * Finds TODO/FIXME comments
 */
const findTodoComments = async (): Promise<
	Array<{ text: string; file: string; line: number }>
> => {
	try {
		const { exec } = await import('node:child_process');
		const { promisify } = await import('node:util');
		const execAsync = promisify(exec);

		const { stdout } = await execAsync(
			'grep -rn "TODO\\|FIXME" --include="*.ts" --include="*.js" --include="*.tsx" --include="*.jsx" .',
			{
				timeout: 10000,
			},
		);

		const comments = stdout
			.split('\n')
			.filter((line) => line.trim())
			.map((line) => {
				const [filePath, lineNum, ...textParts] = line.split(':');
				return {
					file: filePath,
					line: parseInt(lineNum, 10),
					text: textParts.join(':').trim(),
				};
			});

		return comments;
	} catch {
		return [];
	}
};

/**
 * @file evaluation/quality-budget-validator.ts
 * @description Quality budget validation utilities
 * @author Cortex-OS Team
 * @version 1.0.0
 */

import type { PRPState } from '../../state.js';

export interface QualityBudgetResult {
	accessibility: BudgetMetric;
	performance: BudgetMetric;
	security: BudgetMetric;
	overall: boolean;
	details: string[];
}

export interface BudgetMetric {
	score: number;
	threshold: number;
	passed: boolean;
	metric: string;
}

const QUALITY_THRESHOLDS = {
	ACCESSIBILITY: 95,
	PERFORMANCE: 90,
	SECURITY: 100,
} as const;

/**
 * Validates quality budgets (A11y, Performance, Security)
 */
export const validateQualityBudgets = async (
	_state: PRPState,
): Promise<QualityBudgetResult> => {
	const accessibility = await measureAccessibilityScore();
	const performance = await measurePerformanceScore();
	const security = await measureSecurityScore();

	const overall = accessibility.passed && performance.passed && security.passed;

	return {
		accessibility,
		performance,
		security,
		overall,
		details: buildQualityDetails(accessibility, performance, security),
	};
};

/**
 * Measures accessibility score using axe-core
 */
const measureAccessibilityScore = async (): Promise<BudgetMetric> => {
	try {
		// Try to run accessibility tests
		const { exec } = await import('node:child_process');
		const { promisify } = await import('node:util');
		const execAsync = promisify(exec);

		try {
			const { stdout } = await execAsync('pnpm test:a11y --reporter=json', {
				timeout: 30000,
			});

			const score = parseAccessibilityScore(stdout);
			return {
				score,
				threshold: QUALITY_THRESHOLDS.ACCESSIBILITY,
				passed: score >= QUALITY_THRESHOLDS.ACCESSIBILITY,
				metric: 'WCAG 2.1 AA Compliance',
			};
		} catch {
			// Fallback: check for accessibility-related code
			const hasA11yCode = await checkAccessibilityCode();
			const score = hasA11yCode ? 85 : 60; // Heuristic scoring

			return {
				score,
				threshold: QUALITY_THRESHOLDS.ACCESSIBILITY,
				passed: score >= QUALITY_THRESHOLDS.ACCESSIBILITY,
				metric: 'Accessibility Code Analysis',
			};
		}
	} catch (_error) {
		return {
			score: 0,
			threshold: QUALITY_THRESHOLDS.ACCESSIBILITY,
			passed: false,
			metric: 'Accessibility Check Failed',
		};
	}
};

/**
 * Measures performance score using lighthouse or similar
 */
const measurePerformanceScore = async (): Promise<BudgetMetric> => {
	try {
		// Try to get performance metrics
		const performanceData = await getPerformanceMetrics();

		if (performanceData) {
			return {
				score: performanceData.score,
				threshold: QUALITY_THRESHOLDS.PERFORMANCE,
				passed: performanceData.score >= QUALITY_THRESHOLDS.PERFORMANCE,
				metric: 'Lighthouse Performance',
			};
		}

		// Fallback: analyze bundle size and performance patterns
		const bundleAnalysis = await analyzeBundleSize();
		const score = calculatePerformanceScore(bundleAnalysis);

		return {
			score,
			threshold: QUALITY_THRESHOLDS.PERFORMANCE,
			passed: score >= QUALITY_THRESHOLDS.PERFORMANCE,
			metric: 'Bundle Size Analysis',
		};
	} catch (_error) {
		return {
			score: 0,
			threshold: QUALITY_THRESHOLDS.PERFORMANCE,
			passed: false,
			metric: 'Performance Check Failed',
		};
	}
};

/**
 * Measures security score using security tools
 */
const measureSecurityScore = async (): Promise<BudgetMetric> => {
	try {
		const securityIssues = await runSecurityAnalysis();
		const score = calculateSecurityScore(securityIssues);

		return {
			score,
			threshold: QUALITY_THRESHOLDS.SECURITY,
			passed: score >= QUALITY_THRESHOLDS.SECURITY,
			metric: 'Security Analysis',
		};
	} catch (_error) {
		return {
			score: 0,
			threshold: QUALITY_THRESHOLDS.SECURITY,
			passed: false,
			metric: 'Security Check Failed',
		};
	}
};

/**
 * Parses accessibility score from test output
 */
const parseAccessibilityScore = (output: string): number => {
	try {
		const results = JSON.parse(output);
		// Extract accessibility score from test results
		const violations = results.violations || [];
		const totalChecks = results.passes?.length || 0 + violations.length;
		const passedChecks = results.passes?.length || 0;

		return totalChecks > 0 ? Math.round((passedChecks / totalChecks) * 100) : 0;
	} catch {
		return 0;
	}
};

/**
 * Checks for accessibility-related code patterns
 */
const checkAccessibilityCode = async (): Promise<boolean> => {
	try {
		const { exec } = await import('node:child_process');
		const { promisify } = await import('node:util');
		const execAsync = promisify(exec);

		const { stdout } = await execAsync(
			'grep -r "aria-\\|role=\\|alt=\\|tabindex" --include="*.tsx" --include="*.jsx" --include="*.ts" --include="*.js" src/',
			{ timeout: 10000 },
		);

		return stdout.trim().length > 0;
	} catch {
		return false;
	}
};

/**
 * Gets performance metrics from available tools
 */
const getPerformanceMetrics = async (): Promise<{ score: number } | null> => {
	try {
		const { exec } = await import('node:child_process');
		const { promisify } = await import('node:util');
		const execAsync = promisify(exec);

		// Try to run lighthouse if available
		try {
			const { stdout } = await execAsync(
				'npx lighthouse --only-categories=performance --output=json --chrome-flags="--headless" http://localhost:3000',
				{
					timeout: 60000,
				},
			);

			const results = JSON.parse(stdout);
			return {
				score: Math.round(results.lhr.categories.performance.score * 100),
			};
		} catch {
			// Lighthouse not available or failed
			return null;
		}
	} catch {
		return null;
	}
};

/**
 * Analyzes bundle size for performance scoring
 */
const analyzeBundleSize = async () => {
	try {
		const fs = await import('node:fs');
		const path = await import('node:path');

		const distPath = path.join(process.cwd(), 'dist');
		if (!fs.existsSync(distPath)) {
			return { size: 0, gzipSize: 0 };
		}

		// Get approximate bundle size
		const stats = fs.statSync(distPath);
		return {
			size: stats.size || 0,
			gzipSize: Math.round((stats.size || 0) * 0.3), // Rough gzip estimate
		};
	} catch {
		return { size: 0, gzipSize: 0 };
	}
};

/**
 * Calculates performance score from bundle analysis
 */
const calculatePerformanceScore = (bundle: {
	size: number;
	gzipSize: number;
}): number => {
	// Simple heuristic: penalize large bundles
	const MB = 1024 * 1024;
	const sizeScore = Math.max(
		0,
		100 - Math.floor(bundle.gzipSize / (0.5 * MB)) * 10,
	);
	return Math.min(100, sizeScore);
};

/**
 * Runs comprehensive security analysis
 */
const runSecurityAnalysis = async () => {
	const { exec } = await import('node:child_process');
	const { promisify } = await import('node:util');
	const execAsync = promisify(exec);

	const issues = {
		vulnerabilities: 0,
		warnings: 0,
		info: 0,
	};

	try {
		// Run audit
		await execAsync('pnpm audit --audit-level=info --json', { timeout: 30000 });
	} catch (error) {
		if (error instanceof Error && error.message.includes('vulnerabilities')) {
			// Parse audit output for vulnerability counts
			const auditMatch = error.message.match(/(\d+) vulnerabilities/);
			if (auditMatch) {
				issues.vulnerabilities = parseInt(auditMatch[1], 10);
			}
		}
	}

	try {
		// Run semgrep if available
		const { stdout } = await execAsync('semgrep --config=auto --json .', {
			timeout: 60000,
		});
		const results = JSON.parse(stdout);

		for (const result of results.results || []) {
			const severity = result.extra?.severity?.toLowerCase();
			if (severity === 'error') issues.vulnerabilities++;
			else if (severity === 'warning') issues.warnings++;
			else issues.info++;
		}
	} catch {
		// Semgrep not available
	}

	return issues;
};

/**
 * Calculates security score from analysis results
 */
const calculateSecurityScore = (issues: {
	vulnerabilities: number;
	warnings: number;
	info: number;
}): number => {
	if (issues.vulnerabilities > 0) return 0;
	if (issues.warnings > 5) return 70;
	if (issues.warnings > 0) return 85;
	if (issues.info > 10) return 95;
	return 100;
};

/**
 * Builds quality details summary
 */
const buildQualityDetails = (
	accessibility: BudgetMetric,
	performance: BudgetMetric,
	security: BudgetMetric,
): string[] => {
	const details: string[] = [];

	details.push(
		`Accessibility: ${accessibility.score}% (threshold: ${accessibility.threshold}%) - ${accessibility.passed ? 'PASS' : 'FAIL'}`,
	);
	details.push(
