/**
 * @file state.ts
 * @description Cortex Kernel State Management - Deterministic PRP State Schema
 * @author Cortex-OS Team
 * @version 1.0.0
 * @status TDD-DRIVEN
 */

import { randomUUID } from 'node:crypto';
import { z } from 'zod';

/**
 * Generate a deterministic hash from any data structure
 */
export const generateDeterministicHash = (data: any): string => {
	return Math.abs(
		JSON.stringify(data)
			.split('')
			.reduce((a, b) => ((a << 5) - a + b.charCodeAt(0)) | 0, 0),
	).toString();
};

/**
 * Evidence captured during PRP execution
 */
export const EvidenceSchema = z.object({
	id: z.string(),
	type: z.enum([
		'file',
		'command',
		'test',
		'analysis',
		'validation',
		'llm-generation',
	]),
	source: z.string(),
	content: z.string(),
	timestamp: z.string(),
	phase: z.enum(['strategy', 'build', 'evaluation']),
	metadata: z.record(z.any()).optional(),
});

/**
 * Validation gate results for each phase
 */
export const ValidationGateSchema = z.object({
	passed: z.boolean(),
	blockers: z.array(z.string()),
	majors: z.array(z.string()),
	evidence: z.array(z.string()), // Evidence IDs
	timestamp: z.string(),
});

/**
 * Cerebrum decision state
 */
export const CerebrumDecisionSchema = z.object({
	decision: z.enum(['promote', 'recycle', 'pending']),
	reasoning: z.string(),
	confidence: z.number().min(0).max(1),
	timestamp: z.string(),
});

/**
 * Core PRP State following the state machine diagram
 */
export const PRPStateSchema = z.object({
	// Core identifiers
	id: z.string(),
	runId: z.string(),

	// State machine phase
	phase: z.enum(['strategy', 'build', 'evaluation', 'completed', 'recycled']),

	// Input blueprint
	blueprint: z.object({
		title: z.string(),
		description: z.string(),
		requirements: z.array(z.string()),
		metadata: z.record(z.any()).optional(),
	}),

	// Execution outputs by neuron ID
	outputs: z.record(z.any()),

	// Validation results by phase
	validationResults: z.object({
		strategy: ValidationGateSchema.optional(),
		build: ValidationGateSchema.optional(),
		evaluation: ValidationGateSchema.optional(),
	}),

	// Evidence collection
	evidence: z.array(EvidenceSchema),

	// Cerebrum decision
	cerebrum: CerebrumDecisionSchema.optional(),

	// Execution metadata
	metadata: z.object({
		startTime: z.string(),
		endTime: z.string().optional(),
		currentNeuron: z.string().optional(),
		llmConfig: z
			.object({
				provider: z.enum(['mlx', 'ollama']).optional(),
				model: z.string().optional(),
			})
			.optional(),
		executionContext: z.record(z.any()).optional(),
		// Teaching layer extensions
		validationAdjustments: z.record(z.any()).optional(),
		gateModifications: z.record(z.any()).optional(),
		workflowAlterations: z.record(z.any()).optional(),
		// Error tracking
		error: z.string().optional(),
	}),
});

export type PRPState = z.infer<typeof PRPStateSchema>;
export type Evidence = z.infer<typeof EvidenceSchema>;
export type ValidationGate = z.infer<typeof ValidationGateSchema>;
export type CerebrumDecision = z.infer<typeof CerebrumDecisionSchema>;

/**
 * State transition validation
 */
export const validateStateTransition = (
	fromState: PRPState,
	toState: PRPState,
): boolean => {
	const fromPhase = fromState.phase;
	const toPhase = toState.phase;
	const validTransitions: Record<PRPState['phase'], PRPState['phase'][]> = {
		strategy: ['build', 'recycled'],
		build: ['evaluation', 'recycled'],
		evaluation: ['completed', 'recycled'],
		completed: [], // Terminal state
		recycled: ['strategy'], // Can restart
	};

	return validTransitions[fromPhase]?.includes(toPhase) ?? false;
};

/**
 * Create initial PRP state
 */
export const createInitialPRPState = (
	blueprint: PRPState['blueprint'],
	options: {
		id?: string;
		runId?: string;
		deterministic?: boolean;
		llmConfig?: {
			provider?: 'mlx' | 'ollama';
			model?: string;
		};
	} = {},
): PRPState => {
	const now = options.deterministic
		? '2025-01-01T00:00:00.000Z'
		: new Date().toISOString();

	const hash = options.deterministic
		? generateDeterministicHash(blueprint)
		: '';
	const id =
		options.id ??
		(options.deterministic ? `prp-${hash}` : `prp-${randomUUID()}`);

	const runId =
		options.runId ??
		(options.deterministic ? `run-${hash}` : `run-${randomUUID()}`);

	return {
		id,
		runId,
		phase: 'strategy',
		blueprint,
		outputs: {},
		validationResults: {},
		evidence: [],
		metadata: {
			startTime: now,
			llmConfig: options.llmConfig,
		},
	};
};

/**
 * @file teaching/behavior-extension.ts
 * @description Behavior Extension Modules - Adaptive system behavior based on examples
 * @author Cortex-OS Team
 * @version 1.0.0
 */

import type { PRPState } from '../state.js';
import type {
	ExampleCaptureSystem,
	TeachingPattern,
} from './example-capture.js';

/**
 * Behavior extension that can modify kernel behavior
 */
export interface BehaviorExtension {
	id: string;
	name: string;
	description: string;
	trigger: (state: PRPState) => boolean;
	modify: (
		state: PRPState,
		context: ExtensionContext,
	) => Promise<ExtensionResult>;
	confidence: number;
	basedOnPatterns: string[]; // Pattern IDs
}

/**
 * Context provided to behavior extensions
 */
export interface ExtensionContext {
	captureSystem: ExampleCaptureSystem;
	executionHistory: PRPState[];
	userFeedback?: {
		satisfaction: number; // 0-1
		suggestions: string[];
	};
}

/**
 * Result of behavior extension
 */
export interface ExtensionResult {
	modified: boolean;
	changes: {
		type: 'validation_adjustment' | 'gate_modification' | 'workflow_alteration';
		description: string;
		impact: 'low' | 'medium' | 'high';
		parameters: any;
	}[];
	reasoning: string;
	suggestedFeedback?: string;
}

/**
 * Behavior Extension Manager
 *
 * Manages adaptive behavior modifications based on captured examples
 * and learned patterns from user interactions.
 */
export class BehaviorExtensionManager {
	private extensions: Map<string, BehaviorExtension> = new Map();
	private captureSystem: ExampleCaptureSystem;
	private executionHistory: PRPState[] = [];

	constructor(captureSystem: ExampleCaptureSystem) {
		this.captureSystem = captureSystem;
		this.initializeDefaultExtensions();
	}

	/**
	 * Apply behavior extensions to current state
	 */
	async applyExtensions(
		state: PRPState,
		context: Partial<ExtensionContext> = {},
	): Promise<{
		modifiedState: PRPState;
		appliedExtensions: {
			extension: BehaviorExtension;
			result: ExtensionResult;
		}[];
	}> {
		const extensionContext: ExtensionContext = {
			captureSystem: this.captureSystem,
			executionHistory: this.executionHistory,
			...context,
		};

		let modifiedState = { ...state };
		const appliedExtensions: {
			extension: BehaviorExtension;
			result: ExtensionResult;
		}[] = [];

		// Apply extensions in order of confidence
		const sortedExtensions = Array.from(this.extensions.values())
			.filter((ext) => ext.trigger(state))
			.sort((a, b) => b.confidence - a.confidence);

		for (const extension of sortedExtensions) {
			try {
				const result = await extension.modify(modifiedState, extensionContext);

				if (result.modified) {
					// Apply modifications to state
					modifiedState = this.applyModifications(modifiedState, result);
					appliedExtensions.push({ extension, result });

					// Capture this extension application as an example
					this.captureExtensionApplication(
						extension,
						state,
						modifiedState,
						result,
					);
				}
			} catch (error) {
				console.error(`Extension ${extension.id} failed:`, error);
				// Continue with other extensions
			}
		}

		// Update execution history
		this.executionHistory.push(modifiedState);
		if (this.executionHistory.length > 100) {
			this.executionHistory = this.executionHistory.slice(-100); // Keep last 100
		}

		return { modifiedState, appliedExtensions };
	}

	/**
	 * Create behavior extension from teaching pattern
	 */
	createExtensionFromPattern(pattern: TeachingPattern): BehaviorExtension {
		return {
			id: `ext-${pattern.id}`,
			name: `Extension: ${pattern.name}`,
			description: `Auto-generated from pattern: ${pattern.description}`,
			trigger: (state: PRPState) => this.evaluatePatternTrigger(pattern, state),
			modify: async (state: PRPState, context: ExtensionContext) =>
				this.applyPatternModification(pattern, state, context),
			confidence: pattern.effectiveness,
			basedOnPatterns: [pattern.id],
		};
	}

	/**
	 * Register custom behavior extension
	 */
	registerExtension(extension: BehaviorExtension): void {
		this.extensions.set(extension.id, extension);
	}

	/**
	 * Update extension confidence based on outcomes
	 */
	updateExtensionEffectiveness(
		extensionId: string,
		outcome: { success: boolean; userSatisfaction?: number },
	): void {
		const extension = this.extensions.get(extensionId);
		if (!extension) return;

		// Simple confidence adjustment
		const adjustment = outcome.success ? 0.05 : -0.1;
		const userAdjustment = outcome.userSatisfaction
			? (outcome.userSatisfaction - 0.5) * 0.1
			: 0;

		extension.confidence = Math.max(
			0.1,
			Math.min(1.0, extension.confidence + adjustment + userAdjustment),
		);

		this.extensions.set(extensionId, extension);
	}

	/**
	 * Initialize default behavior extensions
	 */
	private initializeDefaultExtensions(): void {
		// Extension 1: Adaptive validation gates
		this.registerExtension({
			id: 'adaptive-validation',
			name: 'Adaptive Validation Gates',
			description: 'Adjusts validation thresholds based on project context',
			trigger: (state) => state.phase === 'strategy' || state.phase === 'build',
			modify: async (state, _context) => {
				const projectComplexity = this.assessProjectComplexity(state.blueprint);
				const historicalSuccess = this.getHistoricalSuccessRate(
					state.blueprint,
				);

				if (projectComplexity === 'simple' && historicalSuccess > 0.8) {
					return {
						modified: true,
						changes: [
							{
								type: 'validation_adjustment',
								description:
									'Relaxed validation for simple, successful project pattern',
								impact: 'low',
								parameters: {
									maxMajorsAllowed: 5, // Increased from 3
									skipMinorValidations: true,
								},
							},
						],
						reasoning: 'Project appears simple and follows successful patterns',
					};
				}

				return {
					modified: false,
					changes: [],
					reasoning: 'No adjustments needed',
				};
			},
			confidence: 0.7,
			basedOnPatterns: [],
		});

		// Extension 2: Smart gate skipping
		this.registerExtension({
			id: 'smart-gate-skip',
			name: 'Smart Gate Skipping',
			description: 'Skips redundant validation gates for certain project types',
			trigger: (state) => state.phase === 'build',
			modify: async (state, _context) => {
				const projectType = this.inferProjectType(state.blueprint);

				if (projectType === 'documentation-only') {
					return {
						modified: true,
						changes: [
							{
								type: 'gate_modification',
								description: 'Skip compilation gates for documentation project',
								impact: 'medium',
								parameters: {
									skipGates: ['backend-compilation', 'frontend-performance'],
									reason: 'Documentation project detected',
								},
							},
						],
						reasoning:
							'Documentation projects do not require compilation validation',
					};
				}

				return {
					modified: false,
					changes: [],
					reasoning: 'No gate skipping applicable',
				};
			},
			confidence: 0.8,
			basedOnPatterns: [],
		});

		// Extension 3: Context-aware evidence collection
		this.registerExtension({
			id: 'context-evidence',
			name: 'Context-Aware Evidence Collection',
			description: 'Adjusts evidence requirements based on project context',
			trigger: (state) => state.evidence.length < 3,
			modify: async (state, _context) => {
				const evidenceNeeds = this.assessEvidenceNeeds(state);

				if (evidenceNeeds.additional.length > 0) {
					return {
						modified: true,
						changes: [
							{
								type: 'workflow_alteration',
								description: 'Enhanced evidence collection for project type',
								impact: 'low',
								parameters: {
									additionalEvidence: evidenceNeeds.additional,
									priority: evidenceNeeds.priority,
								},
							},
						],
						reasoning: `Project requires additional evidence: ${evidenceNeeds.additional.join(', ')}`,
						suggestedFeedback:
							'System automatically enhanced evidence collection based on project analysis',
					};
				}

				return {
					modified: false,
					changes: [],
					reasoning: 'Evidence collection adequate',
				};
			},
			confidence: 0.6,
			basedOnPatterns: [],
		});
	}

	/**
	 * Apply pattern trigger evaluation
	 */
	private evaluatePatternTrigger(
		pattern: TeachingPattern,
		state: PRPState,
	): boolean {
		const conditions = pattern.trigger.conditions;

		// Simple condition matching - in real implementation would be more sophisticated
		if (conditions.phase && conditions.phase !== state.phase) {
			return false;
		}

		return pattern.trigger.confidence > 0.5;
	}

	/**
	 * Apply pattern-based modification
	 */
	private async applyPatternModification(
		pattern: TeachingPattern,
		_state: PRPState,
		_context: ExtensionContext,
	): Promise<ExtensionResult> {
		// Extract modification from pattern
		const modification = pattern.adaptation;

		return {
			modified: true,
			changes: [
				{
					type: modification.type as any,
					description: `Applied pattern: ${pattern.name}`,
					impact: 'medium',
					parameters: modification.parameters,
				},
			],
			reasoning: `Pattern-based modification: ${pattern.description}`,
		};
	}

	/**
	 * Apply modifications to state
	 */
	private applyModifications(
		state: PRPState,
		result: ExtensionResult,
	): PRPState {
		let modifiedState = { ...state };

		for (const change of result.changes) {
			switch (change.type) {
				case 'validation_adjustment':
					// Modify validation thresholds
					modifiedState = this.adjustValidation(
						modifiedState,
						change.parameters,
					);
					break;
				case 'gate_modification':
					// Modify gate behavior
					modifiedState = this.modifyGates(modifiedState, change.parameters);
					break;
				case 'workflow_alteration':
					// Alter workflow behavior
					modifiedState = this.alterWorkflow(modifiedState, change.parameters);
					break;
			}
		}

		return modifiedState;
	}

	/**
	 * Helper methods for state modification
	 */
	private adjustValidation(state: PRPState, parameters: any): PRPState {
		// Implementation would adjust validation thresholds
		return {
			...state,
			metadata: {
				...state.metadata,
				validationAdjustments: parameters,
			},
		};
	}

	private modifyGates(state: PRPState, parameters: any): PRPState {
		return {
			...state,
			metadata: {
				...state.metadata,
				gateModifications: parameters,
			},
		};
	}

	private alterWorkflow(state: PRPState, parameters: any): PRPState {
		return {
			...state,
			metadata: {
				...state.metadata,
				workflowAlterations: parameters,
			},
		};
	}

	/**
	 * Assessment helper methods
	 */
	private assessProjectComplexity(
		blueprint: PRPState['blueprint'],
	): 'simple' | 'medium' | 'complex' {
		const requirementCount = blueprint.requirements?.length || 0;
		const descriptionLength = blueprint.description.length;

		if (requirementCount <= 3 && descriptionLength < 200) return 'simple';
		if (requirementCount <= 8 && descriptionLength < 500) return 'medium';
		return 'complex';
	}

	private getHistoricalSuccessRate(_blueprint: PRPState['blueprint']): number {
		const examples = this.captureSystem.getExamples({ type: 'workflow' });
		if (examples.length === 0) {
			return 0;
		}
		const successes = examples.filter((ex) => ex.outcome.success).length;
		return successes / examples.length;
	}

	private inferProjectType(blueprint: PRPState['blueprint']): string {
		const title = blueprint.title.toLowerCase();
		const description = blueprint.description.toLowerCase();

		if (title.includes('doc') || description.includes('documentation')) {
			return 'documentation-only';
		}
		if (title.includes('api') || description.includes('backend')) {
			return 'backend-service';
		}
		if (title.includes('ui') || description.includes('frontend')) {
			return 'frontend-application';
		}

		return 'full-stack';
	}

	private assessEvidenceNeeds(state: PRPState): {
		additional: string[];
		priority: string;
	} {
		const needs: string[] = [];

		if (
			state.phase === 'strategy' &&
			!state.evidence.some((e) => e.type === 'analysis')
		) {
			needs.push('architecture-analysis');
		}
		if (
			state.phase === 'build' &&
			!state.evidence.some((e) => e.type === 'test')
		) {
			needs.push('test-execution');
		}

		return {
			additional: needs,
			priority: needs.length > 1 ? 'high' : 'medium',
		};
	}

	/**
	 * Capture extension application for learning
	 */
	private captureExtensionApplication(
		extension: BehaviorExtension,
		originalState: PRPState,
		modifiedState: PRPState,
		result: ExtensionResult,
	): void {
		this.captureSystem.captureExample(
			'workflow',
			{
				prpPhase: originalState.phase,
				blueprint: originalState.blueprint,
				inputState: originalState,
			},
			{
				type: 'workflow_modification',
				description: `Extension applied: ${extension.name}`,
				parameters: {
					extensionId: extension.id,
					modifications: result.changes,
				},
				timestamp: new Date().toISOString(),
			},
			{
				resultingState: modifiedState,
				success: true, // Will be updated based on actual outcome
				learningValue: extension.confidence,
			},
			{
				tags: ['extension', 'auto-adaptation', originalState.phase],
			},
			(originalState as any).metadata?.deterministic,
		);
	}

	/**
	 * Get active extensions
	 */
	getExtensions(): BehaviorExtension[] {
		return Array.from(this.extensions.values());
	}

	/**
	 * Get extension by ID
	 */
	getExtension(id: string): BehaviorExtension | undefined {
		return this.extensions.get(id);
	}
}

/**
 * @file teaching/example-capture.ts
 * @description Interactive Teaching Layer - Example Capture and Replay
 * @author Cortex-OS Team
 * @version 1.0.0
 */

import type { PRPState } from '../state.js';
import { generateId } from '../utils/id.js';

/**
 * Captured example for teaching and behavior extension
 */
export interface CapturedExample {
	id: string;
	type: 'workflow' | 'validation' | 'decision' | 'correction';
	context: {
		prpPhase: PRPState['phase'];
		blueprint: PRPState['blueprint'];
		inputState: Partial<PRPState>;
	};
	userAction: {
		type:
			| 'validation_override'
			| 'gate_adjustment'
			| 'neuron_guidance'
			| 'workflow_modification';
		description: string;
		parameters: any;
		timestamp: string;
	};
	outcome: {
		resultingState: Partial<PRPState>;
		success: boolean;
		learningValue: number; // 0-1 score of how valuable this example is
	};
	metadata: {
		capturedBy: string;
		environment: string;
		tags: string[];
	};
}

/**
 * Teaching pattern extracted from examples
 */
export interface TeachingPattern {
	id: string;
	name: string;
	description: string;
	trigger: {
		conditions: any; // Conditions that trigger this pattern
		confidence: number;
	};
	adaptation: {
		type:
			| 'gate_modification'
			| 'workflow_adjustment'
			| 'validation_enhancement';
		parameters: any;
	};
	examples: string[]; // CapturedExample IDs that support this pattern
	effectiveness: number; // Success rate of this pattern
}

/**
 * Example Capture and Replay System
 *
 * Captures user interactions and system decisions to build
 * adaptive behavior patterns for the Cortex Kernel.
 */
export class ExampleCaptureSystem {
	private examples: Map<string, CapturedExample> = new Map();
	private patterns: Map<string, TeachingPattern> = new Map();
	private activeCapture: boolean = true;

	/**
	 * Capture user interaction as learning example
	 */
	captureExample(
		type: CapturedExample['type'],
		context: CapturedExample['context'],
		userAction: CapturedExample['userAction'],
		outcome: CapturedExample['outcome'],
		metadata: Partial<CapturedExample['metadata']> = {},
		deterministic = false,
	): CapturedExample | null {
		if (!this.activeCapture) {
			return null;
		}
		const example: CapturedExample = {
			id: generateId('example', deterministic),
			type,
			context,
			userAction,
			outcome,
			metadata: {
				capturedBy: 'system',
				environment: 'development',
				tags: [],
				...metadata,
			},
		};

		this.examples.set(example.id, example);

		// Trigger pattern learning
		this.updatePatternsFromExample(example);

		return example;
	}

	/**
	 * Capture validation override example
	 */
	captureValidationOverride(
		prpState: PRPState,
		originalValidation: {
			passed: boolean;
			blockers: string[];
			majors: string[];
		},
		userOverride: { passed: boolean; reasoning: string; adjustments: any },
		finalOutcome: { success: boolean; feedback: string },
		deterministic = false,
	): CapturedExample | null {
		return this.captureExample(
			'validation',
			{
				prpPhase: prpState.phase,
				blueprint: prpState.blueprint,
				inputState: { validationResults: prpState.validationResults },
			},
			{
				type: 'validation_override',
				description: `User override: ${userOverride.reasoning}`,
				parameters: {
					originalValidation,
					override: userOverride,
				},
				timestamp: new Date().toISOString(),
			},
			{
				resultingState: { validationResults: prpState.validationResults },
				success: finalOutcome.success,
				learningValue: finalOutcome.success ? 0.8 : 0.3,
			},
			{
				tags: ['validation', 'override', prpState.phase],
			},
			deterministic,
		);
	}

	/**
	 * Capture workflow modification example
	 */
	captureWorkflowModification(
		prpState: PRPState,
		modification: {
			type: 'gate_adjustment' | 'neuron_reordering' | 'phase_skipping';
			description: string;
			changes: any;
		},
		outcome: { improved: boolean; metrics: any },
		deterministic = false,
	): CapturedExample | null {
		return this.captureExample(
			'workflow',
			{
				prpPhase: prpState.phase,
				blueprint: prpState.blueprint,
				inputState: { phase: prpState.phase, outputs: prpState.outputs },
			},
			{
				type: 'workflow_modification',
				description: modification.description,
				parameters: modification,
				timestamp: new Date().toISOString(),
			},
			{
				resultingState: prpState,
				success: outcome.improved,
				learningValue: outcome.improved ? 0.9 : 0.2,
			},
			{
				tags: ['workflow', modification.type, prpState.phase],
			},
			deterministic,
		);
	}

	/**
	 * Replay captured example to validate or extend behavior
	 */
	async replayExample(
		exampleId: string,
		currentState: PRPState,
	): Promise<{
		applicable: boolean;
		suggestedAction?: any;
		confidence: number;
	}> {
		const example = this.examples.get(exampleId);
		if (!example) {
			throw new Error(`Example not found: ${exampleId}`);
		}

		// Analyze context similarity
		const contextSimilarity = this.calculateContextSimilarity(example.context, {
			prpPhase: currentState.phase,
			blueprint: currentState.blueprint,
			inputState: currentState,
		});

		if (contextSimilarity < 0.6) {
			return { applicable: false, confidence: 0 };
		}

		// Suggest action based on example
		const suggestedAction = this.adaptExampleToCurrentContext(
			example,
			currentState,
		);

		return {
			applicable: true,
			suggestedAction,
			confidence: contextSimilarity * example.outcome.learningValue,
		};
	}

	/**
	 * Update learning patterns from new examples
	 */
	private updatePatternsFromExample(example: CapturedExample): void {
		// Simple pattern extraction - in real implementation would use ML
		const patternKey = `${example.type}-${example.context.prpPhase}-${example.userAction.type}`;

		let pattern = this.patterns.get(patternKey);
		if (pattern) {
			// Update existing pattern
			pattern.examples.push(example.id);
			const totalExamples = pattern.examples.length;
			const successfulExamples = pattern.examples
				.map((id) => this.examples.get(id))
				.filter((ex) => ex?.outcome.success).length;

			pattern.effectiveness = successfulExamples / totalExamples;
			pattern.trigger.confidence = Math.min(
				0.9,
				pattern.trigger.confidence + 0.1,
			);
		} else {
			pattern = {
				id: patternKey,
				name: `${example.type} pattern for ${example.context.prpPhase}`,
				description: `Learned pattern from ${example.userAction.type} actions`,
				trigger: {
					conditions: {
						phase: example.context.prpPhase,
						actionType: example.userAction.type,
					},
					confidence: 0.5,
				},
				adaptation: {
					type: 'gate_modification',
					parameters: example.userAction.parameters,
				},
				examples: [example.id],
				effectiveness: example.outcome.success ? 1.0 : 0.0,
			};
		}

		this.patterns.set(patternKey, pattern);
	}

	/**
	 * Calculate similarity between contexts
	 */
	private calculateContextSimilarity(
		context1: CapturedExample['context'],
		context2: CapturedExample['context'],
	): number {
		let similarity = 0;

		// Phase similarity
		if (context1.prpPhase === context2.prpPhase) {
			similarity += 0.3;
		}

		// Blueprint similarity (simple keyword matching)
		const keywords1 = this.extractKeywords(context1.blueprint);
		const keywords2 = this.extractKeywords(context2.blueprint);
		const keywordOverlap = this.calculateKeywordOverlap(keywords1, keywords2);
		similarity += keywordOverlap * 0.4;

		// State similarity (basic structure comparison)
		const statesSimilar = this.compareStates(
			context1.inputState,
			context2.inputState,
		);
		similarity += statesSimilar * 0.3;

		return Math.min(1.0, similarity);
	}

	/**
	 * Adapt example to current context
	 */
	private adaptExampleToCurrentContext(
		example: CapturedExample,
		currentState: PRPState,
	): any {
		// Simple adaptation - in real implementation would be more sophisticated
		return {
			type: example.userAction.type,
			description: `Adapted from example: ${example.userAction.description}`,
			parameters: {
				...example.userAction.parameters,
				adaptedFor: currentState.id,
				originalExample: example.id,
			},
			confidence: 0.7,
		};
	}

	/**
	 * Extract keywords from blueprint for similarity comparison
	 */
	private extractKeywords(blueprint: PRPState['blueprint']): string[] {
		const text = `${blueprint.title} ${blueprint.description} ${blueprint.requirements?.join(' ')}`;
		return text
			.toLowerCase()
			.split(/\s+/)
			.filter((word) => word.length > 3)
			.slice(0, 10); // Top 10 keywords
	}

	/**
	 * Calculate keyword overlap between two sets
	 */
	private calculateKeywordOverlap(
		keywords1: string[],
		keywords2: string[],
	): number {
		const set1 = new Set(keywords1);
		const set2 = new Set(keywords2);
		const intersection = new Set([...set1].filter((x) => set2.has(x)));
		const union = new Set([...set1, ...set2]);

		return union.size > 0 ? intersection.size / union.size : 0;
	}

	/**
	 * Compare states for similarity
	 */
	private compareStates(
		state1: Partial<PRPState>,
		state2: Partial<PRPState>,
	): number {
		// Simple structural comparison
		const keys1 = Object.keys(state1);
		const keys2 = Object.keys(state2);
		const commonKeys = keys1.filter((key) => keys2.includes(key));

		return commonKeys.length / Math.max(keys1.length, keys2.length, 1);
	}

	/**
	 * Get captured examples for analysis
	 */
	getExamples(filter?: {
		type?: CapturedExample['type'];
		phase?: PRPState['phase'];
		tags?: string[];
	}): CapturedExample[] {
		let examples = Array.from(this.examples.values());

		if (filter) {
			if (filter.type) {
				examples = examples.filter((ex) => ex.type === filter.type);
			}
			if (filter.phase) {
				examples = examples.filter(
					(ex) => ex.context.prpPhase === filter.phase,
				);
			}
			if (filter.tags) {
				examples = examples.filter((ex) =>
					filter.tags?.some((tag) => ex.metadata.tags.includes(tag)),
				);
			}
		}

		return examples;
	}

	/**
	 * Get learned patterns
	 */
	getPatterns(): TeachingPattern[] {
		return Array.from(this.patterns.values());
	}

	/**
	 * Enable or disable example capture
	 */
	setCapture(enabled: boolean): void {
		this.activeCapture = enabled;
	}
}

