import type { Agent, Executor } from '@cortex-os/agents';
import type { Step } from '../domain/types.js';

export type AgentBridge = {
	run: (step: Step, taskId: string, input: unknown) => Promise<any>;
};

export const createAgentBridge = (
	exec: Executor,
	getAgent: (id: string) => Agent | undefined,
): AgentBridge => ({
	run: async (step, taskId, input) => {
		const agent = getAgent(step.agentId!);
		if (!agent) throw new Error(`AGENT_NOT_FOUND:${step.agentId}`);
		return exec.run(agent, {
			id: taskId,
			kind: 'custom',
			input,
			budget: { wallClockMs: step.timeoutMs ?? 30_000, maxSteps: 16 },
		});
	},
});

import type { MemoryService } from '@cortex-os/memories';
import { uuid } from '@cortex-os/utils';

export type MemoriesBridge = {
	checkpoint: (runId: string, data: unknown) => Promise<any>;
};

export const createMemoriesBridge = (mem: MemoryService): MemoriesBridge => ({
	checkpoint: async (runId, data) => {
		const id = `wf:${runId}:${uuid()}`;
		return (mem as any).save({
			id,
			kind: 'artifact',
			text: JSON.stringify(data),
			tags: ['orchestrator', 'checkpoint'],
			createdAt: new Date().toISOString(),
			updatedAt: new Date().toISOString(),
			provenance: { source: 'system' },
		});
	},
});

