import path from 'node:path';
import { z } from 'zod';
export const Grant = z.object({
	tool: z.string(),
	actions: z.array(z.string()),
	args: z.record(z.any()).default({}),
	dataClass: z.enum(['public', 'internal', 'sensitive']).default('internal'),
	rate: z.object({ perMinute: z.number().int().min(1) }),
	fsScope: z.array(z.string()).default([]),
});
export type Grant = z.infer<typeof Grant>;

export async function loadGrant(id: string): Promise<Grant> {
	// read from .cortex/policy/tools/*.json, validate by schema
	const fs = await import('node:fs');
	const path = await import('node:path');
	const filePath = path.join(
		process.cwd(),
		'.cortex/policy/tools',
		`${id}.json`,
	);
	const content = fs.readFileSync(filePath, 'utf-8');
	return Grant.parse(JSON.parse(content));
}
// naive in-memory rate limiter per process
const rateMap = new Map<string, number[]>();

export function enforce(
	grant: Grant,
	action: string,
	args: Record<string, unknown>,
) {
	if (!grant.actions.includes(action)) throw new Error('action not allowed');

	// fsScope check for path args
	const p = (args?.path ?? (args as any)?.targetPath) as string | undefined;
	if (p && grant.fsScope.length > 0) {
		const rel = path.relative(process.cwd(), p);
		const allowed = grant.fsScope.some((scope) => {
			const norm = scope.endsWith('/') ? scope : `${scope}/`;
			return rel === scope || rel.startsWith(norm);
		});
		if (!allowed) throw new Error('path not within fsScope');
	}

	// simple rate limiting using sliding window of 60s
	const key = `${grant.tool}:${action}`;
	const now = Date.now();
	const windowMs = 60_000;
	const arr = (rateMap.get(key) ?? []).filter((t) => now - t < windowMs);
	if (arr.length >= grant.rate.perMinute)
		throw new Error('rate limit exceeded');
	arr.push(now);
	rateMap.set(key, arr);
	return true;
}

import { withSpan } from '../observability/otel';
import { auditEvent, record } from './audit';
import {
	type Checkpoint,
	loadLatestCheckpoint,
	saveCheckpoint,
} from './checkpoints';
import { requiresApproval, waitForApproval } from './hitl';
import { enforce, loadGrant } from './policy-engine';

export type Node =
	| 'plan'
	| 'gather'
	| 'critic'
	| 'synthesize'
	| 'verify'
	| 'done';

export interface RetryPolicy {
	maxRetries: number;
	backoffMs: number;
	jitter?: boolean;
}
export interface RuntimeLimits {
	deadlineMs?: number;
}
export interface RunContext {
	runId: string;
	threadId: string;
	signal?: AbortSignal;
}

export interface SupervisorOptions {
	retry?: Partial<Record<Node, RetryPolicy>>;
	limits?: Partial<Record<Node, RuntimeLimits>>;
	handlers?: Partial<Record<Node, NodeFn>>;
}

type NodeFn = (state: any, ctx: RunContext) => Promise<any>;

// Placeholder functions - to be implemented by integrators or replaced in DI
async function planFn(state: any, _ctx: RunContext) {
	return state;
}
async function gatherFn(state: any, _ctx: RunContext) {
	return state;
}
async function criticFn(state: any, _ctx: RunContext) {
	return state;
}
async function buildArtifactProposal(_state: any) {
	return { path: '/tmp/test', content: 'test' };
}
async function commitArtifact(_proposal: any) {
	return {};
}
async function verifyFn(state: any, _ctx: RunContext) {
	return state;
}

const nodeFns: Record<Node, NodeFn> = {
	plan: planFn,
	gather: gatherFn,
	critic: criticFn,
	synthesize: async (state, ctx) => {
		const proposal = await buildArtifactProposal(state);
		if (requiresApproval(proposal)) {
			const ok = await waitForApproval(ctx.runId, 'synthesize', proposal);
			if (!ok) throw new Error('Approval denied');
		}
		enforce(await loadGrant('fs.write'), 'write', {
			path: (proposal as any).path,
		});
		record(
			auditEvent(
				'fs',
				'write',
				{ runId: ctx.runId },
				{ path: (proposal as any).path },
			),
		);
		const next = await commitArtifact(proposal);
		return next;
	},
	verify: verifyFn,
	done: async (s) => s,
};

const edges: Record<Node, Node | null> = {
	plan: 'gather',
	gather: 'critic',
	critic: 'synthesize',
	synthesize: 'verify',
	verify: 'done',
	done: null,
};

function sleep(ms: number) {
	return new Promise((r) => setTimeout(r, ms));
}

// Maximum backoff time to prevent infinite delays (30 seconds)
const MAX_BACKOFF_MS = 30000;

async function withRetry(
	_node: Node,
	fn: () => Promise<any>,
	policy?: RetryPolicy,
) {
	const rp = policy ?? { maxRetries: 0, backoffMs: 0, jitter: true };
	let attempt = 0;
	// First attempt + retries
	// attempt 0: initial, then 1..maxRetries for retries
	for (;;) {
		try {
			return await fn();
		} catch (err) {
			if (attempt >= rp.maxRetries) throw err;
			attempt++;

			// Calculate backoff with jitter and cap
			const jitter = rp.jitter ? Math.floor(Math.random() * rp.backoffMs) : 0;
			const backoffTime = Math.min(rp.backoffMs + jitter, MAX_BACKOFF_MS);

			await sleep(backoffTime);
		}
	}
}

function withDeadline<T>(
	promise: Promise<T>,
	deadlineMs?: number,
	signal?: AbortSignal,
): Promise<T> {
	if (!deadlineMs && !signal) return promise;
	return new Promise<T>((resolve, reject) => {
		let to: NodeJS.Timeout | undefined;
		const onAbort = () => {
			clearTimeout(to as any);
			reject(new Error('Operation aborted'));
		};
		if (signal) {
			if (signal.aborted) return onAbort();
			signal.addEventListener('abort', onAbort, { once: true });
		}
		if (deadlineMs) {
			to = setTimeout(() => {
				if (signal) signal.removeEventListener('abort', onAbort);
				reject(new Error('Deadline exceeded'));
			}, deadlineMs);
		}
		promise.then(
			(v) => {
				if (to) clearTimeout(to);
				if (signal) signal.removeEventListener('abort', onAbort);
				resolve(v);
			},
			(e) => {
				if (to) clearTimeout(to);
				if (signal) signal.removeEventListener('abort', onAbort);
				reject(e);
			},
		);
	});
}

export interface RunOptions extends SupervisorOptions {
	startAt?: Node;
}

export async function runSupervisor(
	initialState: any,
	ctx: RunContext,
	opts: RunOptions = {},
) {
	// Idempotency: if a latest checkpoint exists at or after startAt, resume from it
	const latest = await loadLatestCheckpoint(ctx.runId);
	let node: Node = opts.startAt ?? 'plan';
	let state = initialState;
	if (latest) {
		// resume from the next node after the last completed checkpoint
		node = edges[latest.node] ?? 'done';
		state = latest.state;
	}

	while (node) {
		const fn = opts.handlers?.[node] ?? nodeFns[node];
		if (!fn) throw new Error(`No node function for ${node}`);

		const retry = opts.retry?.[node];
		const limits = opts.limits?.[node];

		state = await withSpan(
			`orchestration.${node}`,
			async () => {
				const exec = () => fn(state, ctx);
				const execWithRetry = () => withRetry(node, exec, retry);
				const result = await withDeadline(
					execWithRetry(),
					limits?.deadlineMs,
					ctx.signal,
				);
				// checkpoint after each successful node
				const cp: Checkpoint = {
					runId: ctx.runId,
					threadId: ctx.threadId,
					node,
					state: result,
					ts: new Date().toISOString(),
				};
				await saveCheckpoint(cp);
				return result;
			},
			{ node, runId: ctx.runId },
		);

		const next = edges[node];
		if (!next) break;
		node = next;
	}

	return state;
}

import { type Span, SpanStatusCode } from '@opentelemetry/api';

export interface EnhancedSpanContext {
	workflowId?: string;
	workflowName?: string;
	workflowVersion?: string;
	stepId?: string;
	stepKind?: string;
	agentId?: string;
	attempt?: number;
	resourceUsage?: {
		memoryBytes?: number;
		cpuUtilization?: number;
	};
	coordinationId?: string;
	phase?: string;
	retryPolicy?: {
		maxRetries: number;
		backoffMs: number;
	};
}

interface Histogram {
	record: (value: number, attributes: Record<string, string>) => void;
}

interface Counter {
	add: (value: number, attributes: Record<string, string>) => void;
}

export interface WorkflowMetrics {
	stepDuration: Histogram;
	coordinationDuration: Histogram;
	stepExecutions: Counter;
	coordinationFailures: Counter;
}

export function gatherSpanAttributes(
	context: EnhancedSpanContext,
): Record<string, any> {
	const attributes: Record<string, any> = {
		'orchestration.version': '1.0.0',
		'span.kind': 'internal',
	};

	if (context.workflowId) attributes['workflow.id'] = context.workflowId;
	if (context.workflowName) attributes['workflow.name'] = context.workflowName;
	if (context.workflowVersion)
		attributes['workflow.version'] = context.workflowVersion;
	if (context.stepId) attributes['workflow.step.id'] = context.stepId;
	if (context.stepKind) attributes['workflow.step.kind'] = context.stepKind;
	if (context.agentId) attributes['agent.id'] = context.agentId;
	if (context.attempt !== undefined)
		attributes['execution.attempt'] = context.attempt;
	if (context.coordinationId)
		attributes['coordination.id'] = context.coordinationId;
	if (context.phase) attributes['coordination.phase'] = context.phase;

	if (context.resourceUsage) {
		if (context.resourceUsage.memoryBytes !== undefined)
			attributes['resource.memory.bytes'] = context.resourceUsage.memoryBytes;
		if (context.resourceUsage.cpuUtilization !== undefined)
			attributes['resource.cpu.utilization'] =
				context.resourceUsage.cpuUtilization;
	}

	if (context.retryPolicy) {
		attributes['retry.max_attempts'] = context.retryPolicy.maxRetries;
		attributes['retry.backoff_ms'] = context.retryPolicy.backoffMs;
	}

	return attributes;
}

export function recordSuccessMetrics(
	name: string,
	duration: number,
	context: EnhancedSpanContext,
	metrics: WorkflowMetrics,
	span: Span,
): void {
	if (name.includes('step')) {
		metrics.stepDuration.record(duration, {
			step_kind: context.stepKind || 'unknown',
			success: 'true',
		});
		metrics.stepExecutions.add(1, {
			step_kind: context.stepKind || 'unknown',
			result: 'success',
		});
	}

	if (name.includes('coordination')) {
		metrics.coordinationDuration.record(duration, {
			phase: context.phase || 'unknown',
			success: 'true',
		});
	}

	span.addEvent(`${name}.completed`, {
		timestamp: Date.now(),
		duration_ms: duration,
		success: true,
	});

	span.setStatus({ code: SpanStatusCode.OK });
}

export function recordErrorMetrics(
	name: string,
	err: any,
	duration: number,
	context: EnhancedSpanContext,
	metrics: WorkflowMetrics,
	span: Span,
): void {
	const errorMessage = String(err?.message ?? err);

	if (name.includes('step')) {
		metrics.stepDuration.record(duration, {
			step_kind: context.stepKind || 'unknown',
			success: 'false',
		});
		metrics.stepExecutions.add(1, {
			step_kind: context.stepKind || 'unknown',
			result: 'failure',
		});
	}

	if (name.includes('coordination')) {
		metrics.coordinationDuration.record(duration, {
			phase: context.phase || 'unknown',
			success: 'false',
		});
		metrics.coordinationFailures.add(1, {
			phase: context.phase || 'unknown',
			error_type: err.code || 'unknown',
		});
	}

	span.addEvent(`${name}.failed`, {
		timestamp: Date.now(),
		duration_ms: duration,
		'error.type': err.constructor.name,
		'error.code': err.code,
		'error.message': errorMessage,
	});

	span.setStatus({ code: SpanStatusCode.ERROR, message: errorMessage });

	span.setAttributes({
		'error.type': err.constructor.name,
		'error.code': err.code || 'unknown',
		'error.message': errorMessage,
		'error.stack': err.stack,
	});
}

import { context, metrics, SpanStatusCode, trace } from '@opentelemetry/api';
import type { EnhancedSpanContext, WorkflowMetrics } from '../lib/telemetry.js';
import {
	gatherSpanAttributes,
	recordErrorMetrics,
	recordSuccessMetrics,
} from '../lib/telemetry.js';

export type { EnhancedSpanContext } from '../lib/telemetry.js';

export const tracer = trace.getTracer('@cortex-os/orchestration');
export const meter = metrics.getMeter('@cortex-os/orchestration');

// Create comprehensive metrics
export const workflowMetrics: WorkflowMetrics & Record<string, any> = {
	// Duration histograms
	stepDuration: meter.createHistogram('workflow_step_duration_ms', {
		description: 'Duration of workflow step execution in milliseconds',
	}),

	coordinationDuration: meter.createHistogram(
		'agent_coordination_duration_ms',
		{
			description: 'Duration of multi-agent coordination in milliseconds',
		},
	),

	// Counters
	retryAttempts: meter.createCounter('workflow_retry_attempts_total', {
		description: 'Total number of retry attempts',
	}),

	stepExecutions: meter.createCounter('workflow_step_executions_total', {
		description: 'Total number of workflow step executions',
	}),

	coordinationFailures: meter.createCounter('coordination_failures_total', {
		description: 'Total number of coordination failures',
	}),

	circuitBreakerTrips: meter.createCounter('circuit_breaker_trips_total', {
		description: 'Total number of circuit breaker trips',
	}),

	// Gauges (UpDown counters)
	activeWorkflows: meter.createUpDownCounter('active_workflows', {
		description: 'Number of currently active workflows',
	}),

	activeAgents: meter.createUpDownCounter('active_agents', {
		description: 'Number of currently active agents',
	}),

	resourceUtilization: meter.createGauge('resource_utilization_ratio', {
		description: 'Resource utilization ratio (0-1)',
	}),
};

export async function withSpan<T>(
	name: string,
	fn: () => Promise<T>,
	attrs?: Record<string, unknown>,
): Promise<T> {
	const span = tracer.startSpan(name, undefined, context.active());
	if (attrs) span.setAttributes(attrs as any);
	try {
		const res = await fn();
		span.setStatus({ code: SpanStatusCode.OK });
		return res;
	} catch (err: any) {
		span.setStatus({
			code: SpanStatusCode.ERROR,
			message: String(err?.message ?? err),
		});
		throw err;
	} finally {
		span.end();
	}
}

export async function withEnhancedSpan<T>(
	name: string,
	fn: () => Promise<T>,
	context: EnhancedSpanContext = {},
): Promise<T> {
	const span = tracer.startSpan(name);
	const startTime = Date.now();
	span.setAttributes(gatherSpanAttributes(context));

	// Add custom events for important milestones
	span.addEvent(`${name}.started`, {
		timestamp: startTime,
		'thread.id': process.pid,
	});

	try {
		const result = await fn();
		const duration = Date.now() - startTime;
		recordSuccessMetrics(name, duration, context, workflowMetrics, span);
		return result;
	} catch (err: any) {
		const duration = Date.now() - startTime;
		recordErrorMetrics(name, err, duration, context, workflowMetrics, span);
		throw err;
	} finally {
		span.end();
	}
}

/**
 * Record retry attempt metrics
 */
export function recordRetryAttempt(
	stepKind: string,
	attempt: number,
	errorType: string,
	delay: number,
): void {
	workflowMetrics.retryAttempts.add(1, {
		step_kind: stepKind,
		attempt_number: attempt.toString(),
		error_type: errorType,
		delay_ms: delay.toString(),
	});
}

/**
 * Record circuit breaker trip
 */
export function recordCircuitBreakerTrip(
	name: string,
	previousState: string,
	reason: string,
): void {
	workflowMetrics.circuitBreakerTrips.add(1, {
		circuit_breaker: name,
		previous_state: previousState,
		reason,
	});
}

/**
 * Update resource utilization metrics
 */
export function updateResourceUtilization(
	resourceType: string,
	utilization: number,
	agentId?: string,
): void {
	workflowMetrics.resourceUtilization.record(utilization, {
		resource_type: resourceType,
		agent_id: agentId || 'unknown',
	});
}

/**
 * Track workflow lifecycle
 */
export function recordWorkflowStart(
	_workflowId: string,
	workflowName: string,
): void {
	workflowMetrics.activeWorkflows.add(1, {
		workflow_name: workflowName,
	});
}

export function recordWorkflowEnd(
	_workflowId: string,
	workflowName: string,
	_success: boolean,
): void {
	workflowMetrics.activeWorkflows.add(-1, {
		workflow_name: workflowName,
	});
}

/**
 * Track agent lifecycle
 */
export function recordAgentActivation(
	agentId: string,
	capabilities: string[],
): void {
	workflowMetrics.activeAgents.add(1, {
		agent_id: agentId,
		capabilities: capabilities.join(','),
	});
}

export function recordAgentDeactivation(agentId: string): void {
	workflowMetrics.activeAgents.add(-1, {
		agent_id: agentId,
	});
}

/**
 * MLX-First Model Provider with Ollama Fallback
 * Handles automatic failover and performance optimization
 */

import { MODEL_STRATEGY } from '../lib/model-strategy.js';

export interface ModelRequest {
	task: string;
	prompt: string;
	context?: string;
	maxTokens?: number;
	temperature?: number;
	stream?: boolean;
}

export interface ModelResponse {
	content: string;
	model: string;
	provider: 'mlx' | 'ollama';
	latency: number;
	tokens?: number;
	cached?: boolean;
}

export interface EmbeddingRequest {
	texts: string[];
	model?: string;
}

export interface EmbeddingResponse {
	embeddings: number[][];
	model: string;
	provider: 'mlx' | 'ollama';
	dimensions: number;
}

export class MLXFirstModelProvider {
	private readonly mlxService: MLXService;
	private readonly ollamaService: OllamaService;
	private readonly healthChecks = new Map<string, boolean>();

	constructor() {
		this.mlxService = new MLXService();
		this.ollamaService = new OllamaService();
		this.startHealthChecking();
	}

	/**
	 * Generate text using MLX-first strategy
	 */
	async generate(task: string, request: ModelRequest): Promise<ModelResponse> {
		const config = MODEL_STRATEGY[task];
		if (!config) {
			throw new Error(`Unknown task: ${task}`);
		}

		const startTime = Date.now();

		// Try MLX first
		try {
			if (this.isHealthy('mlx', config.primary.model)) {
				const response = await this.mlxService.generate({
					model: config.primary.model,
					...request,
				});

				return {
					...response,
					provider: 'mlx',
					latency: Date.now() - startTime,
					model: config.primary.model,
				};
			}
		} catch (error) {
			console.warn(`MLX generation failed for ${task}:`, error);
			this.markUnhealthy('mlx', config.primary.model);
		}

		// Fallback to Ollama
		try {
			const response = await this.ollamaService.generate({
				model: config.fallback.model,
				...request,
			});

			return {
				...response,
				provider: 'ollama',
				latency: Date.now() - startTime,
				model: config.fallback.model,
			};
		} catch (error) {
			console.error(`Both MLX and Ollama failed for ${task}:`, error);
			throw new Error(`All providers failed for task: ${task}`);
		}
	}

	/**
	 * Generate embeddings using MLX-first strategy
	 */
	async embed(request: EmbeddingRequest): Promise<EmbeddingResponse> {
		const config = MODEL_STRATEGY.embeddings;

		// Try MLX first
		try {
			if (this.isHealthy('mlx', config.primary.model)) {
				const response = await this.mlxService.embed({
					model: config.primary.model,
					texts: request.texts,
				});

				return {
					...response,
					provider: 'mlx',
					model: config.primary.model,
				};
			}
		} catch (error) {
			console.warn(`MLX embedding failed:`, error);
			this.markUnhealthy('mlx', config.primary.model);
		}

		// Fallback to Frontier API using an Ollama model
		try {
			const embeddings = await this.ollamaService.embed({
				model: config.fallback.model,
				texts: request.texts,
			});

			return {
				embeddings,
				provider: 'ollama',
				model: config.fallback.model,
				dimensions: embeddings[0]?.length || 0,
			};
		} catch (error) {
			console.error(`Ollama fallback failed for embeddings:`, error);
			throw new Error(`All providers failed for embeddings`);
		}
	}

	/**
	 * Rerank documents using MLX-first strategy
	 */
	async rerank(
		query: string,
		documents: string[],
	): Promise<{ scores: number[]; provider: string }> {
		const config = MODEL_STRATEGY.reranking;

		// Try MLX reranker first
		try {
			if (this.isHealthy('mlx', config.primary.model)) {
				const scores = await this.mlxService.rerank({
					model: config.primary.model,
					query,
					documents,
				});

				return { scores, provider: 'mlx' };
			}
		} catch (error) {
			console.warn(`MLX reranking failed:`, error);
			this.markUnhealthy('mlx', config.primary.model);
		}

		// Fallback to Ollama with comparison prompts
		try {
			const scores = await this.ollamaService.compareRelevance(
				query,
				documents,
			);
			return { scores, provider: 'ollama' };
		} catch (error) {
			console.error(`Ollama reranking fallback failed:`, error);
			throw new Error(`All providers failed for reranking`);
		}
	}

	/**
	 * Health checking for automatic failover
	 */
	private startHealthChecking() {
		setInterval(async () => {
			await this.checkMLXHealth();
			await this.checkOllamaHealth();
		}, 30000); // Check every 30 seconds
	}

	private async checkMLXHealth() {
		try {
			const response = await this.mlxService.healthCheck();
			this.healthChecks.set('mlx', response.healthy);
		} catch {
			this.healthChecks.set('mlx', false);
		}
	}

	private async checkOllamaHealth() {
		try {
			const response = await this.ollamaService.healthCheck();
			this.healthChecks.set('ollama', response.healthy);
		} catch {
			this.healthChecks.set('ollama', false);
		}
	}

	private isHealthy(provider: string, model?: string): boolean {
		// If a model-specific health entry exists, use it; otherwise fall back to provider-level health.
		if (model) {
			const modelKey = `${provider}-${model}`;
			const modelHealth = this.healthChecks.get(modelKey);
			if (typeof modelHealth === 'boolean') return modelHealth;
		}
		return this.healthChecks.get(provider) ?? true; // default to true to attempt primary first
	}

	private markUnhealthy(provider: string, model?: string) {
		const key = model ? `${provider}-${model}` : provider;
		this.healthChecks.set(key, false);
	}

	/**
	 * Get optimal model for a specific task
	 */
	getOptimalModel(
		task: string,
		constraints?: { maxLatency?: number; maxMemory?: string },
	) {
		const config = MODEL_STRATEGY[task];
		if (!config) return null;

		// Consider constraints
		if (constraints?.maxLatency && constraints.maxLatency < 100) {
			// Force MLX for ultra-low latency
			return config.primary;
		}

		if (constraints?.maxMemory === 'light') {
			// Prefer lighter models
			return config.performance.memory === 'light'
				? config.primary
				: config.fallback;
		}

		return this.isHealthy('mlx', config.primary.model)
			? config.primary
			: config.fallback;
	}
}

/**
 * MLX Service Implementation
 */
class MLXService {
	private readonly baseUrl =
		process.env.MLX_SERVICE_URL || 'http://localhost:8765';

	async generate(request: any): Promise<any> {
		const response = await fetch(`${this.baseUrl}/generate`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(request),
		});

		if (!response.ok) {
			throw new Error(`MLX service error: ${response.statusText}`);
		}

		return response.json();
	}

	async embed(request: any): Promise<any> {
		const response = await fetch(`${this.baseUrl}/embed`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(request),
		});

		if (!response.ok) {
			throw new Error(`MLX embedding error: ${response.statusText}`);
		}

		return response.json();
	}

	async rerank(request: any): Promise<number[]> {
		const response = await fetch(`${this.baseUrl}/rerank`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(request),
		});

		if (!response.ok) {
			throw new Error(`MLX rerank error: ${response.statusText}`);
		}

		const result = await response.json();
		return result.scores;
	}

	async healthCheck(): Promise<{ healthy: boolean }> {
		try {
			// Note: global fetch doesn't support timeout option; use AbortController if needed.
			const response = await fetch(`${this.baseUrl}/health`, {
				method: 'GET',
			} as any);
			return { healthy: response.ok };
		} catch {
			return { healthy: false };
		}
	}
}

/**
 * Ollama Service Implementation
 */
class OllamaService {
	private readonly baseUrl = process.env.OLLAMA_URL || 'http://localhost:11434';

	async generate(request: any): Promise<any> {
		const response = await fetch(`${this.baseUrl}/api/generate`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({
				model: request.model,
				prompt: request.prompt,
				stream: false,
				options: {
					temperature: request.temperature || 0.7,
					num_predict: request.maxTokens || 1000,
				},
			}),
		});

		if (!response.ok) {
			throw new Error(`Ollama service error: ${response.statusText}`);
		}

		const result = await response.json();
		return { content: result.response };
	}

	async embed(request: {
		model: string;
		texts: string[];
	}): Promise<number[][]> {
		const response = await fetch(`${this.baseUrl}/api/embed`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({ model: request.model, input: request.texts }),
		});

		if (!response.ok) {
			throw new Error(`Ollama embedding error: ${response.statusText}`);
		}

		const result = await response.json();
		return result.embeddings;
	}

	async compareRelevance(
		query: string,
		documents: string[],
	): Promise<number[]> {
		const scores: number[] = [];

		for (const doc of documents) {
			const response = await this.generate({
				model: 'phi4-mini-reasoning:latest',
				prompt: `Rate the relevance of this document to the query on a scale of 0-1.
Query: "${query}"
Document: "${doc}"
Output only a decimal number between 0 and 1:`,
				maxTokens: 10,
			});

			const score = parseFloat(response.content.trim()) || 0;
			scores.push(Math.max(0, Math.min(1, score)));
		}

		return scores;
	}

	async healthCheck(): Promise<{ healthy: boolean }> {
		try {
			// Note: global fetch doesn't support timeout option; use AbortController if needed.
			const response = await fetch(`${this.baseUrl}/api/tags`, {
				method: 'GET',
			} as any);
			return { healthy: response.ok };
		} catch {
			return { healthy: false };
		}
	}
}

// Internal service classes intentionally not exported

import type { RunState, Step } from '../domain/types.js';

export type Middleware = (
	next: (rs: RunState, step: Step) => Promise<RunState>,
) => (rs: RunState, step: Step) => Promise<RunState>;

// Maximum backoff time to prevent infinite delays (30 seconds)
const MAX_BACKOFF_MS = 30000;

export const withRetry = (): Middleware => (next) => async (rs, step) => {
	const rp = step.retry ?? { maxRetries: 0, backoffMs: 0, jitter: true };
	let attempt = 0;

	for (;;) {
		try {
			return await next(rs, step);
		} catch (e) {
			if (attempt++ >= rp.maxRetries) throw e;

			// Calculate backoff with jitter and cap
			const jitter = rp.jitter ? Math.floor(Math.random() * rp.backoffMs) : 0;
			const backoffTime = Math.min(rp.backoffMs + jitter, MAX_BACKOFF_MS);

			await new Promise((r) => setTimeout(r, backoffTime));
		}
	}
};

/**
 * Core types and interfaces for the Orchestration Engine
 * Defines the fundamental data structures for task planning, execution, and coordination
 */

import { z } from 'zod';

// ================================
// Core Enums
// ================================

export enum TaskStatus {
	PENDING = 'pending',
	PLANNING = 'planning',
	EXECUTING = 'executing',
	COMPLETED = 'completed',
	FAILED = 'failed',
	CANCELLED = 'cancelled',
	PAUSED = 'paused',
}

export enum OrchestrationStrategy {
	SEQUENTIAL = 'sequential',
	PARALLEL = 'parallel',
	ADAPTIVE = 'adaptive',
	HIERARCHICAL = 'hierarchical',
	REACTIVE = 'reactive',
}

export enum AgentRole {
	PLANNER = 'planner',
	EXECUTOR = 'executor',
	COORDINATOR = 'coordinator',
	VALIDATOR = 'validator',
	MONITOR = 'monitor',
	SPECIALIST = 'specialist',
	WORKER = 'worker',
}

// ================================
// Zod Schemas for Runtime Validation
// ================================

export const TaskSchema = z.object({
	id: z.string().uuid(),
	title: z.string().min(1),
	description: z.string(),
	status: z.nativeEnum(TaskStatus),
	priority: z.number().min(1).max(10),
	dependencies: z.array(z.string().uuid()).default([]),
	requiredCapabilities: z.array(z.string()).default([]),
	context: z.record(z.unknown()).default({}),
	metadata: z.record(z.unknown()).default({}),
	createdAt: z.date(),
	updatedAt: z.date().optional(),
	completedAt: z.date().optional(),
	estimatedDuration: z.number().optional(),
	actualDuration: z.number().optional(),
});

export const ExecutionPlanSchema = z
	.object({
		id: z.string().uuid(),
		taskId: z.string().uuid(),
		strategy: z.nativeEnum(OrchestrationStrategy),
		phases: z.array(z.string()),
		dependencies: z.record(z.array(z.string())),
		estimatedDuration: z.number(),
		resourceRequirements: z.object({
			minAgents: z.number().min(1),
			maxAgents: z.number().min(1),
			requiredCapabilities: z.array(z.string()),
			memoryRequirement: z.number().optional(),
			computeRequirement: z.number().optional(),
		}),
		checkpoints: z.array(
			z.object({
				phase: z.string(),
				criteria: z.array(z.string()),
				validation: z.string(),
			}),
		),
		createdAt: z.date(),
		updatedAt: z.date().optional(),
	})
	.strict();

