import { createHash } from 'node:crypto';
import { workflowZ } from './schemas/workflow.zod.js';

// Validation cache to avoid re-validating identical workflows
const validationCache = new Map<
	string,
	{ valid: boolean; result?: any; error?: Error }
>();

// Maximum workflow depth to prevent stack overflow
const MAX_WORKFLOW_DEPTH = 1000;

// Cache cleanup interval (10 minutes)
const CACHE_CLEANUP_INTERVAL = 10 * 60 * 1000;
let cacheCleanupTimer: NodeJS.Timeout | null = null;

interface ValidationResult {
	workflow: any;
	stats: {
		totalSteps: number;
		unreachableSteps: string[];
		maxDepth: number;
		cycleDetected: boolean;
	};
	topologicalOrder: string[];
}

/**
 * Create a hash key for workflow caching
 */
function createWorkflowHash(workflow: any): string {
	// Create a hash based on the workflow structure for caching
	const structureData = JSON.stringify({
		entry: workflow.entry,
		steps: Object.keys(workflow.steps).sort(),
		connections: Object.fromEntries(
			Object.entries(workflow.steps).map(([id, step]: [string, any]) => [
				id,
				{
					next: step.next,
					branches: step.branches?.map((b: any) => b.to).sort(),
				},
			]),
		),
	});

	return createHash('md5').update(structureData, 'utf8').digest('hex');
}

/**
 * Initialize cache cleanup if not already started
 */
function initializeCacheCleanup(): void {
	if (cacheCleanupTimer) return;

	cacheCleanupTimer = setInterval(() => {
		// Clear cache periodically to prevent memory leaks
		validationCache.clear();
	}, CACHE_CLEANUP_INTERVAL);

	// Don't keep the process alive
	if (cacheCleanupTimer.unref) {
		cacheCleanupTimer.unref();
	}
}

function topologicalSort(wf: any, nodes: Set<string>): string[] {
	const inDegree = new Map<string, number>();
	const adj = new Map<string, string[]>();

	for (const id of nodes) {
		inDegree.set(id, 0);
		adj.set(id, []);
	}

	for (const id of nodes) {
		const step = wf.steps[id];

		if (step.next && nodes.has(step.next)) {
			adj.get(id)!.push(step.next);
			inDegree.set(step.next, (inDegree.get(step.next) ?? 0) + 1);
		}

		if (step.branches) {
			for (const branch of step.branches) {
				if (nodes.has(branch.to)) {
					adj.get(id)!.push(branch.to);
					inDegree.set(branch.to, (inDegree.get(branch.to) ?? 0) + 1);
				}
			}
		}
	}

	const queue = Array.from(inDegree.entries())
		.filter(([, deg]) => deg === 0)
		.map(([id]) => id)
		.sort();

	const order: string[] = [];

	while (queue.length > 0) {
		const id = queue.shift();
		if (id === undefined) {
			// Should not happen, but break defensively
			break;
		}
		order.push(id);

		const neighbors = adj.get(id) ?? [];
		for (const next of neighbors) {
			const nextInDegree = inDegree.get(next);
			if (nextInDegree === undefined) {
				// Defensive: skip if next is not in inDegree map
				continue;
			}
			inDegree.set(next, nextInDegree - 1);
			if (inDegree.get(next) === 0) {
				queue.push(next);
				queue.sort();
			}
		}
	}

	if (order.length !== nodes.size) {
		throw new Error('Cycle detected during topological sort');
	}

	return order;
}

/**
 * Validate a workflow definition and ensure it forms a DAG with performance optimizations.
 */
export function validateWorkflow(input: unknown): ValidationResult {
	// Parse and validate schema first
	const wf = workflowZ.parse(input);

	// Create cache key for performance optimization
	const cacheKey = createWorkflowHash(wf);

	// Check cache first
	if (validationCache.has(cacheKey)) {
		const cached = validationCache.get(cacheKey)!;
		if (cached.valid) {
			return cached.result;
		} else {
			throw cached.error;
		}
	}

	// Initialize cache cleanup on first use
	initializeCacheCleanup();

	try {
		const result = validateWorkflowStructure(wf);

		// Cache successful validation
		validationCache.set(cacheKey, { valid: true, result });

		return result;
	} catch (error) {
		// Cache validation error
		validationCache.set(cacheKey, { valid: false, error: error as Error });
		throw error;
	}
}

/**
 * Optimized workflow structure validation
 */
function validateWorkflowStructure(wf: any): ValidationResult {
	const visited = new Set<string>();
	const stack = new Set<string>();
	const unreachableSteps = new Set(Object.keys(wf.steps));
	let maxDepth = 0;
	let cycleDetected = false;

	// Pre-validate all step references
	const stepIds = new Set(Object.keys(wf.steps));

	// Validate entry point exists
	if (!stepIds.has(wf.entry)) {
		throw new Error(`Entry step '${wf.entry}' does not exist`);
	}

	// Pre-validate all next/branch references
	for (const [stepId, step] of Object.entries(wf.steps) as [string, any][]) {
		if (step.next && !stepIds.has(step.next)) {
			throw new Error(
				`Step '${stepId}' references non-existent next step: ${step.next}`,
			);
		}

		if (step.branches) {
			for (const branch of step.branches) {
				if (!stepIds.has(branch.to)) {
					throw new Error(
						`Step '${stepId}' references non-existent branch target: ${branch.to}`,
					);
				}
			}
		}
	}

	// Optimized DFS with path tracking and early termination
	const visit = (
		stepId: string,
		depth: number = 0,
		path: string[] = [],
	): void => {
		// Prevent infinite recursion
		if (depth > MAX_WORKFLOW_DEPTH) {
			throw new Error(
				`Workflow depth exceeds maximum (${MAX_WORKFLOW_DEPTH}). Possible infinite loop involving: ${path.slice(-5).join(' -> ')}`,
			);
		}

		// Track maximum depth
		maxDepth = Math.max(maxDepth, depth);

		// Cycle detection
		if (stack.has(stepId)) {
			cycleDetected = true;
			const cycleStart = path.indexOf(stepId);
			const cycle = path.slice(cycleStart).concat(stepId).join(' -> ');
			throw new Error(`Cycle detected: ${cycle}`);
		}

		// Skip if already processed
		if (visited.has(stepId)) {
			unreachableSteps.delete(stepId);
			return;
		}

		// Mark as reachable and being processed
		stack.add(stepId);
		visited.add(stepId);
		unreachableSteps.delete(stepId);

		const step = wf.steps[stepId];
		const currentPath = [...path, stepId];

		// Visit next step
		if (step.next) {
			visit(step.next, depth + 1, currentPath);
		}

		// Visit branch targets
		if (step.branches) {
			for (const branch of step.branches) {
				visit(branch.to, depth + 1, currentPath);
			}
		}

		// Remove from current processing stack
		stack.delete(stepId);
	};

	// Start validation from entry point
	visit(wf.entry);

	const order = topologicalSort(wf, visited);

	const stats = {
		totalSteps: Object.keys(wf.steps).length,
		unreachableSteps: Array.from(unreachableSteps),
		maxDepth,
		cycleDetected,
	};

	// Warn about unreachable steps (don't fail, just warn)
	if (stats.unreachableSteps.length > 0) {
		console.warn(
			`Workflow contains ${stats.unreachableSteps.length} unreachable steps: ${stats.unreachableSteps.join(', ')}`,
		);
	}

	return {
		workflow: wf,
		stats,
		topologicalOrder: order,
	};
}

/**
 * Validate workflow with detailed performance metrics
 */
export function validateWorkflowWithMetrics(input: unknown): {
	result: ValidationResult;
	metrics: {
		validationTimeMs: number;
		cacheHit: boolean;
		stepCount: number;
		complexity: 'low' | 'medium' | 'high';
	};
} {
	const startTime = performance.now();
	const wf = workflowZ.parse(input);
	const cacheKey = createWorkflowHash(wf);
	const cacheHit = validationCache.has(cacheKey);

	const result = validateWorkflow(input);
	const endTime = performance.now();

	const stepCount = result.stats.totalSteps;
	let complexity: 'low' | 'medium' | 'high';

	if (stepCount <= 10) {
		complexity = 'low';
	} else if (stepCount <= 50) {
		complexity = 'medium';
	} else {
		complexity = 'high';
	}

	return {
		result,
		metrics: {
			validationTimeMs: endTime - startTime,
			cacheHit,
			stepCount,
			complexity,
		},
	};
}

/**
 * Clear validation cache (useful for testing or memory management)
 */
export function clearValidationCache(): void {
	validationCache.clear();
}

/**
 * Get validation cache statistics
 */
export function getValidationCacheStats(): {
	size: number;
	hitRate: number;
	memoryUsage: number;
} {
	// This is a simplified approximation
	const memoryUsage = JSON.stringify(
		Array.from(validationCache.entries()),
	).length;

	return {
		size: validationCache.size,
		hitRate: 0, // Would need separate tracking for actual hit rate
		memoryUsage,
	};
}

/**
 * Optimized validation for batch processing
 */
export function validateWorkflows(inputs: unknown[]): Array<{
	index: number;
	success: boolean;
	result?: ValidationResult;
	error?: Error;
	fromCache: boolean;
}> {
	return inputs.map((input, index) => {
		try {
			const wf = workflowZ.parse(input);
			const cacheKey = createWorkflowHash(wf);
			const fromCache = validationCache.has(cacheKey);

			const result = validateWorkflow(input);

			return {
				index,
				success: true,
				result,
				fromCache,
			};
		} catch (error) {
			return {
				index,
				success: false,
				error: error as Error,
				fromCache: false,
			};
		}
	});
}

/**
 * Check if workflow is likely to be expensive to validate
 */
export function estimateValidationCost(input: unknown): {
	estimatedCost: 'low' | 'medium' | 'high';
	stepCount: number;
	branchingFactor: number;
	estimatedTimeMs: number;
} {
	try {
		const wf = workflowZ.parse(input);
		const stepCount = Object.keys(wf.steps).length;

		let totalBranches = 0;
		for (const step of Object.values(wf.steps) as any[]) {
			if (step.branches) {
				totalBranches += step.branches.length;
			}
			if (step.next) {
				totalBranches += 1;
			}
		}

		const branchingFactor = stepCount > 0 ? totalBranches / stepCount : 0;

		// Rough estimation based on step count and branching
		let estimatedTimeMs: number;
		let estimatedCost: 'low' | 'medium' | 'high';

		if (stepCount <= 10) {
			estimatedTimeMs = 1;
			estimatedCost = 'low';
		} else if (stepCount <= 50) {
			estimatedTimeMs = stepCount * 0.5;
			estimatedCost = 'medium';
		} else {
			estimatedTimeMs = stepCount * branchingFactor * 2;
			estimatedCost = 'high';
		}

		return {
			estimatedCost,
			stepCount,
			branchingFactor,
			estimatedTimeMs,
		};
	} catch {
		return {
			estimatedCost: 'high',
			stepCount: 0,
			branchingFactor: 0,
			estimatedTimeMs: 100,
		};
	}
}

/**
 * @file scripts/mcp-http-demo.ts
 * @description Starts the ASBR AI MCP HTTP server and prints ready-to-curl examples, including ai_search_knowledge.
 * Run with: pnpm nx run @cortex-os/prp-runner:demo:mcp -- --port 8081
 */

import chalk from 'chalk';
import { glob } from 'glob';
import { readFile } from 'node:fs/promises';
import path from 'node:path';
import { getDocsSemsearchConfig } from '../../../.cortex/library/mcp/semsearch';
import { ASBRAIMcpIntegration } from '../src/asbr-ai-mcp-integration.js';

type Args = { port: number };
function parse(argv: string[]): Args {
	let port = 8081;
	for (let i = 0; i < argv.length; i++) {
		const a = argv[i];
		if (a === '--port' || a === '-p') {
			const p = Number(argv[i + 1]);
			if (!Number.isNaN(p)) port = p;
		}
	}
	return { port };
}

async function main() {
	const { port } = parse(process.argv.slice(2));
	const integration = new ASBRAIMcpIntegration();
	await integration.startHTTPServer(port);

	const base = `http://127.0.0.1:${port}`;
	console.log(chalk.cyan('\nMCP HTTP demo running:'));
	console.log(`- ${chalk.green('Tools list')}: GET ${base}/mcp/tools/list`);
	console.log(`- ${chalk.green('Capabilities')}: GET ${base}/mcp/capabilities`);
	console.log(`- ${chalk.green('Health')}: GET ${base}/health`);
	console.log(`- ${chalk.green('Tool call')}: POST ${base}/mcp/tools/call`);

	// Ready-to-curl example for ai_search_knowledge
	const examplePayload = {
		method: 'tools/call',
		params: {
			name: 'ai_search_knowledge',
			arguments: { query: 'project overview', topK: 3, minSimilarity: 0.2 },
		},
	};
	console.log(chalk.magenta('\nCurl example (ai_search_knowledge):'));
	console.log(
		`curl -s ${base}/mcp/tools/call -H 'Content-Type: application/json' -d '${JSON.stringify(examplePayload)}' | jq .`,
	);

	// Optional docs ingestion if toggle is enabled
	const cfg = getDocsSemsearchConfig();
	if (cfg.enabled) {
		console.log(
			chalk.cyan('\nðŸ“– Docs semantic search is enabled. Ingesting docsâ€¦'),
		);
		const docsRoot = path.resolve(process.cwd(), cfg.dir);
		const files = await glob(cfg.glob, {
			cwd: docsRoot,
			absolute: true,
			ignore: ['**/node_modules/**', '**/.git/**', '**/dist/**'],
		});
		const uniques = Array.from(new Set(files));
		const contents: string[] = [];
		const metadata: Record<string, unknown>[] = [];
		for (const f of uniques) {
			try {
				const content = await readFile(f, 'utf8');
				if (!content.trim()) continue;
				contents.push(content);
				metadata.push({ path: f });
				// id derived from relative path; not required by tool call interface
			} catch {
				// Ignore unreadable files during best-effort ingestion
			}
		}
		if (contents.length > 0) {
			const server = integration.getMcpServer();
			await server.callTool({
				method: 'tools/call',
				params: {
					name: 'ai_add_knowledge',
					arguments: { documents: contents, metadata },
				},
			});
			console.log(
				chalk.green(`Ingested ${contents.length} docs for semantic search.`),
			);
		} else {
			console.log(chalk.yellow('No docs found to ingest.'));
		}
	} else {
		console.log(
			chalk.gray(
				'\nTip: export CORTEX_DOCS_SEMSEARCH=1 to enable docs ingestion before running this demo.',
			),
		);
	}
}

main().catch((e) => {
	console.error('mcp-http-demo error:', e);
	process.exit(1);
});

/**
 * @file scripts/semantic-search-demo.ts
 * @description Tiny CLI to ingest a directory of .md files and run a semantic query using the in-memory EmbeddingAdapter.
 * Runs with: pnpm nx run @cortex-os/prp-runner:demo:semsearch -- --dir ./docs --query "your question" --topK 5
 */

import { readFile } from 'node:fs/promises';
import path from 'node:path';
import chalk from 'chalk';
import { glob } from 'glob';
import { createEmbeddingAdapter } from '../src/embedding-adapter.js';

type CLIArgs = {
	dir: string;
	query: string;
	topK: number;
};

function parseArgs(argv: string[]): CLIArgs {
	const args: Record<string, string> = {};
	for (let i = 0; i < argv.length; i++) {
		const a = argv[i];
		if (a === '--dir' || a === '-d') {
			const next = argv[i + 1];
			if (next) {
				args.dir = next;
			}
		} else if (a === '--query' || a === '-q') {
			const next = argv[i + 1];
			if (next) {
				args.query = next;
			}
		} else if (a === '--topK' || a === '-k') {
			const next = argv[i + 1];
			if (next) {
				args.topK = next;
			}
		}
	}

	const dir = args.dir || './docs';
	const query = args.query || 'What is this project about?';
	const topK = Number(args.topK || 5);

	return { dir, query, topK } as CLIArgs;
}

type DocMeta = Record<string, unknown>;
async function loadMarkdownFiles(
	rootDir: string,
): Promise<{ id: string; text: string; metadata: DocMeta }[]> {
	const absoluteRoot = path.resolve(process.cwd(), rootDir);
	const patterns = ['**/*.md', '**/*.mdx', 'README.md'];
	const ignore = ['**/node_modules/**', '**/.git/**', '**/dist/**'];
	const files = (
		await Promise.all(
			patterns.map((p) =>
				glob(p, { cwd: absoluteRoot, absolute: true, ignore }),
			),
		)
	).flat();

	const uniques = Array.from(new Set(files));
	const docs: { id: string; text: string; metadata: DocMeta }[] = [];
	for (const f of uniques) {
		try {
			const content = await readFile(f, 'utf8');
			if (!content.trim()) continue;
			docs.push({
				id: path.relative(absoluteRoot, f) || path.basename(f),
				text: content,
				metadata: { path: f },
			});
		} catch {
			// best-effort; skip unreadable files
		}
	}
	return docs;
}

async function main() {
	const { dir, query, topK } = parseArgs(process.argv.slice(2));

	console.log(chalk.cyan('\nðŸ“š Semantic Search Demo'));
	console.log(`- Root: ${chalk.gray(process.cwd())}`);
	console.log(`- Directory: ${chalk.yellow(dir)}\n`);

	const docs = await loadMarkdownFiles(dir);
	if (docs.length === 0) {
		console.log(
			chalk.yellow(
				'No markdown files found. Try --dir ./docs or another path.',
			),
		);
		process.exit(0);
	}

	const adapter = createEmbeddingAdapter('local');
	await adapter.addDocuments(
		docs.map((d) => d.text),
		docs.map((d) => d.metadata),
		docs.map((d) => d.id),
	);

	const stats = adapter.getStats();
	console.log(
		chalk.green(
			`Indexed ${stats.totalDocuments} documents (dims=${stats.dimensions}).`,
		),
	);

	console.log(chalk.cyan(`\nðŸ”Ž Query:`), chalk.white(`"${query}"`));
	const results = await adapter.similaritySearch({ text: query, topK });

	if (results.length === 0) {
		console.log(chalk.yellow('No results.'));
	} else {
		console.log(chalk.magenta(`\nTop ${results.length} results:`));
		results.forEach((r, i) => {
			const preview = r.text.replace(/\s+/g, ' ').slice(0, 140);
			console.log(
				chalk.bold(`${i + 1}.`),
				chalk.blue(r.id),
				chalk.gray(`(sim=${r.similarity.toFixed(3)})`),
				'\n   ',
				preview,
				preview.length >= 140 ? 'â€¦' : '',
				'\n',
			);
		});
	}

	console.log(chalk.gray('Done.'));
}

main().catch((err) => {
	console.error(chalk.red('Error in semantic-search-demo:'), err);
	process.exit(1);
});

