import { beforeEach, describe, expect, it, vi } from 'vitest';

// Mock memory service types and interfaces
interface MockTenantCtx {
	tenantId: string;
	agentId?: string;
	userId?: string;
}

interface MockMemoryRecord {
	id: string;
	tenantId: string;
	kind: 'doc' | 'chunk' | 'event' | 'decision';
	text: string;
	metadata: Record<string, unknown>;
	embedding: number[];
	createdAt: string;
	ttlDays?: number;
	expireAt?: string;
	policy?: {
		canRead: string[];
		canWrite: string[];
	};
	sourceURI?: string;
}

interface MockVectorQuery {
	tenantId: string;
	queryEmbedding: number[];
	topK: number;
	filter?: Record<string, unknown>;
}

interface MockVectorHit {
	id: string;
	text: string;
	metadata: Record<string, unknown>;
	score: number;
	sourceURI?: string;
}

// Mock Memory Service for testing integration
class MockMemoryService {
	private records: MockMemoryRecord[] = [];
	private embedder: { embed: (texts: string[]) => Promise<number[][]> };
	private vectorSize: number;

	constructor(embedder: any, vectorSize: number = 1024) {
		this.embedder = embedder;
		this.vectorSize = vectorSize;
	}

	async embedOne(text: string): Promise<number[]> {
		const [vec] = await this.embedder.embed([text]);
		if (vec.length !== this.vectorSize) {
			throw new Error(
				`Vector size mismatch: expected ${this.vectorSize}, got ${vec.length}`,
			);
		}
		return vec;
	}

	async putText(
		ctx: MockTenantCtx,
		kind: MockMemoryRecord['kind'],
		text: string,
		metadata: Record<string, unknown> = {},
		ttlDays?: number,
		policy?: MockMemoryRecord['policy'],
		sourceURI?: string,
	): Promise<string> {
		const embedding = await this.embedOne(text);
		const record: MockMemoryRecord = {
			id: `record-${Date.now()}-${Math.random()}`,
			tenantId: ctx.tenantId,
			kind,
			text,
			metadata,
			embedding,
			createdAt: new Date().toISOString(),
			ttlDays,
			expireAt: ttlDays
				? new Date(Date.now() + ttlDays * 24 * 60 * 60 * 1000).toISOString()
				: undefined,
			policy,
			sourceURI,
		};

		this.records.push(record);
		return record.id;
	}

	async search(
		ctx: MockTenantCtx,
		query: Omit<MockVectorQuery, 'tenantId'>,
	): Promise<MockVectorHit[]> {
		return this.records
			.filter((record) => record.tenantId === ctx.tenantId)
			.map((record) => {
				// Calculate cosine similarity
				const similarity = this.cosineSimilarity(
					query.queryEmbedding,
					record.embedding,
				);
				return {
					id: record.id,
					text: record.text,
					metadata: record.metadata,
					score: similarity,
					sourceURI: record.sourceURI,
				};
			})
			.sort((a, b) => b.score - a.score)
			.slice(0, query.topK);
	}

	private cosineSimilarity(a: number[], b: number[]): number {
		const dotProduct = a.reduce((sum, val, i) => sum + val * b[i], 0);
		const magnitudeA = Math.sqrt(a.reduce((sum, val) => sum + val * val, 0));
		const magnitudeB = Math.sqrt(b.reduce((sum, val) => sum + val * val, 0));
		return dotProduct / (magnitudeA * magnitudeB);
	}
}

