/**
 * @file packages/prp-runner/src/enforcement/initial-processor.ts
 * @description Process initial.md files into enforcement profiles for PRP validation
 * @maintainer @jamiescottcraik
 * @version 1.0.0
 * @status IMPLEMENTATION_READY
 */

import { promises as fs } from 'node:fs';
import { join, resolve } from 'node:path';
import type { EnforcementProfile } from '../gates/base.js';

export interface InitialMdContent {
	title: string;
	context: string;
	requirements: string[];
	constraints?: string[];
	references?: string[];
	tests: string[];
	acceptance_criteria: string[];
	// PRP-specific sections
	budgets?: {
		coverage?: { lines?: number; branches?: number };
		performance?: { lcp?: number; tbt?: number };
		accessibility?: { score?: number };
	};
	architecture?: {
		boundaries?: string[];
		naming?: Record<string, string>;
		layout?: string[];
		exceptions?: string[];
	};
	governance?: {
		license?: string;
		owners?: Record<string, string[]>;
		checks?: string[];
	};
}

/**
 * Parse initial.md markdown content into structured data
 */
export function parseInitialMd(content: string): InitialMdContent {
	const lines = content.split('\n');
	const result: InitialMdContent = {
		title: '',
		context: '',
		requirements: [],
		tests: [],
		acceptance_criteria: [],
	};

	let currentSection = '';
	let currentList: string[] = [];
	let inCodeBlock = false;
	let codeBlockLang = '';

	for (let i = 0; i < lines.length; i++) {
		const line = lines[i].trim();

		// Handle code blocks
		if (line.startsWith('```')) {
			if (!inCodeBlock) {
				inCodeBlock = true;
				codeBlockLang = line.slice(3);
			} else {
				inCodeBlock = false;
				// Process collected code block
				if (codeBlockLang === 'yaml' || codeBlockLang === 'json') {
					try {
						const codeContent = currentList.join('\n');
						const parsed =
							codeBlockLang === 'yaml'
								? parseYaml(codeContent)
								: JSON.parse(codeContent);

						// Merge parsed configuration
						if (parsed.budgets)
							result.budgets = { ...result.budgets, ...parsed.budgets };
						if (parsed.architecture)
							result.architecture = {
								...result.architecture,
								...parsed.architecture,
							};
						if (parsed.governance)
							result.governance = {
								...result.governance,
								...parsed.governance,
							};
					} catch (error) {
						console.warn(`Failed to parse ${codeBlockLang} block:`, error);
					}
				}
				currentList = [];
				codeBlockLang = '';
			}
			continue;
		}

		if (inCodeBlock) {
			currentList.push(lines[i]); // Preserve original line with indentation
			continue;
		}

		// Handle headers
		if (line.startsWith('#')) {
			// Finish previous section
			if (currentSection && currentList.length > 0) {
				finishSection(result, currentSection, currentList);
				currentList = [];
			}

			const headerLevel = line.match(/^#+/)?.[0].length || 1;
			const headerText = line.replace(/^#+\s*/, '').toLowerCase();

			if (headerLevel === 1 && !result.title) {
				result.title = line.replace(/^#+\s*/, '');
			}

			currentSection = headerText;
			continue;
		}

		// Handle list items
		if (line.startsWith('-') || line.startsWith('*') || line.match(/^\d+\./)) {
			const listItem = line.replace(/^[-*]\s*/, '').replace(/^\d+\.\s*/, '');
			currentList.push(listItem);
			continue;
		}

		// Handle regular text
		if (line.length > 0) {
			if (
				currentSection === '' ||
				currentSection === 'context' ||
				currentSection === 'description'
			) {
				result.context += (result.context ? '\n' : '') + line;
			} else {
				currentList.push(line);
			}
		}
	}

	// Finish last section
	if (currentSection && currentList.length > 0) {
		finishSection(result, currentSection, currentList);
	}

	return result;
}

/**
 * Simple YAML parser for basic key-value structures
 */
function parseYaml(content: string): Record<string, unknown> {
	const result: Record<string, unknown> = {};
	const lines = content.split('\n');
	let currentObj = result;
	const stack: Record<string, unknown>[] = [result];

	for (const line of lines) {
		if (!line.trim() || line.trim().startsWith('#')) continue;

		const indent = line.length - line.trimStart().length;
		const trimmed = line.trim();

		if (trimmed.includes(':')) {
			const [key, ...valueParts] = trimmed.split(':');
			const value = valueParts.join(':').trim();

			// Handle nested objects
			while (stack.length > Math.floor(indent / 2) + 1) {
				stack.pop();
			}
			currentObj = stack[stack.length - 1];

			if (value === '' || value === '{}' || value === '[]') {
				// Empty object or array
				currentObj[key.trim()] = value === '[]' ? [] : {};
				stack.push(currentObj[key.trim()] as Record<string, unknown>);
			} else if (!Number.isNaN(Number(value))) {
				// Numeric value
				currentObj[key.trim()] = Number(value);
			} else if (value === 'true' || value === 'false') {
				// Boolean value
				currentObj[key.trim()] = value === 'true';
			} else {
				// String value
				currentObj[key.trim()] = value.replace(/^["']|["']$/g, '');
			}
		}
	}

	return result;
} /**
 * Assign collected content to appropriate section
 */
function finishSection(
	result: InitialMdContent,
	section: string,
	content: string[],
) {
	const sectionLower = section.toLowerCase();

	if (sectionLower.includes('requirement')) {
		result.requirements.push(...content);
	} else if (sectionLower.includes('constraint')) {
		result.constraints = [...(result.constraints || []), ...content];
	} else if (sectionLower.includes('reference')) {
		result.references = [...(result.references || []), ...content];
	} else if (sectionLower.includes('test')) {
		result.tests.push(...content);
	} else if (
		sectionLower.includes('acceptance') ||
		sectionLower.includes('criteria')
	) {
		result.acceptance_criteria.push(...content);
	}
}

/**
 * Convert InitialMdContent to EnforcementProfile
 */
export function compileEnforcementProfile(
	initialMd: InitialMdContent,
): EnforcementProfile {
	const defaults: EnforcementProfile = {
		budgets: {
			coverageLines: 95,
			coverageBranches: 90,
			performanceLCP: 2500,
			performanceTBT: 300,
			a11yScore: 95,
		},
		architecture: {
			allowedPackageBoundaries: [],
			namingConventions: {},
			repoLayout: [],
			crossBoundaryImports: [],
		},
		governance: {
			licensePolicy: '(Apache-2.0 OR Commercial)',
			codeownersMapping: {},
			structureGuardExceptions: [],
			requiredChecks: ['test', 'lint', 'type-check'],
		},
	};

	// Override defaults with values from initial.md
	const profile: EnforcementProfile = {
		budgets: {
			...defaults.budgets,
			...(initialMd.budgets?.coverage && {
				coverageLines:
					initialMd.budgets.coverage.lines ?? defaults.budgets.coverageLines,
				coverageBranches:
					initialMd.budgets.coverage.branches ??
					defaults.budgets.coverageBranches,
			}),
			...(initialMd.budgets?.performance && {
				performanceLCP:
					initialMd.budgets.performance.lcp ?? defaults.budgets.performanceLCP,
				performanceTBT:
					initialMd.budgets.performance.tbt ?? defaults.budgets.performanceTBT,
			}),
			...(initialMd.budgets?.accessibility && {
				a11yScore:
					initialMd.budgets.accessibility.score ?? defaults.budgets.a11yScore,
			}),
		},
		architecture: {
			...defaults.architecture,
			...(initialMd.architecture?.boundaries && {
				allowedPackageBoundaries: initialMd.architecture.boundaries,
			}),
			...(initialMd.architecture?.naming && {
				namingConventions: initialMd.architecture.naming,
			}),
			...(initialMd.architecture?.layout && {
				repoLayout: initialMd.architecture.layout,
			}),
			...(initialMd.architecture?.exceptions && {
				crossBoundaryImports: initialMd.architecture.exceptions,
			}),
		},
		governance: {
			...defaults.governance,
			...(initialMd.governance?.license && {
				licensePolicy: initialMd.governance.license,
			}),
			...(initialMd.governance?.owners && {
				codeownersMapping: initialMd.governance.owners,
			}),
			...(initialMd.governance?.checks && {
				requiredChecks: initialMd.governance.checks,
			}),
		},
	};

	return profile;
}

/**
 * Load and process initial.md file from filesystem
 */
export async function loadInitialMd(
	projectRoot: string,
	initialMdPath?: string,
): Promise<EnforcementProfile> {
	const searchPaths = [
		initialMdPath,
		join(projectRoot, 'initial.md'),
		join(projectRoot, '.prp', 'initial.md'),
		join(projectRoot, 'docs', 'initial.md'),
		join(projectRoot, 'INITIAL.md'),
	].filter(Boolean) as string[];

	for (const path of searchPaths) {
		try {
			const fullPath = resolve(path);
			const content = await fs.readFile(fullPath, 'utf-8');
			const parsed = parseInitialMd(content);
			return compileEnforcementProfile(parsed);
		} catch {
			// Swallow read/parse errors for this path; proceed to next candidate.
		}
	}

	// No initial.md found, return defaults
	console.warn('No initial.md found, using default enforcement profile');
	return compileEnforcementProfile({
		title: 'Default Project',
		context: 'No initial.md configuration found',
		requirements: [],
		tests: [],
		acceptance_criteria: [],
	});
}

/**
 * @file packages/prp-runner/src/gates/base.ts
 * @description Base gate interface and common functionality for PRP gates G0-G7
 * @maintainer @jamiescottcraik
 * @version 1.0.0
 * @status IMPLEMENTATION_READY
 */

// Canonical types are provided by @cortex-os/kernel. Import as type-only to avoid runtime cycles.
import type {
	EnforcementProfile,
	Evidence,
	GateResult,
	PRPState,
} from '@cortex-os/kernel';

// Re-export the types for convenience
export type { EnforcementProfile, Evidence, GateResult, PRPState };

export type GateId = 'G0' | 'G1' | 'G2' | 'G3' | 'G4' | 'G5' | 'G6' | 'G7';

export interface GateContext {
	state: PRPState;
	workingDirectory: string;
	projectRoot: string;
	enforcementProfile: EnforcementProfile;
	repoInfo: {
		owner: string;
		repo: string;
		branch: string;
		commitSha: string;
	};
	actor: string;
	strictMode: boolean;
}

export interface AutomatedCheck {
	name: string;
	description: string;
	execute(context: GateContext): Promise<{
		status: 'pass' | 'fail' | 'skip';
		output?: string;
		duration?: number;
		evidence?: Evidence[];
	}>;
}

export interface HumanApprovalSpec {
	role:
	| 'product-owner'
	| 'architect'
	| 'qa-lead'
	| 'code-reviewer'
	| 'security-reviewer'
	| 'maintainer'
	| 'release-manager';
	description: string;
	requiredDecision: 'approved' | 'rejected';
	timeoutMs?: number;
}

/**
 * Base abstract gate implementing common functionality
 */
export abstract class BaseGate {
	abstract readonly id: GateId;
	abstract readonly name: string;
	abstract readonly purpose: string;
	abstract readonly requiresHumanApproval: boolean;
	abstract readonly humanApprovalSpec?: HumanApprovalSpec;
	abstract readonly automatedChecks: AutomatedCheck[];

	/**
	 * Execute the gate with validation and evidence collection
	 */
	async execute(context: GateContext): Promise<GateResult> {
		const startTime = Date.now();
		const timestamp = new Date().toISOString();
		const artifacts: string[] = [];
		const evidence: string[] = [];

		try {
			// Execute automated checks
			const automatedResults = await this.runAutomatedChecks(context);

			// Determine if human approval is required
			const needsApproval =
				this.requiresHumanApproval &&
				this.shouldRequestApproval(automatedResults);

			// Execute gate-specific logic
			const gateSpecificResult = await this.executeGateLogic(
				context,
				automatedResults,
			);
			artifacts.push(...gateSpecificResult.artifacts);
			evidence.push(...gateSpecificResult.evidence);

			// Determine overall status
			const hasFailures = automatedResults.some((r) => r.status === 'fail');
			const status = hasFailures
				? 'failed'
				: needsApproval
					? 'pending'
					: 'passed';

			return {
				id: this.id,
				name: this.name,
				status,
				requiresHumanApproval: needsApproval,
				automatedChecks: automatedResults,
				artifacts,
				evidence,
				timestamp,
				nextSteps: this.generateNextSteps(automatedResults, needsApproval),
			};
		} catch (error) {
			return {
				id: this.id,
				name: this.name,
				status: 'failed',
				requiresHumanApproval: false,
				automatedChecks: [
					{
						name: 'gate-execution',
						status: 'fail',
						output: `Gate execution failed: ${error instanceof Error ? error.message : String(error)}`,
						duration: Date.now() - startTime,
					},
				],
				artifacts,
				evidence,
				timestamp,
			};
		}
	}

	/**
	 * Run all automated checks for this gate
	 */
	private async runAutomatedChecks(context: GateContext) {
		const results = [];

		for (const check of this.automatedChecks) {
			try {
				const result = await check.execute(context);
				results.push({
					name: check.name,
					status: result.status,
					output: result.output,
					duration: result.duration,
				});

				// Add evidence to state if provided
				if (result.evidence) {
					context.state.evidence.push(...result.evidence);
				}
			} catch (error) {
				results.push({
					name: check.name,
					status: 'fail' as const,
					output: `Check failed: ${error instanceof Error ? error.message : String(error)}`,
				});
			}
		}

		return results;
	}

	/**
	 * Determine if human approval should be requested based on automated results
	 */
	protected shouldRequestApproval(
		automatedResults: Array<{ status: 'pass' | 'fail' | 'skip' }>,
	): boolean {
		// Default: request approval if any checks failed or if always required
		return (
			this.requiresHumanApproval &&
			(automatedResults.some((r) => r.status === 'fail') ||
				automatedResults.length === 0)
		);
	}

	/**
	 * Generate next steps based on gate results
	 */
	protected generateNextSteps(
		automatedResults: Array<{ status: 'pass' | 'fail' | 'skip'; name: string }>,
		needsApproval: boolean,
	): string[] {
		const steps: string[] = [];

		const failures = automatedResults.filter((r) => r.status === 'fail');
		if (failures.length > 0) {
			steps.push(
				`Fix ${failures.length} failing checks: ${failures.map((f) => f.name).join(', ')}`,
			);
		}

		if (needsApproval) {
			steps.push(`Request ${this.humanApprovalSpec?.role} approval`);
		}

		if (steps.length === 0) {
			steps.push('Gate passed - proceed to next gate');
		}

		return steps;
	}

	/**
	 * Gate-specific execution logic - override in subclasses
	 */
	protected abstract executeGateLogic(
		context: GateContext,
		automatedResults: Array<{ status: 'pass' | 'fail' | 'skip' }>,
	): Promise<{
		artifacts: string[];
		evidence: string[];
	}>;
}

/**
 * @file packages/prp-runner/src/gates/g5-triage.ts
 * @description G5: Triage - ensure no red findings; maintainers sign-off
 */

import {
	type AutomatedCheck,
	BaseGate,
	type GateContext,
	type HumanApprovalSpec,
} from './base.js';

class NoRedFindingsCheck implements AutomatedCheck {
	name = 'no-red-findings';
	description =
		"Ensure no 'red' category scores exist (based on previous review JSON categories)";

	async execute(_context: GateContext) {
		// Without persisted review JSON categories, assume pass for now
		return {
			status: 'pass' as const,
			output: 'No red findings (assumed)',
			duration: 10,
		};
	}
}

export class G5TriageGate extends BaseGate {
	readonly id = 'G5' as const;
	readonly name = 'Triage';
	readonly purpose = 'Confirm no blockers remain before release readiness';
	readonly requiresHumanApproval = true;

	readonly humanApprovalSpec: HumanApprovalSpec = {
		role: 'maintainer',
		description: 'Maintainer verifies no blockers remain',
		requiredDecision: 'approved',
		timeoutMs: 24 * 60 * 60 * 1000,
	};

	readonly automatedChecks: AutomatedCheck[] = [new NoRedFindingsCheck()];

	protected async executeGateLogic() {
		return { artifacts: [], evidence: [] };
	}
	protected shouldRequestApproval(
		automatedResults: Array<{ status: 'pass' | 'fail' | 'skip' }>,
	): boolean {
		return automatedResults.every((r) => r.status === 'pass');
	}
}

import crypto from 'node:crypto';

export interface EmbeddingConfig {
	provider: 'sentence-transformers' | 'local' | 'mock';
	model?: string;
	dimensions?: number;
	batchSize?: number;
	cachePath?: string;
}

export interface EmbeddingVector {
	id: string;
	text: string;
	vector: number[];
	metadata?: Record<string, any>;
	timestamp: string;
}

export interface EmbeddingQuery {
	text: string;
	topK?: number;
	threshold?: number;
	filter?: Record<string, any>;
}

export interface EmbeddingResult {
	id: string;
	text: string;
	similarity: number;
	metadata?: Record<string, any>;
}

export interface EmbeddingState {
	config: EmbeddingConfig;
	vectorStore: Map<string, EmbeddingVector>;
	pythonPath: string;
}

export const createEmbeddingState = (
	provider: EmbeddingConfig['provider'] = 'sentence-transformers',
): EmbeddingState => {
	const configs: Record<EmbeddingConfig['provider'], EmbeddingConfig> = {
		'sentence-transformers': {
			provider: 'sentence-transformers',
			model: 'Qwen/Qwen3-Embedding-0.6B',
			dimensions: 1024,
		},
		local: {
			provider: 'local',
			model: 'Qwen/Qwen3-Embedding-0.6B',
			dimensions: 1024,
		},
		mock: {
			provider: 'mock',
			model: 'mock-embedding',
			dimensions: 1024,
		},
	};

	const config = configs[provider];
	validateConfig(config);
	return { config, vectorStore: new Map(), pythonPath: 'python' };
};

export const generateEmbeddings = async (
	state: EmbeddingState,
	texts: string | string[],
): Promise<number[][]> => {
	const textArray = Array.isArray(texts) ? texts : [texts];

	switch (state.config.provider) {
		case 'sentence-transformers':
			return generateWithSentenceTransformers(
				state.pythonPath,
				state.config,
				textArray,
			);
		case 'local':
			return generateWithLocal(state.pythonPath, textArray);
		case 'mock':
			return generateWithMock(state.config, textArray);
		default:
			throw new Error(
				`Embedding generation not implemented for provider: ${state.config.provider}`,
			);
	}
};

export const addDocuments = async (
	state: EmbeddingState,
	texts: string[],
	metadata?: Record<string, any>[],
	ids?: string[],
): Promise<{ state: EmbeddingState; ids: string[] }> => {
	const embeddings = await generateEmbeddings(state, texts);
	const newStore = new Map(state.vectorStore);
	const documentIds: string[] = [];

	for (let i = 0; i < texts.length; i++) {
		const id = ids?.[i] || generateId(texts[i]);
		const vector: EmbeddingVector = {
			id,
			text: texts[i],
			vector: embeddings[i],
			metadata: metadata?.[i],
			timestamp: new Date().toISOString(),
		};
		newStore.set(id, vector);
		documentIds.push(id);
	}

	return { state: { ...state, vectorStore: newStore }, ids: documentIds };
};

export const similaritySearch = async (
	state: EmbeddingState,
	query: EmbeddingQuery,
): Promise<EmbeddingResult[]> => {
	const queryEmbedding = await generateEmbeddings(state, query.text);
	const queryVector = queryEmbedding[0];
	const results: EmbeddingResult[] = [];

	for (const doc of state.vectorStore.values()) {
		if (query.filter && !matchesFilter(doc.metadata, query.filter)) {
			continue;
		}
		const similarity = cosineSimilarity(queryVector, doc.vector);
		if (!query.threshold || similarity >= query.threshold) {
			results.push({
				id: doc.id,
				text: doc.text,
				similarity,
				metadata: doc.metadata,
			});
		}
	}

	results.sort((a, b) => b.similarity - a.similarity);
	return query.topK ? results.slice(0, query.topK) : results;
};

export const getDocument = (
	state: EmbeddingState,
	id: string,
): EmbeddingVector | undefined => state.vectorStore.get(id);

export const removeDocument = (
	state: EmbeddingState,
	id: string,
): { state: EmbeddingState; removed: boolean } => {
	const newStore = new Map(state.vectorStore);
	const removed = newStore.delete(id);
	return { state: { ...state, vectorStore: newStore }, removed };
};

export const getStats = (state: EmbeddingState) => {
	const totalVectors = state.vectorStore.size;
	const dimensions = state.config.dimensions || 0;
	const memoryUsage = `${Math.round(((totalVectors * dimensions * 4) / 1024 / 1024) * 100) / 100} MB`;
	return {
		totalDocuments: totalVectors,
		dimensions,
		provider: state.config.provider,
		memoryUsage,
	};
};

const validateConfig = (config: EmbeddingConfig): void => {
	if (!['sentence-transformers', 'local', 'mock'].includes(config.provider)) {
		throw new Error(`Unsupported embedding provider: ${config.provider}`);
	}
};

const generateWithMock = async (
	config: EmbeddingConfig,
	texts: string[],
): Promise<number[][]> => {
	const dims = config.dimensions || 1024;
	// Deterministic pseudo-random vector per text based on SHA256
	return texts.map((t) => {
		const hash = crypto.createHash('sha256').update(t).digest();
		const vec: number[] = new Array(dims).fill(0).map((_, i) => {
			// Use hash bytes cyclically to produce values in [-1,1]
			const byte = hash[i % hash.length];
			return (byte / 255) * 2 - 1;
		});
		// Normalize to unit length
		const norm = Math.sqrt(vec.reduce((s, v) => s + v * v, 0));
		if (norm === 0) return vec.map(() => 0);
		return vec.map((v) => v / norm);
	});
};

const generateWithSentenceTransformers = async (
	pythonPath: string,
	config: EmbeddingConfig,
	texts: string[],
): Promise<number[][]> => {
	const model = config.model || 'Qwen/Qwen3-Embedding-0.6B';
