import { pipeline } from '@xenova/transformers';

export interface RerankerConfig {
	provider: 'transformers' | 'local' | 'mock';
	model?: string;
	batchSize?: number;
}

export interface RerankerResult {
	text: string;
	score: number;
	originalIndex: number;
}

export interface RerankerState {
	config: RerankerConfig;
}

export const createRerankerState = (
	provider: RerankerConfig['provider'] = 'transformers',
): RerankerState => {
	const configs: Record<RerankerConfig['provider'], RerankerConfig> = {
		transformers: {
			provider: 'transformers',
			model: 'Qwen/Qwen2.5-coder-cross-encoder',
		},
		local: {
			provider: 'local',
			model: 'local-reranker-model',
		},
		mock: {
			provider: 'mock',
		},
	};
	const config = configs[provider];
	if (!config) {
		throw new Error(`Unsupported reranker provider: ${provider}`);
	}
	return { config };
};

export const rerank = async (
	state: RerankerState,
	query: string,
	documents: string[],
	topK?: number,
): Promise<RerankerResult[]> => {
	switch (state.config.provider) {
		case 'transformers':
			return rerankWithTransformers(query, documents, topK, state.config.model);
		case 'local':
			return rerankWithLocal(query, documents, topK);
		case 'mock':
			return rerankWithMock(query, documents, topK);
		default:
			throw new Error(
				`Reranking not implemented for provider: ${state.config.provider}`,
			);
	}
};

const rerankWithMock = async (
	query: string,
	documents: string[],
	topK?: number,
): Promise<RerankerResult[]> => {
	const queryLower = query.toLowerCase();
	const results: RerankerResult[] = documents.map((doc, index) => {
		const docLower = doc.toLowerCase();
		const queryWords = new Set(queryLower.split(/\s+/));
		const docWords = new Set(docLower.split(/\s+/));
		const intersection = new Set(
			[...queryWords].filter((w) => docWords.has(w)),
		);
		const overlap = intersection.size;
		const union = new Set([...queryWords, ...docWords]).size;
		const jaccardSimilarity = union === 0 ? 0 : overlap / union;
		const substringBoost = docLower.includes(queryLower) ? 0.2 : 0;
		const score = jaccardSimilarity + substringBoost;
		return { text: doc, score, originalIndex: index };
	});
	results.sort((a, b) => b.score - a.score);
	return topK ? results.slice(0, topK) : results;
};

let crossEncoder: any | null = null;

const rerankWithTransformers = async (
	query: string,
	documents: string[],
	topK?: number,
	model = 'Qwen/Qwen2.5-coder-cross-encoder',
): Promise<RerankerResult[]> => {
	if (!crossEncoder) {
		crossEncoder = await pipeline('text-classification', model, {
			quantized: true,
		});
	}
	const inputs = documents.map((doc) => ({ text: query, text_pair: doc }));
	const outputs = await crossEncoder(inputs);
	const results: RerankerResult[] = outputs.map((out: any, index: number) => ({
		text: documents[index],
		score: out[0].score,
		originalIndex: index,
	}));
	results.sort((a, b) => b.score - a.score);
	return topK ? results.slice(0, topK) : results;
};

const rerankWithLocal = async (
	query: string,
	documents: string[],
	topK?: number,
): Promise<RerankerResult[]> => {
	console.warn('Local reranking not implemented, falling back to mock');
	return rerankWithMock(query, documents, topK);
};

import { Ollama } from 'ollama';
import { z } from 'zod';
import {
  AVAILABLE_MLX_MODELS,
  createMLXAdapter,
  type MLXAdapter,
} from './mlx-adapter.js';

interface OllamaAdapter {
  generate(options: {
    prompt: string;
    temperature?: number;
    maxTokens?: number;
    model?: string;
  }): Promise<{ text: string }>;
}

export interface LLMConfig {
  provider: 'mlx' | 'ollama';
  endpoint?: string;
  model?: string;
  mlxModel?: keyof typeof AVAILABLE_MLX_MODELS;
  knifePath?: string;
}

export interface LLMGenerateOptions {
  prompt?: string;
  temperature?: number;
  maxTokens?: number;
}

export interface LLMState {
  config: LLMConfig;
  ollamaAdapter?: OllamaAdapter;
  mlxAdapter?: MLXAdapter;
}

const llmConfigSchema = z.object({
  provider: z.enum(['mlx', 'ollama']),
  endpoint: z.string().url().optional(),
  model: z.string().optional(),
  mlxModel: z.string().optional(),
  knifePath: z.string().optional(),
});

function normalizeConfig(config: LLMConfig): LLMConfig {
  const normalized: LLMConfig = { ...config };
  if (normalized.provider === 'ollama') {
    if (!normalized.endpoint) throw new Error('Ollama endpoint is required');
    normalized.mlxModel = undefined;
    normalized.knifePath = undefined;
  } else {
    if (!normalized.mlxModel)
      throw new Error('MLX model is required for MLX provider');
    // Convert string to enum key if needed
    if (typeof normalized.mlxModel === 'string') {
      if (Object.keys(AVAILABLE_MLX_MODELS).includes(normalized.mlxModel)) {
        // Already correct type
      } else {
        throw new Error(`Invalid MLX model: ${normalized.mlxModel}`);
      }
    }
    // Don't set endpoint to undefined if it's already set
    normalized.model = undefined;
  }
  return normalized;
}

function createOllamaAdapter(cfg: LLMConfig): OllamaAdapter {
  const client = new Ollama({ host: cfg.endpoint });
  return {
    async generate({ prompt, temperature, maxTokens, model }) {
      const res = await client.generate({
        model: model || cfg.model || 'llama3',
        prompt,
        stream: false,
        options: {
          temperature: temperature ?? 0.7,
          num_predict: maxTokens ?? 512,
        },
      });
      return { text: res.response || '' };
    },
  };
}

export function configureLLM(config: LLMConfig): LLMState {
  const normalized = normalizeConfig(config);
  const cfgRaw = llmConfigSchema.parse(normalized);
  let cfg: LLMConfig;
  if (cfgRaw.provider === 'mlx') {
    if (typeof cfgRaw.mlxModel === 'string') {
      if (Object.keys(AVAILABLE_MLX_MODELS).includes(cfgRaw.mlxModel)) {
        cfg = {
          ...cfgRaw,
          mlxModel: cfgRaw.mlxModel as keyof typeof AVAILABLE_MLX_MODELS,
        };
      } else {
        throw new Error(`Invalid MLX model: ${cfgRaw.mlxModel}`);
      }
    } else {
      cfg = { ...cfgRaw, mlxModel: undefined };
    }
  } else {
    // For non-MLX providers, always set mlxModel: undefined
    cfg = { ...cfgRaw, mlxModel: undefined };
  }
  const state: LLMState = { config: cfg };
  if (cfg.provider === 'ollama') {
    state.ollamaAdapter = createOllamaAdapter(cfg);
  } else if (cfg.mlxModel) {
    state.mlxAdapter = createMLXAdapter(cfg.mlxModel, {
      knifePath: cfg.knifePath,
      maxTokens: 512,
      temperature: 0.7,
    });
  }
  return state;
}

export function getProvider(state: LLMState): string {
  return state.config.provider;
}

export function getModel(state: LLMState): string {
  console.log('getModel debug - provider:', state.config.provider);
  if (state.config.provider === 'ollama') {
    console.log('getModel taking ollama path');
    return state.config.model || 'llama3';
  } else {
    console.log('getModel taking MLX path');
    // For MLX, return the model value (not key) for display purposes
    const mlxModelKey = state.config.mlxModel || 'QWEN_SMALL';
    console.log('getModel debug:', { mlxModelKey, lookup: AVAILABLE_MLX_MODELS[mlxModelKey], allModels: AVAILABLE_MLX_MODELS });
    return AVAILABLE_MLX_MODELS[mlxModelKey] || AVAILABLE_MLX_MODELS.QWEN_SMALL;
  }
}

export function getMLXAdapter(state: LLMState): MLXAdapter | undefined {
  return state.mlxAdapter;
}

export async function listMLXModels(state: LLMState) {
  if (state.config.provider !== 'mlx' || !state.mlxAdapter) {
    throw new Error('MLX adapter not available');
  }
  return state.mlxAdapter.listModels();
}

export async function checkProviderHealth(
  state: LLMState,
): Promise<{ healthy: boolean; message: string }> {
  if (state.config.provider === 'mlx' && state.mlxAdapter) {
    return state.mlxAdapter.checkHealth();
  }
  if (state.config.provider === 'ollama' && state.ollamaAdapter) {
    try {
      await state.ollamaAdapter.generate({ prompt: '', maxTokens: 1 });
      return { healthy: true, message: 'Ollama healthy' };
    } catch (error) {
      return {
        healthy: false,
        message: `Ollama error: ${error instanceof Error ? error.message : String(error)}`,
      };
    }
  }
  return { healthy: false, message: 'Unknown provider' };
}

export async function generate(
  state: LLMState,
  prompt: string,
  options: LLMGenerateOptions = {},
): Promise<string> {
  if (state.config.provider === 'ollama') {
    if (!state.ollamaAdapter) throw new Error('Ollama adapter not initialized');
    const result = await state.ollamaAdapter.generate({
      prompt,
      temperature: options.temperature,
      maxTokens: options.maxTokens,
      model: state.config.model,
    });
    return result.text;
  }
  if (state.config.provider === 'mlx') {
    return generateWithMLX(state, prompt, options);
  }
  throw new Error(
    `Generation not implemented for provider: ${state.config.provider}`,
  );
}

async function generateWithMLX(
  state: LLMState,
  prompt: string,
  options: LLMGenerateOptions,
): Promise<string> {
  if (!state.mlxAdapter) throw new Error('MLX adapter not initialized');
  const health = await state.mlxAdapter.checkHealth();
  if (!health.healthy) {
    throw new Error(`MLX model not healthy: ${health.message}`);
  }
  return state.mlxAdapter.generate({
    prompt,
    maxTokens: options.maxTokens ?? 512,
    temperature: options.temperature ?? 0.7,
  });
}

// Type guard for shutdown method on MLXAdapter
function hasShutdown(
  adapter: unknown,
): adapter is { shutdown: () => Promise<void> } {
  return (
    typeof adapter === 'object' &&
    adapter !== null &&
    typeof (adapter as { shutdown?: unknown }).shutdown === 'function'
  );
}

export async function shutdown(state: LLMState): Promise<void> {
  if (state.mlxAdapter && hasShutdown(state.mlxAdapter)) {
    await state.mlxAdapter.shutdown();
  }
}

export class LLMBridge {
  private readonly state: LLMState;
  constructor(config: LLMConfig) {
    this.state = configureLLM(config);
  }
  getProvider() {
    return getProvider(this.state);
  }
  getModel() {
    return getModel(this.state);
  }
  async generate(prompt: string, options?: LLMGenerateOptions) {
    return generate(this.state, prompt, options);
  }
  async listModels() {
    return listMLXModels(this.state);
  }
  async checkHealth() {
    return checkProviderHealth(this.state);
  }
  async shutdown() {
    await shutdown(this.state);
  }
}

/**
 * @file mlx-adapter.ts
 * @description MLX Adapter for real MLX model integration via mlx-knife
 * @author Cortex-OS Team
 * @version 1.0.0
 * @status TDD-DRIVEN
 */

import { spawn } from 'node:child_process';
import { existsSync } from 'node:fs';

export interface MLXConfig {
	modelName: string;
	maxTokens?: number;
	temperature?: number;
	knifePath?: string;
	cachePath?: string;
	timeoutMs?: number;
}

export interface MLXGenerateOptions {
	prompt: string;
	maxTokens?: number;
	temperature?: number;
	stopTokens?: string[];
}

export interface MLXModelInfo {
	name: string;
	id: string;
	size: string;
	modified: string;
	path: string;
	health: string;
}

/**
 * MLX Adapter - Direct integration with mlx-knife for real MLX model execution
 * Provides a bridge between TypeScript orchestrator and MLX models
 */
export class MLXAdapter {
	private config: MLXConfig;
	private knifePath: string;

	constructor(config: MLXConfig) {
		this.config = config;
		this.knifePath = config.knifePath || 'mlx-knife';
		this.validateConfig();
	}

	/**
	 * Determine whether the runtime environment appears to have mlx-knife available.
	 * We keep this extremely defensive â€“ any error means 'not available'.
	 */
	private isRuntimeAvailable(): boolean {
		// If explicit path provided and exists on FS we assume available.
		if (this.config.knifePath && existsSync(this.config.knifePath)) return true;
		// Best-effort PATH lookup by spawning 'which' synchronously via shell (cheap & safe in tests)
		try {
			const resolved = process.env.PATH?.split(':').some((p) =>
				existsSync(`${p}/mlx-knife`),
			);
			return Boolean(resolved);
		} catch {
			return false;
		}
	}

	/**
	 * Validate MLX configuration
	 */
	private validateConfig(): void {
		if (!this.config.modelName) {
			throw new Error('MLX model name is required');
		}
		if (this.config.timeoutMs !== undefined && this.config.timeoutMs <= 0) {
			throw new Error('timeoutMs must be positive');
		}
	}

	/**
	 * List available MLX models
	 */
	async listModels(): Promise<MLXModelInfo[]> {
		if (!this.isRuntimeAvailable()) {
			// Return empty â€“ caller tests can decide to skip.
			return [];
		}
		try {
			const output = await this.executeCommand(['list']);
			return this.parseModelList(output);
		} catch (error) {
			throw new Error(
				`Failed to list MLX models: ${error instanceof Error ? error.message : String(error)}`,
			);
		}
	}

	/**
	 * Check if a specific model is available
	 */
	async isModelAvailable(modelName: string): Promise<boolean> {
		if (!this.isRuntimeAvailable()) return false;
		try {
			// Try using mlx-knife show command first (faster than listing all models)
			const info = await this.getModelInfo(modelName);
			if (info && info.health === '[OK]') {
				return true;
			}

			// Fallback to listing all models
			const models = await this.listModels();
			const normalizedTarget = this.normalizeModelName(modelName);

			return models.some((model) => {
				const normalizedModelName = this.normalizeModelName(model.name);
				return (
					normalizedModelName === normalizedTarget ||
					model.name.includes(normalizedTarget) ||
					normalizedTarget.includes(model.name) ||
					model.id === modelName
				);
			});
		} catch (_error) {
			return false;
		}
	}

	/**
	 * Generate text using MLX model via mlx-knife
	 */
	async generate(options: MLXGenerateOptions): Promise<string> {
		if (!this.isRuntimeAvailable()) {
			throw new Error(
				`MLX runtime unavailable: cannot generate with ${this.config.modelName}`,
			);
		}
		const { prompt, maxTokens = 512, temperature = 0.7 } = options;

		// Get the actual model name that mlx-knife recognizes
		const actualModelName = await this.getActualModelName(
			this.config.modelName,
		);
		if (!actualModelName) {
			throw new Error(`MLX model not available: ${this.config.modelName}`);
		}

		try {
			const args = [
				'run',
				actualModelName,
				prompt,
				'--max-tokens',
				maxTokens.toString(),
				'--temperature',
				temperature.toString(),
			];

			if (options.stopTokens && options.stopTokens.length > 0) {
				args.push('--stop', ...options.stopTokens);
			}

			const output = await this.executeCommand(args);
			return this.cleanMLXOutput(output);
		} catch (error) {
			throw new Error(
				`MLX generation failed: ${error instanceof Error ? error.message : String(error)}`,
			);
		}
	}

	/**
	 * Get model information
	 */
	async getModelInfo(modelName?: string): Promise<MLXModelInfo | null> {
		if (!this.isRuntimeAvailable()) return null;
		const targetModel = modelName || this.config.modelName;

		try {
			const output = await this.executeCommand(['show', targetModel]);
			return this.parseModelInfo(output);
		} catch (_error) {
			return null;
		}
	}

	/**
	 * Check model health
	 */
	async checkHealth(): Promise<{ healthy: boolean; message: string }> {
		if (!this.isRuntimeAvailable()) {
			return { healthy: false, message: 'MLX runtime unavailable' };
		}
		try {
			const info = await this.getModelInfo();
			if (!info) {
				return {
					healthy: false,
					message: `Model ${this.config.modelName} not found`,
				};
			}

			if (info.health === '[OK]') {
				return { healthy: true, message: 'Model is healthy' };
			} else {
				return { healthy: false, message: `Model health: ${info.health}` };
			}
		} catch (error) {
			return {
				healthy: false,
				message: `Health check failed: ${error instanceof Error ? error.message : String(error)}`,
			};
		}
	}

	/**
	 * Execute mlx-knife command
	 */
	private async executeCommand(args: string[]): Promise<string> {
		return new Promise((resolve, reject) => {
			const child = spawn(this.knifePath, args, {
				stdio: ['pipe', 'pipe', 'pipe'],
				env: process.env,
			});

			let stdout = '';
			let stderr = '';

			child.stdout?.on('data', (data) => {
				stdout += data.toString();
			});

			child.stderr?.on('data', (data) => {
				stderr += data.toString();
			});

			// Set a configurable timeout with reasonable default
			const timeoutMs = this.config.timeoutMs || 30000; // Allow configuration of timeout
			const timeoutHandle = setTimeout(() => {
				child.kill();
				reject(new Error(`MLX command timed out after ${timeoutMs}ms`));
			}, timeoutMs);

			child.on('close', (code) => {
				clearTimeout(timeoutHandle); // Clear timeout on completion
				if (code === 0) {
					resolve(stdout);
				} else {
					reject(new Error(`mlx-knife exited with code ${code}: ${stderr}`));
				}
			});

			child.on('error', (error) => {
				clearTimeout(timeoutHandle); // Clear timeout on error
				reject(new Error(`Failed to spawn mlx-knife: ${error.message}`));
			});
		});
	}

	/**
	 * Parse model list output from mlx-knife
	 */
	private parseModelList(output: string): MLXModelInfo[] {
		const lines = output
			.split('\n')
			.filter(
				(line) => line.trim() && !line.includes('âš ï¸') && !line.includes('NAME'),
			);

		return lines
			.map((line) => {
				const parts = line.trim().split(/\s+/);
				if (parts.length >= 4) {
					return {
						name: parts[0],
						id: parts[1],
						size: parts[2],
						modified: parts.slice(3, -1).join(' '),
						path: '', // Will be filled by show command if needed
						health: '[OK]', // Assume OK if listed
					};
				}
				return null;
			})
			.filter(Boolean) as MLXModelInfo[];
	}

	/**
	 * Parse model info output from mlx-knife show
	 */
	private parseModelInfo(output: string): MLXModelInfo | null {
		const lines = output.split('\n');
		const info: Partial<MLXModelInfo> = {};

		for (const line of lines) {
			if (line.startsWith('Model:')) {
				info.name = line.split('Model:')[1].trim();
			} else if (line.startsWith('Path:')) {
				info.path = line.split('Path:')[1].trim();
			} else if (line.startsWith('Snapshot:')) {
				info.id = line.split('Snapshot:')[1].trim();
			} else if (line.startsWith('Size:')) {
				info.size = line.split('Size:')[1].trim();
			} else if (line.startsWith('Modified:')) {
				info.modified = line.split('Modified:')[1].trim();
			} else if (line.startsWith('Health:')) {
				info.health = line.split('Health:')[1].trim();
			}
		}

		if (info.name && info.id) {
			return info as MLXModelInfo;
		}

		return null;
	}

	/**
	 * Clean MLX output by removing warnings and extra whitespace
	 */
	private cleanMLXOutput(output: string): string {
		return output
			.split('\n')
			.filter((line) => {
				const lowerLine = line.toLowerCase();
				return (
					!line.includes('âš ï¸') &&
					!line.includes('Found') &&
					!line.includes('Command:') &&
					!lowerLine.includes('please move them to:') &&
					!lowerLine.includes('this warning will appear') &&
					!lowerLine.includes('mv /volumes/') &&
					line.trim().length > 0
				);
			})
			.join('\n')
			.trim();
	}

	/**
	 * Get configured model name
	 */
	getModelName(): string {
		return this.config.modelName;
	}

	/**
	 * Update configuration
	 */
	updateConfig(newConfig: Partial<MLXConfig>): void {
		this.config = { ...this.config, ...newConfig };
		this.validateConfig();
	}

	/**
	 * Normalize model name by removing the mlx-community/ prefix if present
	 */
	private normalizeModelName(modelName: string): string {
		return modelName.replace(/^mlx-community\//, '');
	}

	/**
	 * Get the actual model name that mlx-knife recognizes
	 */
	private async getActualModelName(
		configuredName: string,
	): Promise<string | null> {
		try {
			const models = await this.listModels();
			const normalizedTarget = this.normalizeModelName(configuredName);

			// Find exact match first
			const exactMatch = models.find(
				(model) => this.normalizeModelName(model.name) === normalizedTarget,
			);

			if (exactMatch) {
				return exactMatch.name;
			}

			// Find partial match
			const partialMatch = models.find(
				(model) =>
					model.name.includes(normalizedTarget) ||
					normalizedTarget.includes(model.name),
			);

			return partialMatch?.name || null;
		} catch (_error) {
			return null;
		}
	}
}

