import { z } from 'zod';
import { byChars } from '../chunk/index.js';
import type { Chunk, Embedder, Store } from '../lib/index.js';

const schema = z.object({
	source: z.string(),
	text: z.string().min(1),
	embedder: z.custom<Embedder>(
		(e): e is Embedder =>
			typeof e === 'object' &&
			e !== null &&
			typeof (e as any).embed === 'function',
	),
	store: z.custom<Store>(
		(s): s is Store =>
			typeof s === 'object' &&
			s !== null &&
			typeof (s as any).upsert === 'function',
	),
	chunkSize: z.number().int().positive().default(300),
	overlap: z.number().int().nonnegative().default(0),
});

export type IngestTextParams = z.input<typeof schema>;

export async function ingestText(params: IngestTextParams): Promise<void> {
	const { source, text, embedder, store, chunkSize, overlap } =
		schema.parse(params);
	const parts = byChars(text, chunkSize, overlap);
	const now = Date.now();
	const chunks: Chunk[] = parts.map((p, i) => ({
		id: `${source}#${i}`,
		text: p,
		source,
		updatedAt: now,
	}));
	const embeddings = await embedder.embed(chunks.map((c) => c.text));
	if (embeddings.length !== chunks.length) {
		throw new Error(
			`Embedding count (${embeddings.length}) does not match chunk count (${chunks.length})`,
		);
	}
	const withEmb = chunks.map((c, i) => ({
		...c,
		updatedAt: c.updatedAt ?? Date.now(),
		embedding: embeddings[i],
	}));
	await store.upsert(withEmb);
}

import { z } from 'zod';
import { CitationBundler } from './lib/citation-bundler.js';
import type { Chunk, CitationBundle, Embedder, Store } from './lib/index.js';
import { routeByFreshness } from './retrieval/freshness-router.js';
import { ingestText as ingestTextHelper } from './pipeline/ingest.js';

export interface RAGPipelineConfig {
    embedder: Embedder;
    store: Store;
    chunkSize?: number;
    chunkOverlap?: number;
    freshnessEpsilon?: number;
}

export class RAGPipeline {
	private E: Embedder;
	private S: Store;
	private chunkSize: number;
    private chunkOverlap: number;
    private freshnessEpsilon: number;

	constructor(config: RAGPipelineConfig) {
        const schema = z.object({
            embedder: z.custom<Embedder>(
				(e): e is Embedder =>
					typeof e === 'object' &&
					e !== null &&
					typeof (e as any).embed === 'function',
			),
			store: z.custom<Store>(
				(s): s is Store =>
					typeof s === 'object' &&
					s !== null &&
					typeof (s as any).upsert === 'function' &&
					typeof (s as any).query === 'function',
			),
            chunkSize: z.number().int().positive().default(300),
            chunkOverlap: z.number().int().nonnegative().default(0),
            freshnessEpsilon: z.number().min(0).max(1).default(0.02),
        });
        const parsed = schema.parse(config);
        this.E = parsed.embedder;
        this.S = parsed.store;
        this.chunkSize = parsed.chunkSize;
        this.chunkOverlap = parsed.chunkOverlap;
        this.freshnessEpsilon = parsed.freshnessEpsilon;
	}

	async ingest(chunks: Chunk[]): Promise<void> {
		const texts = chunks.map((c) => c.text);
		const embeddings = await this.E.embed(texts);
		if (embeddings.length !== chunks.length) {
			throw new Error(
				`Embedding count (${embeddings.length}) does not match chunk count (${chunks.length})`,
			);
		}
		const toUpsert = chunks.map((c, i) => ({ ...c, embedding: embeddings[i] }));
		await this.S.upsert(toUpsert);
	}

	async ingestText(source: string, text: string): Promise<void> {
		await ingestTextHelper({
			source,
			text,
			embedder: this.E,
			store: this.S,
			chunkSize: this.chunkSize,
			overlap: this.chunkOverlap,
		});
	}

    async retrieve(query: string, topK = 5): Promise<CitationBundle> {
        const [emb] = await this.E.embed([query]);
        const chunks = await this.S.query(emb, topK);
        const routed = routeByFreshness(chunks, { epsilon: this.freshnessEpsilon });
        const bundler = new CitationBundler();
        return bundler.bundle(routed);
    }
}

import type { Chunk } from '../lib/types.js';

export interface FreshnessOptions {
  epsilon?: number; // score tie-break threshold
}

// Sorts primarily by score desc; when within epsilon, prefer newer updatedAt
export function routeByFreshness<T extends Chunk & { score?: number }>(
  chunks: readonly T[],
  opts: FreshnessOptions = {},
): T[] {
  const { epsilon = 0.02 } = opts;
  if (!Array.isArray(chunks) || chunks.length <= 1) return chunks.slice();
  return chunks.slice().sort((a, b) => {
    const as = a.score ?? 0;
    const bs = b.score ?? 0;
    const diff = bs - as;
    if (Math.abs(diff) > epsilon) {
      return diff; // score decides
    }
    const au = a.updatedAt ?? -Infinity;
    const bu = b.updatedAt ?? -Infinity;
    // newer first
    if (bu !== au) return bu - au;
    // stable fallback: keep original relative order (stable sort in modern V8)
    return 0;
  });
}

export default routeByFreshness;

import type { Chunk, Store } from '../lib/index.js';

export function memoryStore(): Store {
    const items: Array<Chunk & { embedding?: number[] }> = [];
    return {
        async upsert(chunks: Chunk[]) {
            for (const c of chunks) {
                const cc = { ...c, updatedAt: c.updatedAt ?? Date.now() } as Chunk;
                const i = items.findIndex((x) => x.id === c.id);
                if (i >= 0) items[i] = cc;
                else items.push(cc);
            }
        },
		async query(embedding: number[], k = 5) {
			function sim(a: number[], b: number[]) {
				if (!a || !b || a.length !== b.length) return 0;
				let dot = 0,
					na = 0,
					nb = 0;
				for (let i = 0; i < a.length; i++) {
					dot += a[i] * b[i];
					na += a[i] * a[i];
					nb += b[i] * b[i];
				}
				const denom = Math.sqrt(na) * Math.sqrt(nb) || 1;
				return dot / denom;
			}
            const scored = items
                .filter((x) => Array.isArray(x.embedding))
                .map((x) => ({
                    ...x,
                    updatedAt: (x as any).updatedAt ?? Date.now(),
                    score: sim(embedding, x.embedding as number[]),
                }))
                .sort((a, b) => (b.score ?? 0) - (a.score ?? 0))
                .slice(0, k);
            return scored;
        },
	};
}

// Minimal typed stub that delegates to the real package at runtime.
type Options = { cwd?: string; dot?: boolean; onlyFiles?: boolean };
type FgType = (
	patterns: string[] | string,
	options?: Options,
) => Promise<string[]>;
type FastGlobModule = { default: FgType };

const fg: FgType = (async (patterns: string[] | string, options?: Options) => {
	const real: FastGlobModule = await import('fast-glob');
	return real.default(patterns, options);
}) as FgType;

export default fg;

// Type-only stub for micromatch using lazy dynamic import to avoid CJS require.
type IsMatch = (
	str: string,
	patterns: string | string[],
	options?: Record<string, unknown>,
) => boolean;

interface MicromatchModule {
	isMatch: IsMatch;
}
let cached: MicromatchModule | null = null;

async function load() {
	if (!cached) {
		const mod: MicromatchModule = await import('micromatch');
		cached = { isMatch: mod.isMatch };
	}
	return cached;
}

// Wrapper exposed as synchronous-looking API; resolves lazily if already loaded.
export const isMatch: IsMatch = (str, patterns, options) => {
	if (cached) return cached.isMatch(str, patterns, options);
	// Not yet loaded: this introduces an async boundary; throw instructional error.
	throw new Error(
		'micromatch not yet loaded - call preloadMicromatch() early in lifecycle',
	);
};

export async function preloadMicromatch() {
	await load();
}

export default { isMatch, preloadMicromatch };

/**
 * @file mTLS Implementation
 * @description Mutual TLS implementation for secure service-to-service communication
 */

import * as tls from 'node:tls';
import { logWithSpan, withSpan } from '@cortex-os/telemetry';
import { type MTLSConfig, MTLSConfigSchema, MTLSError } from '../types.ts';
import { createClientSocket, loadCertificates } from './helpers.ts';

/**
 * mTLS Client for secure service-to-service communication
 */
export class MTLSClient {
	private readonly config: MTLSConfig;
	private tlsSocket?: tls.TLSSocket;

	constructor(config: MTLSConfig) {
		try {
			this.config = MTLSConfigSchema.parse(config);
		} catch (error) {
			throw new MTLSError(
				`Invalid mTLS configuration: ${error instanceof Error ? error.message : 'Unknown error'}`,
				undefined,
				{ originalError: error },
			);
		}
	}

	/**
	 * Establish mTLS connection to a server
	 */
	async connect(host: string, port: number): Promise<void> {
		return withSpan('mtls.connect', async () => {
			try {
				logWithSpan('info', 'Establishing mTLS connection', {
					host,
					port,
					serverName: this.config.serverName,
				});

				const certs = await loadCertificates(this.config);
				this.tlsSocket = await createClientSocket(
					host,
					port,
					this.config,
					certs,
				);
			} catch (error) {
				logWithSpan('error', 'Failed to establish mTLS connection', {
					error: error instanceof Error ? error.message : 'Unknown error',
					host,
					port,
				});

				throw new MTLSError(
					`Failed to establish mTLS connection: ${
						error instanceof Error ? error.message : 'Unknown error'
					}`,
					undefined,
					{ host, port, originalError: error },
				);
			}
		});
	}

	/**
	 * Send data over the mTLS connection
	 */
	async send(data: Buffer | string): Promise<void> {
		return new Promise((resolve, reject) => {
			if (!this.tlsSocket) {
				reject(new MTLSError('No active mTLS connection'));
				return;
			}

			this.tlsSocket.write(data, (error) => {
				if (error) {
					reject(
						new MTLSError(`Failed to send data: ${error.message}`, undefined, {
							originalError: error,
						}),
					);
				} else {
					resolve(void 0);
				}
			});
		});
	}

	/**
	 * Receive data from the mTLS connection
	 */
	async receive(): Promise<Buffer> {
		return new Promise((resolve, reject) => {
			if (!this.tlsSocket) {
				reject(new MTLSError('No active mTLS connection'));
				return;
			}

			const chunks: Buffer[] = [];

			const onData = (chunk: Buffer) => {
				chunks.push(chunk);
			};

			const onEnd = () => {
				cleanup();
				resolve(Buffer.concat(chunks));
			};

			const onError = (error: Error) => {
				cleanup();
				reject(
					new MTLSError(`Failed to receive data: ${error.message}`, undefined, {
						originalError: error,
					}),
				);
			};

			const cleanup = () => {
				this.tlsSocket?.removeListener('data', onData);
				this.tlsSocket?.removeListener('end', onEnd);
				this.tlsSocket?.removeListener('error', onError);
			};

			this.tlsSocket.on('data', onData);
			this.tlsSocket.once('end', onEnd);
			this.tlsSocket.once('error', onError);
		});
	}

	/**
	 * Close the mTLS connection
	 */
	async close(): Promise<void> {
		return new Promise((resolve) => {
			if (!this.tlsSocket) {
				resolve(void 0);
				return;
			}

			this.tlsSocket.end(() => {
				this.tlsSocket = undefined;
				logWithSpan('info', 'mTLS connection closed');
				resolve(void 0);
			});
		});
	}

	/**
	 * Get connection information
	 */
	getConnectionInfo(): {
		authorized: boolean;
		authorizationError?: string;
		peerCertificate?: tls.PeerCertificate;
		cipher?: tls.CipherNameAndProtocol;
	} | null {
		if (!this.tlsSocket) {
			return null;
		}

		return {
			authorized: this.tlsSocket.authorized,
			authorizationError: this.tlsSocket.authorizationError?.message,
			peerCertificate: this.tlsSocket.getPeerCertificate(),
			cipher: this.tlsSocket.getCipher(),
		};
	}
}

/**
 * mTLS Server for accepting secure connections
 */
export class MTLSServer {
	private readonly config: MTLSConfig;
	private readonly connectionHandler?: (socket: tls.TLSSocket) => void;
	private server?: tls.Server;

	constructor(
		config: MTLSConfig,
		connectionHandler?: (socket: tls.TLSSocket) => void,
	) {
		try {
			this.config = MTLSConfigSchema.parse(config);
			this.connectionHandler = connectionHandler;
		} catch (error) {
			throw new MTLSError(
				`Invalid mTLS configuration: ${error instanceof Error ? error.message : 'Unknown error'}`,
				undefined,
				{ originalError: error },
			);
		}
	}

	/**
	 * Start the mTLS server
	 */
	async listen(port: number, host = '0.0.0.0'): Promise<void> {
		return withSpan('mtls.listen', async () => {
			try {
				logWithSpan('info', 'Starting mTLS server', {
					host,
					port,
				});

				const certs = await loadCertificates(this.config);

				return new Promise((resolve, reject) => {
					this.server = tls.createServer({
						ca: certs.ca,
						cert: certs.cert,
						key: certs.key,
						requestCert: true,
						rejectUnauthorized: this.config.rejectUnauthorized,
						minVersion: this.config.minVersion,
						maxVersion: this.config.maxVersion,
					});

					this.server.on('secureConnection', (socket) => {
						logWithSpan('info', 'Secure connection established', {
							remoteAddress: socket.remoteAddress,
							remotePort: socket.remotePort,
							authorized: socket.authorized,
							authorizationError: socket.authorizationError?.message,
						});

						this.handleSecureConnection(socket);
					});

					this.server.on('error', (error) => {
						const errorMessage =
							error instanceof Error ? error.message : 'Unknown error';
						logWithSpan('error', 'mTLS server error', {
							error: errorMessage,
							host,
							port,
						});
						reject(
							new MTLSError(`mTLS server error: ${errorMessage}`, undefined, {
								host,
								port,
								originalError: error,
							}),
						);
					});

					this.server.listen(port, host, () => {
						logWithSpan('info', 'mTLS server listening', {
							host,
							port,
						});
						resolve(void 0);
					});
				});
			} catch (error) {
				logWithSpan('error', 'Failed to start mTLS server', {
					error: error instanceof Error ? error.message : 'Unknown error',
					host,
					port,
				});

				throw new MTLSError(
					`Failed to start mTLS server: ${
						error instanceof Error ? error.message : 'Unknown error'
					}`,
					undefined,
					{ host, port, originalError: error },
				);
			}
		});
	}

	/**
	 * Handle secure connection
	 */
	private handleSecureConnection(socket: tls.TLSSocket): void {
		this.connectionHandler?.(socket);

		socket.on('error', (error) => {
			const errorMessage =
				error instanceof Error ? error.message : 'Unknown error';
			logWithSpan('error', 'Secure connection error', {
				error: errorMessage,
				remoteAddress: socket.remoteAddress,
			});
		});

		socket.on('close', () => {
			logWithSpan('info', 'Secure connection closed', {
				remoteAddress: socket.remoteAddress,
			});
		});
	}

	/**
	 * Close the mTLS server
	 */
	async close(): Promise<void> {
		return new Promise((resolve) => {
			if (!this.server) {
				resolve(void 0);
				return;
			}

			this.server.close(() => {
				this.server = undefined;
				logWithSpan('info', 'mTLS server closed');
				resolve(void 0);
			});
		});
	}
}

/**
 * @file SPIFFE Client Implementation
 * @description SPIFFE Workload API client for certificate management and workload attestation
 */

import { readFileSync } from 'node:fs';
import { logWithSpan, withSpan } from '@cortex-os/telemetry';
import { Agent as UndiciAgent } from 'undici';
import { z } from 'zod';
import {
	type CertificateBundle,
	SPIFFEError,
	type SpiffeId,
	SpiffeIdSchema,
	SpiffeWorkloadResponseSchema,
	type TrustDomainConfig,
	type WorkloadIdentity,
} from '../types.js';
import { extractWorkloadPath } from '../utils/security-utils.ts';

export function convertSelectors(
	selectors: Array<{ type?: string; value?: string }>,
): Record<string, string> {
	const result: Record<string, string> = {};
	selectors.forEach((selector) => {
		if (selector.type && selector.value) {
			result[selector.type] = selector.value;
		}
	});
	return result;
}

export function buildWorkloadIdentity(
	workloadResponse: z.infer<typeof SpiffeWorkloadResponseSchema>,
): WorkloadIdentity {
	const workloadPath = extractWorkloadPath(workloadResponse.spiffe_id);
	if (!workloadPath) {
		throw new SPIFFEError(
			'Invalid SPIFFE ID format',
			workloadResponse.spiffe_id,
		);
	}

	return {
		spiffeId: workloadResponse.spiffe_id,
		trustDomain: workloadResponse.trust_domain,
		workloadPath,
		selectors: convertSelectors(workloadResponse.selectors || []),
		metadata: {
			fetchedAt: new Date(),
			trustDomain: workloadResponse.trust_domain,
		},
	};
}

export function splitPEMCertificates(pemChain: string): string[] {
	const certificates: string[] = [];
	const lines = pemChain.split('\n');
	let currentCert: string[] = [];

	for (const line of lines) {
		if (line.includes('-----BEGIN CERTIFICATE-----')) {
			currentCert = [line];
		} else if (line.includes('-----END CERTIFICATE-----')) {
			currentCert.push(line);
			certificates.push(currentCert.join('\n'));
			currentCert = [];
		} else if (currentCert.length > 0) {
			currentCert.push(line);
		}
	}

	return certificates;
}

/**
 * SPIFFE Workload API Client
 * Implements the SPIFFE Workload API for certificate retrieval and workload attestation
 */
export class SpiffeClient {
	private readonly baseUrl: string;
	private readonly timeout = 10000;
	private readonly config: TrustDomainConfig;
	private readonly certificateCache: Map<
		string,
		{ bundle: CertificateBundle; expiresAt: number }
	> = new Map();
	private readonly certificateTtl: number;
	private readonly dispatcher?: UndiciAgent;

	constructor(config: TrustDomainConfig, certificateTtl = 3600000) {
		this.config = config;
		this.certificateTtl = certificateTtl;
		this.baseUrl = `https://${config.spireServerAddress}:${config.spireServerPort}`;

		if (config.certificateFile && config.keyFile && config.caBundleFile) {
			this.dispatcher = new UndiciAgent({
				connect: {
					cert: readFileSync(config.certificateFile),
					key: readFileSync(config.keyFile),
					ca: readFileSync(config.caBundleFile),
				},
			});
		}
	}

	/**
	 * Perform a fetch request with timeout support
	 */
	private async fetchWithTimeout(
		path: string,
		init?: RequestInit,
	): Promise<Response> {
		const controller = new AbortController();
		const timeoutId = setTimeout(() => controller.abort(), this.timeout);
		try {
			const response = await fetch(`${this.baseUrl}${path}`, {
				...init,
				dispatcher: this.dispatcher,
				signal: controller.signal,
				headers: {
					'Content-Type': 'application/json',
					...(init?.headers || {}),
				},
			});
			return response;
		} finally {
			clearTimeout(timeoutId);
		}
	}

	/**
	 * Perform fetch with retry and exponential backoff
	 */
	private async fetchWithRetry(
		path: string,
		init?: RequestInit,
		retries = 3,
	): Promise<Response> {
		let attempt = 0;
		let delay = 500;

		while (true) {
			try {
				const res = await this.fetchWithTimeout(path, init);
				if (!res.ok && res.status >= 500 && attempt < retries) {
					throw new Error(`HTTP ${res.status}`);
				}
				return res;
			} catch (error) {
				if (attempt >= retries) {
					throw error;
				}
				await new Promise((r) => setTimeout(r, delay));
				delay *= 2;
				attempt++;
			}
		}
	}

	/**
	 * Fetch workload identity and certificates from SPIFFE Workload API
	 */
	async fetchWorkloadIdentity(): Promise<WorkloadIdentity> {
		return withSpan('spiffe.fetchWorkloadIdentity', async (span) => {
			try {
				logWithSpan(
					'info',
					'Fetching workload identity from SPIFFE Workload API',
					{
						trustDomain: this.config.name,
					},
					span,
				);

				const response = await this.fetchWithRetry('/workload/identity', {
					method: 'GET',
				});
				if (!response.ok) {
					throw new Error(
						`Failed to fetch workload identity: HTTP ${response.status} ${response.statusText}`,
					);
				}
				const data = await response.json();
				const workloadResponse = SpiffeWorkloadResponseSchema.parse(data);

				const workloadIdentity = buildWorkloadIdentity(workloadResponse);

				logWithSpan(
					'info',
					'Successfully fetched workload identity',
					{
						spiffeId: workloadIdentity.spiffeId,
						trustDomain: workloadIdentity.trustDomain,
					},
					span,
				);

				return workloadIdentity;
			} catch (error) {
				logWithSpan(
					'error',
					'Failed to fetch workload identity',
					{
						error: error instanceof Error ? error.message : 'Unknown error',
						trustDomain: this.config.name,
					},
					span,
				);

				throw new SPIFFEError(
					`Failed to fetch workload identity: ${
						error instanceof Error ? error.message : 'Unknown error'
					}`,
					undefined,
					{ trustDomain: this.config.name, originalError: error },
				);
			}
		});
	}

	/**
	 * Fetch SVID (SPIFFE Verifiable Identity Document) certificates
	 */
	async fetchSVID(spiffeId?: SpiffeId): Promise<CertificateBundle> {
		return withSpan('spiffe.fetchSVID', async (span) => {
			try {
				const url = new URL('/workload/svid', this.baseUrl);
				if (spiffeId) {
					url.searchParams.set('spiffe_id', spiffeId);
				}

				logWithSpan(
					'info',
					'Fetching SVID certificates',
					{
						spiffeId: spiffeId || 'default',
						trustDomain: this.config.name,
					},
					span,
				);

				const response = await this.fetchWithRetry(url.pathname + url.search, {
					method: 'GET',
				});
				if (!response.ok) {
					throw new Error(
						`Failed to fetch SVID: HTTP ${response.status} ${response.statusText}`,
					);
				}
				const data = await response.json();
				const svidResponse = z
					.object({
						svids: z.array(
							z.object({
								spiffe_id: SpiffeIdSchema,
								certificate: z.string(),
								private_key: z.string(),
								bundle: z.string(),
							}),
						),
					})
					.parse(data);

				if (svidResponse.svids.length === 0) {
					throw new SPIFFEError('No SVIDs returned from SPIFFE Workload API');
				}

				const svid = svidResponse.svids[0];

				const certificateBundle: CertificateBundle = {
					certificates: [svid.certificate],
					privateKey: svid.private_key,
					trustBundle: [svid.bundle],
				};

				this.certificateCache.set(svid.spiffe_id, {
					bundle: certificateBundle,
					expiresAt: Date.now() + this.certificateTtl,
				});

				logWithSpan(
					'info',
					'Successfully fetched SVID certificates',
					{
						spiffeId: svid.spiffe_id,
						certificateCount: certificateBundle.certificates.length,
					},
					span,
				);

				return certificateBundle;
			} catch (error) {
				logWithSpan(
					'error',
					'Failed to fetch SVID certificates',
					{
						error: error instanceof Error ? error.message : 'Unknown error',
						spiffeId: spiffeId || 'default',
					},
					span,
				);

				throw new SPIFFEError(
					`Failed to fetch SVID: ${error instanceof Error ? error.message : 'Unknown error'}`,
					spiffeId,
					{ originalError: error },
				);
			}
		});
	}

	/**
	 * Get cached certificate bundle
	 */
	getCachedCertificate(spiffeId: SpiffeId): CertificateBundle | undefined {
		const entry = this.certificateCache.get(spiffeId);
		if (entry && entry.expiresAt > Date.now()) {
			return entry.bundle;
		}
		if (entry) {
			this.certificateCache.delete(spiffeId);
		}
		return undefined;
	}

	/**
	 * Clear certificate cache
	 */
	clearCertificateCache(): void {
		this.certificateCache.clear();
		logWithSpan('info', 'Certificate cache cleared', {
			trustDomain: this.config.name,
		});
	}

	/**
	 * Validate SPIFFE ID format
	 */
	validateSpiffeId(spiffeId: string): boolean {
		return SpiffeIdSchema.safeParse(spiffeId).success;
	}

	/**
	 * Get trust bundle from SPIFFE Workload API
	 */
	async fetchTrustBundle(): Promise<string[]> {
		return withSpan('spiffe.fetchTrustBundle', async (span) => {
			try {
				logWithSpan(
					'info',
					'Fetching trust bundle',
					{
						trustDomain: this.config.name,
					},
					span,
				);

				const response = await this.fetchWithRetry('/workload/trust-bundle', {
					method: 'GET',
				});
				if (!response.ok) {
					throw new Error(
						`Failed to fetch trust bundle: HTTP ${response.status} ${response.statusText}`,
					);
				}
				const data = await response.json();
				const trustBundleResponse = z
					.object({
						trust_bundle: z.string(),
					})
					.parse(data);

				const certificates = splitPEMCertificates(
					trustBundleResponse.trust_bundle,
				);

				logWithSpan(
					'info',
					'Successfully fetched trust bundle',
					{
						certificateCount: certificates.length,
						trustDomain: this.config.name,
					},
					span,
				);

				return certificates;
			} catch (error) {
				logWithSpan(
					'error',
					'Failed to fetch trust bundle',
					{
						error: error instanceof Error ? error.message : 'Unknown error',
						trustDomain: this.config.name,
					},
					span,
				);

				throw new SPIFFEError(
					`Failed to fetch trust bundle: ${
						error instanceof Error ? error.message : 'Unknown error'
					}`,
					undefined,
					{ trustDomain: this.config.name, originalError: error },
				);
			}
		});
	}
}

/**
 * @file Security Types and Interfaces
 * @description Common types for SPIFFE/SPIRE security implementation
 */

import { z } from 'zod';

// SPIFFE ID Schema
export const SpiffeIdSchema = z
	.string()
	.regex(
		/^spiffe:\/\/[^/]+\/[^/]+.*$/,
		'SPIFFE ID must be in format spiffe://trust-domain/workload-path',
	);

// SPIFFE ID Type
export type SpiffeId = z.infer<typeof SpiffeIdSchema>;

// Workload Identity Schema
