			if (obj.path !== undefined && typeof obj.path !== 'string') return false;
			return true;
		},
	},
}));

describe('HITL (Human-in-the-Loop)', () => {
	describe('requiresApproval', () => {
		it('should return true for sensitive data classification', () => {
			const proposal = { dataClass: 'sensitive' };
			expect(requiresApproval(proposal)).toBe(true);
		});

		it('should return true for paths outside cwd', () => {
			const proposal = { path: '/etc/passwd' };
			expect(requiresApproval(proposal)).toBe(true);
		});

		it('should return false for paths inside cwd', () => {
			const cwd = process.cwd();
			const proposal = { path: `${cwd}/local-file.txt` };
			expect(requiresApproval(proposal)).toBe(false);
		});

		it('should return false for public data classification', () => {
			const proposal = { dataClass: 'public' };
			expect(requiresApproval(proposal)).toBe(false);
		});

		it('should return false for empty proposals', () => {
			expect(requiresApproval({})).toBe(false);
		});

		it('should handle invalid proposal shapes gracefully', () => {
			expect(requiresApproval(null)).toBe(false);
			expect(requiresApproval(undefined)).toBe(false);
			expect(requiresApproval('string')).toBe(false);
			expect(requiresApproval(123)).toBe(false);
			expect(requiresApproval([])).toBe(false);
		});

		it('should handle invalid property types gracefully', () => {
			expect(requiresApproval({ dataClass: 123 })).toBe(false);
			expect(requiresApproval({ path: null })).toBe(false);
			expect(requiresApproval({ dataClass: [] })).toBe(false);
		});

		it('should handle complex nested proposals', () => {
			const proposal = {
				dataClass: 'sensitive',
				path: '/safe/path',
				metadata: {
					author: 'test',
					nested: { data: true },
				},
			};
			expect(requiresApproval(proposal)).toBe(true);
		});
	});

	describe('waitForApproval', () => {
		beforeEach(() => {
			vi.clearAllTimers();
			vi.useFakeTimers();
		});

		afterEach(() => {
			vi.useRealTimers();
			resetHitl();
		});

		it('should resolve with approval when decision is submitted', async () => {
			// Register listener BEFORE invoking to catch the emitted request
			const off = onHitlRequest((req) => {
				submitDecision(req.id, true);
			});
			// Ensure we don't leak listeners
			try {
				const result = await waitForApproval('run-123', 'test-node', {
					dataClass: 'sensitive',
				});
				expect(result).toBe(true);
			} finally {
				off();
			}
		});

		it('should resolve with rejection when decision is negative', async () => {
			const off = onHitlRequest((req) => {
				submitDecision(req.id, false);
			});
			try {
				const result = await waitForApproval('run-456', 'test-node', {
					path: '/dangerous',
				});
				expect(result).toBe(false);
			} finally {
				off();
			}
		});

		it('should timeout after configured duration', async () => {
			// Set a short timeout for testing
			vi.stubEnv('CORTEX_HITL_TIMEOUT_MS', '1000');

			const approvalPromise = waitForApproval('run-timeout', 'test-node', {
				dataClass: 'sensitive',
			});

			vi.advanceTimersByTime(1000);

			await expect(approvalPromise).rejects.toThrow('HITL approval timeout');
		});

		it('should emit structured request events', async () => {
			const mockListener = vi.fn((req) => {
				// Resolve immediately to clear pending timers
				submitDecision(req.id, false);
			});
			const off = onHitlRequest(mockListener);

			// Trigger request emission
			await waitForApproval('run-event-test', 'node-1', {
				dataClass: 'sensitive',
				metadata: 'test',
			});

			try {
				expect(mockListener).toHaveBeenCalledWith(
					expect.objectContaining({
						id: expect.any(String),
						runId: 'run-event-test',
						node: 'node-1',
						proposal: expect.objectContaining({
							dataClass: 'sensitive',
							metadata: 'test',
						}),
						ts: expect.any(String),
					}),
				);
			} finally {
				off();
			}
		});
	});

	describe('submitDecision', () => {
		it('should handle valid decision submission', () => {
			// This is mainly tested through waitForApproval integration
			expect(() => submitDecision('test-id', true)).not.toThrow();
		});

		it('should handle unknown request IDs gracefully', () => {
			expect(() => submitDecision('unknown-id', false)).not.toThrow();
		});
	});

	describe('Edge cases and error conditions', () => {
		it('should handle malformed proposal data', () => {
			const malformedProposals = [
				{ dataClass: '' }, // empty string
				{ path: '' }, // empty path
				{ dataClass: 'sensitive', path: '' }, // mixed valid/invalid
				{ extraField: 'ignored', dataClass: 'public' }, // extra fields
			];

			malformedProposals.forEach((proposal) => {
				expect(() => requiresApproval(proposal)).not.toThrow();
			});
		});

		it('should maintain type safety across all operations', () => {
			// Compile-time type checking is handled by TypeScript
			// Runtime verification through systematic testing
			const validProposal = { dataClass: 'sensitive', path: '/test' };
			const result = requiresApproval(validProposal);
			expect(typeof result).toBe('boolean');
		});
	});
});

/**
 * Proactive Agent Health Monitoring System
 * Monitors agent health, performance, and availability
 */

import { EventEmitter } from 'node:events';

export interface AgentHealthMetrics {
	agentId: string;
	responseTime: number;
	successRate: number;
	errorRate: number;
	lastSeen: Date;
	consecutiveFailures: number;
	totalRequests: number;
	totalFailures: number;
	averageResponseTime: number;
	uptime: number;
	memoryUsage?: number;
	cpuUtilization?: number;
}

export interface AgentHealthThresholds {
	maxResponseTime: number;
	minSuccessRate: number;
	maxErrorRate: number;
	maxConsecutiveFailures: number;
	maxTimeSinceLastSeen: number;
	minUptime: number;
}

export interface AgentHealthStatus {
	agentId: string;
	status: 'healthy' | 'degraded' | 'unhealthy' | 'offline';
	score: number; // 0-100
	issues: string[];
	lastChecked: Date;
	metrics: AgentHealthMetrics;
	thresholds: AgentHealthThresholds;
}

export interface HealthCheckResult {
	agentId: string;
	success: boolean;
	responseTime: number;
	error?: Error;
	timestamp: Date;
}

/**
 * Agent Health Monitor for proactive health management
 */
export class AgentHealthMonitor extends EventEmitter {
	private healthMetrics = new Map<string, AgentHealthMetrics>();
	private healthStatus = new Map<string, AgentHealthStatus>();
	private healthCheckInterval?: NodeJS.Timeout;
	private cleanupInterval?: NodeJS.Timeout;

	private defaultThresholds: AgentHealthThresholds = {
		maxResponseTime: 10000, // 10 seconds
		minSuccessRate: 0.8, // 80%
		maxErrorRate: 0.2, // 20%
		maxConsecutiveFailures: 3,
		maxTimeSinceLastSeen: 60000, // 1 minute
		minUptime: 0.95, // 95%
	};

	constructor(
		private options: {
			healthCheckIntervalMs: number;
			cleanupIntervalMs: number;
			enableProactiveChecks: boolean;
			defaultThresholds?: Partial<AgentHealthThresholds>;
		} = {
			healthCheckIntervalMs: 30000, // 30 seconds
			cleanupIntervalMs: 300000, // 5 minutes
			enableProactiveChecks: true,
		},
	) {
		super();

		if (options.defaultThresholds) {
			this.defaultThresholds = {
				...this.defaultThresholds,
				...options.defaultThresholds,
			};
		}
	}

	/**
	 * Start health monitoring
	 */
	startMonitoring(): void {
		if (this.healthCheckInterval) {
			return; // Already started
		}

		this.healthCheckInterval = setInterval(async () => {
			try {
				await this.performHealthChecks();
			} catch (error) {
				this.emit('monitoringError', { error, timestamp: new Date() });
			}
		}, this.options.healthCheckIntervalMs);

		this.cleanupInterval = setInterval(() => {
			this.cleanupStaleAgents();
		}, this.options.cleanupIntervalMs);

		this.emit('monitoringStarted', { timestamp: new Date() });
	}

	/**
	 * Stop health monitoring
	 */
	stopMonitoring(): void {
		if (this.healthCheckInterval) {
			clearInterval(this.healthCheckInterval);
			this.healthCheckInterval = undefined;
		}

		if (this.cleanupInterval) {
			clearInterval(this.cleanupInterval);
			this.cleanupInterval = undefined;
		}

		this.emit('monitoringStopped', { timestamp: new Date() });
	}

	/**
	 * Register an agent for monitoring
	 */
	registerAgent(
		agentId: string,
		capabilities: string[],
		thresholds?: Partial<AgentHealthThresholds>,
	): void {
		const now = new Date();

		const metrics: AgentHealthMetrics = {
			agentId,
			responseTime: 0,
			successRate: 1.0,
			errorRate: 0.0,
			lastSeen: now,
			consecutiveFailures: 0,
			totalRequests: 0,
			totalFailures: 0,
			averageResponseTime: 0,
			uptime: 1.0,
		};

		const status: AgentHealthStatus = {
			agentId,
			status: 'healthy',
			score: 100,
			issues: [],
			lastChecked: now,
			metrics,
			thresholds: { ...this.defaultThresholds, ...thresholds },
		};

		this.healthMetrics.set(agentId, metrics);
		this.healthStatus.set(agentId, status);

		this.emit('agentRegistered', { agentId, capabilities, timestamp: now });
	}

	/**
	 * Unregister an agent from monitoring
	 */
	unregisterAgent(agentId: string): void {
		this.healthMetrics.delete(agentId);
		this.healthStatus.delete(agentId);

		this.emit('agentUnregistered', { agentId, timestamp: new Date() });
	}

	/**
	 * Record agent activity (success/failure)
	 */
	recordAgentActivity(
		agentId: string,
		result: {
			success: boolean;
			responseTime: number;
			error?: Error;
		},
	): void {
		const metrics = this.healthMetrics.get(agentId);
		if (!metrics) {
			return; // Agent not registered
		}

		const now = new Date();
		metrics.lastSeen = now;
		metrics.totalRequests++;

		// Update response time (moving average)
		metrics.responseTime = result.responseTime;
		metrics.averageResponseTime =
			(metrics.averageResponseTime * (metrics.totalRequests - 1) +
				result.responseTime) /
			metrics.totalRequests;

		if (result.success) {
			metrics.consecutiveFailures = 0;
		} else {
			metrics.totalFailures++;
			metrics.consecutiveFailures++;
		}

		// Recalculate rates
		metrics.successRate =
			(metrics.totalRequests - metrics.totalFailures) / metrics.totalRequests;
		metrics.errorRate = metrics.totalFailures / metrics.totalRequests;

		// Update health status
		this.updateAgentHealthStatus(agentId);

		this.emit('activityRecorded', {
			agentId,
			success: result.success,
			responseTime: result.responseTime,
			error: result.error,
			timestamp: now,
		});
	}

	/**
	 * Perform health checks on all registered agents
	 */
	private async performHealthChecks(): Promise<void> {
		const healthCheckPromises: Promise<void>[] = [];

		for (const [agentId] of this.healthMetrics) {
			if (this.options.enableProactiveChecks) {
				healthCheckPromises.push(this.performAgentHealthCheck(agentId));
			} else {
				// Just update status based on existing metrics
				this.updateAgentHealthStatus(agentId);
			}
		}

		await Promise.allSettled(healthCheckPromises);

		this.emit('healthCheckCompleted', {
			agentCount: this.healthMetrics.size,
			timestamp: new Date(),
		});
	}

	/**
	 * Perform health check on a specific agent
	 */
	private async performAgentHealthCheck(agentId: string): Promise<void> {
		const metrics = this.healthMetrics.get(agentId);
		if (!metrics) return;

		const startTime = Date.now();

		try {
			// Perform actual health check (ping/status check)
			const healthCheckResult = await this.pingAgent(agentId);
			const responseTime = Date.now() - startTime;

			this.recordAgentActivity(agentId, {
				success: healthCheckResult.success,
				responseTime,
				error: healthCheckResult.error,
			});

			if (!healthCheckResult.success && healthCheckResult.error) {
				this.emit('agentHealthCheckFailed', {
					agentId,
					error: healthCheckResult.error,
					responseTime,
					timestamp: new Date(),
				});
			}
		} catch (error) {
			const responseTime = Date.now() - startTime;

			this.recordAgentActivity(agentId, {
				success: false,
				responseTime,
				error: error as Error,
			});

			this.emit('agentHealthCheckError', {
				agentId,
				error,
				responseTime,
				timestamp: new Date(),
			});
		}
	}

	/**
	 * Ping an agent to check if it's responsive
	 */
	private async pingAgent(agentId: string): Promise<HealthCheckResult> {
		const timestamp = new Date();
		const startTime = Date.now();

		try {
			// Implement actual ping logic here
			// This is a placeholder that simulates a health check
			await new Promise((resolve) => setTimeout(resolve, Math.random() * 100));

			const responseTime = Date.now() - startTime;

			// Simulate occasional failures for testing
			const success = Math.random() > 0.05; // 5% failure rate

			return {
				agentId,
				success,
				responseTime,
				error: success ? undefined : new Error('Agent ping failed'),
				timestamp,
			};
		} catch (error) {
			return {
				agentId,
				success: false,
				responseTime: Date.now() - startTime,
				error: error as Error,
				timestamp,
			};
		}
	}

	/**
	 * Update health status for an agent based on current metrics
	 */
	private updateAgentHealthStatus(agentId: string): void {
		const metrics = this.healthMetrics.get(agentId);
		const status = this.healthStatus.get(agentId);

		if (!metrics || !status) return;

		const now = new Date();
		const timeSinceLastSeen = now.getTime() - metrics.lastSeen.getTime();

		// Calculate health score (0-100)
		let score = 100;
		const issues: string[] = [];

		// Check response time
		if (metrics.responseTime > status.thresholds.maxResponseTime) {
			const penalty = Math.min(
				30,
				(metrics.responseTime / status.thresholds.maxResponseTime) * 10,
			);
			score -= penalty;
			issues.push(`High response time: ${metrics.responseTime}ms`);
		}

		// Check success rate
		if (metrics.successRate < status.thresholds.minSuccessRate) {
			const penalty =
				(status.thresholds.minSuccessRate - metrics.successRate) * 100;
			score -= penalty;
			issues.push(
				`Low success rate: ${(metrics.successRate * 100).toFixed(1)}%`,
			);
		}

		// Check consecutive failures
		if (
			metrics.consecutiveFailures >= status.thresholds.maxConsecutiveFailures
		) {
			score -= 20;
			issues.push(`${metrics.consecutiveFailures} consecutive failures`);
		}

		// Check if agent is offline
		if (timeSinceLastSeen > status.thresholds.maxTimeSinceLastSeen) {
			score -= 40;
			issues.push(`Offline for ${Math.round(timeSinceLastSeen / 1000)}s`);
		}

		// Determine status based on score
		let healthStatus: AgentHealthStatus['status'];
		if (score >= 80) {
			healthStatus = 'healthy';
		} else if (score >= 60) {
			healthStatus = 'degraded';
		} else if (score >= 20) {
			healthStatus = 'unhealthy';
		} else {
			healthStatus = 'offline';
		}

		// Update status
		const previousStatus = status.status;
		status.status = healthStatus;
		status.score = Math.max(0, score);
		status.issues = issues;
		status.lastChecked = now;

		// Emit events for status changes
		if (previousStatus !== healthStatus) {
			this.emit('agentStatusChanged', {
				agentId,
				previousStatus,
				newStatus: healthStatus,
				score: status.score,
				issues,
				timestamp: now,
			});

			if (healthStatus === 'unhealthy' || healthStatus === 'offline') {
				this.emit('agentUnhealthy', {
					agentId,
					status: healthStatus,
					score: status.score,
					issues,
					metrics,
					timestamp: now,
				});
			} else if (
				previousStatus === 'unhealthy' ||
				previousStatus === 'offline'
			) {
				this.emit('agentRecovered', {
					agentId,
					previousStatus,
					newStatus: healthStatus,
					score: status.score,
					timestamp: now,
				});
			}
		}
	}

	/**
	 * Get health status for all agents
	 */
	getAgentHealthStatuses(): AgentHealthStatus[] {
		return Array.from(this.healthStatus.values());
	}

	/**
	 * Get health status for a specific agent
	 */
	getAgentHealthStatus(agentId: string): AgentHealthStatus | null {
		return this.healthStatus.get(agentId) || null;
	}

	/**
	 * Get health metrics for a specific agent
	 */
	getAgentMetrics(agentId: string): AgentHealthMetrics | null {
		return this.healthMetrics.get(agentId) || null;
	}

	/**
	 * Check if an agent is healthy
	 */
	isAgentHealthy(agentId: string): boolean {
		const status = this.healthStatus.get(agentId);
		return status ? status.status === 'healthy' : false;
	}

	/**
	 * Get list of unhealthy agents
	 */
	getUnhealthyAgents(): AgentHealthStatus[] {
		return Array.from(this.healthStatus.values()).filter(
			(status) => status.status === 'unhealthy' || status.status === 'offline',
		);
	}

	/**
	 * Get overall system health summary
	 */
	getSystemHealthSummary(): {
		totalAgents: number;
		healthy: number;
		degraded: number;
		unhealthy: number;
		offline: number;
		averageScore: number;
		systemStatus: 'healthy' | 'degraded' | 'unhealthy';
	} {
		const statuses = this.getAgentHealthStatuses();
		const totalAgents = statuses.length;

		let healthy = 0;
		let degraded = 0;
		let unhealthy = 0;
		let offline = 0;
		let totalScore = 0;

		for (const status of statuses) {
			totalScore += status.score;
			switch (status.status) {
				case 'healthy':
					healthy++;
					break;
				case 'degraded':
					degraded++;
					break;
				case 'unhealthy':
					unhealthy++;
					break;
				case 'offline':
					offline++;
					break;
			}
		}

		const averageScore = totalAgents > 0 ? totalScore / totalAgents : 100;

		let systemStatus: 'healthy' | 'degraded' | 'unhealthy';
		if (averageScore >= 80 && unhealthy === 0 && offline === 0) {
			systemStatus = 'healthy';
		} else if (averageScore >= 60) {
			systemStatus = 'degraded';
		} else {
			systemStatus = 'unhealthy';
		}

		return {
			totalAgents,
			healthy,
			degraded,
			unhealthy,
			offline,
			averageScore,
			systemStatus,
		};
	}

	/**
	 * Clean up agents that haven't been seen for a long time
	 */
	private cleanupStaleAgents(): void {
		const now = new Date();
		const staleThreshold = this.options.cleanupIntervalMs * 3; // 3x cleanup interval

		for (const [agentId, metrics] of this.healthMetrics) {
			const timeSinceLastSeen = now.getTime() - metrics.lastSeen.getTime();

			if (timeSinceLastSeen > staleThreshold) {
				this.unregisterAgent(agentId);
				this.emit('staleAgentRemoved', {
					agentId,
					timeSinceLastSeen,
					timestamp: now,
				});
			}
		}
	}

	/**
	 * Update resource usage metrics for an agent
	 */
	updateAgentResources(
		agentId: string,
		resources: {
			memoryUsage?: number;
			cpuUtilization?: number;
		},
	): void {
		const metrics = this.healthMetrics.get(agentId);
		if (metrics) {
			metrics.memoryUsage = resources.memoryUsage;
			metrics.cpuUtilization = resources.cpuUtilization;
			this.updateAgentHealthStatus(agentId);
		}
	}
}

// Global health monitor instance
let globalHealthMonitor: AgentHealthMonitor | null = null;

/**
 * Get or create global health monitor instance
 */
export function getGlobalHealthMonitor(): AgentHealthMonitor {
	if (!globalHealthMonitor) {
		globalHealthMonitor = new AgentHealthMonitor();
	}
	return globalHealthMonitor;
}

// Keep import minimal to avoid heavy deps; mirror signature from a2a bus
export type Event<T = unknown> = { type: string; payload: T };

export type PublishFn = (evt: Event) => void;

import { setAuditPublisher } from './audit';

export function makeA2APublisher(publish: PublishFn) {
	return async (evt: any) => {
		publish({ type: 'audit.event', payload: evt });
	};
}

export function configureAuditPublisherWithBus(publish: PublishFn) {
	setAuditPublisher(makeA2APublisher(publish));
}

import { promises as fs } from 'node:fs';
import path from 'node:path';

export function auditEvent(
	tool: string,
	action: string,
	ctx: { runId: string; traceId?: string },
	args: unknown,
) {
	return {
		id: crypto.randomUUID(),
		type: 'com.cortex.tool.invocation',
		time: new Date().toISOString(),
		subject: { tool, action, runId: ctx.runId, traceId: ctx.traceId ?? '' },
		data: { args },
		datacontenttype: 'application/json',
	};
}
let memoryBuffer: any[] | null = null;
let memoryBufferLimit = 1000;
let externalPublisher:
	| ((evt: ReturnType<typeof auditEvent>) => Promise<void> | void)
	| null = null;

export function enableMemoryAuditBuffer(limit = 1000) {
	memoryBuffer = [];
	memoryBufferLimit = limit;
}
export function getMemoryAuditBuffer() {
	return memoryBuffer ? [...memoryBuffer] : null;
}

export function setAuditPublisher(
	fn: (evt: ReturnType<typeof auditEvent>) => Promise<void> | void,
) {
	externalPublisher = fn;
}

function getAuditLogPath() {
	return (
		process.env.CORTEX_AUDIT_LOG ||
		path.join(process.cwd(), 'report', 'audit.log')
	);
}

export async function record(evt: ReturnType<typeof auditEvent>) {
	// append-only log, and attach to OTEL span if present
	try {
		// Lazy dynamic import to avoid hard dependency during tests / environments without OTEL.
		const otel = await import('@opentelemetry/api');
		const span = otel.trace.getSpan(otel.context.active());
		span?.addEvent('audit', {
			type: evt.type,
			subject: JSON.stringify(evt.subject),
		});
	} catch {
		// Swallow if OTEL not available.
	}
	// Append to memory buffer
	if (memoryBuffer) {
		memoryBuffer.push(evt);
		if (memoryBuffer.length > memoryBufferLimit) memoryBuffer.shift();
	}
	// Append to file as JSONL
	try {
		const file = getAuditLogPath();
		await fs.mkdir(path.dirname(file), { recursive: true });
		await fs.appendFile(file, `${JSON.stringify(evt)}\n`, 'utf8');
	} catch (e) {
		// Fallback to console if file write fails
		console.warn(
			'Audit file logging failed; falling back to console:',
			(e as any)?.message,
		);
		console.log('Audit event:', evt);
	}
	// Publish externally if configured (e.g., A2A bus, OTLP bridge)
	try {
		await externalPublisher?.(evt);
	} catch (e) {
		console.warn('Audit external publish failed:', (e as any)?.message);
	}
}

import { createHash } from 'node:crypto';
import { promises as fs } from 'node:fs';
import path from 'node:path';

export type NodeName =
	| 'plan'
	| 'gather'
	| 'critic'
	| 'synthesize'
	| 'verify'
	| 'done';

export interface Checkpoint<TState = any> {
	runId: string;
	threadId: string;
	node: NodeName;
	state: TState;
	ts: string; // ISO timestamp
	idempotencyKey?: string;
}

export interface CheckpointWithIntegrity<TState = any>
	extends Checkpoint<TState> {
	checksum: string;
	version: string;
	size: number;
}

// Current checkpoint format version
const CHECKPOINT_VERSION = '1.0.0';

/**
 * Calculate checksum for checkpoint integrity validation
 */
function calculateChecksum(checkpoint: Checkpoint): string {
	const data = JSON.stringify({
		runId: checkpoint.runId,
		threadId: checkpoint.threadId,
		node: checkpoint.node,
		state: checkpoint.state,
		ts: checkpoint.ts,
		idempotencyKey: checkpoint.idempotencyKey,
	});
	return createHash('sha256').update(data, 'utf8').digest('hex');
}

/**
 * Validate checkpoint integrity
 */
function validateCheckpointIntegrity<TState = any>(
	checkpoint: CheckpointWithIntegrity<TState>,
): boolean {
	try {
		const expectedChecksum = calculateChecksum(checkpoint);
		return expectedChecksum === checkpoint.checksum;
	} catch (error) {
		console.warn('Failed to validate checkpoint integrity:', error);
		return false;
	}
}

function getDir(): string {
	const base =
		process.env.CORTEX_CHECKPOINT_DIR ||
		path.join(process.cwd(), 'data', 'events', 'checkpoints');
	return base;
}

async function ensureDir(dir: string) {
	await fs.mkdir(dir, { recursive: true });
}

function fileFor(runId: string): string {
	const dir = getDir();
	return path.join(dir, `${runId}.jsonl`);
}

export async function saveCheckpoint<TState = any>(
	cp: Checkpoint<TState>,
): Promise<void> {
	const dir = getDir();
	await ensureDir(dir);

	// Create checkpoint with integrity validation
	const checkpointData = JSON.stringify(cp);
	const checkpointWithIntegrity: CheckpointWithIntegrity<TState> = {
		...cp,
		checksum: calculateChecksum(cp),
		version: CHECKPOINT_VERSION,
		size: checkpointData.length,
	};

	const line = `${JSON.stringify(checkpointWithIntegrity)}\n`;
	await fs.appendFile(fileFor(cp.runId), line, 'utf8');
}

/**
 * Enhanced checkpoint save with explicit integrity validation
 */
export async function saveCheckpointWithIntegrity<TState = any>(
	cp: Checkpoint<TState>,
): Promise<CheckpointWithIntegrity<TState>> {
	const dir = getDir();
	await ensureDir(dir);

	const checkpointData = JSON.stringify(cp);
	const checkpointWithIntegrity: CheckpointWithIntegrity<TState> = {
		...cp,
		checksum: calculateChecksum(cp),
		version: CHECKPOINT_VERSION,
		size: checkpointData.length,
	};

	// Validate before saving
	if (!validateCheckpointIntegrity(checkpointWithIntegrity)) {
		throw new Error(
			`Checkpoint integrity validation failed for runId: ${cp.runId}`,
		);
	}

	const line = `${JSON.stringify(checkpointWithIntegrity)}\n`;
	await fs.appendFile(fileFor(cp.runId), line, 'utf8');

	return checkpointWithIntegrity;
}

export async function loadCheckpointHistory<TState = any>(
	runId: string,
): Promise<Checkpoint<TState>[]> {
	const file = fileFor(runId);
	try {
		const content = await fs.readFile(file, 'utf8');
		const lines = content.split(/\n+/).filter(Boolean);
		const checkpoints: Checkpoint<TState>[] = [];

		for (const line of lines) {
			try {
				const parsed = JSON.parse(line);

				// Check if this is a checkpoint with integrity validation
				if (parsed.checksum && parsed.version) {
					const checkpointWithIntegrity =
						parsed as CheckpointWithIntegrity<TState>;

					// Validate integrity
					if (!validateCheckpointIntegrity(checkpointWithIntegrity)) {
						console.warn('Checkpoint integrity validation failed (skipping)', {
							runId,
							node: parsed.node,
						});
						continue;
					}

					// Extract the base checkpoint (remove integrity fields)
					const { checksum, version, size, ...checkpoint } =
						checkpointWithIntegrity;
					checkpoints.push(checkpoint);
				} else {
					// Legacy checkpoint without integrity validation
					console.warn(
						'Loading legacy checkpoint without integrity validation',
						{ runId, node: parsed.node },
					);
					checkpoints.push(parsed);
				}
			} catch (parseError) {
				console.warn('Failed to parse checkpoint line', {
					runId,
					error: parseError,
				});
				// Continue processing other checkpoints
			}
		}

		return checkpoints;
	} catch (err: any) {
		if (err && err.code === 'ENOENT') return [];
		throw err;
	}
}

/**
 * Load checkpoint history with full integrity validation
 */
export async function loadCheckpointHistoryWithIntegrity<TState = any>(
	runId: string,
): Promise<CheckpointWithIntegrity<TState>[]> {
	const file = fileFor(runId);
	try {
		const content = await fs.readFile(file, 'utf8');
		const lines = content.split(/\n+/).filter(Boolean);
		const checkpoints: CheckpointWithIntegrity<TState>[] = [];

		for (const line of lines) {
			try {
				const parsed = JSON.parse(line);

				// Only return checkpoints with integrity validation
				if (parsed.checksum && parsed.version) {
					const checkpointWithIntegrity =
						parsed as CheckpointWithIntegrity<TState>;

					if (validateCheckpointIntegrity(checkpointWithIntegrity)) {
						checkpoints.push(checkpointWithIntegrity);
					} else {
						console.warn('Checkpoint integrity validation failed', {
							runId,
							node: parsed.node,
						});
					}
				}
			} catch (parseError) {
				console.warn('Failed to parse checkpoint line', {
					runId,
					error: parseError,
				});
			}
		}

		return checkpoints;
	} catch (err: any) {
		if (err && err.code === 'ENOENT') return [];
		throw err;
	}
}

export async function loadLatestCheckpoint<TState = any>(
	runId: string,
): Promise<Checkpoint<TState> | null> {
	const history = await loadCheckpointHistory<TState>(runId);
	if (history.length === 0) return null;
	return history[history.length - 1] as Checkpoint<TState>;
}

/**
 * Load latest checkpoint with integrity validation
 */
export async function loadLatestCheckpointWithIntegrity<TState = any>(
	runId: string,
): Promise<CheckpointWithIntegrity<TState> | null> {
	const history = await loadCheckpointHistoryWithIntegrity<TState>(runId);
	if (history.length === 0) return null;
	return history[history.length - 1];
}

/**
 * Cleanup old checkpoints for a given runId
 */
export async function cleanupOldCheckpoints(
	runId: string,
	keepCount: number = 10,
): Promise<void> {
	try {
		const history = await loadCheckpointHistoryWithIntegrity(runId);

		if (history.length <= keepCount) {
			return; // Nothing to cleanup
		}

		// Keep only the latest N checkpoints
		const toKeep = history.slice(-keepCount);
		const file = fileFor(runId);

		// Rewrite file with only the checkpoints to keep
		const newContent = `${toKeep.map((checkpoint) => JSON.stringify(checkpoint)).join('\n')}\n`;

		await fs.writeFile(file, newContent, 'utf8');

		console.info('Cleaned up old checkpoints', {
			runId,
			kept: toKeep.length,
			removed: history.length - toKeep.length,
		});
	} catch (error) {
		console.warn('Failed to cleanup old checkpoints', { runId, error });
	}
}

/**
 * Verify all checkpoints in a file for integrity issues
 */
export async function verifyCheckpointFile(runId: string): Promise<{
	total: number;
	valid: number;
	invalid: number;
	legacy: number;
}> {
	const file = fileFor(runId);
	const stats = { total: 0, valid: 0, invalid: 0, legacy: 0 };

	try {
		const content = await fs.readFile(file, 'utf8');
		const lines = content.split(/\n+/).filter(Boolean);

		for (const line of lines) {
			stats.total++;
			try {
				const parsed = JSON.parse(line);

				if (parsed.checksum && parsed.version) {
					// Checkpoint with integrity validation
					if (validateCheckpointIntegrity(parsed)) {
						stats.valid++;
					} else {
						stats.invalid++;
					}
				} else {
					// Legacy checkpoint
					stats.legacy++;
				}
			} catch (_parseError) {
				stats.invalid++;
			}
		}
	} catch (err: any) {
		if (err && err.code !== 'ENOENT') {
			throw err;
		}
	}

	return stats;
}

/**
 * Circuit Breaker Implementation for Agent Failure Protection
 * Prevents cascading failures by temporarily disabling failing agents
 */

import { EventEmitter } from 'node:events';

export interface CircuitBreakerOptions {
	failureThreshold: number;
	recoveryTimeoutMs: number;
	monitoringWindowMs: number;
	halfOpenMaxCalls: number;
}

export type CircuitBreakerState = 'closed' | 'open' | 'half-open';

export interface CircuitBreakerStats {
	state: CircuitBreakerState;
	failures: number;
	successes: number;
	lastFailureTime: number;
	lastSuccessTime: number;
	totalCalls: number;
	failureRate: number;
}

/**
 * Circuit Breaker class to prevent cascading failures
 */
export class CircuitBreaker extends EventEmitter {
	private state: CircuitBreakerState = 'closed';
	private failures = 0;
	private successes = 0;
	private lastFailureTime = 0;
	private lastSuccessTime = 0;
	private totalCalls = 0;
	private halfOpenCalls = 0;
	private windowStartTime = Date.now();

	constructor(
		private readonly name: string,
		private readonly options: CircuitBreakerOptions = {
			failureThreshold: 5,
			recoveryTimeoutMs: 60000, // 1 minute
			monitoringWindowMs: 300000, // 5 minutes
			halfOpenMaxCalls: 3,
		},
	) {
		super();
	}

	/**
	 * Execute a function with circuit breaker protection
	 */
	async execute<T>(fn: () => Promise<T>): Promise<T> {
		this.totalCalls++;

		// Check if we need to transition states
		this.updateState();

		if (this.state === 'open') {
			const error = new Error(`Circuit breaker '${this.name}' is open`);
			(error as any).code = 'CIRCUIT_BREAKER_OPEN';
			this.emit('rejected', { name: this.name, error });
			throw error;
		}

		if (
			this.state === 'half-open' &&
			this.halfOpenCalls >= this.options.halfOpenMaxCalls
		) {
			const error = new Error(
				`Circuit breaker '${this.name}' half-open call limit exceeded`,
			);
			(error as any).code = 'CIRCUIT_BREAKER_HALF_OPEN_LIMIT';
			this.emit('rejected', { name: this.name, error });
			throw error;
		}

		try {
			if (this.state === 'half-open') {
				this.halfOpenCalls++;
			}

			const result = await fn();
			this.onSuccess();
			return result;
		} catch (error) {
			this.onFailure(error);
			throw error;
		}
	}

	/**
	 * Handle successful execution
	 */
	private onSuccess(): void {
		this.successes++;
		this.lastSuccessTime = Date.now();
		this.resetWindowIfNeeded();

		if (this.state === 'half-open') {
			// If we've had enough successes in half-open state, close the circuit
			if (this.halfOpenCalls >= this.options.halfOpenMaxCalls) {
				this.state = 'closed';
				this.failures = 0;
				this.halfOpenCalls = 0;
				this.emit('stateChanged', {
					name: this.name,
					previousState: 'half-open',
					currentState: 'closed',
					reason: 'successful_recovery',
				});
			}
		}

		this.emit('success', { name: this.name, stats: this.getStats() });
	}

	/**
	 * Handle failed execution
	 */
	private onFailure(error: any): void {
		this.failures++;
		this.lastFailureTime = Date.now();
		this.resetWindowIfNeeded();

		if (this.state === 'half-open') {
			// Failure in half-open state immediately opens the circuit
			this.state = 'open';
			this.halfOpenCalls = 0;
			this.emit('stateChanged', {
				name: this.name,
				previousState: 'half-open',
				currentState: 'open',
				reason: 'half_open_failure',
			});
		} else if (this.state === 'closed' && this.shouldOpen()) {
			this.state = 'open';
			this.emit('stateChanged', {
				name: this.name,
				previousState: 'closed',
				currentState: 'open',
				reason: 'threshold_exceeded',
			});
		}

		this.emit('failure', { name: this.name, error, stats: this.getStats() });
	}

	/**
	 * Update circuit breaker state based on current conditions
	 */
	private updateState(): void {
		if (this.state === 'open') {
			// Check if we should transition to half-open
			const timeSinceLastFailure = Date.now() - this.lastFailureTime;
			if (timeSinceLastFailure >= this.options.recoveryTimeoutMs) {
				this.state = 'half-open';
				this.halfOpenCalls = 0;
				this.emit('stateChanged', {
					name: this.name,
					previousState: 'open',
					currentState: 'half-open',
					reason: 'recovery_timeout_elapsed',
				});
			}
		}
	}

	/**
	 * Check if circuit should be opened based on failure threshold
	 */
	private shouldOpen(): boolean {
		const windowElapsed = Date.now() - this.windowStartTime;
		if (
			windowElapsed < this.options.monitoringWindowMs &&
			this.totalCalls < this.options.failureThreshold
		) {
			return false; // Not enough data yet
		}

		return this.failures >= this.options.failureThreshold;
	}

	/**
	 * Reset monitoring window if needed
	 */
	private resetWindowIfNeeded(): void {
		const windowElapsed = Date.now() - this.windowStartTime;
		if (windowElapsed >= this.options.monitoringWindowMs) {
			this.windowStartTime = Date.now();
			this.failures = 0;
			this.successes = 0;
			this.totalCalls = 0;
		}
	}

	/**
	 * Get current circuit breaker statistics
	 */
	getStats(): CircuitBreakerStats {
		const totalRequests = this.failures + this.successes;
		const failureRate = totalRequests > 0 ? this.failures / totalRequests : 0;

		return {
			state: this.state,
			failures: this.failures,
			successes: this.successes,
			lastFailureTime: this.lastFailureTime,
			lastSuccessTime: this.lastSuccessTime,
			totalCalls: this.totalCalls,
			failureRate,
		};
	}

	/**
	 * Manually open the circuit breaker
	 */
	open(): void {
		const previousState = this.state;
		this.state = 'open';
		this.lastFailureTime = Date.now();

		this.emit('stateChanged', {
			name: this.name,
			previousState,
			currentState: 'open',
			reason: 'manually_opened',
		});
	}

	/**
	 * Manually close the circuit breaker
	 */
	close(): void {
		const previousState = this.state;
		this.state = 'closed';
		this.failures = 0;
		this.halfOpenCalls = 0;

		this.emit('stateChanged', {
			name: this.name,
			previousState,
			currentState: 'closed',
			reason: 'manually_closed',
		});
	}

	/**
	 * Check if circuit breaker is currently open
	 */
	isOpen(): boolean {
		this.updateState();
		return this.state === 'open';
	}

	/**
	 * Check if circuit breaker is currently closed
	 */
	isClosed(): boolean {
		this.updateState();
		return this.state === 'closed';
	}

	/**
	 * Check if circuit breaker is currently half-open
	 */
	isHalfOpen(): boolean {
		this.updateState();
		return this.state === 'half-open';
	}
}

/**
 * Circuit Breaker Manager for managing multiple circuit breakers
 */
export class CircuitBreakerManager extends EventEmitter {
	private circuitBreakers = new Map<string, CircuitBreaker>();

	/**
	 * Get or create a circuit breaker for the given name
	 */
	getCircuitBreaker(
		name: string,
		options?: Partial<CircuitBreakerOptions>,
	): CircuitBreaker {
		if (!this.circuitBreakers.has(name)) {
			const circuitBreaker = new CircuitBreaker(name, {
				failureThreshold: 5,
				recoveryTimeoutMs: 60000,
				monitoringWindowMs: 300000,
				halfOpenMaxCalls: 3,
				...options,
			});

			// Forward all events from individual circuit breakers
			circuitBreaker.on('stateChanged', (event) =>
				this.emit('stateChanged', event),
			);
			circuitBreaker.on('success', (event) => this.emit('success', event));
			circuitBreaker.on('failure', (event) => this.emit('failure', event));
			circuitBreaker.on('rejected', (event) => this.emit('rejected', event));

			this.circuitBreakers.set(name, circuitBreaker);
		}

		return this.circuitBreakers.get(name)!;
	}

	/**
	 * Execute a function with circuit breaker protection
	 */
	async execute<T>(
		name: string,
		fn: () => Promise<T>,
		options?: Partial<CircuitBreakerOptions>,
	): Promise<T> {
		const circuitBreaker = this.getCircuitBreaker(name, options);
		return circuitBreaker.execute(fn);
	}

	/**
	 * Get statistics for all circuit breakers
	 */
	getAllStats(): Record<string, CircuitBreakerStats> {
		const stats: Record<string, CircuitBreakerStats> = {};
		for (const [name, circuitBreaker] of this.circuitBreakers) {
			stats[name] = circuitBreaker.getStats();
		}
		return stats;
	}

	/**
	 * Get statistics for a specific circuit breaker
	 */
	getStats(name: string): CircuitBreakerStats | null {
		const circuitBreaker = this.circuitBreakers.get(name);
		return circuitBreaker ? circuitBreaker.getStats() : null;
	}

	/**
	 * Remove a circuit breaker
	 */
	removeCircuitBreaker(name: string): boolean {
		const circuitBreaker = this.circuitBreakers.get(name);
		if (circuitBreaker) {
			circuitBreaker.removeAllListeners();
			this.circuitBreakers.delete(name);
			return true;
		}
		return false;
	}

	/**
	 * Clear all circuit breakers
	 */
	clear(): void {
		for (const circuitBreaker of this.circuitBreakers.values()) {
			circuitBreaker.removeAllListeners();
		}
		this.circuitBreakers.clear();
	}
}

// Global circuit breaker manager instance
export const circuitBreakerManager = new CircuitBreakerManager();

/**
 * Error Classification System for Intelligent Retry Strategies
 * Classifies errors to determine appropriate retry behavior
 */

export enum ErrorType {
	RETRYABLE = 'retryable',
	NON_RETRYABLE = 'non_retryable',
	RATE_LIMITED = 'rate_limited',
	RESOURCE_EXHAUSTED = 'resource_exhausted',
	AUTHENTICATION_ERROR = 'authentication_error',
	AUTHORIZATION_ERROR = 'authorization_error',
	TIMEOUT = 'timeout',
	NETWORK_ERROR = 'network_error',
	VALIDATION_ERROR = 'validation_error',
	INTERNAL_ERROR = 'internal_error',
}

export enum RetryStrategy {
	NONE = 'none',
	IMMEDIATE = 'immediate',
	LINEAR = 'linear',
	EXPONENTIAL = 'exponential',
	EXPONENTIAL_WITH_JITTER = 'exponential_with_jitter',
}

export interface ErrorClassification {
	type: ErrorType;
	retryable: boolean;
	strategy: RetryStrategy;
	baseDelayMs: number;
	maxRetries: number;
	backoffMultiplier: number;
	jitter: boolean;
	circuitBreakerEnabled: boolean;
	description: string;
}

/**
 * Error code mappings for different error types
 */
const ERROR_CODE_MAPPINGS: Record<string, ErrorType> = {
	// Network errors (retryable)
	ECONNRESET: ErrorType.NETWORK_ERROR,
	ECONNREFUSED: ErrorType.NETWORK_ERROR,
	ETIMEDOUT: ErrorType.TIMEOUT,
	ENOTFOUND: ErrorType.NETWORK_ERROR,
	EHOSTUNREACH: ErrorType.NETWORK_ERROR,
	ENETUNREACH: ErrorType.NETWORK_ERROR,

	// Permission errors (non-retryable)
	EACCES: ErrorType.AUTHORIZATION_ERROR,
	EPERM: ErrorType.AUTHORIZATION_ERROR,
	EAUTH: ErrorType.AUTHENTICATION_ERROR,

	// Resource errors
	EMFILE: ErrorType.RESOURCE_EXHAUSTED,
	ENFILE: ErrorType.RESOURCE_EXHAUSTED,
	ENOSPC: ErrorType.RESOURCE_EXHAUSTED,
	ENOMEM: ErrorType.RESOURCE_EXHAUSTED,

	// Rate limiting
	RATE_LIMIT: ErrorType.RATE_LIMITED,
	TOO_MANY_REQUESTS: ErrorType.RATE_LIMITED,
	QUOTA_EXCEEDED: ErrorType.RATE_LIMITED,

	// Circuit breaker
	CIRCUIT_BREAKER_OPEN: ErrorType.NON_RETRYABLE,
	CIRCUIT_BREAKER_HALF_OPEN_LIMIT: ErrorType.NON_RETRYABLE,

	// Validation errors (non-retryable)
	VALIDATION_ERROR: ErrorType.VALIDATION_ERROR,
	INVALID_INPUT: ErrorType.VALIDATION_ERROR,
	SCHEMA_VALIDATION_ERROR: ErrorType.VALIDATION_ERROR,
};

/**
 * HTTP status code mappings
 */
const HTTP_STATUS_MAPPINGS: Record<number, ErrorType> = {
	// 4xx Client Errors (mostly non-retryable)
	400: ErrorType.VALIDATION_ERROR,
	401: ErrorType.AUTHENTICATION_ERROR,
	403: ErrorType.AUTHORIZATION_ERROR,
	404: ErrorType.NON_RETRYABLE,
	409: ErrorType.NON_RETRYABLE,
	410: ErrorType.NON_RETRYABLE,
	422: ErrorType.VALIDATION_ERROR,
	429: ErrorType.RATE_LIMITED,

	// 5xx Server Errors (retryable)
	500: ErrorType.INTERNAL_ERROR,
	502: ErrorType.RETRYABLE,
	503: ErrorType.RETRYABLE,
	504: ErrorType.TIMEOUT,
	507: ErrorType.RESOURCE_EXHAUSTED,
};

