import { EventEmitter } from 'node:events';
import { z } from 'zod';

// Outbox message schema
export const OutboxMessageSchema = z.object({
	id: z.string().uuid(),
	aggregateType: z.string(),
	aggregateId: z.string(),
	eventType: z.string(),
	payload: z.record(z.any()),
	metadata: z.record(z.any()).optional(),
	createdAt: z.date(),
	processedAt: z.date().optional(),
	status: z.enum(['pending', 'processing', 'processed', 'failed']),
	retryCount: z.number().default(0),
	maxRetries: z.number().default(3),
	error: z.string().optional(),
});

export type OutboxMessage = z.infer<typeof OutboxMessageSchema>;

// Outbox repository interface
export interface OutboxRepository {
	save(message: OutboxMessage): Promise<void>;
	findPending(limit?: number): Promise<OutboxMessage[]>;
	markProcessed(id: string, processedAt: Date): Promise<void>;
	markFailed(id: string, error: string, retryCount: number): Promise<void>;
	getById(id: string): Promise<OutboxMessage | null>;
	delete(id: string): Promise<void>;
}

// Outbox publisher interface
export interface OutboxPublisher {
	publish(message: OutboxMessage): Promise<void>;
}

// Outbox processor configuration
export interface OutboxProcessorConfig {
	batchSize: number;
	pollingInterval: number;
	maxRetries: number;
	retryDelay: number;
}

// Outbox processor - handles publishing messages from outbox
export class OutboxProcessor extends EventEmitter {
	private isRunning = false;
	private timeoutId?: NodeJS.Timeout;

	constructor(
		private repository: OutboxRepository,
		private publisher: OutboxPublisher,
		private config: OutboxProcessorConfig = {
			batchSize: 10,
			pollingInterval: 5000,
			maxRetries: 3,
			retryDelay: 1000,
		},
	) {
		super();
	}

	async start(): Promise<void> {
		if (this.isRunning) {
			return;
		}

		this.isRunning = true;
		this.emit('started');

		const processMessages = async () => {
			if (!this.isRunning) {
				return;
			}

			try {
				await this.processBatch();
			} catch (error) {
				this.emit('error', error);
			}

			if (this.isRunning) {
				this.timeoutId = setTimeout(() => {
					processMessages().catch((error) => this.emit('error', error));
				}, this.config.pollingInterval);
			}
		};

		// Start processing immediately
		await processMessages();
	}

	stop(): void {
		this.isRunning = false;

		if (this.timeoutId) {
			clearTimeout(this.timeoutId);
			this.timeoutId = undefined;
		}

		this.emit('stopped');
	}

	private async processBatch(): Promise<void> {
		const messages = await this.repository.findPending(this.config.batchSize);

		if (messages.length === 0) {
			return;
		}

		this.emit('batchStarted', messages.length);

		for (const message of messages) {
			try {
				await this.processMessage(message);
			} catch (error) {
				await this.handleMessageError(message, error);
			}
		}

		this.emit('batchCompleted', messages.length);
	}

	private async processMessage(message: OutboxMessage): Promise<void> {
		// Publish the message
		await this.publisher.publish(message);

		// Mark as processed
		await this.repository.markProcessed(message.id, new Date());

		this.emit('messageProcessed', message.id);
	}

	private async handleMessageError(
		message: OutboxMessage,
		error: unknown,
	): Promise<void> {
		const newRetryCount = message.retryCount + 1;
		const errorMessage =
			error instanceof Error
				? error.message
				: typeof error === 'string'
					? error
					: 'Unknown error';

		if (newRetryCount >= this.config.maxRetries) {
			// Mark as failed
			await this.repository.markFailed(message.id, errorMessage, newRetryCount);
			this.emit('messageFailed', message.id, errorMessage);
		} else {
			// Mark as pending for retry
			const retryMessage = {
				...message,
				status: 'pending' as const,
				retryCount: newRetryCount,
				error: errorMessage,
			};
			await this.repository.save(retryMessage);
			this.emit('messageRetried', message.id, newRetryCount);
		}
	}
}

// Outbox service - high-level interface for adding messages
export class OutboxService {
	constructor(private repository: OutboxRepository) {}

	async addMessage(
		aggregateType: string,
		aggregateId: string,
		eventType: string,
		payload: Record<string, unknown>,
		metadata?: Record<string, unknown>,
	): Promise<string> {
		const message: OutboxMessage = {
			id: crypto.randomUUID(),
			aggregateType,
			aggregateId,
			eventType,
			payload,
			metadata,
			createdAt: new Date(),
			status: 'pending',
			retryCount: 0,
			maxRetries: 3,
		};

		await this.repository.save(message);
		return message.id;
	}

	async getMessage(id: string): Promise<OutboxMessage | null> {
		return this.repository.getById(id);
	}

	async deleteMessage(id: string): Promise<void> {
		await this.repository.delete(id);
	}
}

import path from 'node:path';
import { z } from 'zod';
export const Grant = z.object({
	tool: z.string(),
	actions: z.array(z.string()),
	args: z.record(z.any()).default({}),
	dataClass: z.enum(['public', 'internal', 'sensitive']).default('internal'),
	rate: z.object({ perMinute: z.number().int().min(1) }),
	fsScope: z.array(z.string()).default([]),
});
export type Grant = z.infer<typeof Grant>;

export async function loadGrant(id: string): Promise<Grant> {
	// read from .cortex/policy/tools/*.json, validate by schema
	const fs = await import('node:fs');
	const path = await import('node:path');
	const filePath = path.join(
		process.cwd(),
		'.cortex/policy/tools',
		`${id}.json`,
	);
	const content = fs.readFileSync(filePath, 'utf-8');
	return Grant.parse(JSON.parse(content));
}
// naive in-memory rate limiter per process
const rateMap = new Map<string, number[]>();

export function enforce(
	grant: Grant,
	action: string,
	args: Record<string, unknown>,
) {
	if (!grant.actions.includes(action)) throw new Error('action not allowed');

	// fsScope check for path args
	const p = (args?.path ?? (args as any)?.targetPath) as string | undefined;
	if (p && grant.fsScope.length > 0) {
		const rel = path.relative(process.cwd(), p);
		const allowed = grant.fsScope.some((scope) => {
			const norm = scope.endsWith('/') ? scope : `${scope}/`;
			return rel === scope || rel.startsWith(norm);
		});
		if (!allowed) throw new Error('path not within fsScope');
	}

	// simple rate limiting using sliding window of 60s
	const key = `${grant.tool}:${action}`;
	const now = Date.now();
	const windowMs = 60_000;
	const arr = (rateMap.get(key) ?? []).filter((t) => now - t < windowMs);
	if (arr.length >= grant.rate.perMinute)
		throw new Error('rate limit exceeded');
	arr.push(now);
	rateMap.set(key, arr);
	return true;
}

import { withSpan } from '@cortex-os/observability/tracing';
import { auditEvent, record } from './audit';
import {
	type Checkpoint,
	loadLatestCheckpoint,
	saveCheckpoint,
} from './checkpoints';
import { requiresApproval, waitForApproval } from './hitl';
import { enforce, loadGrant } from './policy-engine';

export type Node =
	| 'plan'
	| 'gather'
	| 'critic'
	| 'synthesize'
	| 'verify'
	| 'done';

export interface RetryPolicy {
	maxRetries: number;
	backoffMs: number;
	jitter?: boolean;
}
export interface RuntimeLimits {
	deadlineMs?: number;
}
export interface RunContext {
	runId: string;
	threadId: string;
	signal?: AbortSignal;
}

export interface SupervisorOptions {
	retry?: Partial<Record<Node, RetryPolicy>>;
	limits?: Partial<Record<Node, RuntimeLimits>>;
	handlers?: Partial<Record<Node, NodeFn>>;
}

type NodeFn = (state: any, ctx: RunContext) => Promise<any>;

// Placeholder functions - to be implemented by integrators or replaced in DI
async function planFn(state: any, _ctx: RunContext) {
	return state;
}
async function gatherFn(state: any, _ctx: RunContext) {
	return state;
}
async function criticFn(state: any, _ctx: RunContext) {
	return state;
}
async function buildArtifactProposal(_state: any) {
	return { path: '/tmp/test', content: 'test' };
}
async function commitArtifact(_proposal: any) {
	return {};
}
async function verifyFn(state: any, _ctx: RunContext) {
	return state;
}

const nodeFns: Record<Node, NodeFn> = {
	plan: planFn,
	gather: gatherFn,
	critic: criticFn,
	synthesize: async (state, ctx) => {
		const proposal = await buildArtifactProposal(state);
		if (requiresApproval(proposal)) {
			const ok = await waitForApproval(ctx.runId, 'synthesize', proposal);
			if (!ok) throw new Error('Approval denied');
		}
		enforce(await loadGrant('fs.write'), 'write', {
			path: (proposal as any).path,
		});
		record(
			auditEvent(
				'fs',
				'write',
				{ runId: ctx.runId },
				{ path: (proposal as any).path },
			),
		);
		const next = await commitArtifact(proposal);
		return next;
	},
	verify: verifyFn,
	done: async (s) => s,
};

const edges: Record<Node, Node | null> = {
	plan: 'gather',
	gather: 'critic',
	critic: 'synthesize',
	synthesize: 'verify',
	verify: 'done',
	done: null,
};

function sleep(ms: number) {
	return new Promise((r) => setTimeout(r, ms));
}

// Maximum backoff time to prevent infinite delays (30 seconds)
const MAX_BACKOFF_MS = 30000;

async function withRetry(
	_node: Node,
	fn: () => Promise<any>,
	policy?: RetryPolicy,
) {
	const rp = policy ?? { maxRetries: 0, backoffMs: 0, jitter: true };
	let attempt = 0;
	// First attempt + retries
	// attempt 0: initial, then 1..maxRetries for retries
	for (; ;) {
		try {
			return await fn();
		} catch (err) {
			if (attempt >= rp.maxRetries) throw err;
			attempt++;

			// Calculate backoff with jitter and cap
			const jitter = rp.jitter ? Math.floor(Math.random() * rp.backoffMs) : 0;
			const backoffTime = Math.min(rp.backoffMs + jitter, MAX_BACKOFF_MS);

			await sleep(backoffTime);
		}
	}
}

function withDeadline<T>(
	promise: Promise<T>,
	deadlineMs?: number,
	signal?: AbortSignal,
): Promise<T> {
	if (!deadlineMs && !signal) return promise;
	return new Promise<T>((resolve, reject) => {
		let to: NodeJS.Timeout | undefined;
		const onAbort = () => {
			clearTimeout(to as any);
			reject(new Error('Operation aborted'));
		};
		if (signal) {
			if (signal.aborted) return onAbort();
			signal.addEventListener('abort', onAbort, { once: true });
		}
		if (deadlineMs) {
			to = setTimeout(() => {
				if (signal) signal.removeEventListener('abort', onAbort);
				reject(new Error('Deadline exceeded'));
			}, deadlineMs);
		}
		promise.then(
			(v) => {
				if (to) clearTimeout(to);
				if (signal) signal.removeEventListener('abort', onAbort);
				resolve(v);
			},
			(e) => {
				if (to) clearTimeout(to);
				if (signal) signal.removeEventListener('abort', onAbort);
				reject(e);
			},
		);
	});
}

export interface RunOptions extends SupervisorOptions {
	startAt?: Node;
}

export async function runSupervisor(
	initialState: any,
	ctx: RunContext,
	opts: RunOptions = {},
) {
	// Idempotency: if a latest checkpoint exists at or after startAt, resume from it
	const latest = await loadLatestCheckpoint(ctx.runId);
	let node: Node = opts.startAt ?? 'plan';
	let state = initialState;
	if (latest) {
		// resume from the next node after the last completed checkpoint
		node = edges[latest.node] ?? 'done';
		state = latest.state;
	}

	while (node) {
		const fn = opts.handlers?.[node] ?? nodeFns[node];
		if (!fn) throw new Error(`No node function for ${node}`);

		const retry = opts.retry?.[node];
		const limits = opts.limits?.[node];

		state = await withSpan(
			`orchestration.${node}`,
			async () => {
				const exec = () => fn(state, ctx);
				const execWithRetry = () => withRetry(node, exec, retry);
				const result = await withDeadline(
					execWithRetry(),
					limits?.deadlineMs,
					ctx.signal,
				);
				// checkpoint after each successful node
				const cp: Checkpoint = {
					runId: ctx.runId,
					threadId: ctx.threadId,
					node,
					state: result,
					ts: new Date().toISOString(),
				};
				await saveCheckpoint(cp);
				return result;
			},
			{ runId: ctx.runId, attributes: { node } },
		);

		const next = edges[node];
		if (!next) break;
		node = next;
	}

	return state;
}

import { type Span, SpanStatusCode } from '@opentelemetry/api';

export interface EnhancedSpanContext {
	workflowId?: string;
	workflowName?: string;
	workflowVersion?: string;
	stepId?: string;
	stepKind?: string;
	agentId?: string;
	attempt?: number;
	resourceUsage?: {
		memoryBytes?: number;
		cpuUtilization?: number;
	};
	coordinationId?: string;
	phase?: string;
	retryPolicy?: {
		maxRetries: number;
		backoffMs: number;
	};
}

interface Histogram {
	record: (value: number, attributes: Record<string, string>) => void;
}

interface Counter {
	add: (value: number, attributes: Record<string, string>) => void;
}

export interface WorkflowMetrics {
	stepDuration: Histogram;
	coordinationDuration: Histogram;
	stepExecutions: Counter;
	coordinationFailures: Counter;
}

export function gatherSpanAttributes(
	context: EnhancedSpanContext,
): Record<string, any> {
	const attributes: Record<string, any> = {
		'orchestration.version': '1.0.0',
		'span.kind': 'internal',
	};

	if (context.workflowId) attributes['workflow.id'] = context.workflowId;
	if (context.workflowName) attributes['workflow.name'] = context.workflowName;
	if (context.workflowVersion)
		attributes['workflow.version'] = context.workflowVersion;
	if (context.stepId) attributes['workflow.step.id'] = context.stepId;
	if (context.stepKind) attributes['workflow.step.kind'] = context.stepKind;
	if (context.agentId) attributes['agent.id'] = context.agentId;
	if (context.attempt !== undefined)
		attributes['execution.attempt'] = context.attempt;
	if (context.coordinationId)
		attributes['coordination.id'] = context.coordinationId;
	if (context.phase) attributes['coordination.phase'] = context.phase;

	if (context.resourceUsage) {
		if (context.resourceUsage.memoryBytes !== undefined)
			attributes['resource.memory.bytes'] = context.resourceUsage.memoryBytes;
		if (context.resourceUsage.cpuUtilization !== undefined)
			attributes['resource.cpu.utilization'] =
				context.resourceUsage.cpuUtilization;
	}

	if (context.retryPolicy) {
		attributes['retry.max_attempts'] = context.retryPolicy.maxRetries;
		attributes['retry.backoff_ms'] = context.retryPolicy.backoffMs;
	}

	return attributes;
}

export function recordSuccessMetrics(
	name: string,
	duration: number,
	context: EnhancedSpanContext,
	metrics: WorkflowMetrics,
	span: Span,
): void {
	if (name.includes('step')) {
		metrics.stepDuration.record(duration, {
			step_kind: context.stepKind || 'unknown',
			success: 'true',
		});
		metrics.stepExecutions.add(1, {
			step_kind: context.stepKind || 'unknown',
			result: 'success',
		});
	}

	if (name.includes('coordination')) {
		metrics.coordinationDuration.record(duration, {
			phase: context.phase || 'unknown',
			success: 'true',
		});
	}

	span.addEvent(`${name}.completed`, {
		timestamp: Date.now(),
		duration_ms: duration,
		success: true,
	});

	span.setStatus({ code: SpanStatusCode.OK });
}

export function recordErrorMetrics(
	name: string,
	err: any,
	duration: number,
	context: EnhancedSpanContext,
	metrics: WorkflowMetrics,
	span: Span,
): void {
	const errorMessage = String(err?.message ?? err);

	if (name.includes('step')) {
		metrics.stepDuration.record(duration, {
			step_kind: context.stepKind || 'unknown',
			success: 'false',
		});
		metrics.stepExecutions.add(1, {
			step_kind: context.stepKind || 'unknown',
			result: 'failure',
		});
	}

	if (name.includes('coordination')) {
		metrics.coordinationDuration.record(duration, {
			phase: context.phase || 'unknown',
			success: 'false',
		});
		metrics.coordinationFailures.add(1, {
			phase: context.phase || 'unknown',
			error_type: err.code || 'unknown',
		});
	}

	span.addEvent(`${name}.failed`, {
		timestamp: Date.now(),
		duration_ms: duration,
		'error.type': err.constructor.name,
		'error.code': err.code,
		'error.message': errorMessage,
	});

	span.setStatus({ code: SpanStatusCode.ERROR, message: errorMessage });

	span.setAttributes({
		'error.type': err.constructor.name,
		'error.code': err.code || 'unknown',
		'error.message': errorMessage,
		'error.stack': err.stack,
	});
}

/**
 * @fileoverview Agent adapter for SimLab - interfaces with Cortex-OS PRP system
 * @version 1.0.0
 */

// Local import of PRP runner to avoid build dependency
import {
	type Blueprint,
	createPRPOrchestrator,
	type Neuron,
} from '../../prp-runner/src/index.js';
import { agentRequestSchema } from './schemas.js';
import type { SimScenario, SimTurn } from './types.js';

export interface AgentRequest {
	scenario: SimScenario;
	conversationHistory: SimTurn[];
	userMessage: string;
}

export interface AgentResponse {
	content: string;
	completed?: boolean;
	metadata?: Record<string, unknown>;
}

export interface PRPExecutor {
	executePRP(request: AgentRequest): Promise<AgentResponse>;
}

/**
 * Adapter that interfaces SimLab with the Cortex-OS PRP system.
 * Uses dependency injection for the underlying executor to allow
 * integration with the real kernel while providing a deterministic
 * default for tests.
 */
export class AgentAdapter {
	constructor(private executor: PRPExecutor = new BasicPRPExecutor()) { }

	async execute(request: AgentRequest): Promise<AgentResponse> {
		const parsed = agentRequestSchema.parse(request);
		try {
			const result = await this.executor.executePRP(parsed);
			return {
				content: result.content,
				completed:
					result.completed ??
					this.isGoalAchieved(result.content, parsed.scenario),
				metadata: {
					...result.metadata,
					prpVersion: '1.0.0',
					executedAt: new Date().toISOString(),
				},
			};
		} catch (error) {
			return {
				content: `I apologize, but I encountered an error processing your request: ${error instanceof Error ? error.message : 'Unknown error'
					}`,
				completed: false,
				metadata: {
					error: true,
					errorMessage:
						error instanceof Error ? error.message : 'Unknown error',
				},
			};
		}
	}

	private isGoalAchieved(response: string, scenario: SimScenario): boolean {
		const successIndicators = scenario.success_criteria || [];
		return successIndicators.some((criteria) =>
			response.toLowerCase().includes(criteria.toLowerCase()),
		);
	}
}

class BasicPRPExecutor implements PRPExecutor {
	private classify(goal: string): 'help' | 'info' | 'troubleshoot' | 'general' {
		const lower = goal.toLowerCase();
		if (/(help|assist|support)/.test(lower)) return 'help';
		if (/(information|inform|explain|question)/.test(lower)) return 'info';
		if (/(troubleshoot|problem|error|issue)/.test(lower)) return 'troubleshoot';
		return 'general';
	}

	async executePRP({
		scenario,
		conversationHistory,
		userMessage,
	}: AgentRequest): Promise<AgentResponse> {
		const goalType = this.classify(scenario.goal);
		const lowerMsg = userMessage.toLowerCase();

		// Avoid repeating identical agent responses
		const lastAgentTurn = [...conversationHistory]
			.reverse()
			.find((t) => t.role === 'agent');
		if (lastAgentTurn && lastAgentTurn.content.toLowerCase() === lowerMsg) {
			return {
				content:
					"It looks like we've already covered that. Is there anything else you need?",
			};
		}

		const successHint = scenario.success_criteria[0];

		switch (goalType) {
			case 'help': {
				const hint = successHint
					? `Let's work toward ${successHint}.`
					: 'How can I assist further?';
				return {
					content: `I'm here to help. ${hint}`,
				};
			}
			case 'info':
				if (/[?]/.test(userMessage)) {
					return {
						content:
							"Here's the information you requested: [placeholder details].",
					};
				}
				return {
					content: 'What specific information would you like to know?',
				};
			case 'troubleshoot':
				return {
					content:
						"I understand you're encountering an issue. Let's go through some steps to resolve it.",
				};
			default:
				return {
					content: 'Thank you for your message. How can I assist you today?',
				};
		}
	}
}

export class RealPRPExecutor implements PRPExecutor {
	private orchestrator = createPRPOrchestrator();

	constructor() {
		const neuron: Neuron = {
			id: 'simple',
			role: 'responder',
			phase: 'strategy',
			dependencies: [],
			tools: [],
			requiresLLM: false,
			async execute(_state: unknown, _context: unknown) {
				const startTime = new Date().toISOString();
				const endTime = new Date().toISOString();
				return {
					output: 'PRP response',
					evidence: [],
					nextSteps: [],
					artifacts: [],
					metrics: {
						startTime,
						endTime,
						duration: 0,
						toolsUsed: [],
						filesCreated: 0,
						filesModified: 0,
						commandsExecuted: 0,
					},
				};
			},
		};
		this.orchestrator.registerNeuron(neuron);
	}

	async executePRP({ scenario }: AgentRequest): Promise<AgentResponse> {
		let title = '';
		if (typeof scenario.name === 'string' && scenario.name.length > 0) {
			title = scenario.name;
		} else if (typeof scenario.goal === 'string') {
			title = scenario.goal;
		}
		const blueprint: Blueprint = {
			title,
			description: scenario.goal,
			requirements: [],
		};
		const result = await this.orchestrator.executePRPCycle(blueprint);
		const content = String(result.outputs['simple'] ?? '');
		return { content: `PRP: ${content}` };
	}
}

/**
 * @fileoverview Judge module for SimLab - evaluates simulation results
 * @version 1.0.0
 * @author Cortex-OS Team
 */

import type { SimResult, SimScenario, SimScores, SimTurn } from './types.js';

export interface JudgeConfig {
	strictMode?: boolean;
	requireEvidence?: boolean;
	weights?: {
		goal: number;
		sop: number;
		brand: number;
		factual: number;
	};
}

/**
 * Evaluates simulation conversations against scenarios and SOPs
 */
export class Judge {
	private readonly config: JudgeConfig;

	constructor(config: JudgeConfig = {}) {
		this.config = {
			strictMode: true,
			requireEvidence: true,
			weights: { goal: 0.4, sop: 0.3, brand: 0.2, factual: 0.1 },
			...config,
		};
	}

	/**
	 * Evaluate a conversation against the scenario requirements
	 */
	evaluate(
		scenario: SimScenario,
		turns: SimTurn[],
	): Promise<Omit<SimResult, 'runId' | 'timestamp'>> {
		const scores = this.calculateScores(scenario, turns);
		const failures = this.identifyFailures(scenario, turns, scores);
		const passed = this.determineOverallPass(scores, failures);
		const judgeNotes = this.generateJudgeNotes(
			scenario,
			turns,
			scores,
			failures,
		);

		return Promise.resolve({
			scenarioId: scenario.id,
			passed,
			scores,
			judgeNotes,
			failures,
			turns,
		});
	}

	/**
	 * Calculate individual scores for each evaluation dimension
	 */
	private calculateScores(scenario: SimScenario, turns: SimTurn[]): SimScores {
		const goalScore = this.evaluateGoalAchievement(scenario, turns);
		const sopScore = this.evaluateSOPAdherence(scenario, turns);
		const brandScore = this.evaluateBrandConsistency(scenario, turns);
		const factualScore = this.evaluateFactualAccuracy(scenario, turns);

		return {
			goal: Math.max(0, Math.min(1, goalScore)),
			sop: Math.max(0, Math.min(1, sopScore)),
			brand: Math.max(0, Math.min(1, brandScore)),
			factual: Math.max(0, Math.min(1, factualScore)),
		};
	}

	/**
	 * Evaluate how well the conversation achieved the stated goal
	 */
	private evaluateGoalAchievement(
		scenario: SimScenario,
		turns: SimTurn[],
	): number {
		const agentTurns = turns.filter((turn) => turn.role === 'agent');
		const successCriteria = scenario.success_criteria || [];

		if (successCriteria.length === 0) {
			return 0.5; // Default score if no criteria specified
		}

		let criteriaMetCount = 0;

		for (const criteria of successCriteria) {
			const criteriaLower = criteria.toLowerCase();
			const metInConversation = agentTurns.some((turn) =>
				turn.content.toLowerCase().includes(criteriaLower),
			);

			if (metInConversation) {
				criteriaMetCount++;
			}
		}

		return criteriaMetCount / successCriteria.length;
	}

	/**
	 * Evaluate adherence to Standard Operating Procedures
	 */
	private evaluateSOPAdherence(
		scenario: SimScenario,
		turns: SimTurn[],
	): number {
		const agentTurns = turns.filter((turn) => turn.role === 'agent');
		const sopRefs = scenario.sop_refs || [];

		if (sopRefs.length === 0) {
			return 1.0; // No SOP violations if no SOPs specified
		}

		// Check for common SOP violations
		let violations = 0;
		let totalChecks = 0;

		for (const turn of agentTurns) {
			const content = turn.content.toLowerCase();

			// Check for professional tone
			totalChecks++;
			if (this.hasUnprofessionalLanguage(content)) {
				violations++;
			}

			// Check for proper information handling
			totalChecks++;
			if (this.hasImproperInformationHandling(content)) {
				violations++;
			}

			// Check for proper escalation procedures
			totalChecks++;
			if (this.missesEscalationOpportunity(content)) {
				violations++;
			}
		}

		return totalChecks > 0 ? 1 - violations / totalChecks : 1.0;
	}

	/**
	 * Evaluate brand consistency in responses
	 */
	private evaluateBrandConsistency(
		_scenario: SimScenario,
		turns: SimTurn[],
	): number {
		const agentTurns = turns.filter((turn) => turn.role === 'agent');

		let brandScore = 1.0;

		for (const turn of agentTurns) {
			const content = turn.content.toLowerCase();

			// Check for helpful and supportive tone
			if (!this.hasHelpfulTone(content)) {
				brandScore -= 0.1;
			}

			// Check for clarity and conciseness
			if (!this.isClearAndConcise(content)) {
				brandScore -= 0.1;
			}

			// Check for appropriate empathy
			if (!this.showsAppropriateEmpathy(content)) {
				brandScore -= 0.05;
			}
		}

		return Math.max(0, brandScore);
	}

	/**
	 * Evaluate factual accuracy of information provided
	 */
	private evaluateFactualAccuracy(
		_scenario: SimScenario,
		turns: SimTurn[],
	): number {
		const agentTurns = turns.filter((turn) => turn.role === 'agent');

		// For initial implementation, assume factual accuracy unless obvious errors
		let accuracyScore = 1.0;

		for (const turn of agentTurns) {
			const content = turn.content.toLowerCase();

			// Check for obvious factual errors or uncertainties
			if (this.containsFactualUncertainty(content)) {
				accuracyScore -= 0.1;
			}

			// Check for contradictions within the conversation
			if (this.contradictsEarlierStatements(content, agentTurns)) {
				accuracyScore -= 0.2;
			}
		}

		return Math.max(0, accuracyScore);
	}

	/**
	 * Identify specific failures in the conversation
	 */
	private identifyFailures(
		_scenario: SimScenario,
		turns: SimTurn[],
		scores: SimScores,
	): string[] {
		const failures: string[] = [];

		if (scores.goal < 0.7) {
			failures.push('goal_not_achieved');
		}

		if (scores.sop < 0.8) {
			failures.push('sop_violation');
		}

		if (scores.brand < 0.8) {
			failures.push('brand_inconsistency');
		}

		if (scores.factual < 0.9) {
			failures.push('factual_inaccuracy');
		}

		// Check for required evidence if in strict mode
		if (this.config.requireEvidence && !this.hasEvidence(turns)) {
			failures.push('missing_evidence');
		}

		return failures;
	}

	/**
	 * Determine overall pass/fail based on scores and failures
	 */
	private determineOverallPass(scores: SimScores, failures: string[]): boolean {
		// Fail if any critical failures
		const criticalFailures = ['missing_evidence', 'sop_violation'];
		if (failures.some((failure) => criticalFailures.includes(failure))) {
			return false;
		}

		// Calculate weighted score
		const weights = this.config.weights ?? {
			goal: 0.4,
			sop: 0.3,
			brand: 0.2,
			factual: 0.1,
		};
		const weightedScore =
			scores.goal * weights.goal +
			scores.sop * weights.sop +
			scores.brand * weights.brand +
			scores.factual * weights.factual;

		return weightedScore >= 0.8;
	}

	/**
	 * Generate detailed judge notes
	 */
	private generateJudgeNotes(
		_scenario: SimScenario,
		turns: SimTurn[],
		scores: SimScores,
		failures: string[],
	): string {
		const notes = [
			`Goal Achievement: ${(scores.goal * 100).toFixed(1)}%`,
			`SOP Adherence: ${(scores.sop * 100).toFixed(1)}%`,
			`Brand Consistency: ${(scores.brand * 100).toFixed(1)}%`,
			`Factual Accuracy: ${(scores.factual * 100).toFixed(1)}%`,
		];

		if (failures.length > 0) {
			notes.push(`Failures: ${failures.join(', ')}`);
		}

		notes.push(`Conversation Length: ${turns.length} turns`);

		return notes.join(' | ');
	}

	// Helper methods for evaluation criteria

	private hasUnprofessionalLanguage(content: string): boolean {
		const terms = [/\bstupid\b/i, /\bdumb\b/i, /whatever/i, /\bugh\b/i];
		return terms.some((r) => r.test(content));
	}

	private hasImproperInformationHandling(content: string): boolean {
		const phrases = [/\bi guess\b/i, /probably/i, /i think maybe/i];
		return phrases.some((r) => r.test(content));
	}

	private missesEscalationOpportunity(content: string): boolean {
		const issue = /(can't|cannot|unable)/i.test(content);
		const escalation = /(escalate|transfer|manager)/i.test(content);
		return issue && !escalation;
	}

	private hasHelpfulTone(content: string): boolean {
		const indicators = [/happy to help/i, /glad to assist/i, /let me help/i];
		return indicators.some((r) => r.test(content));
	}

	private isClearAndConcise(content: string): boolean {
		return content.length < 500 && /[.!?]/.test(content);
	}

	private showsAppropriateEmpathy(content: string): boolean {
		const indicators = [
			/sorry/i,
			/understand/i,
			/apologiz(?:e|ing)/i,
			/appreciate/i,
		];
		return indicators.some((r) => r.test(content));
	}

	private containsFactualUncertainty(content: string): boolean {
		const indicators = [/i'm not sure/i, /maybe/i, /i think/i];
		return indicators.some((r) => r.test(content));
	}

	private contradictsEarlierStatements(
		content: string,
		agentTurns: SimTurn[],
	): boolean {
		const lower = content.toLowerCase();
		for (const turn of agentTurns) {
			const prev = turn.content.toLowerCase();
			if (/i can\b/.test(prev) && /i cannot\b/.test(lower)) {
				return true;
			}
			if (/i cannot\b/.test(prev) && /i can\b/.test(lower)) {
				return true;
			}
		}
		return false;
	}

	private hasEvidence(turns: SimTurn[]): boolean {
		const agentTurns = turns.filter((turn) => turn.role === 'agent');
		return agentTurns.some(
			(turn) =>
				turn.content.toLowerCase().includes('evidence') ||
				turn.content.toLowerCase().includes('source') ||
				turn.content.toLowerCase().includes('reference'),
		);
	}
}

export default Judge;

/**
 * @fileoverview Report generator for SimLab - creates batch results and summaries
 * @version 1.0.0
 * @author Cortex-OS Team
 */

import type {
	SimBatchResult,
	SimReport,
	SimResult,
	SimScores,
} from './types.js';

/**
 * Generates reports and summaries from simulation results
 */
export class SimReporter {
	/**
	 * Create a batch result from individual simulation results
	 */
	createBatchResult(batchId: string, scenarios: SimResult[]): SimBatchResult {
		const summary = this.calculateBatchSummary(scenarios);

		return {
			batchId,
			scenarios,
			summary,
			timestamp: new Date().toISOString(),
		};
	}

	/**
	 * Create a comprehensive report from multiple batch results
	 */
	createReport(batchResults: SimBatchResult[]): SimReport {
		const overall = this.calculateOverallMetrics(batchResults);

		return {
			batchResults,
			overall,
			thresholds: {
				minPassRate: 0.9,
				maxP0Failures: 0,
			},
			generatedAt: new Date().toISOString(),
		};
	}

	/**
	 * Calculate summary statistics for a batch of scenarios
	 */
	private calculateBatchSummary(scenarios: SimResult[]) {
		const totalScenarios = scenarios.length;
		const passed = scenarios.filter((s) => s.passed).length;
		const failed = totalScenarios - passed;
		const passRate = totalScenarios > 0 ? passed / totalScenarios : 0;

		// Calculate average scores
		const avgScores = this.calculateAverageScores(scenarios);

		return {
			totalScenarios,
			passed,
			failed,
			passRate,
			avgScores,
		};
	}

	/**
	 * Calculate average scores across all scenarios
	 */
	private calculateAverageScores(scenarios: SimResult[]): SimScores {
		if (scenarios.length === 0) {
			return { goal: 0, sop: 0, brand: 0, factual: 0 };
		}

		const totals = scenarios.reduce(
			(acc, scenario) => ({
				goal: acc.goal + scenario.scores.goal,
				sop: acc.sop + scenario.scores.sop,
				brand: acc.brand + scenario.scores.brand,
				factual: acc.factual + scenario.scores.factual,
			}),
			{ goal: 0, sop: 0, brand: 0, factual: 0 },
		);

		const count = scenarios.length;
		return {
			goal: totals.goal / count,
			sop: totals.sop / count,
			brand: totals.brand / count,
			factual: totals.factual / count,
		};
	}

	/**
	 * Calculate overall metrics across multiple batches
	 */
	private calculateOverallMetrics(batchResults: SimBatchResult[]) {
		const allScenarios = batchResults.flatMap((batch) => batch.scenarios);
		const totalScenarios = allScenarios.length;
		const passedScenarios = allScenarios.filter((s) => s.passed).length;
		const passRate = totalScenarios > 0 ? passedScenarios / totalScenarios : 0;

		// Count critical failures (P0)
		const criticalFailures = allScenarios.filter(
			(s) =>
				s.failures.includes('missing_evidence') ||
				s.failures.includes('sop_violation'),
		).length;

		// Calculate trends (simplified - compare latest vs previous batch)
		const trends = this.calculateTrends(batchResults);

		return {
			passRate,
			criticalFailures,
			trends,
		};
	}

	/**
	 * Calculate trend metrics comparing recent performance
	 */
	private calculateTrends(
		batchResults: SimBatchResult[],
	): Record<string, number> {
		if (batchResults.length < 2) {
			return {};
		}

		const latest = batchResults[batchResults.length - 1];
		const previous = batchResults[batchResults.length - 2];

		return {
			passRateTrend: latest.summary.passRate - previous.summary.passRate,
			goalTrend:
				latest.summary.avgScores.goal - previous.summary.avgScores.goal,
			sopTrend: latest.summary.avgScores.sop - previous.summary.avgScores.sop,
			brandTrend:
				latest.summary.avgScores.brand - previous.summary.avgScores.brand,
			factualTrend:
				latest.summary.avgScores.factual - previous.summary.avgScores.factual,
		};
	}

	/**
	 * Generate a human-readable summary of results
	 */
	generateTextSummary(report: SimReport): string {
		const { overall, batchResults } = report;
		const latestBatch = batchResults[batchResults.length - 1];

		const lines = [
			'=== SimLab Report Summary ===',
			`Overall Pass Rate: ${(overall.passRate * 100).toFixed(1)}%`,
			`Critical Failures: ${overall.criticalFailures}`,
			'',
			'=== Latest Batch ===',
			`Scenarios: ${latestBatch?.summary.totalScenarios || 0}`,
			`Passed: ${latestBatch?.summary.passed || 0}`,
			`Failed: ${latestBatch?.summary.failed || 0}`,
			`Pass Rate: ${((latestBatch?.summary.passRate || 0) * 100).toFixed(1)}%`,
			'',
			'=== Average Scores ===',
			`Goal: ${((latestBatch?.summary.avgScores.goal || 0) * 100).toFixed(1)}%`,
			`SOP: ${((latestBatch?.summary.avgScores.sop || 0) * 100).toFixed(1)}%`,
			`Brand: ${((latestBatch?.summary.avgScores.brand || 0) * 100).toFixed(1)}%`,
			`Factual: ${((latestBatch?.summary.avgScores.factual || 0) * 100).toFixed(1)}%`,
		];

		if (Object.keys(overall.trends).length > 0) {
			lines.push('', '=== Trends ===');
			Object.entries(overall.trends).forEach(([key, value]) => {
				const direction = value > 0 ? 'â†‘' : value < 0 ? 'â†“' : 'â†’';
				lines.push(
					`${key}: ${direction} ${(Math.abs(value) * 100).toFixed(1)}%`,
				);
			});
		}

		return lines.join('\n');
	}

	/**
	 * Check if results meet quality gates
	 */
	checkQualityGates(report: SimReport): {
		passed: boolean;
		failures: string[];
	} {
		const failures: string[] = [];
		const { overall, thresholds } = report;

		if (overall.passRate < thresholds.minPassRate) {
			failures.push(
				`Pass rate ${(overall.passRate * 100).toFixed(1)}% below threshold ${(thresholds.minPassRate * 100).toFixed(1)}%`,
			);
		}

		if (overall.criticalFailures > thresholds.maxP0Failures) {
			failures.push(
				`Critical failures ${overall.criticalFailures} exceed threshold ${thresholds.maxP0Failures}`,
			);
		}

		return {
			passed: failures.length === 0,
			failures,
		};
	}
}

export default SimReporter;

#!/usr/bin/env tsx

import { mkdirSync, readdirSync, readFileSync, writeFileSync } from 'node:fs';
import { basename, join } from 'node:path';
import { SimReporter } from '../report.js';
import type { SimBatchResult, SimReport } from '../types.js';

/**
 * Generate simulation reports and analytics
 * - Reads JSONL files in sim/runs
 * - Aggregates latest N batches into a report
 * - Writes sim/reports/latest.json and a timestamped archive
 */
async function generateReport() {
	console.log('ðŸ“Š Generating SimLab reports...');

	const cwd = process.cwd();
	const runsDir = join(cwd, 'sim/runs');
	const reportsDir = join(cwd, 'sim/reports');
	mkdirSync(reportsDir, { recursive: true });

	// Discover recent run files (JSON or JSONL)
	const files = safeListFiles(runsDir)
		.filter((f) => f.endsWith('.json') || f.endsWith('.jsonl'))
		.sort(); // lexicographic sort keeps date prefixes ordered

	if (files.length === 0) {
		console.warn('âš ï¸  No run files found in sim/runs â€” nothing to report.');
		return;
	}

	// Load up to the last 10 batches for trend calculation
	const take = Math.min(files.length, 10);
	const recent = files.slice(-take);

	const reporter = new SimReporter();
	const batchResults: SimBatchResult[] = [];

	for (const file of recent) {
		const abs = join(runsDir, file);
		const batch = parseBatchFromFile(abs);
		// If parsing fails or results empty, skip
		if (batch && batch.scenarios.length > 0) {
			const batchId = basename(file).replace(/\.(json|jsonl)$/i, '');
			const computed = reporter.createBatchResult(
				batchId,
				batch.scenarios as any,
			);
			batchResults.push(computed);
		}
	}

	if (batchResults.length === 0) {
		console.warn('âš ï¸  No valid batch results parsed from sim/runs.');
		return;
	}

	const report: SimReport = reporter.createReport(batchResults);

	// Write outputs
	const latestPath = join(reportsDir, 'latest.json');
	writeFileSync(latestPath, JSON.stringify(report, null, 2), 'utf-8');

	const stamp = new Date().toISOString().replace(/[:.]/g, '-');
	const archivePath = join(reportsDir, `${stamp}.json`);
	writeFileSync(archivePath, JSON.stringify(report, null, 2), 'utf-8');

	console.log(`âœ… Report written: ${latestPath}`);
	console.log(`ðŸ—‚ï¸  Archived: ${archivePath}`);
}

function safeListFiles(dir: string): string[] {
	try {
		return readdirSync(dir);
	} catch {
		return [];
	}
}

function parseBatchFromFile(absPath: string): { scenarios: any[] } | null {
	try {
		const txt = readFileSync(absPath, 'utf-8');
		if (absPath.endsWith('.jsonl')) {
			const scenarios = txt
				.split(/\r?\n/)
				.map((l) => l.trim())
				.filter(Boolean)
				.map((l) => JSON.parse(l));
			return { scenarios };
		}
		// JSON: either a batch object or array of scenarios
		const parsed = JSON.parse(txt);
		if (Array.isArray(parsed)) return { scenarios: parsed };
		if (parsed && Array.isArray(parsed.scenarios))
			return { scenarios: parsed.scenarios };
		return null;
	} catch (e) {
		console.warn(
			`âš ï¸  Failed to parse ${absPath}:`,
			e instanceof Error ? e.message : e,
		);
		return null;
	}
}

if (import.meta.url === `file://${process.argv[1]}`) {
	generateReport().catch((err) => {
		console.error(err);
		process.exit(1);
	});
}

