/**
 * @fileoverview User simulator for SimLab - generates realistic user interactions
 * @version 1.0.0
 * @author Cortex-OS Team
 */

import type { SimRunnerConfig } from './runner.js';
import type { SimScenario, SimTurn } from './types.js';

interface PersonaStyle {
	formality: 'casual' | 'professional' | 'technical';
	locale: string;
	tone: string;
}

/**
 * Simulates user behavior and responses based on personas and scenarios
 */
export class UserSimulator {
	private readonly config: SimRunnerConfig;
	private readonly rng: () => number;

	constructor(config: SimRunnerConfig) {
		this.config = config;

		// Use deterministic RNG if configured
		if (config.deterministic && config.seed) {
			this.rng = this.createSeededRNG(config.seed);
		} else {
			this.rng = Math.random;
		}
	}

	/**
	 * Generate the initial message from the user based on scenario
	 */
	generateInitialMessage(scenario: SimScenario): Promise<string> {
		const { persona, goal } = scenario;
		const style = this.getPersonaStyle(persona);
		return Promise.resolve(this.formatMessage(goal, style));
	}

	/**
	 * Generate a user response based on conversation history and agent response
	 */
	generateResponse(
		scenario: SimScenario,
		conversationHistory: SimTurn[],
		agentResponse: string,
	): Promise<string | null> {
		const { persona } = scenario;
		const turnCount = conversationHistory.length;

		// Determine if user should continue conversation
		if (this.shouldEndConversation(agentResponse, turnCount)) {
			return Promise.resolve(null);
		}

		// Generate response based on agent's message and persona
		const style = this.getPersonaStyle(persona);

		if (this.isHelpful(agentResponse)) {
			return Promise.resolve(this.generatePositiveResponse(style));
		}

		if (this.needsClarification(agentResponse)) {
			return Promise.resolve(this.generateClarificationRequest(style));
		}

		return Promise.resolve(this.generateFollowUpResponse(style));
	}

	/**
	 * Get communication style based on persona
	 */
	private getPersonaStyle(persona: SimScenario['persona']): PersonaStyle {
		return {
			formality: this.getTechFormalityLevel(persona.tech_fluency),
			locale: persona.locale,
			tone: persona.tone,
		};
	}

	/**
	 * Map tech fluency to formality level
	 */
	private getTechFormalityLevel(
		techFluency: 'low' | 'med' | 'high',
	): 'casual' | 'professional' | 'technical' {
		switch (techFluency) {
			case 'low':
				return 'casual';
			case 'med':
				return 'professional';
			case 'high':
				return 'technical';
			default:
				return 'professional';
		}
	}

	/**
	 * Format initial message based on goal and style
	 */
	private formatMessage(goal: string, style: PersonaStyle): string {
		if (style.formality === 'casual') {
			return `Hi! ${goal}`;
		}

		if (style.formality === 'technical') {
			return `I need assistance with: ${goal}`;
		}

		// Professional tone
		return `Hello, I would like help with ${goal}`;
	}

	/**
	 * Determine if conversation should end
	 */
	private shouldEndConversation(
		agentResponse: string,
		turnCount: number,
	): boolean {
		const completionIndicators = [
			/is there anything else/i,
			/glad i could help/i,
			/problem solved/i,
			/task completed/i,
			/happy to assist further/i,
		];

		const maxTurnsBias =
			this.config.timeout && this.config.timeout < 15000 ? 10 : 20;
		if (turnCount > maxTurnsBias) {
			return true;
		}

		return completionIndicators.some((r) => r.test(agentResponse));
	}

	/**
	 * Check if agent response is helpful
	 */
	private isHelpful(response: string): boolean {
		const helpfulIndicators = [
			/\bhere(?:'s| is)\b/i,
			/i can help/i,
			/solution/i,
			/answer/i,
			/information/i,
			/step-by-step/i,
		];

		return helpfulIndicators.some((r) => r.test(response));
	}

	/**
	 * Check if agent response needs clarification
	 */
	private needsClarification(response: string): boolean {
		const clarificationIndicators = [
			/could you clarify/i,
			/need more information/i,
			/be more specific/i,
			/what do you mean/i,
			/unclear/i,
		];
		return clarificationIndicators.some((r) => r.test(response));
	}

	/**
	 * Generate positive response
	 */
	private generatePositiveResponse(style: PersonaStyle): string {
		const responses = [
			'Thank you, that helps!',
			'Great, that works for me.',
			'Perfect, exactly what I needed.',
		];

		return this.selectRandomResponse(responses, style);
	}

	/**
	 * Generate clarification request
	 */
	private generateClarificationRequest(style: PersonaStyle): string {
		const responses = [
			'Could you explain that in more detail?',
			'I need a bit more information about that.',
			'Can you walk me through that step by step?',
		];

		return this.selectRandomResponse(responses, style);
	}

	/**
	 * Generate follow-up response
	 */
	private generateFollowUpResponse(style: PersonaStyle): string {
		const responses = [
			'I see. What should I do next?',
			'Okay, can you help me with the next step?',
			'I understand. Is there anything else I should know?',
		];

		return this.selectRandomResponse(responses, style);
	}

	/**
	 * Select random response (deterministic if seeded)
	 */
	private selectRandomResponse(
		responses: string[],
		style: PersonaStyle,
	): string {
		let options = responses;
		if (style.formality === 'casual') {
			options = responses.map((r) =>
				r.replace(/Thank you/gi, 'Thanks').replace(/Hello/gi, 'Hey'),
			);
		} else if (style.formality === 'technical') {
			options = responses.map((r) => `Technical: ${r}`);
		}
		const index = Math.floor(this.rng() * options.length);
		return options[index] || options[0];
	}

	/**
	 * Create a seeded random number generator for deterministic results
	 */
	private createSeededRNG(seed: number): () => number {
		let state = seed;
		return () => {
			state = (state * 9301 + 49297) % 233280;
			return state / 233280;
		};
	}
}

export default UserSimulator;

import { spawn } from 'child_process';
import { resolve } from 'path';
import { afterEach, beforeEach, describe, expect, it } from 'vitest';

describe('TDD Coach CLI', () => {
	const cliPath = resolve(__dirname, '../dist/cli/tdd-coach.js');

	it('should show help when run without arguments', async () => {
		const { stdout, stderr } = await runCLI([]);
		expect(stderr).toBe('');
		expect(stdout).toContain('Usage: tdd-coach [options] [command]');
	});

	it('should show version when --version flag is used', async () => {
		const { stdout, stderr } = await runCLI(['--version']);
		expect(stderr).toBe('');
		expect(stdout).toMatch(/\d+\.\d+\.\d+/);
	});

	it('should show help for validate command', async () => {
		const { stdout, stderr } = await runCLI(['validate', '--help']);
		expect(stderr).toBe('');
		expect(stdout).toContain('validate');
		expect(stdout).toContain('Validate changes against TDD principles');
	});

	it('should show help for status command', async () => {
		const { stdout, stderr } = await runCLI(['status', '--help']);
		expect(stderr).toBe('');
		expect(stdout).toContain('status');
		expect(stdout).toContain('Get current TDD status');
	});

	it('should show help for run-tests command', async () => {
		const { stdout, stderr } = await runCLI(['run-tests', '--help']);
		expect(stderr).toBe('');
		expect(stdout).toContain('run-tests');
		expect(stdout).toContain('Run tests and update TDD state');
	});
});

function runCLI(
	args: string[],
): Promise<{ stdout: string; stderr: string; exitCode: number }> {
	return new Promise((resolve, reject) => {
		const cliProcess = spawn('node', [cliPath, ...args], {
			cwd: process.cwd(),
			env: process.env,
		});

		let stdout = '';
		let stderr = '';

		cliProcess.stdout?.on('data', (data) => {
			stdout += data.toString();
		});

		cliProcess.stderr?.on('data', (data) => {
			stderr += data.toString();
		});

		cliProcess.on('close', (code) => {
			resolve({
				stdout,
				stderr,
				exitCode: code || 0,
			});
		});

		cliProcess.on('error', (error) => {
			reject(error);
		});
	});
}

#!/usr/bin/env node

import { Command } from 'commander';
import { createTDDCoach, type TDDCoach } from '../index.js';
import type { ChangeSet } from '../types/TDDTypes.js';
import { InterventionLevel } from '../types/TDDTypes.js';

const program = new Command();

program
	.name('tdd-coach')
	.description('TDD Coach CLI - Enforce Test-Driven Development principles')
	.version('0.1.0');

program
	.command('validate')
	.description('Validate changes against TDD principles')
	.option('-w, --workspace <path>', 'Workspace root path', process.cwd())
	.option('-f, --files <files...>', 'Files to validate')
	.option('--watch', 'Watch for changes and validate continuously')
	.action(async (options) => {
		try {
			const coach = createTDDCoach({
				workspaceRoot: options.workspace,
				config: {
					universalMode: options.watch || false,
					defaultInterventionLevel: InterventionLevel.COACHING,
					adaptiveLearning: true,
				},
			});

			if (options.watch) {
				console.log('Starting TDD Coach in watch mode...');
				await startWatchMode(coach);
			} else if (options.files) {
				await validateFiles(coach, options.files);
			} else {
				console.log('No files specified for validation');
				process.exit(1);
			}
		} catch (error) {
			console.error('Error running TDD Coach:', error);
			process.exit(1);
		}
	});

program
	.command('status')
	.description('Get current TDD status')
	.option('-w, --workspace <path>', 'Workspace root path', process.cwd())
	.action(async (options) => {
		try {
			const coach = createTDDCoach({
				workspaceRoot: options.workspace,
			});

			const status = await coach.getStatus();
			console.log(`TDD State: ${status.state}`);
			console.log(
				`Tests: ${status.testsStatus.passing} passing, ${status.testsStatus.failing} failing`,
			);
			console.log(`Coaching: ${status.coaching}`);
		} catch (error) {
			console.error('Error getting TDD status:', error);
			process.exit(1);
		}
	});

program
	.command('run-tests')
	.description('Run tests and update TDD state')
	.option('-w, --workspace <path>', 'Workspace root path', process.cwd())
	.option('-f, --files <files...>', 'Specific test files to run')
	.action(async (options) => {
		try {
			const coach = createTDDCoach({
				workspaceRoot: options.workspace,
			});

			const results = await coach.runTests(options.files);
			console.log(`Ran ${results.length} tests`);
			const passing = results.filter((r) => r.status === 'pass').length;
			const failing = results.filter((r) => r.status === 'fail').length;
			console.log(`${passing} passing, ${failing} failing`);
		} catch (error) {
			console.error('Error running tests:', error);
			process.exit(1);
		}
	});

async function validateFiles(coach: TDDCoach, files: string[]) {
	// Create a mock ChangeSet for validation
	const changeSet: ChangeSet = {
		files: files.map((file) => ({
			path: file,
			status: 'modified',
			diff: '', // In a real implementation, this would contain the actual diff
			linesAdded: 0,
			linesDeleted: 0,
		})),
		totalChanges: files.length,
		timestamp: new Date().toISOString(),
		author: 'cli-user',
	};

	const response = await coach.validateChange({
		proposedChanges: changeSet,
	});

	// Output the validation results
	console.log(`Validation Result: ${response.allowed ? 'ALLOWED' : 'BLOCKED'}`);
	console.log(`TDD State: ${response.state.current}`);
	console.log(`Coaching Level: ${response.coaching.level}`);
	console.log(`Message: ${response.coaching.message}`);

	if (response.coaching.suggestedActions.length > 0) {
		console.log('Suggested Actions:');
		response.coaching.suggestedActions.forEach((action: string) =>
			console.log(`  - ${action}`),
		);
	}

	// Exit with error code if validation failed
	if (!response.allowed) {
		process.exit(1);
	}
}

async function startWatchMode(coach: TDDCoach) {
	// Start test watching
	await coach.startTestWatching();

	console.log('TDD Coach is now watching for changes. Press Ctrl+C to exit.');

	// Keep the process alive
	process.on('SIGINT', async () => {
		console.log('\nStopping TDD Coach...');
		await coach.stopTestWatching();
		process.exit(0);
	});

	// Keep the process running
	setInterval(() => {}, 1000);
}

program.parse();

import { InterventionLevel, TDDSkillLevel } from '../types/TDDTypes.js';

export interface CoachingAction {
	type: 'block' | 'warn' | 'suggest' | 'guide' | 'allow';
	message: string;
	detailedExplanation?: string;
	suggestedNextSteps?: string[];
	learningResource?: string;
	escalationRequired?: boolean;
}

export interface CoachingSession {
	sessionId: string;
	developerId: string;
	startTime: string;
	currentPhase: string;
	interventions: CoachingIntervention[];
	skillAssessment: TDDSkillLevel;
	progressMetrics: ProgressMetrics;
}

export interface CoachingIntervention {
	timestamp: string;
	trigger: string;
	action: CoachingAction;
	developerResponse?: 'acknowledged' | 'ignored' | 'questioned';
	effectiveness?: number; // 0-1 scale
}

export interface ProgressMetrics {
	cyclesCompleted: number;
	averageCycleTime: number;
	testFirstPercentage: number;
	refactorFrequency: number;
	violationCount: number;
	skillProgression: number; // -1 to 1, negative means regression
}

export class ContextualCoach {
	private skillProfiles: Map<string, TDDSkillLevel> = new Map();
	private sessionHistory: Map<string, CoachingSession[]> = new Map();

	generateCoachingAction(
		violation: string,
		context: {
			developerId?: string;
			currentPhase: string;
			previousViolations: string[];
			timeInPhase: number;
			recentSuccess: boolean;
		},
	): CoachingAction {
		const skillLevel = this.assessDeveloperSkill(context.developerId);
		const intervention = this.determineInterventionLevel(context, skillLevel);

		switch (intervention) {
			case InterventionLevel.COACHING:
				return this.generateCoachingGuidance(violation, context, skillLevel);

			case InterventionLevel.WARNING:
				return this.generateWarning(violation, context, skillLevel);

			case InterventionLevel.BLOCKING:
				return this.generateBlock(violation, context, skillLevel);

			case InterventionLevel.SILENT:
				return this.generateSilentGuidance(violation, context);

			default:
				return this.generateGenericGuidance(violation);
		}
	}

	private assessDeveloperSkill(developerId?: string): TDDSkillLevel {
		if (!developerId) return TDDSkillLevel.BEGINNER;

		const stored = this.skillProfiles.get(developerId);
		if (stored) return stored;

		// Initialize with beginner and learn over time
		this.skillProfiles.set(developerId, TDDSkillLevel.BEGINNER);
		return TDDSkillLevel.BEGINNER;
	}

	private determineInterventionLevel(
		context: {
			previousViolations: string[];
			timeInPhase: number;
			recentSuccess: boolean;
		},
		skillLevel: TDDSkillLevel,
	): InterventionLevel {
		// Escalation logic based on context and skill
		const violationCount = context.previousViolations.length;
		const isStuck = context.timeInPhase > 30 * 60 * 1000; // 30 minutes

		if (skillLevel === TDDSkillLevel.EXPERT && context.recentSuccess) {
			return InterventionLevel.SILENT;
		}

		if (violationCount === 0) {
			return skillLevel === TDDSkillLevel.BEGINNER
				? InterventionLevel.COACHING
				: InterventionLevel.WARNING;
		}

		if (violationCount >= 3 || isStuck) {
			return InterventionLevel.BLOCKING;
		}

		if (violationCount >= 1) {
			return InterventionLevel.WARNING;
		}

		return InterventionLevel.COACHING;
	}

	private generateCoachingGuidance(
		violation: string,
		context: { currentPhase: string },
		skillLevel: TDDSkillLevel,
	): CoachingAction {
		const guidance = this.getPhaseSpecificGuidance(
			violation,
			context.currentPhase,
			skillLevel,
		);

		return {
			type: 'guide',
			message: guidance.message,
			detailedExplanation: guidance.explanation,
			suggestedNextSteps: guidance.steps,
			learningResource: guidance.resource,
		};
	}

	private generateWarning(
		violation: string,
		_context: { currentPhase: string; previousViolations: string[] },
		_skillLevel: TDDSkillLevel,
	): CoachingAction {
		const patternMatch = this.detectViolationPattern(
			_context.previousViolations,
		);

		return {
			type: 'warn',
			message: `âš ï¸ TDD Violation: ${violation}`,
			detailedExplanation: this.getViolationExplanation(violation, _skillLevel),
			suggestedNextSteps: this.getCorrectiveSteps(
				violation,
				_context.currentPhase,
			),
			escalationRequired: patternMatch.isRepeating,
		};
	}

	private generateBlock(
		violation: string,
		context: { currentPhase: string; previousViolations: string[] },
		skillLevel: TDDSkillLevel,
	): CoachingAction {
		return {
			type: 'block',
			message: `ðŸš« TDD Violation Blocked: ${violation}`,
			detailedExplanation: `Multiple violations detected. Let's get back on track with TDD fundamentals.`,
			suggestedNextSteps: [
				'Review the TDD cycle: Red â†’ Green â†’ Refactor',
				'Start with a simple failing test',
				'Implement only what makes the test pass',
				'Consider pair programming or mentoring session',
			],
			escalationRequired: true,
		};
	}

	private generateSilentGuidance(
		_violation: string,
		_context: { currentPhase: string },
	): CoachingAction {
		return {
			type: 'allow',
			message: '', // Silent for experts
			detailedExplanation: `Expert-level flexibility: ${_violation} permitted`,
			suggestedNextSteps: [],
		};
	}

	private generateGenericGuidance(violation: string): CoachingAction {
		return {
			type: 'suggest',
			message: `Consider TDD best practices: ${violation}`,
			suggestedNextSteps: [
				'Review TDD fundamentals',
				'Start with a failing test',
			],
		};
	}

	private getPhaseSpecificGuidance(
		violation: string,
		phase: string,
		skillLevel: TDDSkillLevel,
	): {
		message: string;
		explanation: string;
		steps: string[];
		resource?: string;
	} {
		const baseGuidance = {
			RED: {
				message: 'In the RED phase, focus on writing failing tests',
				explanation:
					'The RED phase is about defining what you want to build through failing tests. Implementation comes later.',
				steps: [
					'Write a test that describes the behavior you want',
					'Run the test to confirm it fails',
					"Don't implement yet - stay in RED until you have a clear failing test",
				],
				resource: undefined as string | undefined,
			},
			GREEN: {
				message:
					'In the GREEN phase, implement the minimum code to make tests pass',
				explanation:
					'The GREEN phase is about making tests pass with the simplest implementation possible. Resist the urge to over-engineer.',
				steps: [
					'Look at the failing test',
					'Write the minimal code to make it pass',
					"Run tests to confirm they're green",
					'Save optimization for the REFACTOR phase',
				],
				resource: undefined as string | undefined,
			},
			REFACTOR: {
				message:
					'In the REFACTOR phase, improve code structure while keeping tests green',
				explanation:
					'The REFACTOR phase is about improving code quality without changing behavior. Tests should remain green throughout.',
				steps: [
					'Keep all tests passing',
					'Improve code structure, readability, and performance',
					'Run tests frequently to ensure nothing breaks',
					'Focus on one improvement at a time',
				],
				resource: undefined as string | undefined,
			},
		};

		const guidance =
			baseGuidance[phase as keyof typeof baseGuidance] || baseGuidance.RED;

		// Adjust for skill level
		if (skillLevel === TDDSkillLevel.BEGINNER) {
			guidance.resource =
				'https://martinfowler.com/articles/practical-test-pyramid.html';
		}

		return guidance;
	}

	private getViolationExplanation(
		violation: string,
		skillLevel: TDDSkillLevel,
	): string {
		const explanations: Record<string, string> = {
			implementation_before_test:
				'Writing implementation before tests skips the RED phase, which is crucial for defining requirements.',
			test_after_implementation:
				'Writing tests after implementation often leads to tests that confirm existing behavior rather than drive design.',
			over_implementation:
				'Implementing more than needed violates the GREEN phase principle of minimal implementation.',
			refactor_with_failing_tests:
				'Refactoring with failing tests is dangerous and violates the safety net that TDD provides.',
		};

		const explanation =
			explanations[violation] ||
			"This action doesn't align with TDD principles.";

		if (skillLevel === TDDSkillLevel.BEGINNER) {
			return `${explanation} As you're learning TDD, strict adherence to the cycle helps build good habits.`;
		}

		return explanation;
	}

	private getCorrectiveSteps(violation: string, _phase: string): string[] {
		const steps: Record<string, string[]> = {
			implementation_before_test: [
				'Undo the implementation',
				'Write a failing test first',
				'Then implement to make it pass',
			],
			over_implementation: [
				'Remove unnecessary code',
				'Keep only what makes the current test pass',
				'Add more tests for additional behavior',
			],
			refactor_with_failing_tests: [
				'First make all tests pass',
				'Then refactor while keeping tests green',
				'Run tests frequently during refactor',
			],
		};

		return (
			steps[violation] || [
				'Return to proper TDD cycle',
				'Start with a failing test',
			]
		);
	}

	private detectViolationPattern(previousViolations: string[]): {
		isRepeating: boolean;
		pattern?: string;
	} {
		if (previousViolations.length < 2) {
			return { isRepeating: false };
		}

		// Look for repeated violations in last 3 attempts
		const recent = previousViolations.slice(-3);
		const unique = new Set(recent);

		if (unique.size === 1) {
			return {
				isRepeating: true,
				pattern: `Repeated ${recent[0]} violations`,
			};
		}

		return { isRepeating: false };
	}

	// Session management
	startSession(developerId: string): CoachingSession {
		const session: CoachingSession = {
			sessionId: this.generateSessionId(),
			developerId,
			startTime: new Date().toISOString(),
			currentPhase: 'UNCLEAR',
			interventions: [],
			skillAssessment: this.assessDeveloperSkill(developerId),
			progressMetrics: {
				cyclesCompleted: 0,
				averageCycleTime: 0,
				testFirstPercentage: 0,
				refactorFrequency: 0,
				violationCount: 0,
				skillProgression: 0,
			},
		};

		const history = this.sessionHistory.get(developerId) || [];
		history.push(session);
		this.sessionHistory.set(developerId, history);

		return session;
	}

	private generateSessionId(): string {
		return `session_${Date.now()}_${crypto.randomUUID().slice(0, 8)}`;
	}

	// Skill progression tracking
	updateSkillAssessment(developerId: string, session: CoachingSession): void {
		const metrics = session.progressMetrics;
		let newSkillLevel =
			this.skillProfiles.get(developerId) || TDDSkillLevel.BEGINNER;

		// Promote based on consistent good practices
		if (
			metrics.testFirstPercentage > 0.8 &&
			metrics.violationCount < 2 &&
			metrics.cyclesCompleted > 5
		) {
			switch (newSkillLevel) {
				case TDDSkillLevel.BEGINNER:
					newSkillLevel = TDDSkillLevel.INTERMEDIATE;
					break;
				case TDDSkillLevel.INTERMEDIATE:
					if (metrics.refactorFrequency > 0.5) {
						newSkillLevel = TDDSkillLevel.ADVANCED;
					}
					break;
				case TDDSkillLevel.ADVANCED:
					if (metrics.averageCycleTime < 300000) {
						// 5 minutes
						newSkillLevel = TDDSkillLevel.EXPERT;
					}
					break;
			}
		}

		// Demote based on persistent violations
		if (metrics.violationCount > 5 || metrics.testFirstPercentage < 0.3) {
			switch (newSkillLevel) {
				case TDDSkillLevel.EXPERT:
					newSkillLevel = TDDSkillLevel.ADVANCED;
					break;
				case TDDSkillLevel.ADVANCED:
					newSkillLevel = TDDSkillLevel.INTERMEDIATE;
					break;
				case TDDSkillLevel.INTERMEDIATE:
					newSkillLevel = TDDSkillLevel.BEGINNER;
					break;
			}
		}

		this.skillProfiles.set(developerId, newSkillLevel);
	}
}

import {
	type ChangeSet,
	type DevelopmentContext,
	TDDState,
	type TDDStateData,
	type TestResult,
	type ValidationResult,
} from '../types/TDDTypes.js';

export interface StateTransition {
	from: TDDState;
	to: TDDState;
	condition: (context: TDDValidationContext) => boolean;
	action: (context: TDDValidationContext) => Promise<void>;
}

export interface TDDValidationContext {
	currentState: TDDStateData;
	proposedChange: ChangeSet;
	testResults: TestResult[];
	developmentContext: DevelopmentContext;
}

export class TDDStateMachine {
	private stateData: TDDStateData;
	private transitions: StateTransition[];

	constructor(initialState: TDDStateData) {
		this.stateData = initialState;
		this.transitions = this.defineTransitions();
	}

	private defineTransitions(): StateTransition[] {
		return [
			{
				from: TDDState.RED,
				to: TDDState.GREEN,
				condition: (ctx) => {
					// Can transition to GREEN when:
					// 1. All tests are now passing
					// 2. The change is minimal and targeted
					const allTestsPass = ctx.testResults.every(
						(test) => test.status === 'pass',
					);
					const changeIsMinimal = this.validateMinimalChange(
						ctx.proposedChange,
						ctx.currentState.failingTests,
					);
					return allTestsPass && changeIsMinimal;
				},
				action: async (_ctx) => {
					await this.unlockImplementationFiles(
						_ctx.proposedChange.files.map((f) => f.path),
					);
					this.logTransition(
						'RED â†’ GREEN: Tests passing with minimal implementation',
					);
				},
			},
			{
				from: TDDState.GREEN,
				to: TDDState.REFACTOR,
				condition: (ctx) => {
					// Can transition to REFACTOR when:
					// 1. All tests still pass
					// 2. No new functionality being added
					const allTestsPass = ctx.testResults.every(
						(test) => test.status === 'pass',
					);
					const noNewFunctionality = this.isRefactorOnly(ctx.proposedChange);
					return allTestsPass && noNewFunctionality;
				},
				action: async (_ctx) => {
					await this.enableRefactorMode();
					this.logTransition(
						'GREEN â†’ REFACTOR: Safe to improve code structure',
					);
				},
			},
			{
				from: TDDState.REFACTOR,
				to: TDDState.RED,
				condition: (ctx) => {
					// Return to RED when:
					// 1. New test is added
					// 2. New functionality is being implemented
					const newTestAdded = this.detectNewTest(ctx.proposedChange);
					const newFunctionality = this.detectNewFunctionality(
						ctx.proposedChange,
					);
					return newTestAdded || newFunctionality;
				},
				action: async (_ctx) => {
					await this.lockImplementationFiles();
					this.logTransition('REFACTOR â†’ RED: New feature development started');
				},
			},
			{
				from: TDDState.UNCLEAR,
				to: TDDState.RED,
				condition: (ctx) => {
					// Move to RED when developer writes a failing test
					const hasFailingTest = ctx.testResults.some(
						(test) => test.status === 'fail',
					);
					const isTestFile = ctx.proposedChange.files.some((f) =>
						this.isTestFile(f.path),
					);
					return hasFailingTest && isTestFile;
				},
				action: async (ctx) => {
					await this.initiateTDDCycle();
					this.logTransition(
						'UNCLEAR â†’ RED: TDD cycle initiated with failing test',
					);
				},
			},
		];
	}

	async validateTransition(
		context: TDDValidationContext,
	): Promise<ValidationResult> {
		const currentState = this.stateData.current;

		// Find applicable transitions
		const possibleTransitions = this.transitions.filter(
			(t) => t.from === currentState,
		);

		for (const transition of possibleTransitions) {
			if (transition.condition(context)) {
				// Valid transition - execute action
				await transition.action(context);
				this.stateData.current = transition.to;
				this.stateData.timestamp = new Date().toISOString();

				return {
					approved: true,
					reason: `Valid TDD transition: ${transition.from} â†’ ${transition.to}`,
				};
			}
		}

		// No valid transition found - provide coaching
		return this.generateCoachingGuidance(context);
	}

	private generateCoachingGuidance(
		context: TDDValidationContext,
	): ValidationResult {
		const currentState = this.stateData.current;

		switch (currentState) {
			case TDDState.RED:
				return this.validateRedPhaseChange(context);
			case TDDState.GREEN:
				return this.validateGreenPhaseChange(context);
			case TDDState.REFACTOR:
				return this.validateRefactorChange(context);
			case TDDState.UNCLEAR:
				return this.validateUnclearPhaseChange(context);
			default:
				return {
					approved: false,
					reason: 'Unknown TDD state - please start with a failing test',
				};
		}
	}

	private validateRedPhaseChange(
		context: TDDValidationContext,
	): ValidationResult {
		const { proposedChange } = context;

		// RED phase: Only test files should be modified
		const nonTestFiles = proposedChange.files.filter(
			(file) => !this.isTestFile(file.path),
		);

		if (nonTestFiles.length > 0) {
			return {
				approved: false,
				reason: 'RED phase: Cannot modify implementation files',
				suggestion: 'Write failing tests first, then implement',
				blockedFiles: nonTestFiles.map((f) => f.path),
			};
		}

		// Validate that new tests actually fail
		const hasFailingTests = context.testResults.some(
			(test) => test.status === 'fail',
		);
		if (!hasFailingTests) {
			return {
				approved: false,
				reason: 'RED phase: Tests must fail before implementation',
				suggestion:
					'Write a test that fails for the feature you want to implement',
			};
		}

		return {
			approved: true,
			reason: 'Valid RED phase: Writing failing tests',
		};
	}

	private validateGreenPhaseChange(
		context: TDDValidationContext,
	): ValidationResult {
		const { proposedChange, testResults } = context;

		// GREEN phase: Implementation changes must be minimal and targeted
		const failingTests = testResults.filter((test) => test.status === 'fail');

		if (failingTests.length === 0) {
			return {
				approved: false,
				reason: 'GREEN phase: No failing tests to make pass',
				suggestion: 'Add a failing test first, or move to REFACTOR phase',
			};
		}

		const minimality = this.checkMinimality(proposedChange, failingTests);
		if (!minimality.isMinimal) {
			return {
				approved: false,
				reason: 'GREEN phase: Change is not minimal',
				suggestion: minimality.suggestion,
				overImplementation: minimality.excessLines,
			};
		}

		return {
			approved: true,
			reason: 'Valid GREEN phase: Minimal implementation to make tests pass',
		};
	}

	private validateRefactorChange(
		context: TDDValidationContext,
	): ValidationResult {
		const { proposedChange, testResults } = context;

		// REFACTOR phase: All tests must remain green
		const failingTests = testResults.filter((test) => test.status === 'fail');
		if (failingTests.length > 0) {
			return {
				approved: false,
				reason: 'REFACTOR phase: Tests are failing',
				suggestion: 'Fix failing tests before refactoring',
			};
		}

		// Must be refactoring, not new functionality
		if (!this.isRefactorOnly(proposedChange)) {
			return {
				approved: false,
				reason: 'REFACTOR phase: Cannot add new functionality',
				suggestion: 'Write a failing test first for new features',
			};
		}

		return {
			approved: true,
			reason: 'Valid REFACTOR phase: Improving code while keeping tests green',
		};
	}

	private validateUnclearPhaseChange(
		_context: TDDValidationContext,
	): ValidationResult {
		return {
			approved: false,
			reason: 'TDD state unclear - please start with a failing test',
			suggestion:
				'Write a test that fails for the feature you want to implement',
		};
	}

	// Helper methods
	private validateMinimalChange(
		change: ChangeSet,
		_failingTests: TestResult[],
	): boolean {
		// Implementation: Check if change is minimal relative to failing tests
		// This would analyze code coverage and change scope
		return change.totalChanges < 50; // Simplified for now
	}

	private isRefactorOnly(change: ChangeSet): boolean {
		// Implementation: Analyze if changes are purely structural
		// Look for patterns like variable renames, method extractions, etc.
		return !change.files.some((f) => f.linesAdded > f.linesDeleted * 1.2);
	}

	private detectNewTest(change: ChangeSet): boolean {
		return change.files.some(
			(f) =>
				(this.isTestFile(f.path) && f.status === 'added') ||
				(f.status === 'modified' && f.linesAdded > 0),
		);
	}

	private detectNewFunctionality(change: ChangeSet): boolean {
		return change.files.some(
			(f) => !this.isTestFile(f.path) && f.linesAdded > 0,
		);
	}

	private isTestFile(path: string): boolean {
		return (
			/\.(test|spec)\.(ts|js|py|rs)$/.test(path) ||
			/test_.*\.py$/.test(path) ||
			/.*_test\.(go|rs)$/.test(path)
		);
	}

	private checkMinimality(
		change: ChangeSet,
		_failingTests: TestResult[],
	): {
		isMinimal: boolean;
		suggestion: string;
		excessLines?: string[];
	} {
		// Simplified minimality check
		if (change.totalChanges > 100) {
			return {
				isMinimal: false,
				suggestion:
					'Break this into smaller changes - make one test pass at a time',
				excessLines: change.files
					.filter((f) => f.linesAdded > 20)
					.map((f) => f.path),
			};
		}

		return {
			isMinimal: true,
			suggestion: 'Change looks appropriately minimal',
		};
	}

	// State management methods
	async unlockImplementationFiles(files: string[]): Promise<void> {
		// Implementation: Grant write permissions to specific files
		console.log(`Unlocking implementation files: ${files.join(', ')}`);
	}

	async enableRefactorMode(): Promise<void> {
		// Implementation: Enable broader file access for refactoring
		console.log(
			'Enabling refactor mode - all files unlocked for structural changes',
		);
	}

	async lockImplementationFiles(): Promise<void> {
		// Implementation: Restrict write access to test files only
		console.log('Locking implementation files - test-only mode active');
	}

	async initiateTDDCycle(): Promise<void> {
		// Implementation: Set up new TDD cycle
		console.log('Initiating new TDD cycle');
	}

	private logTransition(message: string): void {
		console.log(`[TDD State Machine] ${message}`);
	}

	// Public getters
	getCurrentState(): TDDStateData {
		return { ...this.stateData };
	}

	getStateHistory(): string[] {
		// Implementation: Return state transition history
		return [];
	}
}

import * as fs from 'node:fs';
import type { TestResult } from '../types/TDDTypes.js';
import { BaseTestReporter } from './BaseTestReporter.js';

export class PytestReporter extends BaseTestReporter {
	name = 'pytest';
	language = 'python';

	detectsTestFiles(filePath: string): boolean {
		return (
			/test_.*\.py$/.test(filePath) ||
			/_test\.py$/.test(filePath) ||
			filePath.includes('test_') ||
			filePath.includes('conftest.py')
		);
	}

	async runTests(filePaths?: string[]): Promise<TestResult[]> {
		// Check if we're in mock mode (for testing) to avoid spawning real processes
		if (process.env.NODE_ENV === 'test' || this.config.mockMode) {
			return this.createMockPytestResults(filePaths);
		}

		const args = [
			'--json-report',
			'--json-report-file=/tmp/pytest-report.json',
		];

		if (filePaths && filePaths.length > 0) {
			args.push(...filePaths);
		}

		if (this.config.coverage) {
			args.push('--cov');
		}

		try {
			await this.executeCommand('python', ['-m', 'pytest', ...args]);

			// Read the JSON report file
			const reportPath = '/tmp/pytest-report.json';

			if (fs.existsSync(reportPath)) {
				const reportData = fs.readFileSync(reportPath, 'utf8');
				return this.parsePytestOutput(reportData);
			}

			return [];
		} catch (error) {
			console.error('Pytest execution failed:', error);
			return [];
		}
	}

	private parsePytestOutput(output: string): TestResult[] {
		const results: TestResult[] = [];

		try {
			const data = JSON.parse(output);

			if (data.tests) {
				for (const test of data.tests) {
					results.push({
						id: test.nodeid,
						name: test.test || test.nodeid.split('::').pop() || 'unknown',
						status:
							test.outcome === 'passed'
								? 'pass'
								: test.outcome === 'failed'
									? 'fail'
									: 'skip',
						duration: test.duration || 0,
						file: test.file || 'unknown',
						line: test.lineno,
						error:
							test.call?.longrepr ||
							test.setup?.longrepr ||
							test.teardown?.longrepr,
						stack: test.call?.traceback?.join('\n') || undefined,
					});
				}
			}
		} catch (error) {
			console.warn('Failed to parse pytest output:', error);
		}

		return results;
	}

	private createMockPytestResults(filePaths?: string[]): TestResult[] {
		// Create mock test results for testing purposes
		return [
			{
				id: 'test_mock.py::test_example',
				name: 'test_example',
				status: 'pass',
				duration: 25,
				file: filePaths?.[0] || 'test_mock.py',
				line: 5,
			},
		];
	}
}

export class RustTestReporter extends BaseTestReporter {
	name = 'rust-test';
	language = 'rust';

	detectsTestFiles(filePath: string): boolean {
		return (
			filePath.endsWith('.rs') &&
			(filePath.includes('test') ||
				filePath.includes('lib.rs') ||
				filePath.includes('main.rs'))
		);
	}

	async runTests(filePaths?: string[]): Promise<TestResult[]> {
		// Check if we're in mock mode (for testing) to avoid spawning real processes
		if (process.env.NODE_ENV === 'test' || this.config.mockMode) {
			return this.createMockRustResults(filePaths);
		}

		const args = ['test', '--', '--format=json'];

		if (filePaths && filePaths.length > 0) {
			// Rust doesn't support running specific files easily
			// Filter would need to be done via test name patterns
			const testNames = filePaths
				.map((path) => path.replace(/\.rs$/, '').replace(/.*\//, ''))
				.join('|');
			args.push(testNames);
		}

		try {
			const { stdout } = await this.executeCommand('cargo', args);
			return this.parseRustTestOutput(stdout);
		} catch (error) {
			console.error('Rust test execution failed:', error);
			return [];
		}
	}

	private createMockRustResults(filePaths?: string[]): TestResult[] {
		// Create mock test results for testing purposes
		return [
			{
				id: 'test_mock.rs::test_example',
				name: 'test_example',
				status: 'pass',
				duration: 30,
				file: filePaths?.[0] || 'test_mock.rs',
				line: 8,
			},
		];
	}

	private parseRustTestOutput(output: string): TestResult[] {
		const results: TestResult[] = [];

		try {
			const lines = output.split('\n').filter((line) => line.trim());

			for (const line of lines) {
				if (line.startsWith('{')) {
					try {
						const data = JSON.parse(line);

						if (data.type === 'test' && data.event === 'ok') {
							results.push({
								id: data.name,
								name: data.name,
								status: 'pass',
								duration: data.exec_time || 0,
								file: 'unknown', // Rust doesn't provide file info easily
								line: undefined,
								error: undefined,
								stack: undefined,
							});
						} else if (data.type === 'test' && data.event === 'failed') {
							results.push({
								id: data.name,
								name: data.name,
								status: 'fail',
								duration: data.exec_time || 0,
								file: 'unknown',
								line: undefined,
								error: data.stdout || 'Test failed',
								stack: undefined,
							});
						}
					} catch {
						// ignore JSON parse errors for individual lines
					}
				}
			}
		} catch (error) {
			console.warn('Failed to parse Rust test output:', error);
		}

		return results;
	}
}

export class JestReporter extends BaseTestReporter {
	name = 'jest';
	language = 'javascript';

	detectsTestFiles(filePath: string): boolean {
		return (
			/\.(test|spec)\.(js|ts|jsx|tsx)$/.test(filePath) ||
			filePath.includes('__tests__') ||
			filePath.includes('jest.config.')
		);
	}

	async runTests(filePaths?: string[]): Promise<TestResult[]> {
		const args = ['test', '--json'];

		if (filePaths && filePaths.length > 0) {
			args.push(...filePaths);
		}

		if (this.config.coverage) {
			args.push('--coverage');
		}

		try {
			const { stdout } = await this.executeCommand('npm', ['run', ...args]);
			return this.parseJestOutput(stdout);
		} catch (error) {
			console.error('Jest execution failed:', error);
			return [];
		}
	}

	private parseJestOutput(output: string): TestResult[] {
		const results: TestResult[] = [];

		try {
			const data = JSON.parse(output);

			if (data.testResults) {
				for (const fileResult of data.testResults) {
					if (fileResult.assertionResults) {
						for (const assertion of fileResult.assertionResults) {
							results.push({
								id: `${fileResult.name}::${assertion.title}`,
								name: assertion.title,
								status:
									assertion.status === 'passed'
										? 'pass'
										: assertion.status === 'failed'
											? 'fail'
											: 'skip',
								duration: assertion.duration || 0,
								file: fileResult.name,
								line: assertion.location?.line,
								error: assertion.failureMessages?.[0],
								stack: assertion.failureDetails?.[0]?.stack,
							});
						}
					}
				}
			}
		} catch (error) {
			console.warn('Failed to parse Jest output:', error);
		}

		return results;
	}
}

export class GoTestReporter extends BaseTestReporter {
	name = 'go-test';
	language = 'go';

	detectsTestFiles(filePath: string): boolean {
		return /_test\.go$/.test(filePath) || filePath.includes('test');
	}

	async runTests(filePaths?: string[]): Promise<TestResult[]> {
		const args = ['test', '-json'];

		if (filePaths && filePaths.length > 0) {
			// Go uses package paths, not file paths
			const packages = [
				...new Set(
					filePaths.map((path) => path.replace(/\/[^/]*_test\.go$/, '') || '.'),
				),
			];
			args.push(...packages);
		} else {
			args.push('./...');
		}

		try {
			const { stdout } = await this.executeCommand('go', args);
			return this.parseGoTestOutput(stdout);
		} catch (error) {
			console.error('Go test execution failed:', error);
			return [];
		}
	}

	private parseGoTestOutput(output: string): TestResult[] {
		const results: TestResult[] = [];

		try {
			const lines = output.split('\n').filter((line) => line.trim());

			for (const line of lines) {
				if (line.startsWith('{')) {
					try {
						const data = JSON.parse(line);

						if (data.Action === 'pass' || data.Action === 'fail') {
							results.push({
								id: `${data.Package}::${data.Test}`,
								name: data.Test || 'unknown',
								status: data.Action === 'pass' ? 'pass' : 'fail',
								duration: data.Elapsed || 0,
								file: data.Package || 'unknown',
								line: undefined,
								error: data.Output,
								stack: undefined,
							});
						}
					} catch {
						// ignore JSON parse errors for individual lines
					}
				}
			}
		} catch (error) {
			console.warn('Failed to parse Go test output:', error);
		}

		return results;
	}
}

import type { TestResult } from '../types/TDDTypes.js';
import type { TestRunConfiguration } from './BaseTestReporter.js';
import { BaseTestReporter } from './BaseTestReporter.js';

export class MockTestReporter extends BaseTestReporter {
	name = 'mock';
	language = 'mock';
	private mockResults: Map<string, TestResult[]> = new Map();
	private watchCallback?: (results: Map<string, TestResult[]>) => void;

	constructor(config: TestRunConfiguration) {
		super(config);
		this.setupMockData();
	}

	detectsTestFiles(_filePath: string): boolean {
		return true; // Mock reporter accepts all files
	}

	async runTests(_filePaths?: string[]): Promise<TestResult[]> {
		// Simulate test execution delay
		await this.delay(100);

		const allResults: TestResult[] = [];
		for (const [, results] of this.mockResults) {
			allResults.push(...results);
		}
		return allResults;
	}

	async runTestsForFile(filePath: string): Promise<TestResult[]> {
		// Simulate test execution delay
		await this.delay(100);

		return this.mockResults.get(filePath) || [];
	}

	async runAllTests(): Promise<Map<string, TestResult[]>> {
		// Simulate test execution delay
		await this.delay(200);

		return new Map(this.mockResults);
	}

	async startWatching(
		callback: (results: Map<string, TestResult[]>) => void,
	): Promise<void> {
		this.watchCallback = callback;
		// Simulate initial watch callback
		setTimeout(() => {
			if (this.watchCallback) {
				this.watchCallback(new Map(this.mockResults));
			}
		}, 50);
	}

	async stopWatching(): Promise<void> {
		this.watchCallback = undefined;
	}

	getReporterInfo(): Array<{
		name: string;
		language: string;
		available: boolean;
	}> {
		return [
			{ name: 'mock-vitest', language: 'typescript', available: true },
			{ name: 'mock-pytest', language: 'python', available: true },
			{ name: 'mock-rust-test', language: 'rust', available: true },
		];
	}

	// Mock data setup
	private setupMockData(): void {
		this.mockResults.set('src/example.test.ts', [
			{
				id: 'example-test-1',
				name: 'example test 1',
				status: 'pass',
				duration: 10,
				file: 'src/example.test.ts',
				line: 5,
			},
			{
				id: 'example-test-2',
				name: 'example test 2',
				status: 'fail',
				duration: 15,
				file: 'src/example.test.ts',
				line: 10,
				error: 'Expected true but got false',
				stack: 'at example.test.ts:10:5',
			},
		]);

		this.mockResults.set('src/implementation.test.ts', [
			{
				id: 'implementation-test',
				name: 'implementation test',
				status: 'pass',
				duration: 8,
				file: 'src/implementation.test.ts',
				line: 3,
			},
		]);
	}

	// Add mock test results for testing
	addMockTestResult(filePath: string, result: TestResult): void {
		if (!this.mockResults.has(filePath)) {
			this.mockResults.set(filePath, []);
		}
		const results = this.mockResults.get(filePath);
		if (results) {
			results.push(result);
		}
	}

	// Clear mock results
	clearMockResults(): void {
		this.mockResults.clear();
	}

	// Trigger watch callback manually for testing
	triggerWatchCallback(): void {
		if (this.watchCallback) {
			this.watchCallback(new Map(this.mockResults));
		}
	}

	private delay(ms: number): Promise<void> {
		return new Promise((resolve) => setTimeout(resolve, ms));
	}
}
